INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1,'1','அகர முதல எழுத்தெல்லாம் ஆதி<br />பகவன் முதற்றே உலகு','அகரம் எழுத்துக்களுக்கு முதன்மை; ஆதிபகவன், உலகில் வாழும் உயிர்களுக்கு முதன்மை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(2,'2','கற்றதனால் ஆய பயனென்கொல் வாலறிவன்<br />நற்றாள் தொழாஅர் எனின்','தன்னைவிட அறிவில் மூத்த பெருந்தகையாளரின் முன்னே வணங்கி நிற்கும் பண்பு இல்லாவிடில் என்னதான் ஒருவர் கற்றிருந்தாலும் அதனால் என்ன பயன்? ஒன்றுமில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(3,'3','மலர்மிசை ஏகினான் மாணடி சேர்ந்தார்<br />நிலமிசை நீடுவாழ் வார்','மலர் போன்ற மனத்தில் நிறைந்தவனைப் பின்பற்றுவோரின் புகழ்வாழ்வு, உலகில் நெடுங்காலம் நிலைத்து நிற்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(4,'4','வேண்டுதல்வேண் டாமை இலானடி சேர்ந்தார்க்கு<br />யாண்டும் இடும்பை இல','விருப்பு வெறுப்பற்றுத் தன்னலமின்றித் திகழ்கின்றவரைப் பின்பற்றி நடப்பவர்களுக்கு எப்போதுமே துன்பம் ஏற்படுவதில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(5,'5','இருள்சேர் இருவினையும் சேரா இறைவன்<br />பொருள்சேர் புகழ்புரிந்தார் மாட்டு','இறைவன் என்பதற்குரிய பொருளைப் புரிந்து கொண்டு புகழ் பெற விரும்புகிறவர்கள், நன்மை தீமைகளை ஒரே அளவில் எதிர் கொள்வார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(6,'6','பொறிவாயில் ஐந்தவித்தான் பொய்தீர் ஒழுக்க<br />நெறிநின்றார் நீடுவாழ் வார்','மெய், வாய், கண், மூக்கு, செவி எனும் ஐம்பொறிகளையும் கட்டுப்படுத்திய தூயவனின் உண்மையான ஒழுக்கமுடைய நெறியைப் பின்பற்றி நிற்பவர்களின் புகழ்வாழ்வு நிலையானதாக அமையும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(7,'7','தனக்குவமை இல்லாதான் தாள்சேர்ந்தார்க் கல்லால்<br />மனக்கவலை மாற்றல் அரிது','ஒப்பாரும் மிக்காருமில்லாதவனுடைய அடியொற்றி நடப்பவர்களைத் தவிர, மற்றவர்களின் மனக்கவலை தீர வழியேதுமில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(8,'8','அறவாழி அந்தணன் தாள்சேர்ந்தார்க் கல்லால்<br />பிறவாழி நீந்தல் அரிது','அந்தணர் என்பதற்குப் பொருள் சான்றோர் என்பதால், அறக்கடலாகவே விளங்கும் அந்தச் சான்றோரின் அடியொற்றி நடப்பவர்க்கேயன்றி, மற்றவர்களுக்குப் பிற துன்பக் கடல்களைக் கடப்பது என்பது எளிதான காரியமல்ல',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(9,'9','கோளில் பொறியின் குணமிலவே எண்குணத்தான்<br />தாளை வணங்காத் தலை','உடல், கண், காது, மூக்கு, வாய் எனும் ஐம்பொறிகள் இருந்தும், அவைகள் இயங்காவிட்டால் என்ன நிலையோ அதே நிலைதான் ஈடற்ற ஆற்றலும் பண்பும் கொண்டவனை வணங்கி நடக்காதவனின் நிலையும் ஆகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(10,'10','பிறவிப் பெருங்கடல் நீந்துவர் நீந்தார்<br />இறைவன் அடிசேரா தார்','வாழ்க்கை எனும் பெருங்கடலை நீந்திக் கடக்க முனைவோர், தலையானவனாக இருப்பவனின் அடி தொடர்ந்து செல்லாவிடில் நீந்த முடியாமல் தவிக்க நேரிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(11,'11','வானின் றுலகம் வழங்கி வருதலால்<br />தானமிழ்தம் என்றுணரற் பாற்று','உலகத்தை வாழ வைப்பது மழையாக அமைந்திருப்பதால் அதுவே அமிழ்தம் எனப்படுகிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(12,'12','துப்பார்க்குத் துப்பாய துப்பாக்கித் துப்பார்க்குத்<br />துப்பாய தூஉ மழை','யாருக்கு உணவுப் பொருள்களை விளைவித்துத்தர மழை பயன்படுகிறதோ, அவர்களுக்கே அந்த மழை அவர்கள் அருந்தும் உணவாகவும் ஆகி அரிய தியாகத்தைச் செய்கிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(13,'13','விண்இன்று பொய்ப்பின் விரிநீர் வியனுலகத்து<br />உள்நின் றுடற்றும் பசி','கடல்நீர் சூழ்ந்த உலகமாயினும், மழைநீர் பொய்த்து விட்டால் பசியின் கொடுமை வாட்டி வதைக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(14,'14','ஏரின் உழாஅர் உழவர் புயல்என்னும்<br />வாரி வளங்குன்றிக் கால்','மழை என்னும் வருவாய் வளம் குன்றிவிட்டால், உழவுத் தொழில் குன்றி விடும��',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(15,'15','கெடுப்பதூஉம் கெட்டார்க்குச் சார்வாய்மற் றாங்கே<br />எடுப்பதூஉம் எல்லாம் மழை','பெய்யாமல் விடுத்து உயிர்களின் வாழ்வைக் கெடுக்கக் கூடியதும், பெய்வதன் காரணமாக உயிர்களின் நலிந்த வாழ்வுக்கு வளம் சேர்ப்பதும் மழையே ஆகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(16,'16','விசும்பின் துளிவீழின் அல்லால்மற் றாங்கே<br />பசும்புல் தலைகாண் பரிது','விண்ணிலிருந்து மழைத்துளி விழுந்தாலன்றி மண்ணில் பசும்புல் தலை காண்பது அரிதான ஒன்றாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(17,'17','நெடுங்கடலும் தன்நீர்மை குன்றும் தடிந்தெழிலி<br />தான்நல்கா தாகி விடின்','ஆவியான கடல்நீர் மேகமாகி அந்தக் கடலில் மழையாகப் பெய்தால்தான் கடல்கூட வற்றாமல் இருக்கும் மனித சமுதாயத்திலிருந்து புகழுடன் உயர்ந்தவர்களும் அந்தச் சமுதாயத்திற்கே பயன்பட்டால்தான் அந்தச் சமுதாயம் வாழும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(18,'18','சிறப்பொடு பூசனை செல்லாது வானம்<br />வறக்குமேல் வானோர்க்கும் ஈண்டு','வானமே பொய்த்து விடும்போது, அதன்பின்னர் அந்த வானத்தில் வாழ்வதாகச் சொல்லப்படுகிறவர்களுக்கு விழாக்கள் ஏது? வழிபாடுதான் ஏது?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(19,'19','தானம் தவமிரண்டும் தங்கா வியனுலகம்<br />வானம் வழங்கா தெனின்','இப்பேருலகில் மழை பொய்த்து விடுமானால் அது, பிறர் பொருட்டுச் செய்யும் தானத்திற்கும், தன்பொருட்டு மேற்கொள்ளும் நோன்புக்கும் தடங்கலாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(20,'20','நீரின் றமையா துலகெனின் யார்யார்க்கும்<br />வானின் றமையா தொழுக்கு','உலகில் மழையே இல்லையென்றால் ஒழுக்கமே கெடக்கூடும் என்ற நிலை இருப்பதால், நீரின் இன்றியமையாமையை உணர்ந்து செயல்பட வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(21,'21','ஒழுக்கத்து நீத்தார் பெருமை விழுப்பத்து<br />வேண்டும் பனுவல் துணிவு','ஒழுக்கத்தில் உறுதியான துறவிகளின் பெருமை, சான்றோர் நூலில் விருப்பமுடனும், உயர்வாகவும் இடம் பெறும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(22,'22','துறந்தார் பெருமை துணைக்கூறின் வையத்து<br />இறந்தாரை எண்ணிக்கொண் டற்று','உலகில் இறந்தவர்களின் எண்ணிக்கை எவ்வளவு என்று கூற முடியுமா? அதுபோலத்தான் உண்மையாகவே பற்றுகளைத் துறந்த உத்தமர்களின் பெருமையையும் அளவிடவே முடியாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(23,'23','இருமை வகைதெரிந் தீண்டறம் பூண்டார்<br />பெருமை பிறங்கிற் றுலகு','நன்மை எது, தீமை எது என்பதை ஆய்ந்தறிந்து நன்மைகளை மேற்கொள்பவர்களே உலகில் பெருமைக்குரியவர்களாவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(24,'24','உரனென்னுந் தோட்டியான் ஓரைந்தும் காப்பான்<br />வரனென்னும் வைப்பிற்கோர் வித்து','உறுதியென்ற அங்குசம் கொண்டு, ஐம்பொறிகளையும் அடக்கிக் காப்பவன், துறவறம் எனும் நிலத்திற்கு ஏற்ற விதையாவான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(25,'25','ஐந்தவித்தான் ஆற்றல் அகல்விசும்பு ளார்கோமான்<br />இந்திரனே சாலுங் கரி','புலன்களை அடக்க முடியாமல் வழிதவறிச் சென்றிடும் மனிதனுக்குச் சான்றாக இந்திரன் விளங்கி, ஐம்புலன்களால் ஏற்படும் ஆசைகளைக் கட்டுப்படுத்தியதால் வான்புகழ் கொண்டவர்களின் ஆற்றலை எடுத்துக் காட்டுகிறான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(26,'26','செயற்கரிய செய்வார் பெரியர் சிறியர்<br />செயற்கரிய செய்கலா தார்','பெருமை தரும் செயல்களைப் புரிவோரைப் பெரியோர் என்றும், சிறுமையான செயல்களையன்றிப் பெருமைக்குரிய செயல்களைச் செய்யாதவர்களைச் சிறியோர் என்றும் வரையறுத்துவிட முடியும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(27,'27','சுவையொளி ஊறோசை நாற்றமென் றைந்தின்<br />வகைதெரிவான் கட்டே உலகு','ஐம்புலன்களின் இயல்பை உணர்ந்து அவற்றை அடக்கியாளும் திறன் கொண்டவனையே உலகம் போற்றும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(28,'28','நிறைமொழி ��ாந்தர் பெருமை நிலத்து<br />மறைமொழி காட்டி விடும்','சான்றோர்களின் பெருமையை, இந்த உலகில் அழியாமல் நிலைத்து நிற்கும் அவர்களது அறவழி நூல்களே எடுத்துக் காட்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(29,'29','குணமென்னுங் குன்றேறி நின்றார் வெகுளி<br />கணமேயுங் காத்தல் அரிது','குணக்குன்றுகளான பெரியவர்கள் கோபம் கொண்டால் அந்தக் கோபம் அவர்கள் உள்ளத்தில் ஒரு கணம் கூட நிலைத்து நிற்காது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(30,'30','அந்தணர் என்போர் அறவோர்மற் றெவ்வுயிர்க்கும்<br />செந்தண்மை பூண்டொழுக லான்','அனைத்து உயிர்களிடத்திலும் அன்புகொண்டு அருள் பொழியும் சான்றோர் எவராயினும் அவர் அந்தணர் எனப்படுவார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(31,'31','சிறப்பீனும் செல்வமும் ஈனும் அறத்தினூஉங்கு<br />ஆக்கம் எவனோ உயிர்க்கு','சிறப்பையும், செழிப்பையும் தரக்கூடிய அறவழி ஒன்றைத்தவிர ஆக்கமளிக்கக் கூடிய வழி வேறென்ன இருக்கிறது?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(32,'32','அறத்தினூஉங் காக்கமும் இல்லை அதனை<br />மறத்தலின் ஊங்கில்லை கேடு','நன்மைகளின் விளைநிலமாக இருக்கும் அறத்தைப் போல் ஒருவருடைய வாழ்க்கைக்கு ஆக்கம் தரக்கூடியது எதுவுமில்லை; அந்த அறத்தை மறப்பதை விடத் தீமையானதும் வேறில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(33,'33','ஒல்லும் வகையான அறவினை ஓவாதே<br />செல்லும்வாய் எல்லாஞ் செயல்','செய்யக்கூடிய செயல்கள் எவை ஆயினும், அவை எல்லா இடங்களிலும் தொய்வில்லாத அறவழியிலேயே செய்யப்பட வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(34,'34','மனத்துக்கண் மாசிலன் ஆதல் அனைத்தறன்<br />ஆகுல நீர பிற','மனம் தூய்மையாக இருப்பதே அறம்; மற்றவை ஆரவாரத்தைத் தவிர வேறொன்றுமில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(35,'35','அழுக்கா றவாவெகுளி இன்னாச்சொல் நான்கும்<br />இழுக்கா இயன்ற தறம்','பொறாமை, பேராசை, பொங்கும் கோபம், புண்படுத்தும் சொல் ஆகிய இந்த நான்கும் அறவழிக்குப் பொருந்தாதவைகளாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(36,'36','அன்றறிவாம் என்னா தறஞ்செய்க மற்றது<br />பொன்றுங்கால் பொன்றாத் துணை','பிறகு பார்த்துக்கொள்ளலாம் என்று நாள் கடத்தாமல் அறவழியை மேற்கொண்டால் அது ஒருவர் இறந்தபின் கூட அழியாப் புகழாய் நிலைத்துத் துணை நிற்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(37,'37','அறத்தா றிதுவென வேண்டா சிவிகை<br />பொறுத்தானோ டூர்ந்தான் இடை','அறவழியில் நடப்பவர்கள் பல்லக்கில் உட்கார்ந்து செல்பவர்களைப் போல வாழ்க்கையில் வரும் இன்ப துன்பங்கள் இரண்டையும் எளியவாகக் கருதி மகிழ்வுடன் பயணத்தை மேற்கொள்வார்கள் தீய வழிக்குத் தங்களை ஆட்படுத்திக் கொண்டவர்களோ பல்லக்கைத் தூக்கிச் சுமப்பவர்களைப் போல இன்பத்திலும் அமைதி கொள்ளாமல், துன்பத்தையும் தாங்கிக் கொள்ளும் மனப்பக்குவமின்றி வாழ்வையே பெரும் சுமையாகக் கருதுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(38,'38','வீழ்நாள் படாஅமை நன்றாற்றின் அஃதொருவன்<br />வாழ்நாள் வழியடைக்கும் கல்','பயனற்றதாக ஒருநாள்கூடக் கழிந்து போகாமல், தொடர்ந்து நற்செயல்களில் ஈடுபடுபவருக்கு வாழ்க்கைப் பாதையைச் சீராக்கி அமைத்துத் தரும் கல்லாக அந்த நற்செயல்களே விளங்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(39,'39','அறத்தான் வருவதே இன்பமற் றெல்லாம்<br />புறத்த புகழும் இல','தூய்மையான நெஞ்சுடன் நடத்தும் அறவழி வாழ்க்கையில் வருகின்ற புகழால் ஏற்படுவதே இன்பமாகும் அதற்கு மாறான வழியில் வருவது புகழும் ஆகாது; இன்பமும் ஆகாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(40,'40','செயற்பால தோரும் அறனே ஒருவற்கு<br />உயற்பால தோரும் பழி','பழிக்கத் தக்கவைகளைச் செய்யாமல் பாராட்டத்தக்க அறவழிச் செயல்களில் நாட்டம் கொள்வதே ஒருவர்க்குப் புகழ் சேர்க்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(41,'41','இல்வாழ்வான் என்பான் இயல்புடைய மூவர்க்கும்<br />நல்லாற்றின் நின்ற துணை','பெற்றோர், வாழ்க்கைத் துணை, குழந்தைகள் என இயற்கையாக அமைந்திடும் மூவர்க்கும் துணையாக இருப்பது இல்லறம் நடத்துவோர் கடமையாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(42,'42','துறந்தார்க்கும் துவ்வா தவர்க்கும் இறந்தார்க்கும்<br />இல்வாழ்வான் என்பான் துணை','பற்றற்ற துறவிகட்கும், பசியால் வாடுவோர்க்கும், பாதுகாப்பற்றவர்க்கும் இல்லற வாழ்வு நடத்துவோர் துணையாக இருத்தல் வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(43,'43','தென்புலத்தார் தெய்வம் விருந்தொக்கல் தானென்றாங்கு<br />ஐம்புலத்தா றோம்பல் தலை','வாழ்ந்து மறைந்தோரை நினைவுகூர்தல், வாழ்வாங்கு வாழ்வோரைப் போற்றுதல், விருந்தோம்பல், சுற்றம் பேணல் ஆகிய கடமைகளை நிறைவேற்றத் தன்னை நிலைப்படுத்திக் கொள்ளல் எனப்படும் ஐவகை அறநெறிகளும் இல்வாழ்வுக்குரியனவாம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(44,'44','பழியஞ்சிப் பாத்தூண் உடைத்தாயின் வாழ்க்கை<br />வழியெஞ்சல் எஞ்ஞான்றும் இல','பழிக்கு அஞ்சாமல் சேர்த்த பொருள் கணக்கின்றி இருப்பினும் அதைவிட, பழிக்கு அஞ்சிச் சேர்த்த பொருளைப் பகுத்து உண்ணும் பண்பிலேதான் வாழ்க்கையின் ஒழுக்கமே இருக்கிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(45,'45','அன்பும் அறனும் உடைத்தாயின் இல்வாழ்க்கை<br />பண்பும் பயனும் அது','இல்வாழ்க்கை பண்புடையதாகவும் பயனுடையதாகவும் விளங்குவதற்கு அன்பான உள்ளமும் அதையொட்டிய நல்ல செயல்களும் தேவை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(46,'46','அறத்தாற்றின் இல்வாழ்க்கை ஆற்றின் புறத்தாற்றில்<br />போஒய்ப் பெறுவ தெவன்','அறநெறியில் இல்வாழ்க்கையை அமைத்துக் கொண்டவர்கள் பெற்றிடும் பயனை, வேறு நெறியில் சென்று பெற்றிட இயலுமோ? இயலாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(47,'47','இயல்பினான் இல்வாழ்க்கை வாழ்பவன் என்பான்<br />முயல்வாருள் எல்லாம் தலை','நல்வாழ்வுக்கான முயற்சிகளை மேற்கொள்வோரில் தலையானவராகத் திகழ்பவர், இல்வாழ்வின் இலக்கணமுணர்ந்து அதற்கேற்ப வாழ்பவர்தான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(48,'48','ஆற்றின் ஒழுக்கி அறனிழுக்கா இல்வாழ்க்கை<br />நோற்பாரின் நோன்மை உடைத்து','தானும் அறவழியில் நடந்து, பிறரையும் அவ்வழியில் நடக்கச் செய்திடுவோரின் இல்வாழ்க்கை, துறவிகள் கடைப்பிடிக்கும் நோன்பைவிடப் பெருமையுடையதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(49,'49','அறன்எனப் பட்டதே இல்வாழ்க்கை அஃதும்<br />பிறன்பழிப்ப தில்லாயின் நன்று','பழிப்புக்கு இடமில்லாத இல்வாழ்க்கை இல்லறம் எனப் போற்றப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(50,'50','வையத்துள் வாழ்வாங்கு வாழ்பவன் வான்உறையும்<br />தெய்வத்துள் வைக்கப் படும்','தெய்வத்துக்கென எத்தனையோ அருங்குணங்கள் கூறப்படுகின்றன உலகில் வாழ வேண்டிய அறநெறியில் நின்று வாழ்கிறவன் வானில் வாழ்வதாகச் சொல்லப்படும் தெய்வத்துக்கு இணையாக வைத்து மதிக்கப்படுவான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(51,'51','மனைத்தக்க மாண்புடையள் ஆகித்தற் கொண்டான்<br />வளத்தக்காள் வாழ்க்கைத் துணை','இல்லறத்திற்குரிய பண்புகளுடன், பொருள் வளத்துக்குத் தக்கவாறு குடும்பம் நடத்துபவள், கணவனின் வாழ்வுக்குப் பெருந்துணையாவாள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(52,'52','மனைமாட்சி இல்லாள்கண் இல்லாயின் வாழ்க்கை<br />எனைமாட்சித் தாயினும் இல்','நற்பண்புள்ள மனைவி அமையாத இல்வாழ்க்கை எவ்வளவு சிறப்புடையதாக இருந்தாலும் அதற்குத் தனிச்சிறப்புக் கிடையாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(53,'53','இல்லதென் இல்லவள் மாண்பானால் உள்ளதென்<br />இல்லவள் மாணாக் கடை','நல்ல பண்புடைய மனைவி அமைந்த வாழ்க்கையில் எல்லாம் இருக்கும் அப்படியொரு மனைவி அமையாத வாழ்க்கையில் எதுவுமே இருக்காது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(54,'54','பெண்ணின் பெருந்தக்க யாவுள கற்பென்னும்<br />திண்மைஉண் டாகப் பெறின்','கற்பென்னும் திண்மை ��ொண்ட பெண்மையின் உறுதிப் பண்பைப் பெற்றுவிட்டால், அதைவிடப் பெருமைக்குரியது வேறு யாது?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(55,'55','தெய்வம் தொழாஅள் கொழுநற் றொழுதெழுவாள்<br />பெய்யெனப் பெய்யும் மழை','கணவன் வாக்கினைக் கடவுள் வாக்கினை விட மேலானதாகக் கருதி அவனையே தொழுதிடும் மனைவி பெய் என ஆணையிட்டவுடன் அஞ்சி நடுங்கிப் பெய்கின்ற மழையைப் போலத் தன்னை அடிமையாக எண்ணிக் கொள்பவளாவாள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(56,'56','தற்காத்துத் தற்கொண்டாற் பேணித் தகைசான்ற<br />சொற்காத்துச் சோர்விலாள் பெண்','கற்புநெறியில் தன்னையும் தன் கணவனையும் காத்துக் கொண்டு, தமக்குப் பெருமை சேர்க்கும் புகழையும் காப்பாற்றிக் கொள்வதில் உறுதி குலையாமல் இருப்பவள் பெண்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(57,'57','சிறைகாக்கும் காப்பெவன் செய்யும் மகளிர்<br />நிறைகாக்கும் காப்பே தலை','தம்மைத் தாமே காத்துக்கொண்டு சிறந்த பண்புடன் வாழும் மகளிரை அடிமைகளாக நடத்த எண்ணுவது அறியாமையாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(58,'58','பெற்றாற் பெறின்பெறுவர் பெண்டிர் பெருஞ்சிறப்புப்<br />புத்தேளிர் வாழும் உலகு','நற்பண்பு பெற்றவனைக் கணவனாகப் பெற்றால், பெண்டிர்க்கு இல்வாழ்க்கையெனும் புதிய உலகம் பெருஞ் சிறப்பாக அமையும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(59,'59','புகழ்புரிந் தில்லிலோர்க் கில்லை இகழ்வார்முன்<br />ஏறுபோல் பீடு நடை','புகழுக்குரிய இல்வாழ்க்கை அமையாதவர்கள், தம்மைப் பழித்துப் பேசுவோர் முன்பு தலைநிமிர்ந்து நடக்க முடியாமல் குன்றிப் போய் விடுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(60,'60','மங்கலம் என்ப மனைமாட்சி மற்றதன்<br />நன்கலம் நன்மக்கட் பேறு','குடும்பத்தின் பண்பாடுதான் இல்வாழ்க்கையின் சிறப்பு; அதற்கு மேலும் சிறப்பு நல்ல பிள்ளைகளைப் பெற்றிருப்பது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(61,'61','பெறுமவற்றுள் யாமறிவ தில்லை அறிவறிந்த<br />மக்கட்பே றல்ல பிற','அறிவில் சிறந்த நல்ல பிள்ளைகளைவிட இல்வாழ்க்கையில் சிறந்த பேறு வேறு எதுவுமில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(62,'62','எழுபிறப்பும் தீயவை தீண்டா பழிபிறங்காப்<br />பண்புடை மக்கட் பெறின்','பெற்றெடுக்கும் மக்கள் பழிபடராத பண்புடையவர்களாக இருப்பின், ஏழேழு தலைமுறை எனும் அளவுக்குக் காலமெல்லாம் எந்தத் தீமையும் தீண்டாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(63,'63','தம்பொருள் என்பதம் மக்கள் அவர்பொருள்<br />தந்தம் வினையான் வரும்','தம் பொருள் என்பது தம்மக்களையேயாம் அம்மக்களின் பொருள்கள் அவரவர் செயல்களின் விளைவாக வரக்கூடியவை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(64,'64','அமிழ்தினும் ஆற்ற இனிதேதம் மக்கள்<br />சிறுகை அளாவிய கூழ்','சிறந்த பொருளை அமிழ்தம் எனக் குறிப்பிட்டாலுங்கூடத் தம்முடைய குழந்தைகளின் பிஞ்சுக்கரத்தால் அளாவப்பட்ட கூழ் அந்த அமிழ்தத்தைவிடச் சுவையானதாகிவிடுகிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(65,'65','மக்கள்மெய் தீண்டல் உடற்கின்பம் மற்றவர்<br />சொற்கேட்டல் இன்பம் செவிக்கு','தம் குழந்தைகளைத் தழுவி மகிழ்வது உடலுக்கு இன்பத்தையும், அந்தக் குழந்தைகளின் மழலை மொழி கேட்பது செவிக்கு இன்பத்தையும் வழங்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(66,'66','குழலினி தியாழினி தென்பதம் மக்கள்<br />மழலைச்சொல் கேளா தவர்','தங்கள் குழந்தைகளின் மழலைச் சொல்லைக் கேட்காதவர்கள்தான் குழலோசை, யாழோசை ஆகிய இரண்டும் இனிமையானவை என்று கூறுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(67,'67','தந்தை மகற்காற்றும் நன்றி அவையத்து<br />முந்தி இருப்பச் செயல்','தந்தை தன் மக்களுக்குச் செய்யவேண்டிய நல்லுதவி அவர்களை அறிஞர்கள் அவையில் புகழுடன் விளங்குமாறு ஆக்குதலே ஆகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(68,'68','தம்மின்தம் மக்கள் அறிவுடைமை மாநிலத்து<br />மன்னுயிர்க் கெல்லாம் இனிது','பெற்றோரைக் ��ாட்டிலும் பிள்ளைகள் அறிவிற் சிறந்து விளங்கினால், அது பெற்றோருக்கு மட்டுமேயன்றி உலகில் வாழும் அனைவருக்கும் அக மகிழ்ச்சி தருவதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(69,'69','ஈன்ற பொழுதின் பெரிதுவக்கும் தன்மகனைச்<br />சான்றோன் எனக்கேட்ட தாய்','நல்ல மகனைப் பெற்றெடுத்தவள் என்று ஊரார் பாராட்டும் பொழுது அவனைப் பெற்றபொழுது அடைந்த மகிழ்ச்சியைவிட அதிக மகிழ்ச்சியை அந்தத் தாய் அடைவாள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(70,'70','மகன் தந்தைக் காற்றும் உதவி இவன்தந்தை<br />என்நோற்றான் கொல்லெனும் சொல்','``ஆகா! இவனைப் பிள்ளையாகப் பெற்றது இவன் தந்தை பெற்ற பெறும்பேறு, என்று ஒரு மகன் புகழப்படுவதுதான், அவன் தன்னுடைய தந்தைக்குச் செய்யக்கூடிய கைம்மாறு எனப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(71,'71','அன்பிற்கும் உண்டோ அடைக்குந்தாழ் ஆர்வலர்<br />புண்கணீர் பூசல் தரும்','உள்ளத்தில் இருக்கும் அன்பைத் தாழ்ப்பாள் போட்டு அடைத்து வைக்க முடியாது அன்புக்குரியவரின் துன்பங்காணுமிடத்து, கண்ணீர்த்துளி வாயிலாக அது வெளிப்பட்டுவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(72,'72','அன்பிலார் எல்லாம் தமக்குரியர் அன்புடையார்<br />என்பும் உரியர் பிறர்க்கு','அன்பு இல்லாதவர், எல்லாம் தமக்கே என உரிமை கொண்டாடுவர்; அன்பு உடையவரோ தம் உடல், பொருள், ஆவி ஆகிய அனைத்தும் பிறருக்கென எண்ணிடுவர்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(73,'73','அன்போ டியைந்த வழக்கென்ப ஆருயிர்க்கு<br />என்போ டியைந்த தொடர்பு','உயிரும் உடலும்போல் அன்பும் செயலும் இணைந்திருப்பதே உயர்ந்த பொருத்தமாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(74,'74','அன்பீனும் ஆர்வம் உடைமை அதுஈனும்<br />நண்பென்னும் நாடாச் சிறப்பு','அன்பு பிறரிடம் பற்றுள்ளம் கொள்ளச் செய்யும் அந்த உள்ளம், நட்பு எனும் பெருஞ்சிறப்பை உருவாக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(75,'75','அன்புற் றமர்ந்த வழக்கென்ப வையகத்து<br />இன்புற்றார் எய்தும் சிறப்பு','உலகில் இன்புற்று வாழ்கின்றவர்க்கு வாய்க்கும் சிறப்பு, அவர் அன்புள்ளம் கொண்டவராக விளங்குவதன் பயனே என்று கூறலாம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(76,'76','அறத்திற்கே அன்புசார் பென்ப அறியார்<br />மறத்திற்கும் அஃதே துணை','வீரச் செயல்களுக்கும் அன்பு துணையாகத் திகழ்கிறது என்பதை அறியாதவர்களே, அறச் செயல்களுக்கு மட்டுமே அன்பு துணையாக இருப்பதாகக் கூறுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(77,'77','என்பி லதனை வெயில்போலக் காயுமே<br />அன்பி லதனை அறம்','அறம் எதுவென அறிந்தும் அதனைக் கடைப்பிடிக்காதவரை, அவரது மனச்சாட்சியே வாட்டி வதைக்கும் அது வெயிலின் வெம்மை புழுவை வாட்டுவதுபோல இருக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(78,'78','அன்பகத் தில்லா உயிர்வாழ்க்கை வன்பாற்கண்<br />வற்றல் மரந்தளிர்த் தற்று','மனத்தில் அன்பு இல்லாதவருடைய வாழ்க்கை, பாலைவனத்தில் பட்டமரம் தளிர்த்தது போன்றது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(79,'79','புறத்துறுப் பெல்லாம் எவன்செய்யும் யாக்கை<br />அகத்துறுப் பன்பி லவர்க்கு','அன்பு எனும் அகத்து உறுப்பு இல்லாதவர்க்குப் புறத்து உறுப்புகள் அழகாக இருந்து என்ன பயன்?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(80,'80','அன்பின் வழிய துயிர்நிலை அஃதிலார்க்கு<br />என்புதோல் போர்த்த உடம்பு','அன்புநெஞ்சத்தின் வழியில் இயங்குவதே உயிருள்ள உடலாகும்; இல்லையேல், அது எலும்பைத் தோல் போர்த்திய வெறும் உடலேயாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(81,'81','இருந்தோம்பி இல்வாழ்வ தெல்லாம் விருந்தோம்பி<br />வேளாண்மை செய்தற் பொருட்டு','இல்லறத்தைப் போற்றி வாழ்வது, விருந்தினரை வரவேற்று, அவர்க்கு வேண்டிய உதவிகளைச் செய்வதற்காகவே',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(82,'82','விருந்து புறத்ததாத் தானுண்டல் சாவா<br />மருந்தெனினும் வேண்டற்பாற் றன்று','விருந்தினராக வந்தவர��� வெளியே விட்டுவிட்டுச் சாகாத மருந்தாக இருந்தாலும் அதனைத் தான் மட்டும் உண்பது விரும்பத் தக்க பண்பாடல்ல',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(83,'83','வருவிருந்து வைகலும் ஓம்புவான் வாழ்க்கை<br />பருவந்து பாழ்படுதல் இன்று','விருந்தினரை நாள்தோறும் வரவேற்று மகிழ்பவரின் வாழ்க்கை, அதன் காரணமாகத் துன்பமுற்றுக் கெட்டொழிவதில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(84,'84','அகனமர்ந்து செய்யாள் உறையும் முகனமர்ந்து<br />நல்விருந் தோம்புவான் இல்','மனமகிழ்ச்சியை முகமலர்ச்சியால் காட்டி விருந்தினரை வரவேற்பவர் வீட்டில் அமர்ந்து செல்வம் எனும் திருமகள் வாழ்வாள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(85,'85','வித்தும் இடல்வேண்டும் கொல்லோ விருந்தோம்பி<br />மிச்சில் மிசைவான் புலம்','விருந்தினர்க்கு முதலில் உணவளித்து மிஞ்சியதை உண்டு வாழும் பண்பாளன், தன் நிலத்திற்குரிய விதையைக்கூட விருந்தோம்பலுக்குப் பயன்படுத்தாமல் இருப்பானா?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(86,'86','செல்விருந் தோம்பி வருவிருந்து பார்த்திருப்பான்<br />நல்விருந்து வானத் தவர்க்கு','வந்த விருந்தினரை உபசரித்து அவர்களை வழியனுப்பி வைக்கும்போதே, மேலும் வரக்கூடிய விருந்தினரை ஆவலுடன் எதிர்நோக்கி நிற்பவனை, புகழ்வானில் இருப்போர் நல்ல விருந்தினன் என்று வரவேற்றுப் போற்றுவர்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(87,'87','இனைத்துணைத் தென்பதொன் றில்லை விருந்தின்<br />துணைத்துணை வேள்விப் பயன்','விருந்தினராக வந்தவரின் சிறப்பை எண்ணிப் பார்த்து விருந்தோம்பலை ஒரு வேள்வியாகவே கருதலாம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(88,'88','பரிந்தோம்பிப் பற்றற்றேம் என்பர் விருந்தோம்பி<br />வேள்வி தலைப்படா தார்','செல்வத்தைச் சேர்த்துவைத்து அதனை இழக்கும்போது, விருந்தோம்பல் எனும் வேள்விக்கு அது பயன்படுத்தப்படாமற் போயிற்றே என வருந்துவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(89,'89','உடைமையுள் இன்மை விருந்தோம்பல் ஓம்பா<br />மடமை மடவார்கண் உண்டு','விருந்தினரை வரவேற்றுப் போற்றத் தெரியாத அறிவற்றவர்கள் எவ்வளவு பணம் படைத்தவர்களாக இருந்தாலும் தரித்திரம் பிடித்தவர்களாகவே கருதப்படுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(90,'90','மோப்பக் குழையும் அனிச்சம் முகந்திரிந்து<br />நோக்கக் குழையும் விருந்து','அனிச்சம் எனப்படும் பூ, முகர்ந்தவுடன் வாடி விடக் கூடியது அதுபோல் சற்று முகங்கோணி வரவேற்றாலே விருந்தினர் வாடிவிடுவர்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(91,'91','இன்சொலால் ஈரம் அளைஇப் படிறிலவாம்<br />செம்பொருள் கண்டார்வாய்ச் சொல்','ஒருவர் வாயிலிருந்து வரும் சொல் அன்பு கலந்ததாகவும், வஞ்சனையற்றதாகவும், வாய்மையுடையதாகவும் இருப்பின் அதுவே இன்சொல் எனப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(92,'92','அகன்அமர்ந் தீதலின் நன்றே முகனமர்ந்து<br />இன்சொலன் ஆகப் பெறின்','முகம் மலர்ந்து இனிமையாகப் பேசுவது, அகம் குளிர்ந்து ஒன்றைக் கொடுப்பதை விட மேலான பண்பாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(93,'93','முகத்தான் அமர்ந்தின்து நோக்கி அகத்தானாம்<br />இன்சொ லினதே அறம்','முகம் மலர நோக்கி, அகம் மலர இனிய சொற்களைக் கூறுவதே அறவழியில் அமைந்த பண்பாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(94,'94','துன்புறூஉம் துவ்வாமை இல்லாகும் யார்மாட்டும்<br />இன்புறூஉம் இன்சொ லவர்க்கு','இன்சொல் பேசி எல்லோரிடத்திலும் கனிவுடன் பழகுவோர்க்கு `நட்பில் வறுமை எனும் துன்பமில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(95,'95','பணிவுடையன் இன்சொலன் ஆதல் ஒருவற்கு<br />அணியல்ல மற்றுப் பிற','அடக்கமான பண்பும், இனிமையாகப் பேசும் இயல்பும் தவிர, ஒருவருக்குச் சிறந்த அணிகலன் வேறு இருக்க முடியாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(96,'96','அல்லவை தேய அறம்பெருகும் நல்லவை<br />நாடி இனிய சொலின்','தீய செயல்களை அகற்றி அறநெறி தழைக்க��் செய்ய வேண்டுமானால், இனிய சொற்களைப் பயன்படுத்தி நல்வழி எதுவெனக் காட்ட வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(97,'97','நயன்ஈன்று நன்றி பயக்கும் பயன்ஈன்று<br />பண்பின் தலைப்பிரியாச் சொல்','நன்மையான பயனைத் தரக்கூடிய நல்ல பண்பிலிருந்து விலகாத சொற்கள் அவற்றைக் கூறுவோருக்கும் இன்பத்தையும், நன்மையையும் உண்டாக்கக் கூடியவைகளாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(98,'98','சிறுமையுள் நீங்கிய இன்சொல் மறுமையும்<br />இம்மையும் இன்பம் தரும்','சிறுமைத்தனமற்ற இனியசொல் ஒருவனுக்கு அவன் வாழும் போதும், வாழ்ந்து மறைந்த பிறகும் புகழைத் தரக்கூடியதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(99,'99','இன்சொல் இனிதீன்றல் காண்பான் எவன்கொலோ<br />வன்சொல் வழங்கு வது','இனிய சொற்கள் இன்பத்தை வழங்கும் என்பதை உணர்ந்தவர் அதற்கு மாறாக எதற்காகக் கடுஞ் சொற்களைப் பயன்படுத்த வேண்டும்?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(100,'100','இனிய உளவாக இன்னாத கூறல்<br />கனிஇருப்பக் காய்கவர்ந் தற்று','இனிமையான சொற்கள் இருக்கும்போது அவற்றை விடுத்துக் கடுமையாகப் பேசுவது கனிகளை ஒதுக்கி விட்டுக் காய்களைப் பறித்துத் தின்பதற்குச் சமமாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(101,'101','செய்யாமல் செய்த உதவிக்கு வையகமும்<br />வானகமும் ஆற்றல் அரிது','``வாராது வந்த மாமணி என்பதுபோல், ``செய்யாமற் செய்த உதவி என்று புகழத்தக்க அரிய உதவி வழங்கப்பட்டால், அதற்கு இந்த வானமும் பூமியும் கூட ஈடாக மாட்டா',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(102,'102','காலத்தி னாற்செய்த நன்றி சிறிதெனினும்<br />ஞாலத்தின் மாணப் பெரிது','தேவைப்படும் காலத்தில் செய்யப்படும் உதவி சிறிதளவாக இருந்தாலும், அது உலகத்தைவிடப் பெரிதாக மதிக்கப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(103,'103','பயன்தூக்கார் செய்த உதவி நயன்தூக்கின்<br />நன்மை கடலின் பெரிது','என்ன பயன் கிடைக்கும் என்று எண்ணிப் பார்க்காமலே, அன்பின் காரணமாக ஒருவர் செய்த உதவியின் சிறப்பு கடலை விடப் பெரிது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(104,'104','தினைத்துணை நன்றி செயினும் பனைத்துணையாக்<br />கொள்வர் பயன்தெரி வார்','ஒருவர் செய்யும் தினையளவு நன்மையைக்கூட அதனால் பயன்பெறும் நன்றியுள்ளவர் பல்வேறு வகையில் பயன்படக்கூடிய பனையின் அளவாகக் கருதுவார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(105,'105','உதவி வரைத்தன் றுதவி உதவி<br />செயப்பட்டார் சால்பின் வரைத்து','உதவி என்பது, செய்யப்படும் அளவைப் பொருத்துச் சிறப்படைவதில்லை; அந்த உதவியைப் பெறுபவரின் பண்பைப் பொருத்தே அதன் அளவு மதிப்பிடப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(106,'106','மறவற்க மாசற்றார் கேண்மை துறவற்க<br />துன்பத்துள் துப்பாயார் நட்பு','மாசற்றவர்களின் உறவை மறக்கவும் கூடாது; துன்பத்தில் துணை நின்றவர் நட்பைத் துறக்கவும் கூடாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(107,'107','எழுமை எழுபிறப்பும் உள்ளுவர் தங்கண்<br />விழுமந் துடைத்தவர் நட்பு','ஏழேழு தலைமுறைக்கு என்றும் ஏழேழு பிறவிக்கு என்றும் மிகைப்படுத்திச் சொல்வதுபோல, ஒருவருடைய துன்பத்தைப் போக்கியவரின் தூய்மையான நட்பை நினைத்துப் போற்றுவதற்குக் கால எல்லையே கிடையாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(108,'108','நன்றி மறப்பது நன்றன்று நன்றல்லது<br />அன்றே மறப்பது நன்று','ஒருவர் நமக்குச் செய்த நன்மையை மறப்பது நல்லதல்ல; அவர் தீமை செய்திருந்தால் அதை மட்டும் அக்கணமே மறந்து விடுவது நல்லது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(109,'109','கொன்றன்ன இன்னா செயினும் அவர்செய்த<br />ஒன்றுநன் றுள்ளக் கெடும்','ஒருவர் செய்யும் மிகக் கொடுமையான தீமைகூட நமது உள்ளத்தைப் புண்படுத்தாமல் அகன்றுவிட வேண்டுமானால், அந்த ஒருவர் முன்னர் நமக்குச் செய்த நன்மையை மட்டும் நினைத்துப் பார்த்தாலே போதுமானது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(110,'110','எந்நன்றி கொன்���ார்க்கும் உய்வுண்டாம் உய்வில்லை<br />செய்ந்நன்றி கொன்ற மகற்கு','எந்த அறத்தை மறந்தார்க்கும் வாழ்வு உண்டு; ஆனால் ஒருவர் செய்த உதவியை மறந்தார்க்கு வாழ்வில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(111,'111','தகுதி யெனவொன்று நன்றே பகுதியாற்<br />பாற்பட் டொழுகப் பெறின்','பகைவர், அயலோர், நண்பர் எனப்பகுத்துப் பார்த்து ஒருதலைச் சார்பாக நிற்காமல் இருத்தலே நன்மை தரக்கூடிய நடுவுநிலைமை எனும் தகுதியாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(112,'112','செப்பம் உடையவன் ஆக்கஞ் சிதைவின்றி<br />எச்சத்திற் கேமாப் புடைத்து','நடுவுநிலையாளனின் செல்வத்திற்கு அழிவில்லை; அது, வழிவழித் தலைமுறையினர்க்கும் பயன் அளிப்பதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(113,'113','நன்றே தரினும் நடுவிகந்தாம் ஆக்கத்தை<br />அன்றே யொழிய விடல்','நடுவுநிலை தவறுவதால் ஏற்படக்கூடிய பயன் நன்மையையே தரக் கூடியதாக இருந்தாலும், அந்தப் பயனைக் கைவிட்டு நடுவுநிலையைத்தான் கடைப்பிடிக்க வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(114,'114','தக்கார் தகவிலர் என்ப தவரவர் <br />எச்சத்தாற் காணப் படும்','ஒருவர் நேர்மையானவரா அல்லது நெறி தவறி, நீதி தவறி நடந்தவரா என்பது அவருக்குப் பின் எஞ்சி நிற்கப்போகும் புகழ்ச் சொல்லைக் கொண்டோ அல்லது பழிச் சொல்லைக் கொண்டோதான் நிர்ணயிக்கப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(115,'115','கேடும் பெருக்கமும் இல்லல்ல நெஞ்சத்துக்<br />கோடாமை சான்றோர்க் கணி','ஒருவர்க்கு வாழ்வும், தாழ்வும் உலக இயற்கை; அந்த இரு நிலைமையிலும் நடுவுநிலையாக இருந்து உறுதி காட்டுவதே பெரியோர்க்கு அழகாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(116,'116','கெடுவல்யான் என்ப தறிகதன் நெஞ்சம்<br />நடுவொரீஇ அல்ல செயின்','நடுவுநிலைமை தவறிச் செயல்படலாம் என்று ஒரு நினைப்பு ஒருவனுக்கு வந்து விடுமானால் அவன் கெட்டொழியப் போகிறான் என்று அவனுக்கே தெரியவேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(117,'117','கெடுவாக வையா துலகம் நடுவாக<br />நன்றிக்கண் தங்கியான் தாழ்வு','நடுவுநிலைமை தவறாமல் அறவழியில் வாழ்கிற ஒருவருக்கு அதன் காரணமாகச் செல்வம் குவியாமல் வறுமை நிலை ஏற்படுமேயானால் அவரை உலகம் போற்றுமே தவிரத் தாழ்வாகக் கருதாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(118,'118','சமன்செய்து சீர்தூக்குங் கோல்போல் அமைந்தொருபாற்<br />கோடாமை சான்றோர்க் கணி','ஒரு பக்கம் சாய்ந்து விடாமல் நாணயமான தாரசு முள் போல இருந்து நியாயம் கூறுவதுதான் உண்மையான நடுவுநிலைமை என்பதற்கு அழகாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(119,'119','சொற்கோட்டம் இல்லது செப்பம் ஒருதலையா<br />உட்கோட்டம் இன்மை பெறின்','நேர்மையும் நெஞ்சுறுதியும் ஒருவர்க்கு இருந்தால் அவரது சொல்லில் நீதியும் நியாயமும் இருக்கும் அதற்குப் பெயர்தான் நடுவுநிலைமை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(120,'120','வாணிகம் செய்வார்க்கு வாணிகம் பேணிப்<br />பிறவும் தமபோல் செயின்','பிறர் பொருளாக இருப்பினும் அதனைத் தன் பொருளைப் போலவே கருதி நேர்மையுடன் வாணிகம் செய்தலே வணிக நெறியெனப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(121,'121','அடக்கம் அமரருள் உய்க்கும் அடங்காமை<br />ஆரிருள் உய்த்து விடும்','அடக்கம் அழியாத புகழைக் கொடுக்கும் அடங்காமை வாழ்வையே இருளாக்கி விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(122,'122','காக்க பொருளா அடக்கத்தை ஆக்கம்<br />அதனினூஉங் கில்லை உயிர்க்கு','மிக்க உறுதியுடன் காக்கப்படவேண்டியது அடக்கமாகும் அடக்கத்தைவிட ஆக்கம் தரக் கூடியது வேறொன்றும் இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(123,'123','செறிவறிந்து சீர்மை பயக்கும் அறிவறிந்<br />தாற்றின் அடங்கப் பெறின்','அறிந்து கொள்ள வேண்டியவற்றை அறிந்து அதற்கேற்ப அடக்கத்துடன் நடந்து கொள்பவரின் பண்பை உணர்ந்து பாராட்டுகள் குவியும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(124,'124','நிலையின் திரிய�� தடங்கியான் தோற்றம்<br />மலையினும் மாணப் பெரிது','உறுதியான உள்ளமும், அத்துடன் ஆர்ப்பாட்டமற்ற அடக்க உணர்வும் கொண்டவரின் உயர்வு, மலையைவிடச் சிறந்தது எனப் போற்றப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(125,'125','எல்லார்க்கும் நன்றாம் பணிதல் அவருள்ளும்<br />செல்வர்க்கே செல்வம் தகைத்து','பணிவு என்னும் பண்பு, எல்லார்க்கும் நலம் பயக்கும் ஏற்கனவே செல்வர்களாக இருப்பவர்களுக்கு அந்தப் பண்பு, மேலும் ஒரு செல்வமாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(126,'126','ஒருமையுள் ஆமைபோல் ஐந்தடக்கல் ஆற்றின்<br />எழுமையும் ஏமாப் புடைத்து','உறுப்புகளை ஓர் ஓட்டுக்குள் அடக்கிக் கொள்ளும் ஆமையைப் போல் ஐம்பொறிகளையும் அடக்கியாளும் உறுதி, காலமெல்லாம் வாழ்க்கைக்குக் காவல் அரணாக அமையும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(127,'127','யாகாவா ராயினும் நாகாக்க காவாக்காற்<br />சோகாப்பர் சொல்லிழுக்குப் பட்டு','ஒருவர் எதைக் காத்திட முடியாவிட்டாலும் நாவையாவது அடக்கிக் காத்திட வேண்டும் இல்லையேல் அவர் சொன்ன சொல்லே அவர் துன்பத்துக்குக் காரணமாகி விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(128,'128','ஒன்றானுந் தீச்சொல் பொருட்பயன் உண்டாயின்<br />நன்றாகா தாகி விடும்','ஒரு குடம் பாலில் துளி நஞ்சுபோல், பேசும் சொற்களில் ஒரு சொல் தீய சொல்லாக இருந்து துன்பம் விளைவிக்குமானாலும், அந்தப் பேச்சில் உள்ள நல்ல சொற்கள் அனைத்தும் தீயவாகிவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(129,'129','தீயினாற் சுட்டபுண் உள்ளாறும் ஆறாதே<br />நாவினாற் சுட்ட வடு','நெருப்பு சுட்ட புண்கூட ஆறி விடும்; ஆனால் வெறுப்புக் கொண்டு திட்டிய சொற்கள் விளைத்த துன்பம் ஆறவே ஆறாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(130,'130','கதங்காத்துக் கற்றடங்கல் ஆற்றுவான் செவ்வி<br />அறம்பார்க்கும் ஆற்றின் நுழைந்து','கற்பவை கற்றுச், சினம் காத்து, அடக்கமெனும் பண்பு கொண்டவரை அடைந்திட அறமானது வழிபார்த்துக் காத்திருக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(131,'131','ஒழுக்கம் விழுப்பந் தரலான் ஒழுக்கம்<br />உயிரினும் ஓம்பப் படும்','ஒருவர்க்கு உயர்வு தரக் கூடியது ஒழுக்கம் என்பதால், அந்த ஒழுக்கமே உயிரைவிட மேலானதாகப் போற்றப்படுகிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(132,'132','பரிந்தோம்பிக் காக்க ஒழுக்கந் தெரிந்தோம்பித்<br />தேரினும் அஃதே துணை','எந்தெந்த வழிகளில் ஆராய்ந்தாலும் வாழ்க்கையில் ஒழுக்கமே சிறந்த துணை என்பதால், எத்தகைய துன்பத்தை ஏற்றாவது அதைக் காக்க வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(133,'133','ஒழுக்க முடைமை குடிமை இழுக்கம்<br />இழிந்த பிறப்பாய் விடும்','ஒழுக்கம் உடையவராக வாழ்வதுதான் உயர்ந்த குடிப்பிறப்புக்கு எடுத்துக் காட்டாகும் ஒழுக்கம் தவறுகிறவர்கள் யாராயினும் அவர்கள் இழிந்த குடியில் பிறந்தவர்களாகவே கருதப்படுவர்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(134,'134','மறப்பினும் ஓத்துக் கொளலாகும் பார்ப்பான்<br />பிறப்பொழுக்கங் குன்றக் கெடும்','பார்ப்பனன் ஒருவன் கற்றதை மறந்துவிட்டால் மீண்டும் படித்துக் கொள்ள முடியும்; ஆனால், பிறப்புக்குச் சிறப்பு சேர்க்கும் ஒழுக்கத்திலிருந்து அவன் தவறினால் இழிமகனே ஆவான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(135,'135','அழுக்கா றுடையான்கண் ஆக்கம்போன் றில்லை<br />ஒழுக்க மிலான்கண் உயர்வு','பொறாமையுடையவனுக்கும், நல்லொழுக்கமில்லாதவனுக்கும் அமையும் வாழ்வு, உயர்வான வாழ்வாகக் கருதப்பட மாட்டாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(136,'136','ஒழுக்கத்தி னொல்கார் உரவோர் இழுக்கத்தின்<br />ஏதம் படுபாக் கறிந்து','மன உறுதி கொண்டவர்கள் ஒழுக்கம் தவறுவதால் ஏற்படும் இழிவை உணர்ந்திருப்பதால், நல்லொழுக்கம் குன்றிடுமளவிற்கு நடக்க மாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(137,'137','ஒழுக்கத்தி னெய்துவர் மேன்மை இழுக்கத்தின்<br />எய்துவ ரெய்தாப் பழி','ந��்ல நடத்தையினால் உயர்வு ஏற்படும்; இல்லையேல் இழிவான பழி வந்து சேரும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(138,'138','நன்றிக்கு வித்தாகும் நல்லொழுக்கந் தீயொழுக்கம்<br />என்றும் இடும்பை தரும்','நல்லொழுக்கம், வாழ்க்கையில் நன்மைக்கு வித்தாக அமையும் தீயொழுக்கம், தீராத துன்பம் தரும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(139,'139','ஒழுக்க முடையவர்க் கொல்லாவே தீய<br />வழுக்கியும் வாயாற் சொலல்','தவறியும்கூடத் தம் வாயால் தகாத சொற்களைச் சொல்வது ஒழுக்கம் உடையவர்களிடம் இல்லாத பண்பாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(140,'140','உலகத்தோ டொட்ட ஒழுகல் பலகற்றுங்<br />கல்லா ரறிவிலா தார்','உயர்ந்தோர் ஏற்றுக் கொண்ட ஒழுக்கம் எனும் பண்போடு வாழக் கற்காதவர்கள் பல நூல்களைப் படித்திருந்தும்கூட அறிவில்லாதவர்களே ஆவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(141,'141','பிறன்பொருளான் பெட்டொழுகும் பேதைமை ஞாலத்<br />தறம்பொருள் கண்டார்க ணில்','பிறன் மனைவியிடத்து விருப்பம் கொள்ளும் அறியாமை, உலகில் அறநூல்களையும் பொருள் நூல்களையும் ஆராய்ந்து உணர்ந்தவர்களிடம் இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(142,'142','அறன்கடை நின்றாரு ளெல்லாம் பிறன்கடை<br />நின்றாரிற் பேதையா ரில்','பிறன் மனைவியை அடைவதற்குத் துணிந்தவர்கள் அறவழியை விடுத்துத் தீயவழியில் செல்லும் கடைநிலை மனிதர்களைக் காட்டிலும் கீழானவர்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(143,'143','விளிந்தாரின் வேறல்லர் மன்ற தெளிந்தாரில்<br />தீமை புரிந்தொழுகு வார்','நம்பிக் பழகியவர் வீட்டில், அவரது மனைவியிடம் தகாத செயலில் ஈடுபட முனைகிறவன், உயிர் இருந்தும் பிணத்திற்கு ஒப்பானவனேயாவான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(144,'144','எனைத்துணைய ராயினும் என்னாந் தினைத்துணையும்<br />தேரான் பிறனில் புகல்','பிழை புரிகிறோம் என்பதைத் தினையளவுகூடச் சிந்தித்துப் பாராமல், பிறன் மனைவியிடம் விருப்பம் கொள்வது, எத்துணைப் பெருமையுடையவரையும் மதிப்பிழக்கச் செய்துவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(145,'145','எளிதென இல்லிறப்பா னெய்துமெஞ் ஞான்றும்<br />விளியாது நிற்கும் பழி','எளிதாக அடையலாம் என எண்ணிப் பிறனுடைய மனைவியிடம் முறைகேடாக நடப்பவன் என்றும் அழியாத பழிக்கு ஆளாவான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(146,'146','பகைபாவம் அச்சம் பழியென நான்கும்<br />இகவாவாம் இல்லிறப்பான் கண்','பிறன் மனைவியிடம் முறைகேடாக நடக்க நினைப்பவனிடமிருந்து பகை, தீமை, அச்சம், பழி ஆகிய நான்கும் நீங்குவதில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(147,'147','அறனியலான் இல்வாழ்வா னென்பான் பிறனியலான்<br />பெண்மை நயவா தவன்','பிறன் மனைவியிடம் பெண்மை இன்பத்தை நாடிச் செல்லாதவனே அறவழியில் இல்வாழ்க்கை மேற்கொண்டவன் எனப்படுவான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(148,'148','பிறன்மனை நோக்காத பேராண்மை சான்றோர்க்<br />கறனென்றோ ஆன்ற வொழுக்கு','வெறொருவன் மனைவியைக் காம எண்ணத்துடன் நோக்காத பெருங்குணம் அறநெறி மட்டுமன்று; அது ஒழுக்கத்தின் சிகரமும் ஆகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(149,'149','நலக்குரியார் யாரெனின் நாமநீர் வைப்பின்<br />பிறற்குரியாள் தோள்தோயா தார்','பிறன் மனைவியின் தோளைத் தீண்டாதவரே கடல் சூழ் இவ்வுலகின் பெருமைகளை அடைவதற்குத் தகுதியுடையவர்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(150,'150','அறன்வரையா னல்ல செயினும் பிறன்வரையாள்<br />பெண்மை நயவாமை நன்று','பிறன் மனைவியை விரும்பிச் செயல்படுவது அறவழியில் நடக்காதவர் செயலைவிடத் தீமையானதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(151,'151','அகழ்வாரைத் தாங்கும் நிலம்போலத் தம்மை<br />இகழ்வார்ப் பொறுத்தல் தலை','தன்மீது குழி பறிப்போரையே தாங்குகின்ற பூமியைப் போல் தம்மை இகழ்ந்து பேசுகிறவர்களின் செயலையும் பொறுத்துக் கொள்வதே தலைசிறந்த பண்பாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(152,'152','பொறுத்த லிறப்பினை யென்றும் அதனை<br />மறத்த லதனினும் நன்று','அளவுகடந்து செய்யப்பட்ட தீங்கைப் பொறுத்துக் கொள்வதைக் காட்டிலும், அந்தத் தீங்கை அறவே மறந்து விடுவதே சிறந்த பண்பாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(153,'153','இன்மையு ளின்மை விருந்தொரால் வன்மையுள்<br />வன்மை மடவார்ப் பொறை','வறுமையிலும் கொடிய வறுமை, வந்த விருந்தினரை வரவேற்க முடியாதது அதைப் போல வலிமையிலேயே பெரிய வலிமை அறிவிலிகளின் செயலைப் பொறுத்துக் கொள்வது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(154,'154','நிறையுடைமை நீங்காமை வேண்டின் பொறையுடைமை<br />போற்றி யொழுகப் படும்','பொறுமையின் உறைவிடமாக இருப்பவரைத்தான் நிறைவான மனிதர் என்று உலகம் புகழும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(155,'155','ஒறுத்தாரை யொன்றாக வையாரே வைப்பர்<br />பொறுத்தாரைப் பொன்போற் பொதிந்து','தமக்கு இழைக்கப்படும் தீமையைப் பொறுத்துக் கொள்பவர்களை உலகத்தார் பொன்னாக மதித்துப் போற்றுவார்கள் பொறுத்துக் கொள்ளாமல் தண்டிப்பவர்களை அதற்கு ஒப்பாகக் கருத மாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(156,'156','ஒறுத்தார்க் கொருநாளை இன்பம் பொறுத்தார்க்குப்<br />பொன்றுந் துணையும் புகழ்','தமக்குக் கேடு செய்தவரை மன்னித்திடாமல் தண்டிப்பவர்க்கு அந்த ஒரு நாள் மட்டுமே இன்பமாக அமையும் மறப்போம் மன்னிப்போம் எனப் பொறுமை கடைப் பிடிப்பபோருக்கோ, வாழ்நாள் முழுதும் புகழ்மிக்கதாக அமையும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(157,'157','திறனல்ல தற்பிறர் செய்யினும் நோநொந்<br />தறனல்ல செய்யாமை நன்று','பிறர் செய்திடும் இழிவான காரியங்களுக்காகத் துன்பமுற்று வருந்தி, பதிலுக்கு அதே காரியங்களைச் செய்து பழி வாங்காமலிருப்பதுதான் சிறந்த பண்பாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(158,'158','மிகுதியான் மிக்கவை செய்தாரைத் தாந்தந்<br />தகுதியான் வென்று விடல்','ஆணவங் கொண்டு அநீதி விளைவிப்பவர்களை, நாம் நம் பொறுமைக் குணத்தால் வென்று விடலாம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(159,'159','துறந்தாரின் தூய்மை யுடையர் இறந்தார்வாய்<br />இன்னாச்சொல் நோற்கிற் பவர்','எல்லை கடந்து நடந்து கொள்பவர்களின் கொடிய சொற்களைப் பொறுத்துக் கொள்பவர்கள் தூய்மையான துறவிகளைப் போன்றவர்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(160,'160','உண்ணாது நோற்பார் பெரியர் பிறர்சொல்லும்<br />இன்னாச்சொ னோற்பாரிற் பின்','பசி பொறுத்து உண்ணாநோன்பு இருக்கும் உறுதி படைத்தவர்கள்கூடப் பிறர்கூறும் கொடுஞ்சொற்களைப் பொறுத்துக் கொள்பவர்களுக்கு, அடுத்த நிலையில் தான் வைத்துப் போற்றப்படுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(161,'161','ஒழுக்காறாக் கொள்க ஒருவன்தன் நெஞ்சத்<br />தழுக்கா றிலாத இயல்பு','மனத்தில் பொறாமையில்லாமல் வாழும் இயல்பை ஒழுக்கத்திற்குரிய நெறியாகப் பெற்று விளங்கிட வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(162,'162','விழுப்பேற்றின் அஃதொப்ப தில்லையார் மாட்டும்<br />அழுக்காற்றின் அன்மை பெறின்','யாரிடமும் பொறாமை கொள்ளாத பண்பு ஒருவர்க்கு வாய்க்கப் பெறுமேயானால் அதற்கு மேலான பேறு அவருக்கு வேறு எதுவுமில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(163,'163','அறனாக்கம் வேண்டாதான் என்பான் பிறனாக்கம்<br />பேணா தழுக்கறுப் பான்','அறநெறியையும், ஆக்கத்தையும் விரும்பிப் போற்றாதவன்தான், பிறர் பெருமையைப் போற்றாமல் பொறாமைக் களஞ்சியமாக விளங்குவான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(164,'164','அழுக்காற்றின் அல்லவை செய்யார் இழுக்காற்றின்<br />ஏதம் படுபாக் கறிந்து','தீய வழியில் சென்றால் துன்பம் ஏற்படுமென்பதை அறிந்தவர்கள் பொறாமையினால் தீச்செயல்களில் ஈடுபடமாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(165,'165','அழுக்கா றுடையார்க் கதுசாலும் ஒன்னார்<br />வழுக்கியுங் கேடீன் பது','பொறாமைக் குணம் கொண்டவர்களுக்கு அவர்களை வீழ்த்த வேறு பகையே வேண்டா அந்தக் குணமே ��வர்களை வீழ்த்தி விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(166,'166','கொடுப்ப தழுக்கறுப்பான் சுற்றம் உடுப்பதூஉம்<br />உண்பதூஉ மின்றிக் கெடும்','உதவியாக ஒருவருக்குக் கொடுக்கப்படுவதைப் பார்த்துப் பொறாமை கொண்டால் அந்தத் தீய குணம், அவனை மட்டுமின்றி அவனைச் சார்ந்திருப்போரையும் உணவுக்கும், உடைக்கும்கூட வழியில்லாமல் ஆக்கிவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(167,'167','அவ்வித் தழுக்கா றுடையானைச் செய்யவள்<br />தவ்வையைக் காட்டி விடும்','செல்வத்தை இலக்குமி என்றும், வறுமையை அவளது அக்காள் மூதேவி என்றும் வர்ணிப்பதுண்டு பொறாமைக் குணம் கொண்டவனை அக்காளுக்கு அடையாளம் காட்டிவிட்டுத் தங்கை இலக்குமி அவனைவிட்டு அகன்று விடுவாள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(168,'168','அழுக்கா றெனவொரு பாவி திருச்செற்றுத்<br />தீயுழி உய்த்து விடும்','பொறாமை எனும் தீமை ஒருவனுடைய செல்வத்தையும் சிதைத்துத் தீய வழியிலும் அவனை விட்டுவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(169,'169','அவ்விய நெஞ்சத்தான் ஆக்கமும் செவ்வியான்<br />கேடும் நினைக்கப் படும்','பொறாமைக் குணம் கொண்டவனின் வாழ்க்கை வளமாக இருப்பதும், பொறாமைக் குணம் இல்லாதவனின் வாழ்க்கை வேதனையாக இருப்பதும் வியப்புக்குரிய செய்தியாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(170,'170','அழுக்கற் றகன்றாரும் இல்லையஃ தில்லார்<br />பெருக்கத்தில் தீர்ந்தாரு மில்','பொறாமை கொண்டதால் புகழ் பெற்று உயர்ந்தோரும் இல்லை; பொறாமை இல்லாத காரணத்தால் புகழ் மங்கி வீழ்ந்தோரும் இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(171,'171','நடுவின்றி நன்பொருள் வெஃகிற் குடிபொன்றிக்<br />குற்றமும் ஆங்கே தரும்','மனச்சான்றை ஒதுக்கிவிட்டுப் பிறர்க்குரிய அரும் பொருளைக் கவர்ந்துகொள்ள விரும்புகிறவரின் குடியும் கெட்டொழிந்து, பழியும் வந்து சேரும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(172,'172','படுபயன் வெஃகிப் பழிப்படுவ செய்யார்<br />நடுவன்மை நாணு பவர்','நடுவுநிலை தவறுவது நாணித் தலைகுனியத் தக்கது என்று நினைப்பவர் தமக்கு ஒரு பயன் கிடைக்கும் என்பதற்காக, பழிக்கப்படும் செயலில் ஈடுபடமாட்டார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(173,'173','சிற்றின்பம் வெஃகி யறனல்ல செய்யாரே<br />மற்றின்பம் வேண்டு பவர்','அறவழியில் நிலையான பயனை விரும்புகிறவர் உடனடிப் பயன் கிடைக்கிறது என்பதற்காக அறவழி தவறி நடக்க மாட்டார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(174,'174','இலமென்று வெஃகுதல் செய்யார் புலம்வென்ற<br />புன்மையில் காட்சி யவர்','புலனடக்கம் வாய்ந்த தூயவர், வறுமையில் வாடும் நிலையிலேகூடப் பிறர் பொருளைக் கவர்ந்திட விரும்ப மாட்டார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(175,'175','அஃகி யகன்ற அறிவென்னாம் யார்மாட்டும்<br />வெஃகி வெறிய செயின்','யாராயிருப்பினும் அவரது உடைமையை அறவழிக்குப் புறம்பாகக் கவர விரும்பினால் ஒருவருக்குப் பகுத்துணரும் நுண்ணிய அறிவு இருந்துதான் என்ன பயன்?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(176,'176','அருள்வெஃகி யாற்றின்கண் நின்றான் பொருள்வெஃகிப்<br />பொல்லாத சூழக் கெடும்','அருளை விரும்பி அதனை அடைவதற்கான வழியில் செல்பவன் தவறிப்போய்ப் பிறர் பொருளை விரும்பிப் பொல்லாத செயலில் ஈடுபட்டால் கெட்டொழிய நேரிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(177,'177','வேண்டற்க வெஃகியாம் ஆக்கம் விளைவயின்<br />மாண்டற் கரிதாம் பயன்','பிறர் பொருளைக் கவர்ந்து ஒருவன் வளம்பெற விரும்பினால் அந்த வளத்தின் பயன், நலம் தருவதாக இருக்காது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(178,'178','அஃகாமை செல்வத்திற் கியாதெனின் வெஃகாமை <br />வேண்டும் பிறன்கைப் பொருள்','தன்னுடைய செல்வச் செழிப்பு குறையாமலிருக்க வேண்டுமென்றால் பிறருடைய பொருளையும் தானே அடைய வேண்டுமென்று ஆசைப்படாமலிருக்க வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(179,'179','அறனறிந்து வெஃகா அறிவுடையார்ச��� சேருந்<br />திறனறிந் தாங்கே திரு','பிறர் பொருளைக் கவர விரும்பாத அறநெறி உணர்ந்த அறிஞர் பெருமக்களின் ஆற்றலுக்கேற்ப அவர்களிடம் செல்வம் சேரும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(180,'180','இறலீனும் எண்ணாது வெஃகின் விறலீனும்<br />வேண்டாமை யென்னுஞ் செருக்கு','விளைவுகளைப் பற்றி நினைக்காமல் பிறர் பொருளைக் கவர்ந்துகொள்ள விரும்பினால் அழிவும், அத்தகைய விருப்பம் கொள்ளாதிருந்தால் வாழ்க்கையில் வெற்றியும் கிட்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(181,'181','அறங்கூறா னல்ல செயினும் ஒருவன்<br />புறங்கூறா னென்றல் இனிது','அறநெறியைப் போற்றாமலும், அவ்வழியில் நடக்காமலும்கூட இருக்கின்ற சிலர் மற்றவர்களைப் பற்றிப் புறம் பேசாமல் இருந்தால், அது அவர்களுக்கு நல்லது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(182,'182','அறனழீஇ யல்லவை செய்தலின் தீதே<br />புறனழீஇப் பொய்த்து நகை','ஒருவரை நேரில் பார்க்கும் பொழுது பொய்யாகச் சிரித்துப் பேசிவிட்டு, அவர் இல்லாத இடத்தில் அவரைப் பற்றிப் பொல்லாங்கு பேசுவது அறவழியைப் புறக்கணித்து விட்டு, அதற்கு மாறான காரியங்களைச் செய்வதைவிடக் கொடுமையானது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(183,'183','புறங்கூறிப் பொய்த்துயிர் வாழ்தலிற் சாதல்<br />அறங்கூறும் ஆக்கந் தரும்','கண்ட இடத்தில் ஒன்றும், காணாத இடத்தில் வேறொன்றுமாகப் புறங்கூறிப் பொய்மையாக நடந்து உயிர் வாழ்வதைவிடச் சாவது நன்று',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(184,'184','கண்ணின்று கண்ணறச் சொல்லினுஞ் சொல்லற்க<br />முன்னின்று பின்னோக்காச் சொல்','நேருக்கு நேராக ஒருவரது குறைகளைக் கடுமையாகச் சொன்னாலும் சொல்லலாம், ஆனால் பின் விளைவுகளை எண்ணிப் பார்க்காமல் நேரில் இல்லாத ஒருவரைப் பற்றிக் குறை கூறுவது தவறு',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(185,'185','அறஞ்சொல்லும் நெஞ்சத்தான் அன்மை புறஞ்சொல்லும்<br />புன்மையாற் காணப் படும்','ஒருவன் பிறரைப்பற்றிப் புறம் பேசுகிற சிறுமைத்தன்மையைக்கொண்டே அவன் அறவழி நிற்பவன் அல்லன் என்பதை எளிதில் தெரிந்து கொள்ளலாம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(186,'186','பிறன்பழி கூறுவான் தன்பழி யுள்ளுந்<br />திறன்தெரிந்து கூறப் படும்','பிறர்மீது ஒருவன் புறங்கூறித் திரிகிறான் என்றால் அவனது பழிச் செயல்களை ஆராய்ந்து அவற்றில் கொடுமையானவைகளை அவன் மீது கூற நேரிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(187,'187','பகச்சொல்லிக் கேளிர்ப் பிரிப்பர் நகச்சொல்லி<br />நட்பாடல் தேற்றா தவர்','இனிமையாகப் பழகி நட்புறவைத் தொடரத் தெரியாதவர்கள், நட்புக் கெடுமளவுக்குப் புறங்கூறி நண்பர்களை இழந்து விடுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(188,'188','துன்னியார் குற்றமுந் தூற்றும் மரபினர்<br />என்னைகொல் ஏதிலார் மாட்டு','நெருங்கிப் பழகியவரின் குறையைக்கூடப் புறம் பேசித் தூற்றுகிற குணமுடையவர்கள் அப்படிப் பழகாத அயலாரைப் பற்றி என்னதான் பேச மாட்டார்கள்?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(189,'189','அறனோக்கி யாற்றுங்கொல் வையம் புறனோக்கிப்<br />புன்சொ லுரைப்பான் பொறை','ஒருவர் நேரில் இல்லாதபோது பழிச்சொல் கூறுவோனுடைய உடலை `இவனைச் சுமப்பதும் அறமே என்று கருதித்தான் நிலம் சுமக்கிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(190,'190','ஏதிலார் குற்றம்போல் தங்குற்றங் காண்கிற்பின்<br />தீதுண்டோ மன்னு முயிர்க்கு','பிறர் குற்றத்தைக் காண்பவர்கள் தமது குற்றத்தையும் எண்ணிப் பார்ப்பார்களேயானால் புறங்கூறும் பழக்கமும் போகும்; வாழ்க்கையும் நிம்மதியாக அமையும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(191,'191','பல்லார் முனியப் பயனில சொல்லுவான்<br />எல்லாரும் எள்ளப் படும்','பலரும் வெறுக்கும்படியான பயனற்ற சொற்களைப் பேசுபவரை எல்லோரும் இகழ்ந்துரைப்பார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(192,'192','பயனில பல்லார்முன் சொல்லல் நயனில<br />நட்டார்கட் செய்தலிற் றீது','பலர��முன் பயனில்லாத சொற்களைக் கூறுவது, நட்புக்கு மாறாகச் செயல்படுவதைக் காட்டிலும் தீமையுடையதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(193,'193','நயனில னென்பது சொல்லும் பயனில<br />பாரித் துரைக்கும் உரை','பயனற்றவைகளைப்பற்றி ஒருவன் விரிவாகப் பேசிக் கொண்டிருப்பதே அவனைப் பயனற்றவன் என்று உணர்த்தக் கூடியதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(194,'194','நயன்சாரா நன்மையின் நீக்கும் பயன்சாராப்<br />பண்பில்சொல் பல்லா ரகத்து','பயனற்றதும், பண்பற்றதுமான சொற்களைப் பலர்முன் பகர்தல் மகிழ்ச்சியைக் குலைத்து, நன்மையை மாய்க்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(195,'195','சீர்மை சிறப்பொடு நீங்கும் பயனில<br />நீர்மை யுடையார் சொலின்','நல்ல பண்புடையவர் பயனில்லாத சொற்களைக் கூறுவாரானால் அவருடைய மேம்பாடு அவர்க்குரிய மதிப்போடு நீங்கி விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(196,'196','பயனில்சொல் பாராட்டு வானை மகனெனல்<br />மக்கட் பதடி யெனல்','பயனற்றவைகளைச் சொல்லிப் பயன்பெற நினைப்பவனை, மனிதன் என்பதைவிட அவன் ஒரு பதர் என்பதே பொருத்தமானதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(197,'197','நயனில சொல்லினுஞ் சொல்லுக சான்றோர்<br />பயனில சொல்லாமை நன்று','பண்பாளர்கள், இனிமையல்லாத சொற்களைக்கூடச் சொல்லி விடலாம்; ஆனால் பயனில்லாத சொற்களைச் சொல்லாமல் இருப்பதே நல்லது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(198,'198','அரும்பய னாயும் அறிவினார் சொல்லார்<br />பெரும்பய னில்லாத சொல்','அரும்பயன்களை ஆராய்ந்து அறியக்கூடிய ஆற்றல் படைத்தவர், பெரும்பயன் விளைவிக்காத எந்தச் சொல்லையும் பயன்படுத்த மாட்டார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(199,'199','பொருள்தீர்ந்த பொச்சாந்துஞ் சொல்லார் மருள்தீர்ந்த<br />மாசறு காட்சி யவர்','மயக்கம் சிறிதுமில்லாத மாசற்ற அறிவுடையவர் மறந்தும்கூடப் பயனற்ற சொற்களைச் சொல்ல மாட்டார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(200,'200','சொல்லுக சொல்லிற் பயனுடைய சொல்லற்க<br />சொல்லிற் பயனிலாச் சொல்','பயனளிக்காத சொற்களை விடுத்து மனத்தில் பதிந்து பயனளிக்கக் கூடிய சொற்களையே கூற வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(201,'201','தீவினையார் அஞ்சார் விழுமியார் அஞ்சுவர்<br />தீவினை யென்னுஞ் செறுக்கு','தீயவர்கள் தீவினை செய்ய அஞ்சமாட்டார்கள்; தீவினையால் மகிழ்ச்சி ஏற்படுவதாயினும் அதனைச் செய்திடச் சான்றோர் அஞ்சி நடுங்குவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(202,'202','தீயவை தீய பயத்தலால் தீயவை<br />தீயினும் அஞ்சப் படும்','தீய செயல்களால் தீமையே விளையும் என்பதால் அச்செயல்களைத் தீயை விடக் கொடுமையானவையாகக் கருதி அவற்றைச் செய்திட அஞ்சிட வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(203,'203','அறிவினு ளெல்லாந் தலையென்ப தீய<br />செறுவார்க்குஞ் செய்யா விடல்','தீமை செய்தவர்க்கு அதையே திருப்பிச் செய்யாமலிருத்தலை, எல்லா அறிவிலும் முதன்மையான அறிவு என்று போற்றுவர்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(204,'204','மறந்தும் பிறன்கேடு சூழற்க சூழின்<br />அறஞ்சூழுஞ் சூழ்ந்தவன் கேடு','மறந்தும்கூட மற்றவர்க்குக் கேடு செய்ய நினைக்கக் கூடாது; அப்படி நினைத்தால் அவனுக்குக் கேடு உண்டாக்க அவனை அறம் முற்றுகையிட்டு விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(205,'205','இலனென்று தீயவை செய்யற்க செய்யின்<br />இலனாகும் மற்றுப் பெயர்த்து','வறுமையின் காரணமாக ஒருவன் தீய செயல்களில் ஈடுபடக்கூடாது; அப்படி ஈடுபட்டால் மீண்டும் அவன் வறுமையிலேயே வாட வேண்டியிருக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(206,'206','தீப்பால தான்பிறர்கண் செய்யற்க நோய்ப்பால<br />தன்னை அடல்வேண்டா தான்','வேதனை விளைவிக்கும் தீய செயல்கள் தன்னைத் தாக்கலாகாது என எண்ணுகிறவன் அவனும் அத்தீங்குகளைப் பிறருக்குச் செய்யாமல் இருக்க வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(207,'207','எனைப்பகை யுற்றாரும் உய்வர் வினைப்��கை<br />வீயாது பின்சென் றடும்','ஒருவர் நேரடியான பகைக்குத் தப்பி வாழ முடியும்; ஆனால், அவர் செய்யும் தீய வினைகள் பெரும் பகையாகி அவரைத் தொடர்ந்து வருத்திக்கொண்டே இருக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(208,'208','தீயவை செய்தார் கெடுதல் நிழல்தன்னை<br />வீயா தடியுறைந் தற்று','ஒருவருடைய நிழல் அவருடனேயே ஒன்றியிருப்பதைப்போல், தீய செயல்களில் ஈடுபடுகிறவர்களை விட்டுத் தீமையும் விலகாமல், தொடர்ந்து ஒட்டிக் கொண்டிருக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(209,'209','தன்னைத்தான் காதல னாயின் எனைத்தொன்றுந்<br />துன்னற்க தீவினைப் பால்','தனது நலத்தை விரும்புகிறவன் தீய செயல்களின் பக்கம் சிறிதளவுகூட நெருங்கலாகாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(210,'210','அருங்கேடன் என்ப தறிக மருங்கோடித்<br />தீவினை செய்யான் எனின்','வழிதவறிச் சென்று பிறர்க்குத் தீங்கு விளைவிக்காதவர்க்கு எந்தக் கேடும் ஏற்படாது என்பதை அறிந்து கொள்க',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(211,'211','கைம்மாறு வேண்டா கடப்பாடு மாரிமாட்<br />டென்னாற்றுங் கொல்லோ உலகு','கைம்மாறு கருதி மழை பொழிவதில்லை; அந்த மழையைப் போன்றவர்கள் கைம்மாறு கருதி எந்த உதவியும் செய்பவர்கள் அல்லர்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(212,'212','தாளாற்றித் தந்த பொருளெல்லாந் தக்கார்க்கு<br />வேளாண்மை செய்தற் பொருட்டு','தகுதியுடையோர் நலனுக்கு உதவிடும் பொருட்டே ஒருவன் முயன்று திரட்டிய பொருள் பயன்பட வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(213,'213','புத்தே ளுலகத்தும் ஈண்டும் பெறலரிதே<br />ஒப்புரவின் நல்ல பிற','பிறர்க்கு உதவிடும் பண்பாகிய ``ஒப்புரவு என்பதைவிடச் சிறந்த பண்பினை இன்றைய உலகிலும், இனிவரும் புதிய உலகிலும் காண்பது அரிது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(214,'214','ஒத்த தறிவான் உயிர்வாழ்வான் மற்றையான்<br />செத்தாருள் வைக்கப் படும்','ஒப்புரவை அறிந்து பிறருக்கு உதவியாகத் தன் வாழ்வை அமைத்துக் கொள்பவனே உயிர்வாழ்பவன் எனக் கருதப்படுவான்; அதற்கு மாறானவன் இறந்தவனே ஆவான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(215,'215','ஊருணி நீர்நிறைந் தற்றே உலகவாம்<br />பேரறி வாளன் திரு','பொதுநல நோக்குடன் வாழ்கின்ற பேரறிவாளனின் செல்வமானது ஊர் மக்கள் அனைவருக்கும் பயன் தரும் நீர் நிறைந்த ஊருணியைப் போன்றதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(216,'216','பயன்மரம் உள்ளூர்ப் பழுத்தற்றால் செல்வம்<br />நயனுடை யான்கண் படின்','ஈர நெஞ்சம் கொண்டவனிடம் செல்வம் சேருமேயானால் அது, ஊரின் நடுவே செழித்து வளர்ந்த மரம், பழுத்துக் குலுங்குவது போல எல்லோர்க்கும் பயன்படுவதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(217,'217','மருந்தாகித் தப்பா மரத்தற்றால் செல்வம்<br />பெருந்தகை யான்கண் படின்','பிறருக்கு உதவிடும் பெருந்தன்மையாம் ஒப்புரவு உடையவனிடம், செல்வம் சேர்ந்தால் அது ஒரு நல்ல மரத்தின் எல்லா உறுப்புகளும் மருந்தாகப் பயன்படுவது போன்றதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(218,'218','இடனில் பருவத்தும் ஒப்புரவிற் கொல்கார்<br />கடனறி காட்சி யவர்','தம்மிடம் வளம் நீங்கி, வறுமை வந்துற்ற காலத்திலும், பிறர்க்கு உதவிடும் ஒப்புரவில் தளராதவர், கடமையுணர்ந்த தகைமையாளர்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(219,'219','நயனுடையான் நல்கூர்ந்தா னாதல் செயும்நீர<br />செய்யா தமைகலா வாறு','பிறர்க்கு உதவி செய்வதையே கடமையாகக் கொண்ட பெருந்தகையாளன் ஒருவன், வறுமையடைந்து விட்டான் என்பதை உணர்த்துவது அவனால் பிறர்க்கு உதவிட முடியாமல் செயலிழந்து போகும் நிலைமைதான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(220,'220','ஒப்புரவி னால்வருங் கேடெனின் அஃதொருவன்<br />விற்றுக்கோள் தக்க துடைத்து','பிறருக்கு உதவுகின்ற சிறப்புடைய உலக ஒழுக்கம், கேடு விளைவிக்கக் கூடியதாக இருப்பின், அக்கேடு, ஒருவன் தன்னை விற்றாவது வாங்கிக் கொள்ளக் கூடிய மதிப்புடையத���கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(221,'221','வறியார்க்கொன் றீவதே ஈகைமற் றெல்லாங்<br />குறியெதிர்ப்பை நீர துடைத்து','இல்லாதவர்க்கு வழங்குவதே ஈகைப் பண்பாகும் மற்றவர்களுக்கு வழங்குவது என்பது ஏதோ ஓர் ஆதாயத்தை எதிர்பார்த்து வழங்கப்படுவதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(222,'222','நல்லா றெனினுங் கொளல்தீது மேலுலகம்<br />இல்லெனினும் ஈதலே நன்று','பிறரிடமிருந்து நல்வழியில் பொருளைப் பெற்றாலும் அது பெருமையல்ல; சிறுமையே ஆகும் கொடை வழங்குவதால் மேலுலகம் என்று சொல்லப்படுவது கிட்டிவிடப் போவதில்லை; எனினும் பிறர்க்குக் கொடுத்து வாழ்வதே சிறந்த வாழ்க்கையாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(223,'223','இலனென்னும் எவ்வம் உரையாமை ஈதல்<br />குலனுடையான் கண்ணே யுள','தமக்குள்ள வறுமைத் துன்பத்தைக் காட்டிக்கொள்ளாமல் பிறருக்கு ஈவது உயர்ந்த குடிப்பிறந்தவரின் பண்பாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(224,'224','இன்னா திரக்கப் படுதல் இரந்தவர்<br />இன்முகங் காணு மளவு','ஈதல் பண்புடையவர்க்குத் தம்மை நாடி வரும் இரவலரின் புன்னகை பூத்த முகத்தைக் கண்டு இன்புறும் வரையில், அவருக்காக இரக்கப்படுவதும் ஒரு துன்பமாகவே தோன்றும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(225,'225','ஆற்றுவா ராற்றல் பசியாற்றல் அப்பசியை<br />மாற்றுவா ராற்றலிற் பின்','பசியைப் பொறுத்துக் கொள்ளும் நோன்பைக் கடைப்பிடிப்பதைவிடப் பசித்திருக்கும் ஒருவருக்கு உணவு அளிப்பதே சிறந்ததாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(226,'226','அற்றார் அழிபசி தீர்த்தல் அஃதொருவன்<br />பெற்றான் பொருள்வைப் புழி','பட்டினி எனச் சொல்லி வந்தவரின் பசியைத் தீர்ப்பது வீண் போகாது அதுவே, தான் தேடிய பொருளைப் பிற்காலத்தில் உதவுவதற்கு ஏற்பச் சேமித்து வைக்கக்கூடிய கருவூலமாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(227,'227','பாத்தூண் மரீஇ யவனைப் பசியென்னுந்<br />தீப்பிணி தீண்ட லரிது','பகிர்ந்து உண்ணும் பழக்கம் உடையவர்களைப் பசியென்னும் கொடிய நோய் அணுகுவதில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(228,'228','ஈத்துவக்கும் இன்பம் அறியார்கொல் தாமுடைமை<br />வைத்திழக்கும் வன்க ணவர்','ஏழை எளியோர்க்கு எதுவும் அளித்திடாமல் ஈ.ட்டிய பொருள் அனைத்தையும் இழந்திடும் ஈ.வு இரக்கமற்றோர், பிறர்க்கு வழங்கி மகிழ்வதில் ஏற்படும் இன்பத்தை அறியமாட்டாரோ?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(229,'229','இரத்தலின் இன்னாது மன்ற நிரப்பிய<br />தாமே தமிய ருணல்','பிறர்க்கு ஈவதால் குறையக் கூடுமென்று, குவித்து வைத்துள்ளதைத் தாமே உண்ணுவது என்பது கையேந்தி இரந்து நிற்பதைக் காட்டிலும் கொடுமையானது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(230,'230','சாதலின் இன்னாத தில்லை இனிததூஉம்<br />ஈத லியையாக் கடை','சாவு எனும் துன்பத்தைவிட வறியவர்க்கு எதுவும் வழங்க இயலாத மனத்துன்பம் பெரியது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(231,'231','ஈத லிசைபட வாழ்தல் அதுவல்ல<br />தூதிய மில்லை உயிர்க்கு','கொடைத் தன்மையும், குன்றாத புகழும்தவிர வாழ்க்கைக்கு ஆக்கம் தரக் கூடியது வேறெதுவும் இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(232,'232','உரைப்பா ருரைப்பவை யெல்லாம் இரப்பார்க்கொன்<br />றீவார்மேல் நிற்கும் புகழ்','போற்றுவோர் போற்றுவனவெல்லாம் இல்லாதவர்க்கு ஒன்று வழங்குவோரின் புகழைக் குறித்தே அமையும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(233,'233','ஒன்றா உலகத் துயர்ந்த புகழல்லாற்<br />பொன்றாது நிற்பதொன் றில்','ஒப்பற்றதாகவும், அழிவில்லாததாகவும் இந்த உலகத்தில் நிலைத்திருப்பது புகழைத் தவிர வேறு எதுவுமே இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(234,'234','நிலவரை நீள்புகழ் ஆற்றின் புலவரைப்<br />போற்றாது புத்தே ளுலகு','இனிவரும் புதிய உலகம்கூட இன்றைய உலகில் தன்னலம் துறந்து புகழ் ஈட்டிய பெருமக்களை விடுத்து, அறிவாற்றல் உடையவரை மட்டும் போற்றிக் கொண்டிராது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(235,'235','நத்தம்போல் கேடும் உளதாகுஞ் சாக்காடும்<br />வித்தகர்க் கல்லால் அரிது','துன்பங்களுக்கிடையேகூட அவற்றைத் தாங்கும் வலிமையால் தமது புகழை வளர்த்துக் கொள்வதும், தமது சாவிலும்கூடப் புகழை நிலை நாட்டுவதும் இயல்பான ஆற்றலுடையவருக்கே உரிய செயலாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(236,'236','தோன்றின் புகழோடு தோன்றுக அஃதிலார்<br />தோன்றலின் தோன்றாமை நன்று','எந்தத் துறையில் ஈடுபட்டாலும் அதில் புகழுடன் விளங்கவேண்டும்; இயலாதவர்கள் அந்தத் துறையில் ஈடுபடாமல் இருப்பதே நல்லது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(237,'237','புகழ்பட வாழாதார் தந்நோவார் தம்மை<br />இகழ்வாரை நோவ தெவன்','உண்மையான புகழுடன் வாழ முடியாதவர்கள், அதற்காகத் தம்மை நொந்து கொள்ள வேண்டுமே தவிரத் தமது செயல்களை இகழ்ந்து பேசுகிறவர்களை நொந்து கொள்வது எதற்காக?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(238,'238','வசையென்ப வையத்தார்க் கெல்லாம் இசையென்னும்<br />எச்சம் பெறாஅ விடின்','தமக்குப் பிறகும் எஞ்சி நிற்கக் கூடிய புகழைப் பெறாவிட்டால், அது அந்த வாழ்க்கைக்கே வந்த பழியென்று வையம் கூறும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(239,'239','வசையிலா வண்பயன் குன்றும் இசையிலா<br />யாக்கை பொறுத்த நிலம்','புகழ் எனப்படும் உயிர் இல்லாத வெறும் மனித உடலைச் சுமந்தால், இந்தப்பூமி நல்ல விளைவில்லாத நிலமாகக் கருதப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(240,'240','வசையொழிய வாழ்வாரே வாழ்வார் இசையொழிய<br />வாழ்வாரே வாழா தவர்','பழி உண்டாகாமல் வாழ்வதே வாழ்க்கை எனப்படும், புகழ் இல்லாதவர் வாழ்வதும் வாழாததும் ஒன்றுதான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(241,'241','அருட்செல்வஞ் செல்வத்துள் செல்வம் பொருட்செல்வம்<br />பூரியார் கண்ணு முள','கொடிய உள்ளம் கொண்ட இழிமக்களிடம்கூடக் கோடிக்கணக்கில் செல்வம் குவிந்திருக்கலாம்; ஆனாலும் அந்தச் செல்வம் அருட் செல்வத்துக்கு ஈடாகாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(242,'242','நல்லாற்றான் நாடி யருளாள்க பல்லாற்றால்<br />தேரினும் அஃதே துணை','பலவழிகளால் ஆராய்ந்து கண்டாலும் அருள் உடைமையே வாழ்க்கைக்குத் துணையாய் விளங்கும் நல்வழி எனக் கொள்ளல் வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(243,'243','அருள்சேர்ந்த நெஞ்சினார்க் கில்லை இருள்சேர்ந்த<br />இன்னா உலகம் புகல்','அருள் நிறைந்த மனம் படைத்தவர் அறியாமை எனும் இருள் சூழ்ந்த துன்ப உலகில் உழலமாட்டார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(244,'244','மன்னுயி ரோம்பி அருளாள்வாற் கில்லென்ப<br />தன்னுயி ரஞ்சும் வினை','எல்லா உயிர்களிடத்தும் கருணைக்கொண்டு அவற்றைக் காத்திடுவதைக் கடமையாகக் கொண்ட சான்றோர்கள் தமது உயிரைப் பற்றிக் கவலை அடைய மாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(245,'245','அல்லல் அருளாள்வார்க் கில்லை வளிவழங்கு<br />மல்லன்மா ஞாலங் கரி','உள்ளத்தில் ஊறிடும் அருளின் இயக்கத்தினால் துன்பத்தை உணராமல் கடமையாற்றலாம் என்பதற்கு, காற்றின் இயக்கத்தினால் வலிமையுடன் திகழும் இந்தப் பெரிய உலகமே சான்று',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(246,'246','பொருள்நீங்கிப் பொச்சாந்தா ரென்பர் அருள்நீங்கி<br />அல்லவை செய்தொழுகு வார்','அருளற்றவர்களாய்த் தீமைகளைச் செய்து வாழ்பவர்கள், பொருளற்றவர்களாகவும், கடமை மறந்தவர்களாகவும் ஆவர்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(247,'247','அருளில்லார்க் கவ்வுலகம் இல்லை பொருளில்லார்க்<br />கிவ்வுலகம் இல்லாகி யாங்கு','பொருள் இல்லாதவர்களுக்கு இல்லற வாழ்க்கை சிறப்பாக இராது அதுபோலவே கருணை உள்ளம் இல்லாதவர்களின் துறவற வாழ்க்கையும் சிறப்பாக அமையாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(248,'248','பொருளற்றார் பூப்ப ரொருகால் அருளற்றார்<br />அற்றார்மற் றாதல் அரிது','பொருளை இழந்தவர் அதனை மீண்டும் தேடிப் பெறலாம் அருளை இழந்தால் இழந்ததுதான்; மீண்டும் பெற இயலாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(249,'249','தெருளாதான் மெய்ப்பொருள் கண்டற்றால் தேரின்<br />அருளாதான் செய்யும் அறம்','அறிவுத் தெளிவு இல்லாதவன் ஒரு நூலின் உண்மைப் பொருளைக் கண்டறிய முடியுமா? அது போலத்தான் அருள் இல்லாதவன் செய்யும் அறச்செயலும் இருக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(250,'250','வலியார்முன் தன்னை நினைக்கதான் தன்னின்<br />மெலியார்மேற் செல்லு மிடத்து','தன்னைவிட மெலிந்தவர்களைத் துன்புறுத்த நினைக்கும் போது, தன்னைவிட வலியவர் முன்னால் அஞ்சி நிற்கும் நிலைமை தனக்கு இருப்பதை மறந்துவிடக் கூடாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(251,'251','தன்னூன் பெருக்கற்குத் தான்பிறி தூனுண்பான்<br />எங்ஙனம் ஆளும் அருள்','தன் உடலை வளர்ப்பதற்காக வேறொரு உயிரின் உடலை உணவாக்கிக் கொள்பவர் எப்படிக் கருணையுள்ளம் கொண்டவராக இருக்க முடியும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(252,'252','பொருளாட்சி போற்றாதார்க் கில்லை அருளாட்சி<br />ஆங்கில்லை ஊன்றின் பவர்க்கு','பொருளைப் பேணிக் காத்திடாதவர்க்குப் பொருள் உடையவர் என்னும் சிறப்பு இல்லை; புலால் உண்பவர்க்கும் அருள் உடையவர் என்னும் சிறப்பு இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(253,'253','படைகொண்டார் நெஞ்சம்போல் நன்றூக்கா தொன்றன்<br />உடல்சுவை யுண்டார் மனம்','படைக் கருவியைப் பயன்படுத்துவோர் நெஞ்சமும், ஓர் உயிரின் உடலைச் சுவைத்து உண்பவர் நெஞ்சமும், அருளுடைமையைப் போற்றக் கூடியவைகள் அல்ல',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(254,'254','அருளல்ல தியாதெனிற் கொல்லாமை கோறல்<br />பொருளல்ல தவ்வூன் தினல்','கொல்லாமை அருளுடைமையாகும்; கொல்லுதல் அருளற்ற செயலாகும் எனவே ஊன் அருந்துதல் அறம் ஆகாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(255,'255','உண்ணாமை யுள்ள துயிர்நிலை ஊனுண்ண<br />அண்ணாத்தல் செய்யா தளறு','உயிர்களை உணவாக்கிக் கொள்ளச் சகதிக்குழியும் வாய் திறவாது; புலால் உண்ணாதவர்கள் இருப்பதால், பல உயிர்கள் கொல்லப்படாமல் வாழ்கின்றன',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(256,'256','தினற்பொருட்டால் கொல்லா துலகெனின் யாரும்<br />விலைப்பொருட்டால் ஊன்றருவா ரில்','புலால் உண்பதற்காக உலகினர் உயிர்களைக் கொல்லாதிருப்பின், புலால் விற்பனை செய்யும் தொழிலை எவரும் மேற்கொள்ள மாட்டார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(257,'257','உண்ணாமை வேண்டும் புலாஅல் பிறிதொன்றன்<br />புண்ண துணர்வார்ப் பெறின்','புலால் என்பது வேறோர் உயிரின் உடற்புண் என்பதை உணர்ந்தோர் அதனை உண்ணாமல் இருக்கவேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(258,'258','செயிரின் தலைப்பிரிந்த காட்சியா ருண்ணார்<br />உயிரின் தலைப்பிரிந்த ஊன்','மாசற்ற மதியுடையோர், ஓர் உயிரைப் பிரித்து அதன் ஊனை உண்ண மாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(259,'259','அவிசொரிந் தாயிரம் வேட்டலின் ஒன்றன்<br />உயிர்செகுத் துண்ணாமை நன்று','நெய் போன்ற பொருள்களைத் தீயிலிட்டு ஆயிரம் வேள்விகளை நடத்துவதைவிட உண்பதற்காக ஓர் உயிரைப் போக்காமலிருப்பது நல்லது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(260,'260','கொல்லான் புலாலை மறுத்தானைக் கைகூப்பி<br />எல்லா உயிருந் தொழும்','புலால் உண்ணாதவர்களையும், அதற்காக உயிர்களைக் கொல்லாதவர்களையும் எல்லா உயிரினங்களும் வணங்கி வாழ்த்தும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(261,'261','உற்றநோய் நோன்றல் உயிர்க்குறுகண் செய்யாமை<br />அற்றே தவத்திற் குரு','எதையும் தாங்கும் இதயத்தைப் பெற்றிருப்பதும், எந்த உயிருக்கும் தீங்கு செய்யாமல் இருப்பதும்தான் ``தவம் என்று கூறப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(262,'262','தவமுந் தவமுடையார்க் காகும் அவமதனை<br />அஃதிலார் மேற்கொள் வது','உறுதிப்பாடும், மன அடக்கமும் உடையவருக்கே தவத்தின் பெருமை வாய்க்கும் எனவே கட்டுப்பாடான ஒழுக்கம் இல்லாதவர்கள், தவத்தை மேற்கொள்வது வீண் செயலேயாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(263,'263','துறந்தார்க்குத் துப்புரவு வேண்டி மறந்தார்கொன்<br />மற்றை யவர்கள் தவம்','துறவிகளுக்குத் துணை நிற்க விரும்புகிறோம் என்பதற்காகத் தாங்கள் கடைப்பிடிக்க வேண்டிய தவ ஒழுக்கத்தை மற்றவர்கள் மறந்து விடக் கூடாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(264,'264','ஒன்னார்த் தெறலும் உவந்தாரை யாக்கலும்<br />எண்ணின் தவத்தான் வரும்','மன உறுதியும் கட்டுப்பாடும் கொண்டு தவமென்னும் நோன்பு வலிமையுடையதாக அமைந்தால்தான், எண்ணிய மாத்திரத்தில் பகைவரை வீழ்த்தவும் நண்பரைக் காக்கவும் முடியும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(265,'265','வேண்டிய வேண்டியாங் கெய்தலால் செய்தவம்<br />ஈண்டு முயலப் படும்','உறுதிமிக்க நோன்பினால் விரும்பியதை விரும்பியவாறு அடைய முடியுமாதலால், அது விரைந்து முயன்று செய்யப்படுவதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(266,'266','தவஞ்செய்வார் தங்கருமஞ் செய்வார்மற் றல்லார்<br />அவஞ்செய்வார் ஆசையுட் பட்டு','அடக்கமும், அன்பு நெறியும், துன்பங்களைத் தாங்கும் பொறுமையும் வாய்ந்த தவம் மேற்கொண்டவர்கள் மட்டுமே தமது கடமையைச் செய்பவர்கள்; அதற்கு மாறானவர்கள், ஆசையால் அலைக்கழிக்கப்பட்டு வீணான செயல்களில் ஈடுபடுபவர்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(267,'267','சுடச்சுடரும் பொன்போல் ஒளிவிடுந் துன்பஞ்<br />சுடச்சுட நோற்கிற் பவர்க்கு','தம்மைத் தாமே வருத்திக் கொண்டு ஒரு குறிக்கோளுக்காக நோன்பு நோற்பவர்களை எந்தத் துன்பங்கள் தாக்கினாலும் அவர்கள் சுடச்சுட ஒளிவிடும் பொன்னைப் போல் புகழ் பெற்றே உயர்வார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(268,'268','தன்னுயிர் தானறப் பெற்றானை ஏனைய<br />மன்னுயி ரெல்லாந் தொழும்','``தனது உயிர் என்கிற பற்றும், ``தான் என்கிற செருக்கும் கொள்ளாதவர்களை உலகம் புகழ்ந்து பாராட்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(269,'269','கூற்றங் குதித்தலுங் கைகூடும் நோற்றலின்<br />ஆற்றல் தலைப்பட் டவர்க்கு','எத்தனைத் துன்பங்கள் வரினும் தாங்கிக் குறிக்கோளில் உறுதியாக நிற்கும் ஆற்றலுடையவர்கள் சாவையும் வென்று வாழ்வார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(270,'270','இலர்பல ராகிய காரணம் நோற்பார்<br />சிலர்பலர் நோலா தவர்','ஆற்றலற்றவர்கள் பலராக இருப்பதற்குக் காரணம், மன உறுதி கொண்டவர் சிலராக இருப்பதும், உறுதியற்றவர் பலராக இருப்பதும் தான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(271,'271','வஞ்ச மனத்தான் படிற்றொழுக்கம் பூதங்கள்<br />ஐந்தும் அகத்தே நகும்','ஒழுக்க சீலரைப் போல உலகத்தை ஏமாற்றும் வஞ்சகரைப் பார்த்து அவரது உடலில் கலந்துள்ள நிலம், நீர், தீ, காற்று, வெளி எனப்படும் பஞ்சபூதங்களும் தமக்குள் சிரித்துக் கொள்ளும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(272,'272','வானுயர் தோற்றம் எவன்செய்யுந் தன்நெஞ்சத்<br />தானறி குற்றப் படின்','தன் மனத்திற்குக் குற்றம் என்று தெரிந்தும்கூட அதைச் செய்பவர், துறவுக்கோலம் பூண்டிருப்பதால் எந்தப் பயனும் இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(273,'273','வலியில் நிலைமையான் வல்லுருவம் பெற்றம்<br />புலியின்தோல் போர்த்துமேய்ந் தற்று','மனத்தை அடக்க முடியாதவர் துறவுக்கோலம் பூணுவது, பசு ஒன்று புலித்தோலைப் போர்த்திக் கொண்டு பயிரை மேய்வது போன்றதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(274,'274','தவமறைந் தல்லவை செய்தல் புதல்மறைந்து<br />வேட்டுவன் புள்சிமிழ்த் தற்று','புதரில் மறைந்து கொண்டு வேடன் பறவைகளைக் கண்ணி வைத்துப் பிடிப்பதற்கும், தவக்கோலத்தில் இருப்பவர்கள் தகாத செயல்களில் ஈடுபடுவதற்கும் வேறுபாடு இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(275,'275','பற்றற்றேம் என்பார் படிற்றொழுக்கம் எற்றெற்றென்<br />றேதம் பலவுந் தரும்','எத்தகைய செயல் புரிந்துவிட்டோம் என்று தமக்குத் தாமே வருந்த வேண்டிய துன்பம், பற்றுகளை விட்டு விட்டதாகப் பொய்கூறி, உலகை ஏமாற்றுவோர்க்கு வந்து சேரும��',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(276,'276','நெஞ்சின் துறவார் துறந்தார்போல் வஞ்சித்து<br />வாழ்வாரின் வன்கணா ரில்','உண்மையிலேயே மனதாரப் பற்றுகளைத் துறக்காமல் துறந்தவரைப் போல் வாழ்கின்ற வஞ்சகர்களைவிட இரக்கமற்றவர் யாருமில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(277,'277','புறங்குன்றி கண்டனைய ரேனும் அகங்குன்றி<br />மூக்கிற் கரியா ருடைத்து','வெளித்தோற்றத்துக்குக் குன்றிமணி போல் சிவப்பாக இருந்தாலும், குன்றிமணியின் முனைபோலக் கறுத்த மனம் படைத்தவர்களும் உலகில் உண்டு',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(278,'278','மனத்தது மாசாக மாண்டார்நீ ராடி<br />மறைந்தொழுகு மாந்தர் பலர்','நீருக்குள் மூழ்கியோர் தம்மை மறைத்துக் கொள்வது போல, மாண்புடையோர் எனும் பெயருக்குள் தம்மை மறைத்துக்கொண்டு மனத்தில் மாசுடையோர் பலர் உலவுகின்றனர்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(279,'279','கணைகொடியது யாழ்கோடு செவ்விதாங் கனை<br />வினைபடு பாலாற் கொளல்','நேராகத் தோன்றும் அம்பு, கொலைச் செயல் புரியும் வளைந்து தோன்றும் யாழ், இசை, இன்பம் பயக்கும் அது போலவே மக்களின் பண்புகளையும் அவர்களது செயலால் மட்டுமே உணர்ந்து கொள்ள வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(280,'280','மழித்தலும் நீட்டலும் வேண்டா உலகம்<br />பழித்த தொழித்து விடின்','உலகத்தாரின் பழிப்புக்கு உள்ளாகும் செயல்களைத் துறக்காமல் ஒரு துறவி, தனது தலையை மொட்டையடித்துக் கொண்டோ, சடாமுடி வளர்த்துக் கொண்டோ கோலத்தை மட்டும் மாற்றிக் கொள்வது ஒரு ஏமாற்று வித்தையே ஆகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(281,'281','எள்ளாமை வேண்டுவா னென்பான் எனைத்தொன்றுங்<br />கள்ளாமை காக்கதன் நெஞ்சு','எந்தப் பொருளையும் களவாடும் நினைவு தன் நெஞ்சை அணுகாமல் பார்த்துக் (காத்துக்) கொள்பவனே இகழ்ச்சிக்கு ஆட்படாமல் வாழ முடியும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(282,'282','உள்ளத்தால் உள்ளலுந் தீதே பிறன்பொருளைக்<br />கள்ளத்தால் கள்வே மெனல்','பிறருக்குரிய பொருளைச் சூழ்ச்சியினால் கவர்ந்து கொள்ளலாமா என்று ஒருவன் நினைப்பதேகூடக் குற்றமாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(283,'283','களவினா லாகிய ஆக்கம் அளவிறந்<br />தாவது போலக் கெடும்','கொள்ளயடித்துப் பொருள் குவிப்பது, முதலில் பெரிதாகத் தோன்றினாலும், அந்தச் செயல் ஏற்கனவே இருந்த செல்வத்தையும் அடித்து கொண்டு போய்விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(284,'284','களவின்கண் கன்றிய காதல் விளைவின்கண்<br />வீயா விழுமந் தரும்','களவு செய்வதில் ஒருவனுக்கு ஏற்படும் தணியாத தாகம், அதனால் உருவாகும் விளைவுகளால் தீராத துன்பத்தை உண்டாக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(285,'285','அருள்கருதி அன்புடைய ராதல் பொருள்கருதிப்<br />பொச்சாப்புப் பார்ப்பார்க ணில்','மறந்திருக்கும் நேரம் பார்த்துப் பிறர் பொருளைக் களவாட எண்ணுபவரிடத்தில், அருள் கருதி அன்பாக நடக்கும் பண்பு இருக்காது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(286,'286','அளவின்கண் நின்றொழுக லாற்றார் களவின்கண்<br />கன்றிய காத லவர்','ஓர் எல்லைக்குட்பட்டு வாழ்வைச் செம்மையாக அமைத்துக் கொள்ளாதவர்கள், களவு செய்து பிறர் பொருளைக் கொள்வதில் நாட்டமுடையவராவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(287,'287','களவென்னுங் காரறி வாண்மை அளவென்னும்<br />ஆற்றல் புரிந்தார்க ணில்','அளவறிந்து வாழ்க்கை நடத்துகிற ஆற்றலுடையவர்களிடம், களவாடுதல் எனும் சூதுமதி கிடையாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(288,'288','அளவறிந்தார் நெஞ்சத் தறம்போல நிற்குங்<br />களவறிந்தார் நெஞ்சில் கரவு','நேர்மையுள்ளவர் நெஞ்சம் அறவழியில் செல்லும்; கொள்ளையடிப்போர் நெஞ்சமோ குறுக்குவழியான வஞ்சக வழியில் செல்லும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(289,'289','அளவல்ல செய்தாங்கே வீவர் களவல்ல<br />மற்றைய தேற்றா தவர்','அளவு என்பதைத் தவிர வேறு நல்வழிகளை நாடாதவர்கள், வரம்பு கடந்த செயல்களால் வாழ்விழந்து வீழ்வார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(290,'290','கள்வார்க்குத் தள்ளும் உயிர்நிலை கள்ளார்க்குத் <br />தள்ளாது புத்தே ளுலகு','களவாடுபவர்க்கு உயிர் வாழ்வதேகூடத் தவறிப்போகும்; களவை நினைத்தும் பார்க்காதவர்க்கோ, புகழுலக வாழ்க்கை தவறவே தவறாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(291,'291','வாய்மை எனப்படுவ தியாதெனின் யாதொன்றந்<br />தீமை யிலாத சொலல்','பிறருக்கு எள்முனையளவு தீமையும் ஏற்படாத ஒரு சொல்லைச் சொல்வதுதான் வாய்மை எனப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(292,'292','பொய்ம்மையும் வாய்மை யிடத்த புரைதீர்ந்த<br />நன்மை பயக்கு மெனின்','குற்றமற்ற நன்மையை விளைவிக்கக் கூடுமானால் பொய்யான சொல்லும்கூட வாய்மை என்று கூறத்தக்க இடத்தைப் பெற்றுவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(293,'293','தன்நெஞ் சறிவது பொய்யற்க பொய்த்தபின்<br />தன்நெஞ்சே தன்னைச் சுடும்','மனச்சாட்சிக்கு எதிராகப் பொய் சொல்லக்கூடாது; அப்படிச் சொன்னால், சொன்னவரின் மனமே அவரைத் தண்டிக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(294,'294','உள்ளத்தாற் பொய்யா தொழுகின் உலகத்தார்<br />உள்ளத்து ளெல்லாம் உளன்','மனத்தால்கூடப் பொய்யை நினைக்காமல் வாழ்பவர்கள், மக்கள் மனத்தில் நிலையான இடத்தைப் பெறுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(295,'295','மனத்தொடு வாய்மை மொழியின் தவத்தொடு<br />தானஞ்செய் வாரின் தலை','உதட்டளவில் இன்றி உளமார வாய்மை பேசுகிறவர்கள் தவமும், தானமும் செய்கின்றவர்களைவிட உயர்ந்தவர்களாவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(296,'296','பொய்யாமை யன்ன புகழில்லை எய்யாமை<br />எல்லா அறமுந் தரும்','பொய் இல்லாமல் வாழ்வது போன்ற புகழ் மிக்க வாழ்வு வேறு எதுவுமில்லை; என்றும் நீங்காத அறவழி நலன்களை அளிப்பது அந்த வாழ்வேயாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(297,'297','பொய்யாமை பொய்யாமை ஆற்றின் அறம்பிற<br />செய்யாமை செய்யாமை நன்று','செய்யக்கூடாததைச் செய்யாததால் விளையும் நன்மையைவிடப் பொய் கூறாத பண்பு பொய்த்துப் போகாமல் கடைப்பிடிக்கும் அறவழி நன்மை தருவதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(298,'298','புறந்தூய்மை நீரா னமையும் அகந்தூய்மை<br />வாய்மையால் காணப் படும்','நீரில் குளிப்பதால் உடலின் அழுக்கு மட்டுமே நீங்கும்; மனம் அழுக்குப்படாமல் தூய்மையுடன் விளங்கிட, சொல்லிலும் செயலிலும் வாய்மை வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(299,'299','எல்லா விளக்கும் விளக்கல்ல சான்றோர்க்குப்<br />பொய்யா விளக்கே விளக்கு','புறத்தின் இருளைப் போக்கும் விளக்குகளைவிட அகத்தின் இருளைப் போக்கும் பொய்யாமை எனும் விளக்கே ஒருவனை உயர்ந்தோன் எனக் காட்டும் ஒளிமிக்க விளக்காகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(300,'300','யாமெய்யாக் கண்டவற்று ளில்லை எனைத்தொன்றும்<br />வாய்மையின் நல்ல பிற','வாய்மையைப் போல் சிறந்த பண்பு வேறொன்றுமே இல்லை என்பதுதான் ஆராய்ந்து உணரப்பட்ட உண்மையாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(301,'301','செல்லிடத்துக் காப்பான் சினங்காப்பான் அல்லிடத்துக்<br />காக்கினென் காவாக்கா லென்','தன் சினம் பலிதமாகுமிடத்தில் சினம் கொள்ளாமல் இருப்பவனே சினங்காப்பவன்; பலிக்காத இடத்தில் சினத்தைக் காத்தால் என்ன? காக்காவிட்டால் என்ன?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(302,'302','செல்லா இடத்துச் சினந்தீது செல்லிடத்தும்<br />இல்லதனின் தீய பிற','வலியோரிடம் சினம் கொண்டால், அதனால் கேடு விளையும் மெலியோரிடம் சினம் கொண்டாலும் அதைவிடக் கேடு வேறொன்றுமில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(303,'303','மறத்தல் வெகுளியை யார்மாட்டுந் தீய<br />பிறத்தல் அதனான் வரும்','யார்மீது சினம் கொண்டாலும் அதை மறந்துவிட வேண்டும் இல்லாவிட்டால் அந்தச் சினமே தீய விளைவுகளுக்குக் காரணமாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(304,'304','நகையும் உவகையுங் கொல்லுஞ் சினத்தின்<br />பகையும் உளவோ பிற','சினம் கொள்கிறவர்களுக்கு முகமலர்ச்சி மாத்திரமின்றி மனமகிழ்ச்சியும் மறைந்து போய் விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(305,'305','தன்னைத்தான் காக்கின் சினங்காக்க காவாக்கால்<br />தன்னையே கொல்லுஞ் சினம்','ஒருவன் தன்னைத்தானே காத்துக் கொள்ள வேண்டுமானால், சினத்தைக் கைவிட வேண்டும் இல்லையேல் சினம், அவனை அழித்துவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(306,'306','சினமென்னுஞ் சேர்ந்தாரைக் கொல்லி இனமென்னும்<br />ஏமப் புணையைச் சுடும்','சினங்கொண்டவரை அழிக்கக் கூடியதாகச் சினமென்னும் தீயே இருப்பதால், அது அவரை மட்டுமின்றி, அவரைப் பாதுகாக்கும் தோணி போன்ற சுற்றத்தையும் அழித்துவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(307,'307','சினத்தைப் பொருளென்று கொண்டவன் கேடு<br />நிலத்தறைந்தான் கைபிழையா தற்று','நிலத்தைக் கையால் அறைந்தவனுக்கு அவன் கைதான் வலிக்கும் அது போலத்தான் சினத்தைப் பண்பாகக் கொண்டவன் நிலையும் ஆகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(308,'308','இணரெரி தோய்வன்ன இன்னா செயினும்<br />புணரின் வெகுளாமை நன்று','தீயினால் சுட்டெரிப்பது போன்ற துன்பங்களை ஒருவன் தொடர்ந்து செய்தாலும் அதற்காக வருந்தி அவன் உறவு கொள்ள வரும் போது சினங்கொள்ளாமல் இருப்பதே நல்லது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(309,'309','உள்ளிய தெல்லாம் உடனெய்தும் உள்ளத்தால்<br />உள்ளான் வெகுளி யெனின்','உள்ளத்தால் சினங்கொள்ளாதவனாக இருந்தால் எண்ணியவற்றையெல்லாம் உடனடியாகப் பெற முடியும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(310,'310','இறந்தார் இறந்தா ரனையர் சினத்தைத்<br />துறந்தார் துறந்தார் துணை','எல்லையற்ற சினம் கொள்வார் இறந்தவர்க்கு ஒப்பாவார் சினத்தை அறவே துறந்தவர் துறவிக்கு ஒப்பாவார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(311,'311','சிறப்பீனுஞ் செல்வம் பெறினும் பிறர்க்கின்னா<br />செய்யாமை மாசற்றார் கோள்','மிகுந்த செழிப்பைத் தருகின்ற செல்வத்தைப் பெறக் கூடுமென்றாலும் அதன் பொருட்டுப் பிறருக்குப் கேடு செய்யாமலிருப்பதே மாசற்றவர்களின் கொள்கையாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(312,'312','கறுத்தின்னா செய்தவக் கண்ணும் மறுத்தின்னா<br />செய்யாமை மாசற்றார் கோள்','சினங்கொண்டு சொல்லாலோ செயலாலோ ஒருவன் துன்பம் தரும்போது அந்தத் துன்பத்தை அவனுக்குத் திரும்பச் செய்யாமல் தாங்கிக் கொள்வதே சிறந்த மனிதரின் கொள்கையாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(313,'313','செய்யாமற் செற்றார்க்கும் இன்னாத செய்தபின்<br />உய்யா விழுமந் தரும்','யாருக்கும் கேடு செய்யாமல் இருப்பவருக்குப் பகைவர் கேடு செய்துவிட்டால் அதற்குப் பதிலாக அவருக்கு வரும் கேடு மீளாத் துன்பம் தரக் கூடியதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(314,'314','இன்னாசெய் தாரை ஒறுத்தல் அவர்நாண<br />நன்னயஞ் செய்து விடல்','நமக்குத் தீங்கு செய்தவரைத் தண்டிப்பதற்குச் சரியான வழி, அவர் வெட்கித் தலைகுனியும்படியாக அவருக்கு நன்மை செய்வதுதான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(315,'315','அறிவினான் ஆகுவ துண்டோ பிறிதின்நோய்<br />தந்நோய்போற் போற்றாக் கடை','பிற உயிர்களுக்கு வரும் துன்பத்தைத் தம் துன்பம் போலக் கருதிக் காப்பாற்ற முனையாதவர்களுக்கு அறிவு இருந்தும் அதனால் எந்தப் பயனுமில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(316,'316','இன்னா எனத்தா னுணர்ந்தவை துன்னாமை<br />வேண்டும் பிறன்கட் செயல்','ஒருவன் தன்னுடைய வாழ்க்கையில் துன்பமானவை என்று அனுபவித்து அறிந்தவற்றை, மற்றவர்க்குச் செய்யாமலிருக்க வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(317,'317','எனைத்தானும் எஞ்ஞான்றும் யார்க்கும் மனத்தானாம்<br />மாணாசெய் யாமை தலை','எவ்வளவிலும், எப்பொழுதும், எவரையும் இழிவுபடுத்தும் செயலை மனத்தால்கூட நினைக்காமல் இருப்பதே முதன்மையான சிறப்பாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(318,'318','தன்னுய��ர்க் கின்னாமை தானறிவான் என்கொலோ<br />மன்னுயிர்க் கின்னா செயல்','பிறர் தரும் துன்பத்தால் தனக்கேற்படும் துன்பத்தை உணர்ந்தவன் அந்தத் துன்பத்தைப் பிற உயிர்களுக்குத் தரவும் கூடாதல்லவா?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(319,'319','பிறர்க்கின்னா முற்பகல் செய்யின் தமக்கின்னா<br />பிற்பகல் தாமே வரும்','பிறருக்குத் தீங்கு விளைவித்துவிட்டோம் என்று ஒருவர் மகிழ்ந்து கொண்டிருக்கும்போதே, அதேபோன்ற தீங்கு அவரையே தாக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(320,'320','நோயெல்லா நோய்செய்தார் மேலவா நோய்செய்யார்<br />நோயின்மை வேண்டு பவர்','தீங்கு செய்தவருக்கே தீங்குகள் வந்து சேரும்; எனவே தீங்கற்ற வாழ்வை விரும்புகிறவர்கள், பிறருக்குத் தீங்கிழைத்தல் கூடாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(321,'321','அறவினை யாதெனின் கொல்லாமை கோறல்<br />பிறவினை எல்லாந் தரும்','எந்த உயிரையும் கொல்லாதிருப்பதே அறச்செயலாகும் கொலை செய்தல் தீயவினைகள் அனைத்தையும் விளைவிக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(322,'322','பகுத்துணடு பல்லுயி ரோம்புதல் நூலோர்<br />தொகுத்தவற்று ளெல்லாந் தலை','இருப்போர் இல்லாதோர் என்றில்லாமல், கிடைத்ததைப் பகிர்ந்துகொண்டு, எல்லா உயிர்களும் வாழ வேண்டும் என்ற சமநிலைக் கொள்கைக்கு ஈடானது வேறு எதுவுமே இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(323,'323','ஒன்றாக நல்லது கொல்லாமை மற்றதன்<br />பின்சாரப் பொய்யாமை நன்று','அறங்களின் வரிசையில் முதலில் கொல்லாமையும் அதற்கடுத்துப் பொய்யாமையும் இடம் பெறுகின்றன',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(324,'324','நல்லா றெனப்படுவ தியாதெனின் யாதொன்றுங் <br />கொல்லாமை சூழும் நெறி','எந்த உயிரையும் கொல்லக் கூடாது எனும் நெறி காப்பதுதான் நல்லற வழி எனப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(325,'325','நிலையஞ்சி நீத்தாரு ளெல்லாங் கொலையஞ்சிக்<br />கொல்லாமை சூழ்வான் தலை','உலகியல் நிலையை வெறுத்துத் துறவு பூண்டவர் எல்லோரையும்விடக் கொலையை வெறுத்துக் கொல்லாமையைக் கடைப்பிடிப்பவரே சிறந்தவராவார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(326,'326','கொல்லாமை மேற்கொண் டொழுகுவான் வாழ்நாள்மேல்<br />செல்லா துயிருண்ணுங் கூற்று','கொலை செய்யாமையை வாழ்வில் அறநெறியாகக் கொண்டவரின் பெருமையை வியந்து, சாவுகூட அவர் உயிரைப் பறிக்கத் தயங்கி நிற்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(327,'327','தன்னுயிர் நீப்பினுஞ் செய்யற்க தான்பிறி<br />தின்னுயிர் நீக்கும் வினை','தன்னுயிரே போவதாக இருப்பினும்கூட அதற்காக இன்னொரு உயிரைப் போக்கும் செயலில் ஈடுபடக்கூடாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(328,'328','நன்றாகும் ஆக்கம் பெரிதெனினுஞ் சான்றோர்க்குக்<br />கொன்றாகும் ஆக்கங் கடை','பெரிதாக நன்மை தரக்கூடிய அளவுக்கு ஒரு கொலை பயன்படக் கூடுமெனினும், நல்ல பண்புடைய மக்கள், அந்த நன்மையை இழிவானதாகவே கருதுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(329,'329','கொலைவினைய ராகிய மாக்கள் புலைவினையர்<br />புன்மை தெரிவா ரகத்து','பகுத்தறிவை இழந்து செயல்படும் கொலைகாரர்களைச் சான்றோர் உள்ளம், இழிதகைப் பிறவிகளாகவே கருதும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(330,'330','உயிருடம்பின் நீக்கியா ரென்ப செயிருடம்பின்<br />செல்லாத்தீ வாழ்க்கை யவர்','வறுமையும் நோயும் மிகுந்த தீய வாழ்க்கையில் உழல்வோர், ஏற்கனவே கொலைகள் பல செய்தவராக இருப்பர் என்று முன்னோர் கூறுவர்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(331,'331','நில்லாத வற்றை நிலையின என்றுணரும்<br />புல்லறி வாண்மை கடை','நிலையற்றவைகளை நிலையானவை என நம்புகின்ற அறியாமை மிக இழிவானதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(332,'332','கூத்தாட் டவைக்குழாத் தற்றே பெருஞ்செல்வம்<br />போக்கும் அருவிளிந் தற்று','சேர்த்து வைத்த பணமும் சொத்தும் ஒருவரை விட்டுப் போவது, கூத்து முடிந்ததும் மக்கள் அரங்கத்தை விட்டுக் கலைந்து செல்வத��ப் போன்றதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(333,'333','அற்கா இயல்பிற்றுச் செல்வம் அதுபெற்றால்<br />அற்குப ஆங்கே செயல்','நம்மை வந்தடையும் செல்வம் நிலையற்றது என்பதை உணர்ந்து அதைக் கொண்டு அப்பொழுதே நிலையான நற்செயல்களில் ஈடுபட வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(334,'334','நாளென ஒன்றுபோற் காட்டி உயிரீரும்<br />வாள துணர்வார்ப் பெறின்','வாழ்க்கையைப் பற்றி உணர்ந்தவர்கள், நாள் என்பது ஒருவரின் ஆயுளை அறுத்துக் குறைத்துக் கொண்டேயிருக்கும் வாள் என்று அறிவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(335,'335','நாச்செற்று விக்குள்மேல் வாராமுன் நல்வினை<br />மேற்சென்று செய்யாப் படும்','வாழ்க்கையின் நிலையாமையை உணர்ந்து நம் உயிர் இருக்கும் போதே உயர்ந்த நற்பணிகளை ஆற்றிட முனைய வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(336,'336','நெருந லுளனொருவன் இன்றில்லை என்னும்<br />பெருமை யுடைத்திவ் வுலகு','இந்த உலகமானது, நேற்று உயிருடன் இருந்தவரை இன்று இல்லாமல் செய்து விட்டோம் என்ற அகந்தையைப் பெருமையாகப் கொண்டதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(337,'337','ஒருபொழுதும் வாழ்வ தறியார் கருதுப<br />கோடியு மல்ல பல','ஒரு பொழுதுகூட வாழ்க்கையைப் பற்றி உண்மையைச் சிந்தித்து அறியாதவர்களே, ஆசைக்கோர் அளவின்றி மனக் கோட்டைகள் கட்டுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(338,'338','குடம்பை தனித்தொழியப் புள்பறந் தற்றே<br />உடம்போ டுயிரிடை நட்பு','உடலுக்கும் உயிருக்கும் உள்ள உறவு முட்டைக்கும் பறவைக் குஞ்சுக்கும் உண்டான உறவு போன்றதுதான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(339,'339','உறங்குவது போலுஞ் சாக்கா டுறங்கி<br />விழிப்பது போலும் பிறப்பு','நிலையற்ற வாழ்க்கையில், உறக்கத்திற்குப் பிறகு விழிப்பதைப் போன்றது பிறப்பு; திரும்ப விழிக்க முடியாத மீளா உறக்கம் கொள்வதே இறப்பு',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(340,'340','புக்கி லமைந்தின்று கொல்லோ உடம்பினுள்<br />துச்சி லிருந்த உயிர்க்கு','உடலுடன் தங்கியுள்ள உயிருக்கு அதனைப் பிரிந்தால் வேறு புகலிடம் கிடையாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(341,'341','யாதனின் யாதனின் நீங்கியான் நோதல்<br />அதனின் அதனின் அலன்','ஒருவன் பல வகையான பற்றுகளில் எந்த ஒன்றை விட்டு விட்டாலும், குறிப்பிட்ட அந்தப் பற்று காரணமாக வரும் துன்பம், அவனை அணுகுவதில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(342,'342','வேண்டினுண் டாகத் துறக்க துறந்தபின்<br />ஈண்டியற் பால பல','ஒருவனைத் துன்பம் துளைத்தெடுக்காமல் இருக்க எல்லாம் இருக்கும் போதே அவற்றைக் துறந்து விடுவானேயானால், அவன் உலகில் பெறக்கூடிய இன்பங்கள் பலவாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(343,'343','அடல்வேண்டும் ஐந்தன் புலத்தை விடல்வேண்டும்<br />வேண்டிய வெல்லாம் ஒருங்கு','ஐம் புலன்களையும் அடக்கி வெல்வதும், அப்புலன்கள் விரும்புகின்றவற்றையெல்லாம் விட்டுவிடுவதும் துறவுக்கு இலக்கணமாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(344,'344','இயல்பாகும் நோன்பிற்கொன் றின்மை உடைமை<br />மயலாகும் மற்றும் பெயர்த்து','ஒரு பற்றும் இல்லாதிருத்தலே துறவுக்கு ஏற்றதாகும் ஓன்றன் மேல் பற்று வைப்பினும், அது மேன்மேலும் பற்றுகளைப் பெருக்கி மயங்கச் செய்துவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(345,'345','மற்றுந் தொடர்ப்பா டெவன்கொல் பிறப்பறுக்கல்<br />உற்றார்க் குடம்பும் மிகை','பிறந்ததால் ஏற்படும் துன்பத்தைப் போக்க முயல்கின்ற துறவிகளுக்கு அவர்களின் உடம்பே மிகையான ஒன்றாக இருக்கும்போது, அதற்கு மேலும் வேறு தொடர்பு எதற்காக?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(346,'346','யானென தென்னுஞ் செருக்கறுப்பான் வானோர்க்<br />குயர்ந்த உலகம் புகும்','யான், எனது என்கின்ற ஆணவத்தை அறவே விலக்கி விட்டவன், வான்புகழையும் மிஞ்சுகின்ற உலகப் புகழுக்கு உரியவனாவான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(347,'347','பற்றி விடாஅ இடும்பைகள் பற்றினைப்<br />பற்றி விடாஅ தவர்க்கு','பற்றுகளைப் பற்றிக்கொண்டு விடாதவர்களைத் துன்பங்களும் விடாமல் பற்றிக் கொள்கின்றன',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(348,'348','தலைப்பட்டார் தீரத் துறந்தார் மயங்கி<br />வலைப்பட்டார் மற்றை யவர்','அரைகுறையாக இல்லாமல் அனைத்தும் துறந்தவர்களே உயர்ந்த நிலையை அடைவார்கள் அவ்வாறு துறவாதவர்கள் அறியாமையென்னும் வலையில் சிக்கியவர்களாவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(349,'349','பற்றற்ற கண்ணே பிறப்பறுக்கும் மற்று<br />நிலையாமை காணப் படும்','பற்றுகளைத் துறந்துவிட்டால், பிறப்பில் ஏற்படும் இன்ப துன்பங்கள் வருவதில்லை இல்லையேல், அந்த இன்ப துன்பங்கள் மாறிமாறி வரக்கூடிய நிலையாமை தோன்றும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(350,'350','பற்றுக பற்றற்றான் பற்றினை அப்பற்றைப்<br />பற்றுக பற்று விடற்கு','எதிலும் பற்றில்லாதவராக யார் இருக்கிறாரோ அவரிடம் மட்டும் பற்றுக் கொள்ள வேண்டும் துறவறத்தினர் தம் பற்றுகளை விட்டொழிப்பதற்கு அத்தகையோரிடம் கொள்ளும் பற்றுதான் துணை நிற்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(351,'351','பொருளல்ல வற்றைப் பொருளென் றுணரும்<br />மருளானாம் மாணாப் பிறப்பு','பொய்யான ஒரு பொருளை மெய்ப்பொருள் என்று மயங்கி நம்புகிறவனின் வாழ்க்கை சிறப்பாக அமையாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(352,'352','இருள்நீங்கி இன்பம் பயக்கும் மருள்நீங்கி<br />மாசறு காட்சி யவர்க்கு','மயக்கம் தெளிந்து மாசற்ற உண்மையை உணர்ந்தால் அறியாமை அகன்று நலம் தோன்றும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(353,'353','ஐயத்தின் நீங்கித் தெளிந்தார்க்கு வையத்தின் <br />வான நணிய துடைத்து','ஐயப்பாடுகளைத் தெளிந்த ஆராய்ச்சி வாயிலாகத் தீர்த்துக் கொண்டவர்களுக்குப் பூமியைவிட வானம் மிக அருகில் இருப்பதாகக் கருதுகின்ற ஊக்கம் ஏற்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(354,'354','ஐயுணர் வெய்தியக் கண்ணும் பயமின்றே<br />மெய்யுணர் வில்லா தவர்க்கு','உண்மையைக் கண்டறிந்து தெளிவடையாதவர்கள், தமது ஐம்புலன்களையும் அடக்கி வெற்றி கண்டிருந்தாலும் கூட அதனால் அவர்களுக்கு எந்தப் பயனும் இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(355,'355','எப்பொரு ளெத்தன்மைத் தாயினும் அப்பொருள்<br />மெய்ப்பொருள் காண்ப தறிவு','வெளித்தோற்றத்தைப் பார்த்து ஏமாந்து விடாமல், அதுபற்றிய உண்மையை உணர்வதுதான் அறிவுடைமையாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(356,'356','கற்றீண்டு மெய்ப்பொருள் கண்டார் தலைப்படுவர்<br />மற்றீண்டு வாரா நெறி','துறவற வாழ்வுக்குத் தகுதியுடையவராகச் செய்திடும் அனைத்தையும் கற்று, உண்மைப் பொருள் உணர்ந்து அதன்படி ஒழுகுபவர், மீண்டும் இல்லற வாழ்க்கையை விரும்ப மாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(357,'357','ஓர்த்துள்ளம் உள்ள துணரின் ஒருதலையாப்<br />பேர்த்துள்ள வேண்டா பிறப்பு','உண்மையை ஆராய்ந்து உறுதியாக உணர்பவர்கள் மீண்டும் பிறப்பு உண்டு எனக் கருத மாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(358,'358','பிறப்பென்னும் பேதைமை நீங்கச் சிறப்பென்னுஞ்<br />செம்பொருள் காண்ப தறிவு','அடுத்த பிறப்பு எனக் கூறப்படும் அறியாமையைப் போக்கித் தெளிந்த உண்மையை நிலைநாட்டுவதுதான் அறிவுடைமையாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(359,'359','சார்புணர்ந்து சார்பு கெடவொழுகின் மற்றழித்துச்<br />சார்தரா சார்தரு நோய்','துன்பங்கள் நம்மைச் சாராமல் இருக்க வேண்டுமானால், அத்துன்பங்களுக்குக் காரணமானவற்றை உணர்ந்து அவற்றின் மீதுள்ள பற்றை விலக்கிக் கொள்ள வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(360,'360','காமம் வெகுளி மயக்கம் இவைமூன்றன்<br />நாமங் கெடக்கெடு நோய்','விருப்பு, வெறுப்பு, அறியாமை இவற்றுக்கு இடம் தராதவர்களை நெருங்குகிற துன்பம் அழிந்துவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(361,'361','அவாவென்ப எல்லா உயிர்க்கும���ஞ் ஞான்றுந்<br />தவாஅப் பிறப்பீனும் வித்து','ஆசையை, எல்லா உயிர்களிடமும், எல்லாக் காலத்திலும் தவறாமல் தோன்றி முளைக்கும் விதை என்று கூறலாம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(362,'362','வேண்டுங்கால் வேண்டும் பிறவாமை மற்றது<br />வேண்டாமை வேண்ட வரும்','விரும்புவதானால் பிறக்காமலே இருந்திருக்கவேண்டும் என்று ஒருவன் எண்ணுகிற அளவுக்கு ஏற்படுகிற துன்ப நிலை, ஆசைகளை ஒழிக்காவிடில் வரும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(363,'363','வேண்டாமை யன்ன விழுச்செல்வம் ஈண்டில்லை<br />ஆண்டும் அஃதொப்ப தில்','தீமை விளைவிக்கும் ஆசைகளை வேண்டாம் என்று புறக்கணிப்பதைப் போன்ற செல்வம் இங்கு எதுவுமில்லை; வேறு எங்கும் கூட அத்தகைய ஒப்பற்ற செல்வம் இல்லையென்றே கூறலாம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(364,'364','தூஉய்மை யென்ப தவாவின்மை மற்றது<br />வாஅய்மை வேண்ட வரும்','தூய்மை என்பது பேராசையற்ற தன்மையாகும் அத்தூய்மை வாய்மையை நாடுவோர்க்கே வாய்க்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(365,'365','அற்றவ ரென்பார் அவாவற்றார் மற்றையார்<br />அற்றாக அற்ற திலர்','ஆசையனைத்தும் விட்டவரே துறவி எனப்படுவார் முற்றும் துறவாதவர், தூய துறவியாக மாட்டார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(366,'366','அஞ்சுவ தோரும் அறனே ஒருவனை<br />வஞ்சிப்ப தோரும் அவா','ஒருவரை வஞ்சித்துக் கெடுப்பதற்குக் காரணமாக இருப்பது ஆசையேயாகும் எனவே, ஆசைக்கு அடிமையாகக் கூடாது என்ற அச்சத்துடன் வாழ வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(367,'367','அவாவினை ஆற்ற அறுப்பின் தவாவினை<br />தான்வேண்டு மாற்றான் வரும்','கெடாமல் வாழ்வதற்குரிய நிலை, ஒருவன் விரும்புமாறு வாய்ப்பதற்கு, அவன் பேராசைக் குணத்தை முற்றிலும் ஒழித்தவனாக இருக்க வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(368,'368','அவாவில்லார்க் கில்லாகுந் துன்பமஃ துண்டேல்<br />தவாஅது மேன்மேல் வரும்','ஆசை இல்லாதவர்களுக்குத் துன்பம் இல்லை ஆசை உண்டானால், அதைத் தொடர்ந்து துன்பமும் மேலும் மேலும் வந்து கொண்டிருக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(369,'369','இன்பம் இடையறா தீண்டும் அவாவென்னுந்<br />துன்பத்துள் துன்பங் கெடின்','பெருந்துன்பம் தரக்கூடிய பேராசை ஒழிந்தால் வாழ்வில் இன்பம் விடாமல் தொடரும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(370,'370','ஆரா இயற்கை அவாநீப்பின் அந்நிலையே<br />பேரா இயற்கை தரும்','இயல்பாகவே எழும் அடங்காத பேராசையை அகற்றி வாழும் நிலை, நீங்காத இன்பத்தை இயல்பாகவே தரக்கூடியதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(371,'371','ஆகூழால் தோன்றும் அசைவின்மை கைப்பொருள்<br />போகூழால் தோன்று மடி','ஆக்கத்திற்கான இயற்கை நிலை சோர்வு தலை காட்டாத ஊக்கத்தைக் கொடுக்கும் ஊக்கத்தின் அழிவுக்கான இயற்கைநிலை சோம்பலை ஏற்படுத்தும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(372,'372','பேதைப் படுக்கும் இழவூழ் அறிவகற்றும்<br />ஆகலூ ழுற்றக் கடை','அழிவுதரும் இயற்கை நிலை, அறியாமையை உண்டாக்கும்; ஆக்கம் தரும் இயற்கை நிலை, அதற்கேற்ப அறிவை விரிவாக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(373,'373','நுண்ணிய நூல்பல கற்பினும் மற்றுந்தன்<br />உண்மை யறிவே மிகும்','கூரிய அறிவு வழங்கக் கூடிய நூல்களை ஒருவர் கற்றிருந்த போதிலும் அவரது இயற்கை அறிவே மேலோங்கி நிற்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(374,'374','இருவே றுலகத் தியற்கை திருவேறு<br />தெள்ளிய ராதலும் வேறு','உலகின் இயற்கை நிலை இரு வேறுபட்டதாகும் ஒருவர் செல்வமுடையவராகவும், ஒருவர் அறிவுடையவராகவும் இருப்பதே அந்த வேறுபாடாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(375,'375','நல்லவை யெல்லாஅந் தீயவாந் தீயவும் <br />நல்லவாஞ் செல்வஞ் செயற்கு','நல்ல செயல்களை ஆற்ற முற்படும்போது அவை தீமையில் போய் முடிந்துவிடுவதும், தீய செயல்களை ஆற்றிட முனையும்போது அவை நல்லவைகளாக முடிந்து விடுவதும் இயற்கை நிலை எனப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(376,'376','பரியினும் ஆகாவாம் பாலல்ல உய்த்துச்<br />சொரியினும் போகா தம','தனக்கு உரிமையல்லாதவற்றை எவ்வளவுதான் பாதுகாப்பாக வைத்தாலும் அவை தங்காமல் போய்விடக் கூடும்; உரிமையுள்ளவற்றை எங்கே கொண்டு போய்ப் போட்டாலும் அவை எங்கும் போகமாட்டா',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(377,'377','வகுத்தான் வகுத்த வகையல்லாற் கோடி<br />தொகுத்தார்க்குந் துய்த்த லரிது','வகுத்து முறைப்படுத்திய வாழ்க்கை நெறியை ஒட்டி நடக்கா விட்டால் கோடிப் பொருள் குவித்தாலும், அதன் பயனை அனுபவிப்பது என்பது அரிதேயாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(378,'378','துறப்பார்மன் துப்புர வில்லார் உறற்பால<br />ஊட்டா கழியு மெனின்','நுகர்வதற்குரியது எதுவுமில்லை என்ற உறுதியினால், தம்மை வருத்தக்கூடிய உணர்வுகள் வந்து வருத்தாமல் நீங்கிவிடுமானால் துறவறம் மேற்கொள்வர்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(379,'379','நன்றாங்கால் நல்லவாக் காண்பவர் அன்றாங்கால்<br />அல்லற் படுவ தெவன்','நன்மையும் தீமையும் வாழ்க்கையில் மாறி மாறி வரும். நன்மை கண்டு மகிழ்கிறவர்கள், தீமை விளையும்போது மட்டும் மனம் கலங்குவது ஏன்?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(380,'380','ஊழிற் பெருவலி யாவுள மற்றொன்று<br />சூழினுந் தான்முந் துறும்','இயற்கை நிலையை மாற்றி மற்றொரு செயற்கை நிலையை அமைத்திட முனைந்தாலும், இயற்கை நிலையே முதன்மையாக வந்து நிற்பதால் அதைவிட வலிமையானவையாக வேறு எவை இருக்கின்றன?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(381,'381','படைகுடி கூழமைச்சு நட்பரண் ஆறும்<br />உடையான் அரசரு ளேறு','ஆற்றல்மிகு படை, அறிவார்ந்த குடிமக்கள், குறையா வளம், குறையற்ற அமைச்சு, முரிபடாத நட்பு, மோதியழிக்க முடியாத அரண் ஆகிய ஆறு சிறப்புகளும் உடையதே அரசுகளுக்கிடையே ஆண் சிங்கம் போன்ற அரசாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(382,'382','அஞ்சாமை ஈகை அறிவூக்கம் இந்நான்கும்<br />எஞ்சாமை வேந்தற் கியல்பு','துணிவு, இரக்க சிந்தை, அறிவாற்றல், உயர்ந்த குறிக்கோளை எட்டும் முயற்சி ஆகிய நான்கு பண்புகளும் அரசுக்குரிய தகுதிகளாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(383,'383','தூங்காமை கல்வி துணிவுடைமை அம்மூன்றும்<br />நீங்கா நிலனாள் பவற்கு','காலம் தாழ்த்தாத விரைவான நடவடிக்கைகளும், அறிவுடைமையும், துணிவும் நாடாளுகின்றவர்களுக்குத் தேவையானவையும், நீங்காமல் நிலைத்திருக்க வேண்டியவையுமான பண்புகளாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(384,'384','அறனிழுக்கா தல்லவை நீக்கி மறனிழுக்கா<br />மான முடைய தரசு','அறநெறி தவறாமலும், குற்றமேதும் இழைக்காமலும், வீரத்துடனும், மானத்துடனும் ஆட்சி நடத்துபவர்களே சிறந்தவர்களாவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(385,'385','இயற்றலும் ஈட்டலுங் காத்தலுங் காத்த<br />வகுத்தலும் வல்ல தரசு','முறையாக நிதி ஆதாரங்களை வகுத்து, அரசாங்கக் கருவூலத்திற்கான வருவாயைப் பெருக்கி, அதைப் பாதுக்காத்துத் திட்டமிட்டுச் செலவிடுவதுதான் திறமையான நல்லாட்சிக்கு இலக்கணமாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(386,'386','காட்சி கெளியன் கடுஞ்சொல்லன் அல்லனேல்<br />மீக்கூறும் மன்னன் நிலம்','காட்சிக்கு எளிமையும், கடுஞ்சொல் கூறாத இனிய பண்பாடும் உடைய அரசைத்தான் உலகம் புகழும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(387,'387','இன்சொலால் ஈத்தளிக்க வல்லாற்குத் தன்சொலால்<br />தான்கண் டனைத்திவ் வுலகு','வாக்கில் இனிமையும், பிறர்க்கு வழங்கிக் காத்திடும் தன்மையும் கொண்டவர்க்கு இவ்வையகமே வசப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(388,'388','முறைசெய்து காப்பாற்றும் மன்னவன் மக்கட்<br />கிறையென்று வைக்கப் படும்','நீதிநெறியுடன் அரசு நடத்தி, மக்களைக் காப்பாற்றும் ஆட்சியாளன்தான் மக்களுக்குத் தலைவன் எனப் போற்றப்படுவான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(389,'389','செவிகைப்பச் சொற்பொறுக்கும் பண்புடை வேந்தன்<br />கவிகைக்கீழ்த் தங்கு முலகு','காதைக் குடையக்கூடிய கடுஞ்சொற்களையும் பொறுத்துக் கொள்கிற பண்பாளரின் அரசுக்குத்தான் மக்களிடம் மதிப்பு இருக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(390,'390','கொடையளி செங்கோல் குடியோம்பல் நான்கும்<br />உடையானாம் வேந்தர்க் கொளி','நலவாழ்வுக்கு வேண்டியவற்றை வழங்கியும், நிலையுணர்ந்து கருணை காட்டியும், நடுநிலை தவறாமல் ஆட்சி நடத்தியும், மக்களைப் பேணிக் காப்பதே ஓர் அரசுக்குப் புகழொளி சேர்ப்பதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(391,'391','கற்க கசடறக் கற்பவை கற்றபின் <br />நிற்க அதற்குத் தக','பிழை இல்லாதவற்றைத் தனது குறைகள் நீங்குமளவுக்குக் கற்றுக்கொள்ள வேண்டும் கற்ற பிறகு அதன்படி நடக்கவேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(392,'392','எண்ணென்ப ஏனை யெழுத்தென்ப இவ்விரண்டுங்<br />கண்ணென்ப வாழும் உயிர்க்கு','எண்ணும் எழுத்தும் எனப்படும் அறிவுக் கண்களைப் பெற்றவர்களே, உயிர் வாழ்வோர் எனக் கருதப்படுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(393,'393','கண்ணுடைய ரென்பவர் கற்றோர் முகத்திரண்டு<br />புண்ணுடையர் கல்லா தவர்','கண்ணில்லாவிடினும் அவர் கற்றவராக இருப்பின் கண்ணுடையவராகவே கருதப்படுவார் கல்லாதவருக்குக் கண் இருப்பினும் அது புண் என்றே கருதப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(394,'394','உவப்பத் தலைக்கூடி உள்ளப் பிரிதல்<br />அனைத்தே புலவர் தொழில்','மகிழ்ச்சி பொங்கிடச் சேர்ந்து பழகுவதும், பிரிந்திட நேரும் போது மனங்கலங்குவதும் அறிவிற் சிறந்தோர் செயலாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(395,'395','உடையார்முன் இல்லார்போல் ஏக்கற்றுங் கற்றார்<br />கடையரே கல்லா தவர்','அறிவுடையார் முன் அறிவில்லாதவர் போல் தாழ்ந்து நின்று, மேலும் கற்றுக்கொள்பவர்களின் ஆர்வத்தைக் கற்றுக் கொள்ளாதவர்கள் கடைநிலை மாந்தராக கருதப்படுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(396,'396','தொட்டனைத் தூறு மணற்கேணி மாந்தர்க்குக்<br />கற்றனைத் தூறும் அறிவு','தோண்டத் தோண்ட ஊற்றுநீர் கிடைப்பது போலத் தொடர்ந்து படிக்கப் படிக்க அறிவு பெருகிக் கொண்டே இருக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(397,'397','யாதானும் டாடாமால் ஊராமால் என்னொருவன்<br />சாந்துணையுங் கல்லாத வாறு','கற்றோர்க்கு எல்லா நாடுகளிலும் எல்லா ஊர்களிலும் சிறப்பு என்கிறபோது, ஒருவன் சாகும் வரையில் கற்காமல் காலம் கழிப்பது ஏனோ?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(398,'398','ஒருமைக்கண் தான்னற்ற கல்வி ஒருவற் <br />கெழுமையும் ஏமாப் புடைத்து','ஒரு தலைமுறையில் பெறும் கல்வி அறிவானது, ஏழேழு தலைமுறைக்கும் பாதுகாப்பாக அமையும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(399,'399','தாமின் புறுவ துலகின் புறக்கண்டு<br />காமுறுவர் கற்றறிந் தார்','தமக்கு இன்பம் தருகின்ற கல்வியறிவு உலகத்தாருக்கும் இன்பம் தருவதைக் கண்டு, அறிஞர்கள் மேலும் மேலும் பலவற்றைக் கற்றிட விரும்புவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(400,'400','கேடில் விழுச் செல்வங் கல்வி யொருவற்கு<br />மாடல்ல மற்றை யவை','கல்வி ஒன்றே அழிவற்ற செல்வமாகும் அதற்கொப்பான சிறந்த செல்வம் வேறு எதுவும் இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(401,'401','அரங்கின்றி வட்டாடி யற்றே நிரம்பிய<br />நூலின்றிக் கோட்டி கொளல்','நிறைந்த அறிவாற்றல் இல்லாமல் அவையில் பேசுவது ஆடுவதற்கான கட்டம் போட்டுக் கொள்ளாமலே சொக்கட்டான் விளையாடுவதைப் போன்றதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(402,'402','கல்லாதான் சொற்கா முறுதன் முலையிரண்டும்<br />இல்லாதாள் பெண்காமுற் றற்று','கல்லாதவனின் சொல்கேட்க விரும்புவது, மார்பகம் இல்லாத பெண்மீது மையல் கொள்வதற்கு ஒப்பானது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(403,'403','கல்லா தவரும் நனிநல்லர் கற்றார்முன்<br />சொல்லா திருக்கப் பெறின்','கற்றவர்களின் முன்னிலையில் எதுவும் பேசாமல் இருக்கக் கற்றிருந்தால் கல்வ�� கற்காதவர்கள்கூட நல்லவர்களாகவே கருதப்படுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(404,'404','கல்லாதான் ஒட்பங் கழியநன் றாயினுங்<br />கொள்ளார் அறிவுடை யார்','கல்வி கற்காதவனுக்கு இயற்கையாகவே அறிவு இருந்தாலும்கூட, அவனைக் கல்வியில் சிறந்தோன் என்று அறிவுடையோர் ஒப்புக் கொள்ள மாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(405,'405','கல்லா ஒருவன் தகைமை தலைப்பெய்து<br />சொல்லாடச் சோர்வு படும்','கல்வியறிவில்லாதவர்கள் தங்களைப் பெரிய மேதைகளைப் போல் காட்டிக் கொள்ளும் போலி வேடம், கற்றுத் தேர்ந்த அறிஞர்களிடம் அவர்கள் உரையாடும்போது கலைந்து போய்விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(406,'406','உளரென்னும் மாத்திரைய ரல்லால் பயவாக்<br />களரனையர் கல்லா தவர்','கல்லாதவர்களைக் களர்நிலத்துக்கு ஒப்பிடுவதே பொருத்தமானது காரணம் அவர்கள் வெறும் நடைப்பிணங்களாகவே கருதப்படுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(407,'407','நுண்மாண் நுழைபுல மில்லான் எழினலம்<br />மண்மாண் புனைபாவை யற்று','அழகான தோற்றம் மட்டுமே இருந்து, ஆழ்ந்து தெளிந்த அறிவில்லாமல் இருப்பவர்கள், கண்ணைக் கவரும் மண் பொம்மையைப் போன்றவர்களாகவே மதிக்கப்படுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(408,'408','நல்லார்கண் பட்ட வறுமையின் இன்னாதே<br />கல்லார்கண் பட்ட திரு','முட்டாள்களிடம் குவிந்துள்ள செல்வம், நல்லவர்களை வாட்டும் வறுமையைவிட அதிக துன்பத்தைத் தரும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(409,'409','மேற்பிறந்தா ராயினுங் கல்லாதார் கீழ்ப்பிறந்துங்<br />கற்றா ரனைத்திலர் பாடு','கற்றவர் என்ற பெருமை, உயர்ந்தவர் தாழ்ந்தவர் என்ற வேறுபாட்டைப் போக்கிவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(410,'410','விலங்கொடு மக்க ளனையர் இலங்குநூல்<br />கற்றாரோ டேனை யவர்','மனிதர்களுக்கும் விலங்குகளுக்குமிடையே என்ன வேற்றுமையோ, அதே அளவு வேற்றுமை அறிவு நூல்களைப் படித்தவர்களுக்கும், அந்த நூல்களைப் படிக்காதவர்களுக்கும் இடையே உண்டு',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(411,'411','செல்வத்துட் செல்வஞ் செவிச்செல்வம் அச்செல்வஞ்<br />செல்வத்து ளெல்லாந் தலை','செழுமையான கருத்துகளைச் செவிவழியாகப் பெறும் செல்வமே எல்லாச் செல்வங்களுக்கும் தலையாய செல்வமாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(412,'412','செவிக்குண வில்லாத போழ்து சிறிது<br />வயிற்றுக்கும் ஈயப் படும்','செவி வழியாக இன்பம் தரும் உணவு இல்லாதபோதே சிறிதளவு உணவு வயிற்றுக்குத் தரும் நிலை ஏற்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(413,'413','செவியுணவிற் கேள்வி யுடையார் அவியுணவின்<br />ஆன்றாரோ டொப்பர் நிலத்து','குறைந்த உணவருந்தி நிறைந்த அறிவுடன் விளங்கும் ஆன்றோர்க்கு ஒப்பாகக் கேள்வி ஞானம் எனும் செவியுணவு அருந்துவோர் எண்ணப்படுவர்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(414,'414','கற்றில னாயினுங் கேட்க அஃதொருவற்<br />கொற்கத்தின் ஊற்றாந் துணை','நூல்களைக் கற்காவிட்டாலும், கற்றவரிடம் கேட்டுத் தெரிந்து கொண்டால், அது நடை தளர்ந்தவனுக்கு உதவிடும் ஊன்றுகோலைப் போலத் துணையாக அமையும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(415,'415','இழுக்க லுடையுழி ஊற்றுக்கோ லற்றே<br />ஒழுக்க முடையார்வாய்ச் சொல்','வழுக்கு நிலத்தில் நடப்பதற்கு ஊன்றுகோல் உதவுவது போல் ஒழுக்கம் உடையவர்களின் அறிவுரையானது உதவும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(416,'416','எனைத்தானும் நல்லவை கேட்க அனைத்தானும்<br />ஆன்ற பெருமை தரும்','நல்லவற்றை எந்த அளவுக்குக் கேட்கிறோமோ அந்த அளவுக்குப் பெருமை கிடைக்கும் என்ற நம்பிக்கையுடன் இருக்க வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(417,'417','பிழைத்துணர்ந்தும் பேதைமை சொல்லா ரிழைத்துணர்ந்<br />தீண்டிய கேள்வி யவர்','எதையும் நுணுகி ஆராய்வதுடன் கேள்வி அறிவும் உடையவர்கள், சிலவற்றைப் பற்றித் தவறாக உணர்ந்திருந்தாலும் கூட, அப்போதும் அறிவற்ற முறையில் பேசமாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(418,'418','கேட்பினுங் கேளாத் தகையவே கேள்வியால்<br />தோட்கப் படாத செவி','இயற்கையாகவே கேட்கக்கூடிய காதுகளாக இருந்தாலும் அவை நல்லோர் உரைகளைக் கேட்க மறுத்தால் செவிட்டுக் காதுகள் என்றே கூறப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(419,'419','நுணங்கிய கேள்விய ரல்லார் வணங்கிய<br />வாயின ராத லரிது','தெளிவான கேள்வியறிவு இல்லாதவர்கள், அடக்கமாகப் பேசும் அமைதியான பண்புடையவர்களாக இருக்க இயலாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(420,'420','செவியிற் சுவையுணரா வாயுணர்வின் மாக்கள்<br />அவியினும் வாழினு மென்','செவிச்சுவை உணராமல் வாயின் சுவைக்காக மட்டுமே வாழும் மக்கள் உயிரோடு இருப்பதும் ஒன்றுதான் இல்லாமற் போவதும் ஒன்றுதான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(421,'421','அறிவற்றங் காக்குங் கருவி செறுவார்க்கும்<br />உள்ளழிக்க லாகா அரண்','பகையால் அழிவு வாராமல் பாதுகாக்கும் அரண், அறிவு ஒன்றுதான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(422,'422','சென்ற இடத்தாற் செலவிடா தீதொரீஇ<br />நன்றின்பா லுய்ப்ப தறிவு','மனம் போகும் வழியெல்லாம் போக விடாமல் தீய வழிகளைத் தள்ளிவிட்டு, நல்வழியைத் தேர்வு செய்வதே அறிவுடைமையாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(423,'423','எப்பொருள் யார்யார்வாய்க் கேட்பினும் அப்பொருள்<br />மெய்ப்பொருள் காண்ப தறிவு','எந்தவொரு பொருள்குறித்து எவர் எதைச் சொன்னாலும், அதை அப்படியே நம்பி ஏற்றுக் கொள்ளாமல் உண்மை எது என்பதை ஆராய்ந்து தெளிவதுதான் அறிவுடைமையாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(424,'424','எண்பொருள வாகச் செலச்சொல்லித் தான்பிறர்வாய்<br />நுண்பொருள் காண்ப தறிவு','நாம் சொல்ல வேண்டியவைகளை எளிய முறையில் கேட்போரின் இதயத்தில் பதியுமாறு சொல்லிப் பிறர் சொல்லும் நுட்பமான கருத்துக்களையும் ஆராய்ந்து தெளிவதே அறிவுடைமையாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(425,'425','உலகந் தழீஇய தொட்பம் மலர்தலுங்<br />கூம்பலு மில்ல தறிவு','உயர்ந்தோரே உலகோர் எனப்படுவதால் அவர்களுடன் நட்பு கொண்டு இன்பம் துன்பம் ஆகிய இரண்டையும் ஒரே நிலையாகக் கருதுவதே அறிவுடைமையாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(426,'426','எவ்வ துறைவ துலக முலகத்தோ<br />டவ்வ துறைவ தறிவு','உயர்ந்தோர் வழியில் உலகம் எவ்வாறு நடைபெறுகின்றதோ அதற்கேற்ப நடந்து கொள்வதே அறிவாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(427,'427','அறிவுடையார் ஆவ தறிவார் அறிவிலார்<br />அஃதறி கல்லா தவர்','ஒரு விளைவுக்கு எதிர் விளைவு எப்படியிருக்குமென அறிவுடையவர்கள்தான் சிந்திப்பார்கள்; அறிவில்லாதவர்கள் சிந்திக்க மாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(428,'428','அஞ்சுவ தஞ்சாமை பேதைமை அஞ்சுவ<br />தஞ்சல் அறிவார் தொழில்','அறிவில்லாதவர்கள்தான் அஞ்ச வேண்டியதற்கு அஞ்ச மாட்டார்கள் அறிஞர்கள் மட்டுமே அஞ்ச வேண்டியதற்கு அஞ்சுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(429,'429','எதிரதாக் காக்கும் அறிவினார்க் கில்லை<br />அதிர வருவதோர் நோய்','வருமுன் அறிந்து காத்துக்கொள்ளும் திறனுடையவர்களுக்கு அதிர்ச்சி தரக்கூடிய துன்பம் ஏற்படாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(430,'430','அறிவுடையார் எல்லா முடையார் அறிவிலார்<br />என்னுடைய ரேனு மிலர்','அறிவு இல்லாதவர்களுக்கு வேறு எது இருந்தாலும் பெருமையில்லை; அறிவு உள்ளவர்களுக்கு வேறு எது இல்லாவிட்டாலும் சிறுமை இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(431,'431','செருக்குஞ் சினமுஞ் சிறுமையும் இல்லார்<br />பெருக்கம் பெருமித நீர்த்து','இறுமாப்பு, ஆத்திரம், இழிவான நடத்தை இவைகள் இல்லாதவர்களுடைய செல்வாக்குதான் மதிக்கத் தக்கதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(432,'432','இவறலும் மாண்பிறந்த மானமும் மாணா<br />உவகையும் ஏதம் இறைக்கு','மனத்தில் பேராசை, மான உணர்வில் ஊனம், மாசுபடியும் செயல்களில் மகிழ்ச்சி ஆகியவை தலை��ைக்குரிய தகுதிக்கே பெருங்கேடுகளாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(433,'433','தினைத்துணையாங் குற்றம் வரினும் பனைத்துணையாக்<br />கொள்வர் பழிநாணு வார்','பழிக்கு நாணுகின்றவர்கள், தினையளவு குற்றத்தையும் பனையளவாகக் கருதி, அதைச் செய்யாமல், தங்களைக் காத்துக் கொள்வார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(434,'434','குற்றமே காக்க பொருளாகக் குற்றமே<br />அற்றந் தரூஉம் பகை','குற்றம் புரிவது அழிவை உண்டாக்கக் கூடிய பகையாக மாறுவதால் குற்றம் புரியாமல் இருப்பது என்பதையே நோக்கமாகக் கொள்ள வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(435,'435','வருமுன்னர்க் காவாதான் வாழ்க்கை எரிமுன்னர்<br />வைத்தூறு போலக் கெடும்','முன்கூட்டியே எச்சரிக்கையாக இருந்து ஒரு தவறான செயலைத் தவிர்த்துக் கொள்ளாதவருடைய வாழ்க்கையானது நெருப்பின் முன்னால் உள்ள வைக்கோல் போர் போலக் கருகிவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(436,'436','தன்குற்ற நீக்கிப் பிறர்குற்றங் காண்கிற்பின்<br />என்குற்ற மாகும் இறைக்கு','முதலில் தனக்குள்ள குறையை நீக்கிக் கொண்டு அதன் பின்னர் பிறர் குறையைக் கண்டு சொல்லும் தலைவனுக்கு என்ன குறை நேரும்?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(437,'437','செயற்பால செய்யா திவறியான் செல்வம்<br />உயற்பால தன்றிக் கெடும்','நற்பணிகளைச் செய்யாமல் சேமித்து வைக்கப்படும் கருமியின் செல்வம் பயன் ஏதுமின்றிப் பாழாகிவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(438,'438','பற்றுள்ள மென்னும் இவறன்மை எற்றுள்ளும்<br />எண்ணப் படுவதொன் றன்று','எல்லாக் குற்றங்களையும்விடத் தனிப்பெருங் குற்றமாகக் கருதப்படுவது பொருள் சேர்ப்பதில் பற்றுக்கொண்டு எவருக்கும் எதுவும் ஈயாமல் வாழ்வதுதான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(439,'439','வியவற்க எஞ்ஞான்றுந் தன்னை நயவற்க<br />நன்றி பயவா வினை','எந்தவொரு காலகட்டத்திலும் தன்னைத்தானே உயர்வாக எண்ணிடும் தற்பெருமைகொண்டு நன்மை தராத செயல்களில் ஈடுபடக் கூடாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(440,'440','காதல காதல் அறியாமை உய்க்கிற்பின்<br />ஏதில ஏதிலார் நூல்','தமது விருப்பத்தைப் பகைவர் அறிந்து கொள்ள முடியாமல் நிறைவேற்றுபவரிடம் அந்தப் பகைவரின் எண்ணம் பலிக்காமற் போய்விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(441,'441','அறனறிந்து மூத்த அறிவுடையார் கேண்மை<br />திறனறிந்து தேர்ந்து கொளல்','அறமுணர்ந்த மூதறிஞர்களின் நட்பைப் பெறும் வகை அறிந்து, அதனைத் தேர்ந்தெடுத்துக் கொள்ள வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(442,'442','உற்றநோய் நீக்கி உறாஅமை முற்காக்கும்<br />பெற்றியார்ப் பேணிக் கொளல்','வந்துள்ள துன்பத்தைப் போக்கி, மேலும் துன்பம் நேராமல் காக்கவல்ல பெரியோர்களைத் துணையாகக் கொள்ள வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(443,'443','அரியவற்று ளெல்லாம் அரிதே பெரியாரைப்<br />பேணித் தமராக் கொளல்','பெரியவர்களைப் போற்றிப் பாராட்டி அவர்களுடன் உறவாடுதல் எல்லாப் பேறுகளையும் விடப் பெரும் பேறாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(444,'444','தம்மிற் பெரியார் தமரா ஒழுகுதல்<br />வன்மையு ளெல்லாந் தலை','அறிவு ஆற்றல் ஆகியவற்றில் தம்மைக் காட்டிலும் சிறந்த பெரியவராய் இருப்பவரோடு உறவுகொண்டு அவர்வழி நடப்பது மிகப்பெரும் வலிமையாக அமையும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(445,'445','சூழ்வார்கண் ணாக ஒழுகலான் மன்னவன்<br />சூழ்வாரைச் சூழ்ந்து கொளல்','கண்ணாக இருந்து எதனையும் கண்டறிந்து கூறும் அறிஞர் பெருமக்களைச் சூழ வைத்துக் கொண்டிருப்பதே ஆட்சியாளர்க்கு நன்மை பயக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(446,'446','தக்கா ரினத்தனாய்த் தானொழுக வல்லானைச்<br />செற்றார் செயக்கிடந்த தில்','அறிவும், ஆற்றலும் கொண்ட ஒருவன், தன்னைச் சூழவும் அத்தகையோரையே கொண்டிருந்தால் பகைவர்களால் எந்தத் தீங்கையும் விளைவிக்க முடியாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(447,'447','இடிக்குந் துணையாரை யாள்வாரை யாரே<br />கெடுக்குந் தகைமை யவர்','இடித்துரைத்து நல்வழி காட்டுபவரின் துணையைப் பெற்று நடப்பவர்களைக் கெடுக்கும் ஆற்றல் யாருக்கு உண்டு?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(448,'448','இடிப்பாரை இல்லாத ஏமரா மன்னன்<br />கெடுப்பா ரிலானுங் கெடும்','குறையை உணர்த்துவோர் இல்லாத அரசு தானாகவே கெடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(449,'449','முதலிலார்க் கூதிய மில்லை மதலையாஞ்<br />சார்பிலார்க் கில்லை நிலை','கட்டடத்தைத் தாங்கும் தூண் போலத் தம்மைத் தாங்கி நிற்கக் கூடிய துணையில்லாதவர்களின் நிலை, முதலீடு செய்யாத வாணிபத்தில் வருவாய் இல்லாத நிலையைப் போன்றதேயாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(450,'450','பல்லார் பகைகொளலிற் பத்தடுத்த தீமைத்தே<br />நல்லார் தொடர்கை விடல்','நல்லவர்களின் தொடர்பைக் கைவிடுவது என்பது பலருடைய பகையைத் தேடிக் கொள்வதை விடக் கேடு விளைவிக்கக் கூடியதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(451,'451','சிற்றினம் அஞ்சும் பெருமை சிறுமைதான்<br />சுற்றமாச் சூழ்ந்து விடும்','பெரியோர், கீழ்மக்களின் கூட்டத்தோடு சேர மாட்டார்கள் ஆனால் சிறியோர்களோ இனம் இனத்தோடு சேருமென்பதுபோல் அந்தக் கீழ் மக்கள் கூட்டத்துடன் சேர்ந்து கொள்வார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(452,'452','நிலத்தியல்பான் நீர்திரிந் தற்றாகும் மாந்தர்க்<br />கினத்தியல்ப தாகும் அறிவு','சேர்ந்த நிலத்தின் தன்மையால் நீரானது வேறுபட்டு அந்த நிலத்தின் தன்மையை அடைந்துவிடும் அதுபோல மக்களின் அறிவும், தாங்கள் சேர்ந்த இனத்தின் தன்மையைப் பெற்றதாகிவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(453,'453','மனத்தானாம் மாந்தர்க் குணர்ச்சி இனத்தானாம்<br />இன்னா னெனப்படுஞ் சொல்','ஒருவரின் உணர்ச்சி, மனத்தைப் பொருத்து அமையும் அவர் இப்படிப்பட்டவர் என்று அளந்து சொல்வது அவர் சேர்ந்திடும் கூட்டத்தைப் பொருத்து அமையும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(454,'454','மனத்து ளதுபோலக் காட்டி ஒருவற்<br />கினத்துள தாகும் அறிவு','ஒருவரின் அறிவு அவரது மனத்தின் இயல்பு என்பது போல் தோன்றினாலும், அது அவர் சேர்ந்த கூட்டத்தாரின் தொடர்பால் வெளிப்படுவதேயாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(455,'455','மனந்தூய்மை செய்வினை தூய்மை இரண்டும்<br />இனந்தூய்மை தூவா வரும்','ஒருவன் கொண்டுள்ள தொடர்பு தூய்மையானதாக இருந்தால்தான் அவனுடைய மனமும் செயலும் தூய்மையானவையாக இருக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(456,'456','மனந்தூயார்க் கெச்சநன் றாகும் இனந்தூயார்க்<br />கில்லைநன் றாகா வினை','மனத்தின் தூய்மையால் புகழும், சேர்ந்த இனத்தின் தூய்மையால் நற்செயல்களும் விளையும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(457,'457','மனநலம் மன்னுயிர்க் காக்கம் இனநலம்<br />எல்லாப் புகழுந் தரும்','மனத்தின் நலம் உயிருக்கு ஆக்கமாக விளங்கும் இனத்தின் நலமோ எல்லாப் புகழையும் வழங்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(458,'458','மனநலம் நன்குடைய ராயினுஞ் சான்றோர்க்<br />கினநலம் ஏமாப் புடைத்து','மனவளம் மிக்க சான்றோராக இருப்பினும் அவர் சேர்ந்துள்ள கூட்டத்தினரைப் பொருத்தே வலிமை வந்து வாய்க்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(459,'459','மனநலத்தி னாகும் மறுமைமற் றஃதும்<br />இனநலத்தி னேமாப் புடைத்து','நல்ல உறுதியான உள்ளம் படைத்த உயர்ந்தோராக இருந்தாலும் அவர் சார்ந்த இனத்தின் உறுதியும் அவருக்கு வலிமையான துணையாக அமையக் கூடியதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(460,'460','நல்லினத்தி னூங்குந் துணையில்லை தீயினத்தின்<br />அல்லற் படுப்பதூஉ மில்','நல்ல இனத்தைக் காட்டிலும் துணையாக இருப்பதும், தீய இனத்தைக் காட்டிலும் துன்பம் தரக்கூடியதும் எதுவுமே இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(461,'461','அழிவதூஉம் ஆவதூஉம் ஆகி வழிபயக்கும்<br />ஊதியமுஞ் சூழ்ந்து செயல்','எந்த அளவுக்���ு நன்மை கிடைக்கும் அல்லது தீமை ஏற்படும் என்று விளைவுகளைக் கணக்குப் பார்த்த பிறதே ஒரு செயலில் இறங்க வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(462,'462','தெரிந்த இனத்தொடு தேர்ந்தெண்ணிச் செய்வார்க்<br />கரும்பொருள் யாதொன்று மில்','தெளிந்து தேர்ந்த நண்பர்களுடன், சேர்ந்து, ஆற்ற வேண்டிய செயலை ஆராய்ந்து, தாமும் நன்கு சிந்தித்துச் செய்தால் ஆகாதது ஒன்றுமில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(463,'463','ஆக்கங் கருதி முதலிழக்குஞ் செய்வினை<br />ஊக்கா ரறிவுடை யார்','பெரும் ஆதாயம் கிட்டுமென்று எதிர்பார்த்துக் கை முதலையும் இழந்து விடக்கூடிய காரியத்தை அறிவுடையவர்கள் செய்யமாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(464,'464','தெளிவி லதனைத் தொடங்கார் இளிவென்னும்<br />ஏதப்பா டஞ்சு பவர்','களங்கத்துக்குப் பயப்படக் கூடியவர்கள்தான் விளைவுகளை எண்ணிப் பார்த்து அந்தக் களங்கம் தரும் காரியத்தில் இறங்காமல் இருப்பார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(465,'465','வகையறச் சூழா தெழுதல் பகைவரைப்<br />பாத்திப் படுப்பதோ ராறு','முன்னேற்பாடுகளை முழுமையாக ஆராய்ந்து செய்யாமல் பகைவரை ஒடுக்க முனைவது அந்தப் பகைவரின் வலிமையை நிலையாக வளர்க்கும் வழியாக ஆகிவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(466,'466','செய்தக்க அல்ல செயக்கெடுஞ் செய்தக்க<br />செய்யாமை யானுங் கெடும்','செய்யக் கூடாததைச் செய்வதால் கேடு ஏற்படும்; செய்ய வேண்டியதைச் செய்யாமல் விட்டாலும் கேடு ஏற்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(467,'467','எண்ணித் துணிக கருமந் துணிந்தபின்<br />எண்ணுவ மென்ப திழுக்கு','நன்றாகச் சிந்தித்த பிறகே செயலில் இறங்க வேண்டும்; இறங்கிய பிறகு சிந்திக்கலாம் என்பது தவறு',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(468,'468','ஆற்றின் வருந்தா வருத்தம் பலர்நின்று<br />போற்றினும் பொத்துப் படும்','எத்தனை பேர்தான் துணையாக இருந்தாலும் முறையாகச் செய்யப்படாத முயற்சி இறுதியில் முடங்கிப் போய்விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(469,'469','நன்றாற்ற லுள்ளுந் தவறுண் டவரவர்<br />பண்பறிந் தாற்றாக் கடை','ஒருவருடைய இயல்பைப் புரிந்து கொண்டுதான் நன்மையைக் கூடச் செய்ய வேண்டும் இல்லாவிட்டால் அதுவே தீமையாகத் திருப்பித் தாக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(470,'470','எள்ளாத எண்ணிச் செயல்வேண்டுந் தம்மொடு<br />கொள்ளாத கொள்ளா துலகு','தம்முடைய நிலைமைக்கு மாறான செயல்களை உயர்ந்தோர் பாராட்டமாட்டார்கள் என்பதால், அவர்கள் பழித்துரைக்காத செயல்களையே செய்திடல் வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(471,'471','வினைவலியுந் தன்வலியும் மாற்றான் வலியும்<br />துணைவலியுந் தூக்கிச் செயல்','செயலின் வலிமை, தனது வலிமை, பகைவரின் வலிமை, இருசாராருக்கும் துணையாக இருப்போரின் வலிமை ஆகியவற்றை ஆராய்ந்தறிந்தே அந்தச் செயலில் ஈடுபட வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(472,'472','ஒல்வ தறிவ தறிந்ததன் கண்தங்கிச்<br />செல்வார்க்குச் செல்லாத தில்','ஒரு செயலில் ஈடுபடும்போது அச்செயலைப் பற்றிய அனைத்தையும் ஆராய்ந்தறிந்து முயற்சி மேற்கொண்டால் முடியாதது எதுவுமில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(473,'473','உடைத்தம் வலியறியார் ஊக்கத்தின் ஊக்கி<br />இடைக்கண் முரிந்தார் பலர்','தம்முடைய வலிமையின் அளவை அறியாமல் உணர்ச்சி வயப்பட்டு ஒரு செயலைத் தொடங்கி இடையில் கெட்டுப் போனவர்கள் பலர் உண்டு',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(474,'474','அமைந்தாங் கொழுகான் அளவறியான் தன்னை<br />வியந்தான் விரைந்து கெடும்','மற்றவர்களை மதிக்காமலும், தன் வலிமையை உணர்ந்து கொள்ளாமலும், தன்னைத் தானே பெரிதாக விளம்பரப் படுத்திக் கொண்டிருப்பவர்கள் விரைவில் கெட்டுத் தொலைவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(475,'475','பீலிபெய் சாகாடும் அச்சிறும் அப்பண்டஞ்<br />சால மிகுத்துப் பெயின்','மயில் இறகாக இருந��தாலும்கூட அதிகமாக ஏற்றப்பட்டால் வண்டியின் அச்சு முரிகின்ற அளவுக்கு அதற்குப் பலம் வந்து விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(476,'476','நுனிக்கொம்பர் ஏறினார் அஃதிறந் தூக்கின்<br />உயிர்க்கிறுதி ஆகி விடும்','தன்னைப்பற்றி அதிகமாகக் கணக்குப் போட்டுக் கொண்டு, எல்லை மீறிப் போகிற ஒருவர், நுனிக் கிளையில் ஏறியவர் அதற்கு மேலும் ஏறிட முயற்சி செய்தால் என்ன ஆவாரோ அந்தக் கதிக்கு ஆளாவார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(477,'477','ஆற்றின் அளவறிந் தீக அதுபொருள்<br />போற்றி வழங்கு நெறி','வருவாய் அளவை அறிந்து, அதனை வகுத்து வழங்குவதே பொருளைச் சீராகக் காத்து வாழும் வழியாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(478,'478','ஆகா றளவிட்டி தாயினுங் கேடில்லை<br />போகா றகலாக் கடை','எல்லை கடந்த செலவு இல்லாமல் இருக்குமேயானால் வரவு, குறைவாக இருப்பதால் கேடு எதுவும் விளைவதில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(479,'479','அளவறிந்து வாழாதான் வாழ்க்கை உளபோல<br />இல்லாகித் தோன்றாக் கெடும்','இருப்பது, இயற்றக்கூடியது, இனியும் ஈட்டக்கூடியது ஆகியவற்றின் அளவு அறிந்து செயல் திட்டங்களை வகுத்துக் கொள்ளாவிட்டால், வலிமையோ அல்லது வளமோ இருப்பதுபோல் தோன்றினாலும்கூட இல்லாமல் மறைந்து போய்விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(480,'480','உளவரை தூக்காத ஒப்புர வாண்மை <br />வளவரை வல்லைக் கெடும்','தன்னிடமுள்ள பொருளின் அளவை ஆராய்ந்து பார்க்காமல் அளவின்றிக் கொடுத்துக் கொண்டேயிருந்தால் அவனது வளம் விரைவில் கெடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(481,'481','பகல்வெல்லுங் கூகையைக் காக்கை இகல்வெல்லும்<br />வேந்தர்க்கு வேண்டும் பொழுது','பகல் நேரமாக இருந்தால் கோட்டானைக் காக்கைவென்று விடும் எனவே எதிரியை வீழ்த்துவதற்கு ஏற்ற காலத்தைத் தேர்ந்தெடுக்க வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(482,'482','பருவத்தோ டொட்ட ஒழுகல் திருவினைத்<br />தீராமை ஆர்க்குங் கயிறு','காலம் உணர்ந்து அதற்கேற்பச் செயல்படுதல், அந்த நற்செயலின் வெற்றியை நழுவவிடாமல் கட்டிப்பிணிக்கும் கயிறாக அமையும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(483,'483','அருவினை யென்ப உளவோ கருவியாற்<br />கால மறிந்து செயின்','தேவையான சாதனங்களுடன் உரிய நேரத்தையும் அறிந்து செயல்பட்டால் முடியாதவை என்று எவையுமே இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(484,'484','ஞாலங் கருதினுங் கைகூடுங் காலம்<br />கருதி இடத்தாற் செயின்','உரிய காலத்தையும் இடத்தையும் ஆய்ந்தறிந்து செயல்பட்டால் உலகமேகூடக் கைக்குள் வந்துவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(485,'485','காலங் கருதி இருப்பர் கலங்காது<br />ஞாலங் கருது பவர்','கலக்கத்துக்கு இடம் தராமல் உரிய காலத்தை எதிர்பார்த்துப் பொறுமையாக இருப்பவர்கள் இந்த உலகத்தையேகூட வென்று காட்டுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(486,'486','ஊக்க முடையான் ஒடுக்கம் பொருதகர்<br />தாக்கற்குப் பேருந் தகைத்து','கொடுமைகளைக் கண்டும்கூட உறுதி படைத்தவர்கள் அமைதியாக இருப்பது அச்சத்தினால் அல்ல; அது ஆட்டுக்கடா ஒன்று தனது பகையைத் தாக்குவதற்குத் தன் கால்களைப் பின்னுக்கு வாங்குவதைப் போன்றதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(487,'487','பொள்ளென ஆங்கே புறம்வேரார் காலம்பார்த்<br />துள்வேர்ப்பர் ஒள்ளி யவர்','பகையை வீழ்த்திட அகத்தில் சினங்கொண்டாலும் அதனை வெளிப்படுத்தாமல் இடம் காலம் இரண்டுக்கும் காத்திருப்பதே அறிவுடையார் செயல்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(488,'488','செறுநரைக் காணிற் சுமக்க இறுவரை<br />காணிற் கிழக்காந் தலை','பகைவர்க்கு முடிவு ஏற்பட்டு அவர்கள் தாமாகவே தலைகீழாகக் கவிழ்ந்திடும் உரிய நேரம் வரும் வரையில் தங்களின் பகையுணர்வைப் பொறுமையுடன் தாங்கிக் கொள்ள வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(489,'489','எய்தற் கரிய தியைந்தக்கால் அந்நிலையே<br />செய்தற் கரிய செயல்','கிடைப்பதற்கு அரிய காலம் வாயக்கும்போது அதைப் பயன்படுத்திக் கொண்டு அப்போதே செயற்கரிய செய்து முடிக்க வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(490,'490','கொக்கொக்க கூம்பும் பருவத்து மற்றதன்<br />குத்தொக்க சீர்த்த இடத்து','காலம் கைகூடும் வரையில் கொக்குபோல் பொறுமையாகக் காத்திருக்கவேண்டும் காலம் வாய்ப்பாகக் கிடைத்ததும் அது குறி தவறாமல் குத்துவது போல் செய்து முடிக்க வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(491,'491','தொடங்கற்க எவ்வினையும் எள்ளற்க முற்றும்<br />இடங்கண்ட பின்னல் லது','ஈடுபடும் செயல் ஒன்றும் பெரிதல்ல என இகழ்ச்சியாகக் கருதாமல், முற்றிலும் சரியான இடத்தைத் தேர்ந்தெடுத்து அச்செயலில் இறங்க வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(492,'492','முரண்சேர்ந்த மொய்ம்பி னவர்க்கும் அரண்சேர்ந்தாம்<br />ஆக்கம் பலவுந் தரும்','வரும்பகையை எதிர்க்கும் வலிமை இருப்பினும், அத்துடன் அரணைச் சார்ந்து போரிடும் வாய்ப்பும் இணையுமானால் பெரும்பயன் கிட்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(493,'493','ஆற்றாரும் ஆற்றி அடுப இடனறிந்து<br />போற்றார்கண் போற்றிச் செயின்','தாக்குதல் நடத்துவதற்குரிய இடத்தையும் தேர்ந்து, தம்மையும் காத்துக்கொண்டு பகைவருடன் மோதினால் வலிமையில்லாதவர்க்கும் வலிமை ஏற்பட்டு வெற்றி கிட்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(494,'494','எண்ணியார் எண்ணம் இழப்பர் இடனறிந்து<br />துன்னியார் துன்னிச் செயின்','ஏற்ற இடமறிந்து தொடர்ந்து தாக்கினால் பகைவர்கள், வெற்றி என்பதை நினைத்துக்கூடப் பார்க்க மாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(495,'495','நெடும்புனலுள் வெல்லும் முதலை அடும்புனலின்<br />நீங்கின் அதனைப் பிற','தண்ணீரில் இருக்கும் வரையில்தான் முதலைக்குப் பலம்; தண்ணீரைவிட்டு வெளியே வந்து விட்டால் ஒரு சாதாரண உயிர்கூட அதனை விரட்டி விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(496,'496','கடலோடா கால்வல் நெடுந்தேர் கடலோடும்<br />நாவாயும் ஓடா நிலத்து','ஒரு செயலுக்குரிய இடத்தைத் தேர்ந்தெடுப்பவர் `தேர் கடலிலே ஓடாது `கப்பல் நிலத்தில் போகாது என்பதையாவது தெரிந்தவராக இருக்க வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(497,'497','அஞ்சாமை யல்லால் துணைவேண்டா எஞ்சாமை<br />எண்ணி யிடத்தாற் செயின்','ஒரு செயலுக்குரிய வழி முறைகளைக் குறையின்றிச் சிந்தித்துச் செய்யுமிடத்து, அஞ்சாமை ஒன்றைத் தவிர, வேறு துணை தேவையில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(498,'498','சிறுபடையான் செல்லிடஞ் சேரின் உறுபடையான்<br />ஊக்கம் அழிந்து விடும்','சிறிய படை என்றாலும் அது தனக்குரிய இடத்தில் இருந்து போரிட்டால் பெரிய படையை வென்று விட முடியும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(499,'499','சிறைநலனுஞ் சீறும் இலரெனினும் மாந்தர்<br />உறைநிலத்தோ டொட்ட லரிது','பாதுகாப்புக்கான கோட்டையும், மற்றும் பல படைச் சிறப்புகளும் இல்லாதிருப்பினும், அப்பகைவர் வாழும் நிலையான இடத்திற்குப் படையெடுத்துச் சென்று தாக்குவது எளிதான செயல் அல்ல',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(500,'500','காலாழ் களரின் நரியடுங் கண்ணஞ்சா<br />வேலாள் முகத்த களிறு','வேலேந்திய வீரர்களை வீழ்த்துகின்ற ஆற்றல் படைத்த யானை, சேற்றில் சிக்கி விட்டால் அதனை நரிகள் கூடக் கொன்று விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(501,'501','அறம்பொருள் இன்பம் உயிரச்சம் நான்கின்<br />திறந்தெரிந்து தேறப் படும்','அறவழியில் உறுதியானவனாகவும், பொருள் வகையில் நாணயமானவனாகவும், இன்பம் தேடி மயங்காதவனாகவும், தன்னுயிருக்கு அஞ்சாதவனாகவும் இருப்பவனையே ஆய்ந்தறிந்து ஒரு பணிக்கு அமர்த்த வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(502,'502','குடிப்பிறந்து குற்றத்தின் நீங்கி வடுப்பரியும்<br />நாணுடையான் கட்டே தெளிவு','குற்றமற்றவனாகவும், பழிச்செயல் புரிந்திட அஞ்சி நாணுகின்றவனாகவும் இருப்பவனையே உயர்குடியில் பிறந்தவன் எனத் தெளிவு கொள்ள வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(503,'503','அரியகற் றாசற்றார் கண்ணுந் தெரியுங்கால்<br />இன்மை அரிதே வெளிறு','அரிய நூல்கள் பல கற்றவர் என்றும், எக்குறையும் அற்றவர் என்றும் புகழப்படுவோரைக்கூட அழமாக ஆராய்ந்து பார்க்கும்போது அவரிடம் அறியாமை என்பது அறவே இல்லை எனக் கணித்துவிட இயலாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(504,'504','குணநாடிக் குற்றமு நாடி அவற்றுள்<br />மிகைநாடி மிக்க கொளல்','ஒருவரின் குணங்களையும், அவரது குறைகளையும் ஆராய்ந்து பார்த்து அவற்றில் மிகுதியாக இருப்பவை எவை என்பதைத் தெரிந்து அதன் பிறகு அவரைப் பற்றிய ஒரு தெளிவான முடிவுக்கு வரவேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(505,'505','பெருமைக்கும் ஏனைச் சிறுமைக்குந் தத்தங்<br />கருமமே கட்டளைக் கல்','ஒருவர் செய்யும் காரியங்களையே உரைகல்லாகக் கொண்டு, அவர் தரமானவரா அல்லது தரங்கெட்டவரா என்பதைப் புரிந்து கொள்ளலாம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(506,'506','அற்றாரைத் தேறுதல் ஓம்புக மற்றவர்<br />பற்றிலர் நாணார் பழி','நெறியற்றவர்களை ஒரு பணிக்குத் தேர்வு செய்வது கூடாது அவர்கள் உலகத்தைப் பற்றிக் கவலைப்படாமல், பழிக்கு நாணாமல் செயல்படுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(507,'507','காதன்மை கந்தா அறிவறியார்த் தேறுதல்<br />பேதைமை எல்லாந் தரும்','அறிவில்லாதவரை அன்பு காரணமாகத் தேர்வு செய்வது அறியாமை மட்டுமல்ல; அதனால் பயனற்ற செயல்களே விளையும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(508,'508','தேரான் பிறனைத் தெளிந்தான் வழிமுறை<br />தீரா இடும்பை தரும்','ஆராய்ந்து பார்க்காமல் ஒருவரைத் துணையாகத் தேர்வு செய்து, அமர்த்திக் கொண்டால் அவரால் வருங்காலத் தலைமுறையினர்க்கும் நீங்காத துன்பம் விளையும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(509,'509','தேறற்க யாரையுந் தேராது தேர்ந்தபின்<br />தேறுக தேறும் பொருள்','நன்கு ஆராய்ந்து தெளிந்த பிறகு ஒருவரிடம் நம்பிக்கை வைக்க வேண்டும் ஆராய்ந்து பாராமல் யாரையும் நம்பிவிடக் கூடாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(510,'510','தேரான் தெளிவுந் தெளிந்தான்கண் ஐயுறவும்<br />தீரா இடும்பை தரும்','ஆராயாமல் ஒருவரைத் தேர்வு செய்து ஏற்றுக் கொள்வதும், ஆராய்ந்து தேர்வு செய்து ஏற்றுக்கொண்டபின் அவரைக் சந்தேகப்படுவதும் தீராத துன்பத்தைத் தரும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(511,'511','நன்மையும் தீமையும் நாடி நலம்புரிந்த<br />தன்மையான் ஆளப் படும்','நன்மை எது தீமை எது என ஆராய்ந்து அறிந்த, நற்செயலில் மட்டுமே நாட்டங் கொண்டவர்கள் எப்பணியினை ஆற்றிடவும் தகுதி பெற்றவராவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(512,'512','வாரி பெருக்கி வளம்படுத் துற்றவை<br />ஆராய்வான் செய்க வினை','வருமானம் வரக்கூடிய வழிகளை விரிவாக்கி, வளங்களையும் பெருக்கி, இடையூறுகளையும் ஆராய்ந்து நீக்கிட வல்லவனே செயலாற்றும் திறனுடையவன்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(513,'513','அன்பறிவு தேற்றம் அவாவின்மை இந்நான்கும்<br />நன்குடையான் கட்டே தெளிவு','அன்பு, அறிவு, செயலாற்றும் திறமை, பேராசைப் படாத குணம் ஆகிய நான்கு பண்புகளையும் நிலையாகப் பெற்றிருப்பவரைத் தேர்வு செய்வதே நலம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(514,'514','எனைவகையான் தேறியக் கண்ணும் வினைவகையான்<br />வேறாகும் மாந்தர் பலர்','எவ்வளவுதான் வழிமுறைகளை ஆராய்ந்து தெளிந்து தேர்ந்தெடுத்தாலும் செயல்படும் பொழுது வேறுபடுகிறவர்கள் பலர் இருப்பர்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(515,'515','அறிந்தாற்றிச் செய்கிற்பாற் கல்லால் வினைதான்<br />சிறந்தானென் றேவற்பாற் றன்று','ஆய்ந்தறிந்து செய்து முடிக்கும் ஆற்றல் உள்ளவர்களை அல்லாமல் வேறொருவரைச் சிறந்தவர் எனக் கருதி ஒரு செயலில் ஈடுபடுத்தக் கூடாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(516,'516','செய்வானை நாடி வினைநாடிக��� காலத்தோ<br />டெய்த உணர்ந்து செயல்','செயலாற்ற வல்லவனைத் தேர்ந்து, செய்யப்பட வேண்டிய செயலையும் ஆராய்ந்த, காலமுணர்ந்து அதனைச் செயல்படுத்தவேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(517,'517','இதனை இதனால் இவன்முடிக்கும் என்றாய்ந்<br />ததனை அவன்கண் விடல்','ஒரு காரியத்தை ஒருவர் எப்படி செய்து முடிப்பார் என்பதை ஆராய்ந்து பார்த்து, அதற்குப் பிறகு அந்தக் காரியத்தை அவரிடம் ஒப்படைக்க வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(518,'518','வினைக்குரிமை நாடிய பின்றை அவனை<br />அதற்குரிய னாகச் செயல்','ஒரு செயலில் ஈடுபடுவதற்கு ஏற்றவனா என்பதை ஆராய்ந்து அறிந்த பிறகே, அவனை அந்தச் செயலில் ஈடுபடுத்த வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(519,'519','வினைக்கண் வினையுடையான் கேண்மைவே றாக<br />நினைப்பானை நீங்குந் திரு','எடுத்த காரியத்தை முடிப்பதில் கண்ணும் கருத்துமாக இருப்பவரின் உறவைத் தவறாக எண்ணுபவரை விட்டுப் பெருமை அகன்று விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(520,'520','நாடோறு நாடுக மன்னன் வினைசெய்வான்<br />கோடாமை கோடா துலகு','உழைப்போர் உள்ளம் வாடாமல் இருக்கும் வரையில் உலகின் செழிப்பும் வாடாமல் இருக்கும் எனவே உழைப்போர் நிலையை ஒவ்வொரு நாளும் அரசினர் ஆய்தறிந்து ஆவன செய்ய வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(521,'521','பற்றற்ற கண்ணும் பழைமைபா ராட்டுதல்<br />சுற்றத்தார் கண்ணே உள','ஒருவருக்கு வறுமை வந்த நேரத்திலும் அவரிடம் பழைய உறவைப் பாராட்டும் பண்பு உடையவர்களே சுற்றத்தார் ஆவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(522,'522','விருப்பறாச் சுற்றம் இயையின் அருப்பறா<br />ஆக்கம் பலவுந் தரும்','எந்த நிலைமையிலும் அன்பு குறையாத சுற்றம் ஒருவருக்குக் கிடைத்தால் அது அவருக்கு ஆக்கமும், வளர்ச்சியும் அளிக்கக் கூடியதாக அமையும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(523,'523','அளவளா வில்லாதான் வாழ்க்கை குளவளாக்<br />கோடின்றி நீர்நிறைந் தற்று','உற்றார் உறவினர் எனச் சூழ இருப்போருடன் அன்பு கலந்து மகிழ்ந்து பழகாதவனுடைய வாழ்க்கையானது; கரையில்லாத குளத்தில் நீர் நிறைந்ததைப் போலப் பயனற்றதாகி விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(524,'524','சுற்றத்தாற் சுற்றப் படவொழுகல் செல்வந்தான்<br />பெற்றத்தாற் பெற்ற பயன்','தன் இனத்தார், அன்புடன் தன்னைச் சூழ்ந்து நிற்க வாழும் வாழ்க்கையே ஒருவன் பெற்ற செல்வத்தினால் கிடைத்திடும் பயனாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(525,'525','கொடுத்தலும் இன்சொல்லும் ஆற்றின் அடுக்கிய<br />சுற்றத்தாற் சுற்றப் படும்','வள்ளல் தன்மையும், வாஞ்சைமிகு சொல்லும் உடையவனை அடுத்தடுத்துச் சுற்றத்தார் சூழ்ந்து கொண்டேயிருப்பார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(526,'526','பெருங்கொடையான் பேணான் வெகுளி அவனின்<br />மருங்குடையார் மாநிலத் தில்','பெரிய கொடையுள்ளம் கொண்டவனாகவும், வெகுண்டு எழும் சீற்றத்தை விலக்கியவனாகவும் ஒருவன் இருந்தால் அவனைப் போல் சுற்றம் சூழ இருப்போர் உலகில் யாரும் இல்லை எனலாம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(527,'527','காக்கை கரவா கரைந்துண்ணும் ஆக்கமும் <br />அன்னநீ ரார்க்கே உள','தனக்குக் கிடைத்ததை மறைக்காமல் தனது சுற்றத்தைக் கூவி அழைத்துக் காக்கை உண்ணும் அந்தக் குணம் உடையவர்களுக்கு மட்டுமே உலகில் உயர்வு உண்டு',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(528,'528','பொதுநோக்கான் வேந்தன் வரிசையா நோக்கின்<br />அதுநோக்கி வாழ்வார் பலர்','அனைத்து மக்களும் சமம் எனினும், அவரவர்க்குரிய ஆற்றலுக்கேற்ப அவர்களைப் பயன்படுத்திக் கொண்டால், அந்த அரசை அனைவரும் அரணாகச் சூழ்ந்து நிற்பர்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(529,'529','தமராகித் தற்றுறந்தார் சுற்றம் அமராமைக்<br />காரண மின்றி வரும்','உறவினராக இருந்து ஏதோ ஒரு காரணம் கூறிப் பிரிந்து சென்றவர்கள், அந்தக் காரணம் பொருந்தாது என்று உணரும்போது மீண்டும் உறவு கொள்ள வருவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(530,'530','உழைப்பிரிந்து காரணத்தின் வந்தானை வேந்தன்<br />இழைத்திருந் தெண்ணிக் கொளல்','ஏதோ காரணம் கற்பித்துப் பிரிந்து போய், மீண்டும் தலைவனிடம் தக்க காரணத்தினால் வந்தவரை, நன்கு ஆராய்ந்து ஏற்றுக் கொள்ளல் வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(531,'531','இறந்த வெகுளியின் தீதே சிறந்த <br />உவகை மகிழ்ச்சியிற் சோர்வு','அகமகிழ்ச்சியினால் ஏற்படும் மறதி, அடங்காத சினத்தினால் ஏற்படும் விளைவை விடத் தீமையானது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(532,'532','பொச்சாப்புக் கொல்லும் புகழை அறிவினை<br />நிச்ச நிரப்புக்கொன் றாங்கு','நாளும் தொடர்ந்து வாட்டுகின்ற வறுமை, அறிவை அழிப்பது போல மறதி, புகழை அழித்து விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(533,'533','பொச்சாப்பார்க் கில்லை புகழ்மை யதுவுலகத்<br />தெப்பானூ லோர்க்குந் துணிவு','மறதி உடையவர்களுக்கு, மங்காப் புகழ் இல்லை என்பதே அனைத்தும் கற்றுணர்ந்த அறிஞர்களின் முடிவான கருத்தாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(534,'534','அச்ச முடையார்க் கரணில்லை ஆங்கில்லை<br />பொச்சாப் புடையார்க்கு நன்கு','பயத்தினால் நடுங்குகிறவர்களுக்குத் தம்மைச் சுற்றிப் பாதுகாப்புக்கான அரண் கட்டப்பட்டிருந்தாலும் எந்தப் பயனுமில்லை அதைப் போலவே என்னதான் உயர்ந்த நிலையில் இருந்தாலும் மறதி உடையவர்களுக்கு அந்த நிலையினால் எந்தப் பயனுமில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(535,'535','முன்னுறக் காவா திழுக்கியான் தன்பிழை<br />பின்னூ றிரங்கி விடும்','முன்கூட்டியே சிந்தித்துத் தன்னைக் காத்துக் கொள்ளத் தவறியவன், துன்பம் வந்தபிறகு தன் பிழையை எண்ணிக் கவலைப்பட நேரிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(536,'536','இழுக்காமை யார்மாட்டும் என்றும் வழுக்காமை<br />வாயின் அதுவொப்ப தில்','ஒருவரிடம், மறவாமை என்னும் பண்பு தவறாமல் பொருந்தியிருக்குமேயானால், அதைவிட அவருக்கு நன்மை தரக்கூடியது வேறு எதுவும் இருக்க முடியாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(537,'537','அரியவென் றாகாத இல்லைபொச் சாவாக்<br />கருவியாற் போற்றிச் செயின்','மறதியில்லாமலும், அக்கறையுடனும் செயல்பட்டால், முடியாதது என்று எதுவுமே இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(538,'538','புகழ்ந்தவை போற்றிச் செயல்வேண்டுஞ் செய்யா<br />திகழ்ந்தார்க் கெழுமையும் இல்','புகழுக்குரிய கடமைகளைப் போற்றிச் செய்திடல் வேண்டும் அப்படிச் செய்யாமல் புறக்கணிக்கப்பவர்களுக்கு வாழ்க்கையில் உயர்வே இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(539,'539','இகழ்ச்சியிற் கெட்டாரை உள்ளுக தாந்தம்<br />மகிழ்ச்சியின் மைந்துறும் போழ்து','மமதையால் பூரித்துப்போய்க் கடமைகளை மறந்திருப்பவர்கள், அப்படி மறந்துபோய் அழிந்து போனவர்களை நினைத்துப் பார்த்துத் திருந்திக் கொள்ள வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(540,'540','உள்ளிய தெய்தல் எளிதுமன் மற்றுந்தான்<br />உள்ளிய துள்ளப் பெறின்','கொண்ட குறிக்கோளில் ஊக்கத்துடன் இருந்து அதில் வெற்றி காண்பதிலேயே நாட்டமுடையவர்களுக்கு அந்தக் குறிக்கோளை அடைவது எளிதானதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(541,'541','ஓர்ந்துகண் ணோடா திறைபுரிந் தியார்மாட்டும்<br />தேர்ந்துசெய் வஃதே முறை','குற்றம் இன்னதென்று ஆராய்ந்து எந்தப் பக்கமும் சாயாமல் நடுவுநிலைமை தவறாமல் வழங்கப்படுவதே நீதியாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(542,'542','வானோக்கி வாழும் உலகெல்லாம் மன்னவன்<br />கோனோக்கி வாழுங் குடி','உலகில் உள்ள உயிர்கள் வாழ்வதற்கு மழை தேவைப்படுவது போல ஒரு நாட்டின் குடிமக்கள் வாழ்வதற்கு நல்லாட்சி தேவைப்படுகிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(543,'543','அந்தணர் நூற்கும் அறத்திற்கும் ஆதியாய்<br />நின்றது மன்னவன் கோல்','ஓர் அரசின் செங்கோன்மைதான் அறவோர் நூல்களுக்கும் அறவழிச் செ���ல்களுக்கும் அடிப்படையாக அமையும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(544,'544','குடிதழீஇக் கோலோச்சு மாநில மன்னன்<br />அடிதழீஇ நிற்கும் உலகு','குடிமக்களை அரவணைத்து ஆட்சி நடத்தும் நல்லரசின் அடிச்சுவட்டை நானிலமே போற்றி நிற்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(545,'545','இயல்புளிக் கோலோச்சு மன்னவ னாட்ட<br />பெயலும் விளையுளுந் தொக்கு','நீதி வழுவாமல் ஓர் அரசு நாட்டில் இருக்குமேயானால் அது, பருவகாலத்தில் தவறாமல் பெய்யும் மழையினால் வளமான விளைச்சல் கிடைப்பதற்கு ஒப்பானதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(546,'546','வேலன்று வென்றி தருவது மன்னவன்<br />கோலதூஉங் கோடா தெனின்','ஓர் அரசுக்கு வெற்றியைத் தருவது பகைவரை வீழ்த்தும் வேலல்ல; குடிமக்களை வாழவைக்கும் வளையாத செங்கோல்தான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(547,'547','இறைகாக்கும் வையகம் எல்லாம் அவனை <br />முறைகாக்கும் முட்டாச் செயின்','நீதி வழுவாமல் ஓர் அரசு நடைபெற்றால் அந்த அரசை அந்த நீதியே காப்பாற்றும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(548,'548','எண்பதத்தான் ஓரா முரறசெய்யா மன்னவன்<br />தண்பதத்தான் தானே கெடும்','ஆடம்பரமாகவும், ஆராய்ந்து நீதி வழங்காமலும் நடைபெறுகிற அரசு தாழ்ந்த நிலையடைந்து தானாகவே கெட்டொழிந்து விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(549,'549','குடிபுறங் காத்தோம்பிக் குற்றங் கடிதல்<br />வடுவன்று வேந்தன் தொழில்','குடிமக்களைப் பாதுகாத்துத் துணை நிற்பதும், குற்றம் செய்தவர்கள் யாராயினும் தனக்கு இழுக்கு வரும் என்று கருதாமல் தண்டிப்பதும் அரசின் கடமையாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(550,'550','கொலையிற் கொடியாரை வேந்தொறுத்தல் பைங்கூழ்<br />களைகட் டதனொடு நேர்','கொலை முதலிய கொடுமைகள் புரிவோரை, ஓர் அரசு தண்டனைக்குள்ளாக்குவது பயிரின் செழிப்புக்காகக் களை எடுப்பது போன்றதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(551,'551','கொலைமேற்கொண் டாரிற் கொடிதே அலைமேற்கொண்<br />டல்லவை செய்தொழுகும் வேந்து','அறவழி மீறிக் குடிமக்களைத் துன்புறுத்தும் அரசு, கொலையைத் தொழிலாகக் கொண்டவரைவிடக் கொடியதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(552,'552','வேலொடு நின்றான் இடுவென் றதுபோலும்<br />கோலொடு நின்றான் இரவு','ஆட்சிக்கோல் ஏந்தியிருப்பவர்கள் தமது குடிமக்களிடம் அதிகாரத்தைக் காட்டிப் பொருளைப் பறிப்பது, வேல் ஏந்திய கொள்ளைக்காரனின் மிரட்டலைப் போன்றது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(553,'553','நாடொறும் நாடி முறைசெய்யா மன்னவன்<br />நாடொறும் நாடு கெடும்','ஆட்சியினால் விளையும் நன்மை தீமைகளை நாள் தோறும் ஆராய்ந்து அவற்றிக்குத் தக்கவாறு நடந்து கொள்ளாத அரசு அமைந்த நாடு சீர்குலைந்து போய்விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(554,'554','கூழுங் குடியும் ஒருங்கிழக்கும் கோல்கோடிச்<br />சூழாது செய்யும் அரசு','நாட்டுநிலை ஆராயாமல் கொடுங்கோல் புரியும் அரசு, நிதி ஆதாரத்தையும் மக்களின் மதிப்பையும் இழந்துவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(555,'555','அல்லற்பட் டாற்றா தழுதகண் ணீரன்றே<br />செல்வத்தைத் தேய்க்கும் படை','கொடுமை பொறுக்க முடியாமல் மக்கள் சிந்தும் கண்ணீர் ஆட்சியை அழிக்கும் படைக்கருவியாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(556,'556','மன்னார்க்கு மன்னுதல் செங்கோன்மை அஃதின்றேல்<br />மன்னாவாம் மன்னர்க் கொளி','நீதிநெறி தவறாக செங்கோன்மைதான் ஓர் அரசுக்கு புகழைத் தரும் இல்லையேல் அந்த அரசின் புகழ் நிலையற்றுச் சரிந்து போகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(557,'557','துளியின்மை ஞாலத்திற் கெற்றற்றே வேந்தன்<br />அளியின்மை வாழும் உயிர்க்கு','மழையில்லாவிடில் துன்பமுறும் உலகத்தைப் போல் அருள் இல்லாத அரசினால் குடிமக்கள் தொல்லைப்படுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(558,'558','இன்மையின் இன்னா துடைமை முறைசெய்யா<br />மன்னவன் கோற்கீழ்ப் படின்','வறுமையின்றி வாழ்ந���தால்கூட அந்த வாழ்க்கை கொடுங்கோல் ஆட்சியின் கீழ் அமைந்துவிட்டால் வறுமைத் துன்பத்தை விட அதிகத் துன்பம் தரக் கூடியது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(559,'559','முறைகோடி மன்னவன் செய்யின் உறைகோடி<br />ஒல்லாது வானம் பெயல்','முறை தவறிச் செயல்படும் ஆட்சியில் நீரைத்தேக்கிப் பயனளிக்கும் இடங்கள் பாழ்பட்டுப் போகுமாதலால், வான் வழங்கும் மழையைத் தேக்கி வைத்து வளம் பெறவும் இயலாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(560,'560','ஆபயன் குன்றும் அறுதொழிலோர் நூல்மறப்பர்<br />காவலன் காவான் எனின்','ஓர் அரசு நாட்டை முறைப்படி காக்கத் தவறினால் ஆக்கப்பணிகள் எதுவும் நடக்காது; முக்கியமான தொழில்களும் தேய்ந்து விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(561,'561','தக்காங்கு நாடித் தலைச்செல்லா வண்ணத்தால்<br />ஒத்தாங் கொறுப்பது வேந்து','நடைபெற்ற குற்றங்களை நடுநிலை தவறாமல் ஆராய்ந்தறிந்து, மீண்டும் அவை நிகழா வண்ணம் அக்குற்றங்களுக்கேற்பத் தண்டனை கிடைக்கச் செய்வதே அரசின் கடமையாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(562,'562','கடிதோச்சி மெல்ல வெறிக நெடிதாக்கம்<br />நீங்காமை வேண்டு பவர்','குற்றங்கள் நிகழாமல் இருக்கக் கண்டிக்கும்போது கடுமை காட்டித், தண்டிக்கும் போது மென்மை காட்டுகிறவர்களின் செல்வாக்குதான் தொய்வின்றி நெடுநாள் நீடிக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(563,'563','வெருவந்த செய்தொழுகும் வெங்கோல னாயின்<br />ஒருவந்தம் ஒல்லைக் கெடும்','குடிமக்கள் அஞ்சும்படியாகக் கொடுங்கோல் நடத்தும் அரசு நிச்சயமாக விரைவில் அழியும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(564,'564','இறைகடியன் என்றுரைக்கும் இன்னாச்சொல் வேந்தன்<br />உறைகடுகி ஒல்லைக் கெடும்','கடுஞ்சொல் உரைக்கும் கொடுங்கோல் என்று குடிமக்களால் கருதப்படும் அரசு, தனது பெருமையை விரைவில் இழக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(565,'565','அருஞ்செவ்வி இன்னா முகத்தான் பெருஞ்செல்வம்<br />பேஎய்கண் டன்ன துடைத்து','யாரும் எளிதில் காண முடியாதவனாகவும், கடுகடுத்த முகத்துடனும் இருப்பவனிடம் குவிந்துள்ள பெரும் செல்வம் பேய்த் தோற்றம் எனப்படும் அஞ்சத்தகும் தோற்றமேயாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(566,'566','கடுஞ்சொல்லன் கண்ணிலன் ஆயின் நெடுஞ்செல்வம்<br />நீடின்றி ஆங்கே கெடும்','கடுஞ்சொல்லும், கருணையற்ற உள்ளமும் கொண்டவர்களின் பெருஞ்செல்வம் நிலைக்காமல் அழிந்துவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(567,'567','கடுமொழியுங் கையிகந்த தண்டமும் வேந்தன்<br />அடுமுரண் தேய்க்கும் அரம்','கடுஞ்சொல்லும், முறைகடந்த தண்டனையும் ஓர் அரசின் வலிமையைத் தேய்த்து மெலியச் செய்யும் அரம் எனும் கருவியாக அமையும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(568,'568','இனத்தாற்றி எண்ணாத வேந்தன் சினத்தாற்றிச்<br />சீறிற் சிறுகுந் திரு','கூட்டாளிகளிடம் கலந்து பேசாமல் சினத்திற்கு ஆளாகிக் கோணல் வழி நடக்கும் அரசு தானாகவே வீழ்ந்து விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(569,'569','செருவந்த போழ்திற் சிறைசெய்யா வேந்தன்<br />வெருவந்து வெய்து கெடும்','முன்கூட்டியே உரிய பாதுகாப்பு இல்லாமல் இருக்கும் வேந்தன், போர் வந்துவிட்டால் அதற்கு அஞ்சி விரைவில் வீழ நேரிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(570,'570','கல்லார்ப் பிணிக்குங் கடுங்கோல் அதுவல்ல<br />தில்லை நிலக்குப் பொறை','கொடுங்கோல் அரசு படிக்காதவர்களைத் தனக்கு பக்கபலமாக்கிக் கொள்ளும் அதைப்போல பூமிக்குப் பாரம் வேறு எதுவுமில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(571,'571','கண்ணோட்டம் என்னுங் கழிபெருங் காரிகை<br />உண்மையான் உண்டிவ் வுலகு','இந்த உலகம், அன்பும் இரக்கமும் இணைந்த கண்ணோட்டம் எனப்படுகிற பெரும் அழகைக் கொண்டவர்கள் இருப்பதால்தான் பெருமை அடைகிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(572,'572','கண்ணோட்டத் துள்ள துலகியல் அஃதிலார்<br />உண்மை நிலக்குப் பொ��ை','அன்புடன் அரவணைத்து இரக்கம் காட்டும் கண்ணோட்டம் எனப்படும் உலகியலுக்கு, மாறாக இருப்பவர்கள் இந்தப் பூமிக்குச் சுமையாவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(573,'573','பண்ணென்னாம் பாடற் கியைபின்றேற் கண்ணென்னாங்<br />கண்ணோட்டம் இல்லாத கண்','இரக்க உணர்வு, அன்பு எனும் கண்ணோட்டத்துடன் பொருந்தி வராத கண்ணும், பாடலுடன் பொருந்தி வராத இசையும் பயன் தராதவையாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(574,'574','உளபோல் முகத்தெவன் செய்யும் அளவினாற்<br />கண்ணோட்டம் இல்லாத கண்','அகத்தில் அன்பையும் இரக்கத்தையும் சுரக்கச் செய்யாத கண்கள் முகத்தில் உள்ளவைபோல் தோன்றுவதைத் தவிர, வேறு எந்தப்பயனும் இல்லாதவைகளாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(575,'575','கண்ணிற் கணிகலங் கண்ணோட்டம் அஃதின்றேற்<br />புண்ணென் றுணரப் படும்','கருணையுள்ளம் கொண்டவருடைய கண்ணே கண் என்று கூறப்படும் இல்லையானால் அது கண் அல்ல; புண்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(576,'576','மண்ணோ டியைந்த மரத்தனையர் கண்ணோ<br />டியைடந்துகண் ணோடா தவர்','ஒருவர்க்குக் கண் இருந்தும்கூட அந்தக் கண்ணுக்குரிய அன்பும் இரக்கமும் இல்லாவிட்டால் அவர் மரத்துக்கு ஒப்பானவரே ஆவார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(577,'577','கண்ணோட்டம் இல்லவர் கண்ணிலர் கண்ணுடையார்<br />கண்ணோட்டம் இன்மையும் இல்','கருணை மனம் கொண்டவர்க்கு இருப்பதே கண்கள் எனப்படும்; கருணையற்றோர் கண்ணற்றோர் என்றே கருதப்படுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(578,'578','கருமஞ் சிதையாமற் கண்ணோட வல்லார்க்<br />குரிமை உடைத்திவ் வுலகு','கடமை தவறாமையிலும், கருணை பொழிவதிலும் முதன்மையாக இருப்போருக்கு இந்த உலகமே உரிமையுடையதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(579,'579','ஒறுத்தாற்றும் பண்பினார் கண்ணுங்கண் ணோடிப்<br />பொறுத்தாற்றும் பண்பே தலை','அழிக்க நினைத்திடும் இயல்புடையவரிடத்திலும் பொறுமை காட்டுவது மிக உயர்ந்த பண்பாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(580,'580','பெயக்கண்டு நஞ்சுண் டமைவர் நயத்தக்க<br />நாகரிகம் வேண்டு பவர்','கருணை உள்ளமும் பண்பாடும் உள்ளவர்கள், தம்முடன் பழகியவர்கள் நஞ்சு கொடுத்தாலும் அதை அருந்திக் களிப்படைவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(581,'581','ஒற்றும் உரைசான்ற நூலும் இவையிரண்டும்<br />தெற்றென்க மன்னவன் கண்','நேர்மையும் திறனும் கொண்ட ஒற்றரும், நீதியுரைக்கும் அறநூலும் ஓர் அரசின் கண்களாகக் கருதப்பட வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(582,'582','எல்லார்க்கும் எல்லாம் நிகழ்பவை எஞ்ஞான்றும்<br />வல்லறிதல் வேந்தன் தொழில்','நண்பர், பகைவர், நடுநிலையாளர் ஆகிய எல்லாரிடத்திலும் நிகழும் எல்லா நிகழ்வுகளையும், எல்லாக் காலங்களிலும் ஒற்றரைக் கொண்டு விரைவாக அறிந்து கொள்வது அரசுக்குரிய கடமையாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(583,'583','ஒற்றினான் ஒற்றிப் பொருள்தெரியா மன்னவன்<br />கொற்றங் கொளக்கிடந்த தில்','நாட்டு நிலவரத்தை ஒற்றர்களைக் கொண்டு அறிந்து அதன் விளைவுகளை ஆராய்ந்து நடந்திடாத அரசின் கொற்றம் தழைத்திட வழியே இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(584,'584','வினைசெய்வார் தஞ்சுற்றம் வேண்டாதா ரென்றாங்<br />கனைவரையும் ஆராய்வ தொற்று','ஓர் அரசில் உளவறியும் ஒற்றர் வேலை பார்ப்பவர்கள், வேண்டியவர், வேண்டாதவர், சுற்றத்தார் என்றெல்லாம் பாகுபாடு கருதாமல் பணிபுரிந்தால்தான் அவர்களை நேர்மையான ஒற்றர்கள் எனக் கூற முடியும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(585,'585','கடாஅ உருவொடு கண்ணஞ்சா தியாண்டும்<br />உகாஅமை வல்லதே ஒற்று','சந்தேகப்பட முடியாத தோற்றத்துடனும் அப்படிச் சந்தேகப்பட்டுப் பார்ப்பவர்களுக்கு அஞ்சாமலும், என்ன நேர்ந்தாலும் மனத்தில் உள்ளதை வெளிப்படுத்தாமலும் உள்ளவர்களே ஒற்றர்களாகப் பணியாற்ற முடியும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(586,'586','துறந்தார் படிவத்த ராகி இறந்தாராய்ந்<br />தென்செயினுஞ் சோர்வில தொற்று','ஆராய்ந்திட வந்த நிகழ்வில் தொடர்பற்றவரைப் போலக் காட்டிக்கொண்டு, அதனைத் தீர ஆராய்ந்து, அதில் எத்துணைத் துன்பம் வரினும் தாங்கிக் கொண்டு, தம்மை யாரென்று வெளிப்படுத்திக் கொள்ளாதவரே சிறந்த ஒற்றர்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(587,'587','மறைந்தவை கேட்கவற் றாகி அறிந்தவை<br />ஐயப்பா டில்லதே ஒற்று','மற்றவர்கள் மறைவாகக் கூடிச்செய்யும் காரியங்களை, அவர்களுடன் இருப்பவர் வாயிலாகக் கேட்டறிந்து அவற்றின் உண்மையைத் தெளிவாகத் தெரிந்து கொள்வதே உளவறியும் திறனாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(588,'588','ஒற்றொற்றித் தந்த பொருளையும் மற்றுமோர்<br />ஒற்றினால் ஒற்றிக் கொளல்','ஓர் உளவாளி, தனது திறமையினால் அறிந்து சொல்லும் செய்தியைக் கூட மற்றோர் உளவாளி வாயிலாகவும் அறிந்து வரச் செய்து, இரு செய்திகளையும் ஒப்பிட்டுப் பார்த்த பிறகே அது, உண்மையா அல்லவா என்ற முடிவுக்கு வரவேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(589,'589','ஒற்றொற் றுணராமை யாள்க உடன்மூவர்<br />சொற்றொக்க தேறப் படும்','ஓர் ஒற்றரை மற்றோர் ஒற்றர் அறியமுடியாதபடி மூன்று ஒற்றர்களை இயங்கவைத்து அம்மூவரும் சொல்வது ஒத்திருந்தால் அது உண்மையெனக் கொள்ளலாம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(590,'590','சிறப்பறிய ஒற்றின்கண் செய்யற்க செய்யிற் <br />புறப்படுத்தான் ஆகும் மறை','ஓர் ஒற்றரின் திறனை வியந்து பிறர் அறியச் சிறப்புச் செய்தால், ஒளிவு மறைவாக இருக்கவேண்டிய செய்தியை, வெளிப்படுத்தியதாகிவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(591,'591','உடையர் எனப்படுவ தூக்கமஃ தில்லார்<br />உடைய துடையரோ மற்று','ஊக்கம் உடையவரே உடையவர் எனப்படுவர் ஊமில்லாதவர் வேறு எதை உடையவராக இருந்தாலும் அவர் உடையவர் ஆக மாட்டார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(592,'592','உள்ள முடைமை உடைமை பொருளுடைமை<br />நில்லாது நீங்கி விடும்','ஊக்கம் எனும் ஒரு பொருளைத் தவிர, வேறு எதனையும் நிலையான உடைமை என்று கூற இயலாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(593,'593','ஆக்கம் இழந்தேமென் றல்லாவார் ஊக்கம்<br />ஒருவந்தங் கைத்துடை யார்','ஊக்கத்தை உறுதியாகக் கொண்டிருப்பவர்கள், ஆக்கம் இழக்க நேர்ந்தாலும் அப்போதுகூட ஊக்கத்தை இழந்து கலங்க மாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(594,'594','ஆக்கம் அதர்வினாய்ச் செல்லும் அசைவிலா<br />ஊக்க முடையா னுழை','உயர்வு, உறுதியான ஊக்கமுடையவர்களைத் தேடிக் கண்டுபிடித்து அவர்களிடம் போய்ச் சேரும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(595,'595','வெள்ளத் தனைய மலர்நீட்டம் மாந்தர்தம்<br />உள்ளத் தனைய துயர்வு','தண்ணீரின் அளவுதான் அதில் மலர்ந்துள்ள தாமரைத் தண்டின் அளவும் இருக்கும் அதுபோல மனிதரின் வாழ்க்கையின் உயர்வு அவர் மனத்தில் கொண்டுள்ள ஊக்கத்தின் அளவே இருக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(596,'596','உள்ளுவ தெல்லாம் உயர்வுள்ளல் மற்றது<br />தள்ளினுந் தள்ளாமை நீர்த்து','நினைப்பதெல்லாம் உயர்ந்த நினைப்பாகவே இருக்கவேண்டும் அது கைகூடாவிட்டாலும் அதற்காக அந்த நினைப்பை விடக்கூடாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(597,'597','சிதைவிடத் தொல்கார் உரவோர் புதையம்பிற்<br />பட்டுப்பா டூன்றுங் களிறு','உடல் முழுதும் அம்புகளால் துளைக்கப்பட்டாலும் யானையானது உறுதி தளராமல் இருப்பதுபோல, ஊக்கமுடையவர்கள், அழிவே வந்தாலும் அதற்காகக் கவலைப்படமாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(598,'598','உள்ளம் இலாதவர் எய்தார் உலகத்து<br />வள்ளியம் என்னுஞ் செருக்கு','அள்ளி வழங்கும் ஆர்வம் இல்லாத ஒருவர் தம்மை வள்ளல் எனப் பெருமைப்பட்டுக் கொள்ள வழியே இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(599,'599','பரியது கூர்ங்கோட்ட தாயினும் யானை<br />வெரூஉம் புலிதாக் குறின்','உருவத்தைவிட ஊக்கமே வலிவானது என்பதற்கு எடுத்துக்காட்டு: கொழுத்த உட��்பும் கூர்மையான கொம்புகளுங்கொண்ட யானை, தன்னைத் தாக்க வரும் புலியைக் கண்டு அஞ்சி நடுங்குவது தான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(600,'600','உரமொருவற் குள்ள வெறுக்கையஃ தில்லார்<br />மரமக்க ளாதலே வேறு','மனத்தில் உறுதியான ஊக்கமில்லாதவர்கள் உருவத்தில் மனிதர்களாகக் காணப்பட்டாலும் மரங்களுக்கும் அவர்களுக்கும் வேறுபாடு இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(601,'601','குடியென்னுங் குன்றா விளக்கம் மடியென்னும்<br />மாசூர மாய்ந்து கெடும்','பிறந்த குடிப் பெருமை என்னதான் ஒளிமயமாக இருந்தாலும், சோம்பல் குடிகொண்டால் அது மங்கிப் போய் இருண்டு விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(602,'602','மடியை மடியா ஒழுகல் குடியைக்<br />குடியாக வேண்டு பவர்','குலம் சிறக்க வேண்டுமானால், சோம்பலை ஒழித்து, ஊக்கத்துடன் முயற்சிகளை மேற்கொள்ள வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(603,'603','மடிமடிக் கொண்டொழுகும் பேதை பிறந்த<br />குடிமடியுந் தன்னினு முந்து','அறிவும் அக்கறையுமில்லாத சோம்பேறி பிறந்த குடி, அவனுக்கு முன் அழிந்து போய் விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(604,'604','குடிமடிந்து குற்றம் பெருகும் மடிமடிந்து<br />மாண்ட உஞற்றி லவர்க்கு','சோம்பேறித்தனமானவர்களின் வாழ்க்கையில் குற்றங்களும் பெருகிவிடும்; குடும்பப் பெருமையும் சீரழிந்து போய்விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(605,'605','நெடுநீர் மறவி மடிதுயில் நான்கும்<br />கெடுநீரார் காமக் கலன்','காலம் தாழ்த்துதல், மறதி, சோம்பல், அளவுக்கு மீறிய தூக்கம் ஆகிய நான்கும், கெடுகின்ற ஒருவர் விரும்பியேறும் தோணிகளாம்!',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(606,'606','படியுடையார் பற்றமைந்தக் கண்ணும் மடியுடையார்<br />மாண்பயன் எய்தல் அரிது','தகுதியுடையவரின் அன்புக்குப் பாத்திரமானவராக இருப்பினும் சோம்பலுடையவர்கள் பெருமை எனும் பயனை அடைவதென்பது அரிதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(607,'607','இடிபுரிந் தெள்ளுஞ்சொற் கேட்பர் மடிபுரிந்து<br />மாண்ட உஞற்றி லவர்','முயற்சி செய்வதில் அக்கறையின்றிச் சோம்பேறிகளாய் வாழ்பவர்கள் இகழ்ச்சிக்கு ஆளாவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(608,'608','மடிமை குடிமைக்கண் தங்கிற்றன் னென்னார்க்<br />கடிமை புகுத்தி விடும்','பெருமைமிக்க குடியில் பிறந்தவராயினும், அவரிடம் சோம்பல் குடியேறி விட்டால் அதுவே அவரைப் பகைவர்களுக்கு அடிமையாக்கிவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(609,'609','குடியாண்மை யுள்வந்த குற்றம் ஒருவன்<br />மடியாண்மை மாற்றக் கெடும்','தன்னை ஆட்கொண்டுள்ள சோம்பலை ஒருவன் அகற்றிவிட்டால், அவனது குடிப்பெருமைக்கும், ஆண்மைக்கும் சிறப்பு தானே வந்து சேரும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(610,'610','மடியிலா மன்னவன் எய்தும் அடியளந்தான்<br />தாஅய தெல்லாம் ஒருங்கு','சோம்பல் இல்லாதவர் அடையும் பயன், சோர்வில்லாத ஒரு மன்னன், அவன் சென்ற இடமனைத்தையும் தனது காலடி எல்லைக்குள் கொண்டு வந்ததைப் போன்றதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(611,'611','அருமை உடைத்தென் றசாவாமை வேண்டும்<br />பெருமை முயற்சி தரும்','நம்மால் முடியுமா என்று மனத்தளர்ச்சி அடையாமல், முடியும் என்ற நம்பிக்கையுடன் முயற்சி செய்தால் அதுவே பெரிய வலிமையாக அமையும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(612,'612','வினைக்கண் வினைகெடல் ஓம்பல் வினைக்குறை<br />தீர்ந்தாரின் தீர்ந்தன் றுலகு','எந்தச் செயலில் ஈடுபட்டாலும் அதனை முழுமையாகச் செய்து முடிக்க வேண்டும் இல்லையேல் அரைக்கிணறு தாண்டிய கதையாகி விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(613,'613','தாளாண்மை என்னுந் தகைமைக்கண் தங்கிற்றே<br />வேளாண்மை என்னுஞ் செருக்கு','பிறருக்கு உதவி புரியும் பெருமித உணர்வு, விடா முயற்சி மேற்கொள்ளக்கூடிய உயர்ந்த இயல்புடையவர்களிடம் நிலை பெற்றிருக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(614,'614','தாள���ண்மை இல்லாதான் வேளாண்மை பேடிகை<br />வாளாண்மை போலக் கெடும்','ஊக்கமில்லாதவர் உதவியாளராக இருப்பதற்கும், ஒரு பேடி, கையிலே வாள்தூக்கி வீசுவதற்கும் வேறுபாடு ஒன்றுமில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(615,'615','இன்பம் விழையான் வினைவிழைவான் தன்கேளிர்<br />துன்பம் துடைத்தூன்றும் தூண்','தன்னலம் விரும்பாமல், தான் மேற்கொண்ட செயலை நிறைவேற்ற விரும்புகின்றவன் தன்னைச் சூழ்ந்துள்ள சுற்றத்தார், நண்பர்கள், நாட்டு மக்கள் ஆகிய அனைவரின் துன்பம் துடைத்து, அவர்களைத் தாங்குகிற தூணாவான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(616,'616','முயற்சி திருவினை ஆக்கும் முயற்றின்மை<br />இன்மை புகுத்தி விடும்','முயற்சி இல்லாமல் எதுவும் இல்லை முயற்சிதான் சிறப்பான செயல்பாடுகளுக்குக் காரணமாக அமையும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(617,'617','மடியுளாள் மாமுகடி என்ப மடியிலான்<br />தாளுளாள் தாமரையி னாள்','திருமகள், மூதேவி எனப்படும் சொற்கள் முறையே முயற்சியில் ஊக்கமுடையவரையும், முயற்சியில் ஊக்கமற்ற சோம்பேறியையும் சுட்டிக் காட்டிப் பயன்படுவனவாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(618,'618','பொறியின்மை யார்க்கும் பழியன் றறிவறிந்<br />தாள்வினை இன்மை பழி','விதிப்பயனால் பழி ஏற்படும் என்பது தவறு, அறிய வேண்டியவற்றை அறிந்து செயல்படாமல் இருப்பதே பெரும்பழியாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(619,'619','தெய்வத்தான் ஆகா தெனினும் முயற்சிதன்<br />மெய்வருத்தக் கூலி தரும்','கடவுளே என்று கூவி அழைப்பதால் நடக்காத காரியம் ஒருவர் முயற்சியுடன் உழைக்கும் போது அந்த உழைப்புக் கேற்ற வெற்றியைத் தரும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(620,'620','ஊழையும் உப்பக்கங் காண்பர் உலைவின்றித்<br />தாழா துஞற்று பவர்','``ஊழ் என்பது வெல்ல முடியாத ஒன்று என்பார்கள் சோர்வில்லாமல் முயற்சி மேற்கொள்பவர்கள் அந்த ஊழையும் தோல்வி அடையச் செய்வார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(621,'621','இடுக்கண் வருங்கால் நகுக அதனை <br />அடுத்தூர்வ தஃதொப்ப தில்','சோதனைகளை எதிர்த்து வெல்லக் கூடியது, அந்தச் சோதனைகளைக் கண்டு கலங்காமல் மகிழ்வுடன் இருக்கும் மனம்தான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(622,'622','வெள்ளத் தனைய இடும்பை அறிவுடையான்<br />உள்ளத்தின் உள்ளக் கெடும்','வெள்ளம்போல் துன்பம் வந்தாலும் அதனை வெல்லும் வழி யாது என்பதை அறிவுடையவர்கள் நினைத்த மாத்திரத்திலேயே அத்துன்பம் விலகி ஓடி விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(623,'623','இடும்பைக் கிடும்பை படுப்பர் இடும்பைக்<br />கிடும்பை படாஅ தவர்','துன்பம் சூழும் போது, துவண்டு போகாதவர்கள் அந்தத் துன்பத்தையே துன்பத்தில் ஆழ்த்தி அதனைத் தோல்வியுறச் செய்வார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(624,'624','மடுத்தவா யெல்லாம் பகடன்னான் உற்ற<br />இடுக்கண் இடர்ப்பா டுடைத்து','தடங்கல் நிறைந்த கரடுமுரடான பாதையில் பெரும் பாரத்தை எருது இழுத்துக் கொண்டு போவது போல, விடா முயற்சியுடன் செயல்பட்டால் துன்பங்களுக்கு முடிவு ஏற்பட்டு வெற்றி கிட்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(625,'625','அடுக்கி வரினும் அழிவிலான் உற்ற<br />இடுக்கண் இடுக்கட் படும்','துன்பங்களைக் கண்டு கலங்காதவனை, விடாமல் தொடரும் துன்பங்கள், துன்பப்பட்டு அழிந்து விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(626,'626','அற்றேமென் றல்லற் படுபவோ பெற்றேமென்<br />றோம்புதல் தேற்றா தவர்','இத்தனை வளத்தையும் பெற்றுள்ளோமே யென்று மகிழந்து அதைக் காத்திட வேண்டுமென்று கருதாதவர்கள் அந்த வளத்தை இழக்க நேரிடும் போது மட்டும் அதற்காகத் துவண்டு போய் விடுவார்களா?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(627,'627','இலக்கம் உடம்பிடும்பைக் கென்று கலக்கத்தைக்<br />கையாறாக் கொள்ளாதா மேல்','துன்பம் என்பது உயிருக்கும் உடலுக்கும் இயல்பானதே என்பதை உணர்ந்த பெரியோர், துன்பம் வரும் போது அதனைத் ���ுன்பமாகவே கருத மாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(628,'628','இன்பம் விழையான் இடும்பை இயல்பென்பான்<br />துன்ப முறுதல் இலன்','இன்பத்தைத் தேடி அலையாமல், துன்பம் வந்தாலும் அதை இயல்பாகக் கருதிப்கொள்பவன் அந்தத் துன்பத்தினால் துவண்டு போவதில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(629,'629','இன்பத்துள் இன்பம் விழையாதான் துன்பத்துள்<br />துன்ப முறுதல் இலன்','இன்பம் வரும் பொழுது அதற்காக ஆட்டம் போடாதவர்கள், துன்பம் வரும் பொழுதும் அதற்காக வாட்டம் கொள்ள மாட்டார்கள் இரண்டையும் ஒன்றுபோல் கருதும் உறுதிக்கு இது எடுத்துக்காட்டு',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(630,'630','இன்னாமை இன்பம் எனக்கொளின் ஆகுந்தன்<br />ஒன்னார் விழையுஞ் சிறப்பு','துன்பத்தை இன்பமாகக் கருதும் மனஉறுதி கொண்டவர்களுக்கு, அவர்களது பகைவர்களும் பாராட்டுகிற பெருமை வந்து சேரும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(631,'631','கருவியும் காலமும் செய்கையும் செய்யும்<br />அருவினையும் மாண்ட தமைச்சு','உரிய கருவி, உற்ற காலம், ஆற்றும் வகை, ஆற்றிடும் பணி ஆகியவற்றை ஆய்ந்தறிந்த செயல்படுபவனே சிறந்த அமைச்சன்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(632,'632','வன்கண் குடிகாத்தல் கற்றறிதல் ஆள்வினையோ<br />டைந்துடன் மாண்ட தமைச்சு','அமைச்சரவை என்பது, துணிவுடன் செயல்படுதல், குடிகளைப் பாதுகாத்தல், அறநூல்களைக் கற்றல், ஆவன செய்திட அறிதல், அயராத முயற்சி ஆகிய ஐந்தும் கொண்டதாக விளங்க வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(633,'633','பிரித்தலும் பேணிக் கொளலும் பிரிந்தார்ப்<br />பொருத்தலும் வல்ல தமைச்சு','அமைச்சருக்குரிய ஆற்றல் என்பது (நாட்டின் நலனுக்காக) பகைவர்க்குத் துணையானவர்களைப் பிரித்தல், நாட்டுக்குத் துணையாக இருப்போரின் நலன் காத்தல், பிரிந்து சென்று பின்னர் திருந்தியவர்களைச் சேர்த்துக் கொளல் எனும் செயல்களில் காணப்படுவதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(634,'634','தெரிதலுந் தேர்ந்து செயலும் ஒருதலையாச்<br />சொல்லலும் வல்ல தமைச்சு','ஒரு செயலைத் தேர்ந்தெடுத்தாலும், அதனை நிறைவேற்றிட வழிவகைகளை ஆராய்ந்து ஈடுபடுதலும், முடிவு எதுவாயினும் அதனை உறுதிபடச் சொல்லும் ஆற்றல் படைத்திருத்தலும் அமைச்சருக்குரிய சிறப்பாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(635,'635','அறனறிந் தான்றமைந்த சொல்லானெஞ் ஞான்றுந்<br />திறனறிந்தான் தேர்ச்சித் துணை','அறநெறி உணர்ந்தவராகவும், சொல்லாற்றல் கொண்டவராகவும், செயல்திறன் படைத்தவராகவும் இருப்பவரே ஆலோசனைகள் கூறக்கூடிய துணையாக விளங்க முடியும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(636,'636','மதிநுட்ப நூலோ டுடையார்க் கதிநுட்பம்<br />யாவுள முன்னிற் பவை','நூலறிவுடன் இயற்கையான மதி நுட்பமும் உள்ளவர்களுக்கு முன்னால் எந்த சூழ்ச்சிதான் எதிர்த்து நிற்க முடியும்? முடியாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(637,'637','செயற்கை அறிந்தக் கடைத்து முலகத்<br />தியற்கை அறிந்து செயல்','செயலாற்றல் பற்றிய நூலறிவைப் பெற்றிருந்தாலும், உலக நடைமுறைகளை உணர்ந்து பார்த்தே அதற்கேற்றவாறு அச்செயல்களை நிறைவேற்ற வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(638,'638','அறிகொன் றறியான் எனினும் உறுதி<br />உழையிருந்தான் கூறல் கடன்','சொன்னதையும் கேட்காமல், சொந்த அறிவும் இல்லாமல் இருப்போர்க்கு, அருகிலுள்ள அமைச்சர்கள்தான் துணிவோடு நல்ல யோசனைகளைக் கூற வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(639,'639','பழுதெண்ணும் மந்திரியிற் பக்கத்துள் தெவ்வோர்<br />எழுபது கோடி உறும்','தவறான வழிமுறைகளையே சிந்தித்துச் செயல்படுகிற அமைச்சர் ஒருவர் அருகிலிருப்பதை விட எழுபது கோடி எதிரிகள் பக்கத்தில் இருப்பது எவ்வளவோ மேலாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(640,'640','முறைப்படச் சூழ்ந்து முடிவிலவே செய்வர்<br />திறப்பா டிலாஅ தவர்','முறைப்படித் தீட்டப்படும் திட்டங்க���்கூடச் செயல் திறன் இல்லாதவர்களிடம் சிக்கினால் முழுமையாகாமல் முடங்கித்தான் கிடக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(641,'641','நாநல மென்னும் நலனுடைமை அந்நலம்<br />யாநலத் துள்ளதூஉம் அன்று','சொல்வன்மைக்கு உள்ள சிறப்பு வேறு எதற்குமில்லை எனவே அது செல்வங்களில் எல்லாம் சிறந்த செல்வமாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(642,'642','ஆக்கமுங் கேடும் அதனால் வருதலாற்<br />காத்தோம்பல் சொல்லின்கட் சோர்வு','ஆக்கமும் அழிவும் சொல்லால் ஏற்படும் என்பதால், எந்தவொரு சொல்லிலும் குறைபாடு நேராமல் கவனமாக இருக்க வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(643,'643','கேட்டார்ப் பிணிக்கும் தகையவாய்க் கேளாரும்<br />வேட்ப மொழிவதாம் சொல்','கேட்போரைக் கவரும் தன்மையுடைதாகவும், கேட்காதவரும் தேடிவந்து விரும்பிக் கேட்கக் கூடியதாகவும் அமைவதே சொல்வன்மை எனப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(644,'644','திறனறிந்து சொல்லுக சொல்லை அறனும்<br />பொருளும் அதனினூஉங் கில்','காரணத்தைத் தெளிவாக அறிந்து ஒன்றைச் சொல்ல வேண்டும் அந்தச் சொல் வன்மையைப் போன்ற அறமும், உண்மைப் பொருளும் வேறெதுவும் இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(645,'645','சொல்லுக சொல்லைப் பிறிதோர்சொல் அச்சொல்லை<br />வெல்லுஞ்சொல் இன்மை அறிந்து','இந்தச் சொல்லை இன்னொரு சொல் வெல்லாது என்று உணர்ந்த பிறகே அந்தச் சொல்லைப் பயன்படுத்த வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(646,'646','வேட்பத்தாஞ் சொல்லிப் பிறர்சொற் பயன்கோடல்<br />மாட்சியின் மாசற்றார் கோள்','மற்றவர்கள் விரும்பிக் கேட்டு உணரும்படியாகக் கருத்துக்களைச் சொல்வதும், மற்றவர்கள் கூறும் சொற்களின் பயனை ஆராய்ந்து ஏற்றுக் கொள்வதும் அறிவுடையார் செயலாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(647,'647','சொலல்வல்லன் சோர்விலன் அஞ்சா னவனை<br />இகல்வெல்லல் யார்க்கும் அரிது','சொல்லாற்றல் படைத்தவனாகவும், சோர்வு அறியாதவனாகவும், அஞ்சா நெஞ்சங் கொண்டவனாகவும் இருப்பவனை எதிர்த்து எவராலும் வெல்ல முடியாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(648,'648','விரைந்து தொழில்கேட்கும் ஞாலம் நிரந்தினிது<br />சொல்லுதல் வல்லார்ப் பெறின்','வகைப்படுத்தியும், சுவையாகவும் கருத்துக்களைச் சொல்லும் வல்லமையுடையோர் சுட்டிக்காட்டும் பணியை, உலகத்தார் உடனடியாக நிறைவேற்ற முனைவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(649,'649','பலசொல்லக் காமுறுவர் மன்றமா சற்ற<br />சிலசொல்லல் தேற்றா தவர்','குறையில்லாத சில சொற்களைக் கொண்டு தெளிவான விளக்கம் தந்திட இயலாதவர்கள்தான் பல சொற்களைத் திரும்பத் திரும்பக் கூறிக் கொண்டிருப்பார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(650,'650','இணரூழ்த்து நாறா மலரனையர் கற்ற<br />துணர விரிந்துரையா தார்','கற்றதைப் பிறர் உணர்ந்து கொள்ளும் வகையில் விளக்கிச் சொல்ல முடியாதவர், கொத்தாக மலர்ந்திருந்தாலும் மணம் கமழாத மலரைப் போன்றவர்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(651,'651','துணைநலம் ஆக்கந் தரூஉம் வினைநலம்<br />வேண்டிய எல்லாந் தரும்','ஒருவருக்குக் கிடைக்கும் துணைவர்களால் வலிமை பெருகும்; அவர்களுடன் கூடி ஆற்றிடும் நற்செயல்களால் எல்லா நலன்களும் கிட்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(652,'652','என்றும் ஒருவுதல் வேண்டும் புகழொடு<br />நன்றி பயவா வினை','புகழையும், நன்மையையும் தராத தூய்மையற்ற செயல்களை எந்த நிலையிலும் செய்யாமல் அவற்றை விட்டொழிக்க வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(653,'653','ஓஒதல் வேண்டும் ஒளிமாழ்குஞ் செய்வினை<br />ஆஅது மென்னு மவர்','மேன்மேலும் உயர்ந்திட வேண்டுமென விரும்புகின்றவர்கள், தம்முடைய செயல்களால் தமது புகழ் கெடாமல் கவனமாக இருந்திட வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(654,'654','இடுக்கண் படினும் இளிவந்த செய்யார்<br />நடுக்கற்ற காட்சி யவர்','தெளிவான அறிவும் உறுதியும் கொண்டவர்கள் துன்ப��்திலிருந்து விடுபடுவதற்காகக்கூட இழிவான செயலில் ஈடுபட மாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(655,'655','எற்றென் றிரங்குவ செய்யற்க செய்வானேல்<br />மற்றன்ன செய்யாமை நன்று','`என்ன தவறு செய்துவிட்டோம் என நினைத்துக் கவலைப்படுவதற்குரிய காரியங்களைச் செய்யக்கூடாது ஒருகால் அப்படிச் செய்து விட்டாலும் அச்செயலை மீண்டும் தொடராதிருப்பதே நன்று',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(656,'656','ஈன்றாள் பசிகாண்பான் ஆயினுஞ் செய்யற்க<br />சான்றோர் பழிக்கும் வினை','பசியால் துடிக்கும் தனது தாயின் வேதனையைத் தணிப்பதற்காகக்கூட இழிவான செயலில் ஈடுபடக்கூடாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(657,'657','பழிமலைந் தெய்திய ஆக்கத்திற் சான்றோர்<br />கழிநல் குரவே தலை','பழிக்கு அஞ்சாமல் இழிவான செயல்களைப் புரிந்து செல்வந்தராக வாழ்வதைவிட, கொடிய வறுமை தாக்கினாலும் கவலைப்படாமல் நேர்மையாளராக வாழ்வதே மேலானதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(658,'658','கடிந்த கடிந்தொரார் செய்தார்க் கவைதாம்<br />முடிந்தாலும் பீழை தரும்','தகாதவை என ஒதுக்கப்பட்ட செயல்களை ஒதுக்கிவிடாமல் செய்பவர்களுக்கு ஒரு வேளை அச்செயல்கள் நிறைவேறினாலும் துன்பமே ஏற்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(659,'659','அழக்கொண்ட எல்லாம் அழப்போம் இழப்பினும் <br />பிற்பயக்கும் நற்பா லவை','பிறர் அழத் திரட்டிய செல்வம் அழ அழப் போய்விடும் நல்வழியில் வந்த செல்வமென்றால் அதனை இழந்தாலும் மீண்டும் வந்து பயன் தரும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(660,'660','சலத்தாற் பொருள்செய்தே மார்த்தல் பசுமண் <br />கலத்துணீர் பெய்திரீஇ யற்று','தவறான வழிகளில் பொருளைச் சேர்த்து அதைக் காப்பாற்ற நினைப்பது, பச்சை மண்ணால் செய்யப்பட்ட பாத்திரத்தில் நீரை ஊற்றி, அதைப் பாதுகாக்க நினைப்பதைப் போன்றதுதான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(661,'661','வினைத்திட்பம் என்ப தொருவன் மனத்திட்பம்<br />மற்றைய எல்லாம் பிற','மற்றவை எல்லாம் இருந்தும் ஒருவரது மனத்தில் உறுதி மட்டும் இல்லாவிட்டால் அவரது செயலிலும் உறுதி இருக்காது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(662,'662','ஊறொரால் உற்றபின் ஒல்காமை இவ்விரண்டின்<br />ஆறென்பர் ஆய்ந்தவர் கோள்','இடையூறு வருவதற்கு முன்பே அதனை நீக்கிட முனைவது, மீறி வந்து விடுமேயானால் மனம் தளராது இருப்பது ஆகிய இரண்டு வழிகளுமே அறிவுடையோர் கொள்கையாம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(663,'663','கடைக்கொட்கச் செய்தக்க தாண்மை இடைக்கொட்கின்<br />எற்றா விழுமந் தரும்','செய்து முடிக்கும் வரையில் ஒரு செயலைப்பற்றி வெளிப்படுத்தாமலிருப்பதே செயலாற்றும் உறுதி எனப்படும் இடையில் வெளியே தெரிந்துவிட்டால் அச்செயலை நிறைவேற்ற முடியாத அளவுக்கு இடையூறு ஏற்படக்கூடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(664,'664','சொல்லுதல் யார்க்கும் எளிய அரியவாம்<br />சொல்லிய வண்ணம் செயல்','சொல்லுவது எல்லோருக்கும் எளிது; சொல்லியதைச் செய்து முடிப்பதுதான் கடினம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(665,'665','வீறெய்தி மாண்டார் வினைத்திட்பம் வேந்தன்கண்<br />ஊறெய்தி உள்ளப் படும்','செயல் திறனால் சிறப்புற்ற மாண்புடையவரின் வினைத் திட்பமானது, ஆட்சியாளரையும் கவர்ந்து பெரிதும் மதித்துப் போற்றப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(666,'666','எண்ணிய எண்ணியாங் கெய்துப எண்ணியார்<br />திண்ணியர் ஆகப் பெறின்','எண்ணியதைச் செயல்படுத்துவதில் உறுதி உடையவர்களாக இருந்தால் அவர்கள் எண்ணியவாறே வெற்றி பெறுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(667,'667','உருவுகண் டெள்ளாமை வேண்டும் உருள்பெருந்தேர்க்<br />கச்சாணி யன்னார் உடைத்து','உருவத்தால் சிறியவர்கள் என்பதற்காக யாரையும் கேலி செய்து அலட்சியப்படுத்தக் கூடாது பெரிய தேர் ஓடுவதற்குக் காரணமான அச்சாணி உருவத்தால் சிறியதுதான் என்பதை உணர வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(668,'668','கலங்காது கண்ட வினைக்கண் துளங்காது<br />தூக்கங் கடிந்து செயல்','மனக் குழப்பமின்றித் தெளிவாக முடிவு செய்யப்பட்ட ஒரு செயலைத் தளர்ச்சியும், தாமதமும் இடையே ஏற்படாமல் விரைந்து நிறைவேற்ற வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(669,'669','துன்பம் உறவரினும் செய்க துணிவாற்றி<br />இன்பம் பயக்கும் வினை','இன்பம் தரக்கூடிய செயல் என்பது, துன்பம் வந்தாலும் அதனைப் பொருட்படுத்தாமல் துணிவுடன் நிறைவேற்றி முடிக்கக் கூடியதேயாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(670,'670','எனைத்திட்ப மெய்தியக் கண்ணும் வினைத்திட்பம்<br />வேண்டாரை வேண்டா துலகு','எவ்வளவுதான் வலிமையுடையவராக இருப்பினும் அவர் மேற்கொள்ளும் செயலில் உறுதியில்லாதவராக இருந்தால், அவரை உலகம் மதிக்காது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(671,'671','சூழ்ச்சி முடிவு துணிவெய்தல் அத்துணிவு<br />தாழ்ச்சியுள் தங்குதல் தீது','ஒரு செயலில் ஈடுபட முடிவெடுக்கும்போது அச்செயலால் விளையும் சாதக பாதகங்கள் பற்றிய ஆராய்ச்சியும் முடிவடைந்திருக்க வேண்டும் முடிவெடுத்த பிறகு காலந்தாழ்த்துவது தீதாக முடியும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(672,'672','தூங்குக தூங்கிச் செயற்பால தூங்கற்க<br />தூங்காது செய்யும் வினை','நிதானமாகச் செய்ய வேண்டிய காரியங்களைத் தாமதித்துச் செய்யலாம்; ஆனால் விரைவாகச் செய்ய வேண்டிய காரியங்களில் தாமதம் கூடாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(673,'673','ஒல்லும்வா யெல்லாம் வினைநன்றே ஒல்லாக்கால்<br />செல்லும்வாய் நோக்கிச் செயல்','இயலும் இடங்களில் எல்லாம் செயல் முடிப்பது நலம் தரும் இயலாத இடமாயின் அதற்கேற்ற வழியை அறிந்து அந்தச் செயலை முடிக்க வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(674,'674','வினைபகை யென்றிரண்டின் எச்சம் நினையுங்கால்<br />தீயெச்சம் போலத் தெறும்','எற்ற செயலையோ, எதிர்கொண்ட பகையையோ முற்றாக முடிக்காமல் விட்டுவிட்டால் அது நெருப்பை அரை குறையாக அணைத்தது போலக் கேடு விளைவிக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(675,'675','பொருள்கருவி காலம் வினையிடனொ டைந்தும்<br />இருள்தீர எண்ணிச் செயல்','ஒரு காரியத்தில் ஈடுபடுவதற்கு முன்பு, அதற்குத் தேவையான பொருள், ஏற்ற கருவி, காலம், மேற்கொள்ளப் போகும் செயல்முறை, உகந்த இடம் ஆகிய ஐந்தையும் குறையில்லாமல் பார்த்துக் கொள்ள வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(676,'676','முடிவும் இடையூறும் முற்றியாங் கெய்தும்<br />படுபயனும் பார்த்துச் செயல்','ஈடுபடக்கூடிய ஒரு செயலால் எதிர்பார்க்கப்படும் பயன், அதற்கான முயற்சிக்கு இடையே வரும் தடைகள், அச்செயலாற்றுதவற்கான முறை ஆகிய அனைத்தையும் முதலில் ஆராய்ந்து அறிந்து கொள்ள வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(677,'677','செய்வினை செய்வான் செயன்முறை அவ்வினை<br />உள்ளறிவான் உள்ளங் கொளல்','ஒரு செயலில் ஈடுபடுகிறவன், அச்செயல் குறித்து முழுமையாக உணர்ந்தவனின் கருத்தினை முதலில் அறிந்து கொள்ள வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(678,'678','வினையான் வினையாக்கிக் கோடல் நனைகவுள்<br />யானையால் யானையாத் தற்று','ஒரு செயலில் ஈடுபடும்போது, அச்செயலின் தொடர்பாக மற்றொரு செயலையும் முடித்துக் கொள்வது ஒரு யானையைப் பயன்படுத்தி மற்றொரு யானையைப் பிடிப்பது போன்றதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(679,'679','நட்டார்க்கு நல்ல செயலின் விரைந்ததே<br />ஒட்டாரை ஒட்டிக் கொளல்','நன்பருக்கு நல்லுதவி செய்வதைக் காட்டிலும் பகைவராயிருப்பவரைத் தம்முடன் பொருந்துமாறு சேர்த்துக் கொள்ளுதல் விரைந்து செய்யத் தக்கதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(680,'680','உறைசிறியார் உண்ணடுங்கல் அஞ்சிக் குறைபெறிற் <br />கொள்வர் பெரியார்ப் பணிந்து','தம்மைவிட வலிமையானவர்களை எதிர்ப்பதற்குத் தம்முடன் இருப்பவர்களே அஞ்சும்போது தாம் எதிர்பார்க்���ும் பலன் கிட்டுமானால் அவர்கள் வலியோரை வணங்கி ஏற்றுக் கொள்வார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(681,'681','அன்புடைமை ஆன்ற குடிப்பிறத்தல் வேந்தவாம்<br />பண்புடைமை தூதுரைப்பான் பண்பு','அன்பான குணமும், புகழ்வாய்ந்த குடிப்பிறப்பும், அரசினர் பாராட்டக்கூடிய நல்ல பண்பாடும் பெற்றிருப்பதே தூதருக்குரிய தகுதிகளாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(682,'682','அன்பறி வாராய்ந்த சொல்வன்மை தூதுரைப்பார்க்<br />கின்றி யமையாத மூன்று','தூது செல்பவருக்குத் தேவைப்படும் மூன்று முக்கியமான பண்புகள் அன்பு, அறிவு, ஆராய்ந்து பேசும் சொல்வன்மை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(683,'683','நூலாருள் நூல்வல்லன் ஆகுதல் வேலாருள் <br />வென்றி வினையுரைப்பான் பண்பு','வேற்று நாட்டாரிடம், தனது நாட்டுக்கு வெற்றி ஏற்படும் வண்ணம் செய்தி உரைத்திடும் தூதுவன், நூலாய்ந்து அறிந்தவர்களிலேயே வல்லவனாக இருத்தல் வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(684,'684','அறிவுரு வாராய்ந்த கல்வியிம் மூன்றன்<br />செறிவுடையான் செல்க வினைக்கு','தூது உரைக்கும் செயலை மேற்கொள்பவர் அறிவு, தோற்றப் பொலிவு, ஆய்ந்து தெளிந்த கல்வி ஆகிய மூன்றும் நிறைந்தவராக இருத்தல் வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(685,'685','தொகச்சொல்லித் தூவாத நீக்கி நகச்சொல்லி<br />நன்றி பயப்பதாந் தூது','சினத்தைத் தூண்டாமல் மகிழத்தக்க அளவுக்குச் செய்திகளைத் தொகுத்தும், தேவையற்ற செய்திகளை ஒதுக்கியும், நல்ல பயனளிக்கும் விதமாகச் சொல்லுவதே சிறந்த தூதருக்கு அழகாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(686,'686','கற்றுக்கண் அஞ்சான் செலச்சொல்லிக் காலத்தால் <br />தக்க தறிவதாந் தூது','கற்றறிவாளனாகவும், பகைவரின் கனல்கக்கும் பார்வைக்கு அஞ்சாதவனாகவும், உள்ளத்தில் பதியுமாறு உரைப்பவனாகவும், உரிய நேரத்தில் உணரவேண்டியதை உணர்ந்து கொள்பவனாகவும் இருப்பவனே சிறந்த தூதனாவான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(687,'687','கடனறிந்து காலங் கருதி இடனறிந்<br />தெண்ணி உரைப்பான் தலை','ஆற்றவேண்டிய கடமையை அறிந்து, அதற்குரிய காலத்தையும் இடத்தையும் தேர்ந்து, சொல்ல வேண்டியதைத் தெளிவாகச் சிந்தித்துச் சொல்பவனே சிறந்த தூதனாவான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(688,'688','தூய்மை துணைமை துணிவுடைமை இம்மூன்றின்<br />வாய்மை வழியுரைப்பான் பண்பு','துணிவு, துணை, தூய ஒழுக்கம் ஆகிய இம்மூன்றும் தூதுவர்க்குத் தேவையானவைகளாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(689,'689','விடுமாற்றம் வேந்தர்க் குரைப்பான் வடுமாற்றம்<br />வாய்சோரா வன்க ணவன்','ஓர் அரசின் கருத்தை மற்றோர் அரசுக்கு எடுத்துரைக்கும் தூதன், வாய்தவறிக்கூட, குற்றம் தோய்ந்த சொற்களைக் கூறிடாத உறுதி படைத்தவனாக இருத்தல் வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(690,'690','இறுதி பயப்பினும் எஞ்சா திறைவற் <br />குறுதி பயப்பதாம் தூது','தனக்கு அழிவே தருவதாக இருந்தாலும், அதை எண்ணிப் பயந்து விடாமல் உறுதியுடன் கடமையாற்றுகிறவனே தன்னுடைய தலைவனுக்கு நம்பிக்கையான தூதனாவான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(691,'691','அகலா தணுகாது தீக்காய்வார் போல்க<br />இகல்வேந்தர்ச் சேர்ந்தொழுகு வார்','முடிமன்னருடன் பழகுவோர் நெருப்பில் குளிர் காய்வதுபோல அதிகமாக நெருங்கிவிடாமலும், அதிகமாக நீங்கிவிடாமலும் இருப்பார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(692,'692','மன்னர் விழைப விழையாமை மன்னரான்<br />மன்னிய ஆக்கந் தரும்','மன்னர் விரும்புகின்றவைகளைத் தமக்கு வேண்டுமெனத் தாமும் விரும்பாமலிருத்தால் அவர்க்கு அந்த மன்னர் வாயிலாக நிலையான ஆக்கத்தை அளிக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(693,'693','போற்றின் அரியவை போற்றல் கடுத்தபின்<br />தேற்றுதல் யார்க்கும் அரிது','தமக்கு மேலேயுள்ளவர்களிடத்திலிருந்து தம்மைக் காத்துக் கொள்ள விரும்புகிறவர்கள் பொறுத்துக் கொள்ள முடியாத குற்றங்களைச் செய்யாமல் இருக்கவேண்டும் அப்படி செய்துவிட்டால் அதன் பிறகு தம் மீது ஏற்பட்ட சந்தேகத்தை நீக்குவது எளிதான காரியமல்ல',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(694,'694','செவிச்சொல்லுஞ் சேர்ந்த நகையும் அவித்தொழுகல்<br />ஆன்ற பெரியா ரகத்து','ஆற்றல் வாய்ந்த பெரியவர்கள் முன்னே, மற்றவர்கள் காதுக்குள் பேசுவதையும், அவர்களுடன் சேர்ந்து சிரிப்பதையும் தவிர்த்து, அடக்கமெனும் பண்பைக் காத்திடல் வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(695,'695','எப்பொருளும் ஓரார் தொடரார்மற் றப்பொருளை<br />விட்டக்காற் கேட்க மறை','பிறருடன் மறைவாகப் பேசிக் கொண்டிருக்கும்போது அதை ஒட்டுக் கேட்கவும் கூடாது; அது என்னவென்று வினவிடவும் கூடாது அவர்களே அதுபற்றிச் சொன்னால் மட்டுமே கேட்டுக்கொள்ள வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(696,'696','குறிப்பறிந்து காலங் கருதி வெறுப்பில<br />வேண்டுப வேட்பச் சொலல்','ஒருவரின் மனநிலை எவ்வாறு உள்ளது என்பதை அறிந்து, தக்க காலத்தைத் தேர்ந்தெடுத்து, வெறுப்புக்குரியவைகளை விலக்கி, விரும்பத் தக்கதை மட்டுமே, அவர் விரும்பும் வண்ணம் சொல்ல வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(697,'697','வேட்பன சொல்லி வினையில எஞ்ஞான்றும்<br />கேட்பினும் சொல்லா விடல்','விரும்பிக் கேட்டாலும் கூட, பயனுள்ளவற்றை மட்டுமே சொல்லிப் பயனற்றவைகளைச் சொல்லாமல் விட்டுவிட வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(698,'698','இளையர் இனமுறையர் என்றிகழார் நின்ற<br />ஒளியோ டொழுகப் படும்','எமக்கு இளையவர்தான்; இன்ன முறையில் உறவுடையவர் தான் என்று ஆட்சிப் பொறுப்பில் இருப்போரை இகழ்ந்துரைக்காமல், அவர்கள் அடைந்துள்ள பெருமைக்கேற்பப் பண்புடன் நடந்து கொள்ள வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(699,'699','கொளப்பட்டேம் என்றெண்ணிக் கொள்ளாத செய்யார்<br />துளக்கற்ற காட்சி யவர்','ஆட்சியால் நாம் ஏற்றுக் கொள்ளப்பட்டவராயிற்றே என்ற துணிவில், ஏற்றுகொள்ள முடியாத காரியங்களைத் தெளிந்த அறிவுடையவர்கள் செய்ய மாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(700,'700','பழையம் எனக்கருதிப் பண்பல்ல செய்யும் <br />கெழுதகைமை கேடு தரும்','நெடுங்காலமாக நெருங்கிப் பழகுகிற காரணத்தினாலேயே தகாத செயல்களைச் செய்திட உரிமை எடுத்துக்கொள்வது கேடாகவே முடியும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(701,'701','கூறாமை நோக்கிக் குறிப்பறிவான் எஞ்ஞான்றும்<br />மாறாநீர் வையக் கணி','ஒருவர் எதுவும் பேசாமலிருக்கும் போதே அவர் என்ன நினைக்கிறார் என்பதை முகக்குறிப்பால் உணருகிறவன் உலகத்திற்கே அணியாவான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(702,'702','ஐயப் படாஅ தகத்த துணர்வானைத்<br />தெய்வத்தோ டொப்பக் கொளல்','ஒருவன் மனத்தில் உள்ளத்தைத், தெளிவாக உணர்ந்து கொள்ளக்கூடிய சக்தி தெய்வத்திற்கே உண்டு என்று கூறினால், அந்தத் திறமை படைத்த மனிதனையும் அத்தெய்வத்தோடு ஒப்பிடலாம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(703,'703','குறிப்பிற் குறிப்புணர் வாரை உறுப்பினுள்<br />யாது கொடுத்துங் கொளல்','ஒருவரின் முகக் குறிப்பைக் கொண்டே அவரது உள்ளக் குறிப்பை அறிந்து கொள்ளக்கூடிய ஆற்றலுடையவரை, எந்தப் பொறுப்பைக் கொடுத்தாவது துணையாக்கிக் கொள்ளவேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(704,'704','குறித்தது கூறாமைக் கொள்வாரோ டேனை<br />உறுப்போ ரனையரால் வேறு','உறுப்புகளால் வேறுபடாத தோற்றமுடையவராக இருப்பினும், ஒருவர் மனத்தில் உள்ளதை, அவர் கூறாமலே உணரக்கூடியவரும், உணர முடியாதவரும் அறிவினால் வேறுபட்டவர்களேயாவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(705,'705','குறிப்பிற் குறிப்புணரா வாயின் உறுப்பினுள்<br />என்ன பயத்தவோ கண்','ஒருவரது முகக்குறிப்பு, அவரது உள்ளத்தில் இருப்பதைக் காட்டி விடும் என்கிறபோது, அந்தக் குறிப்பை உணர்ந்து கொள்ள முடியாத கண்கள் இருந்தும் என��ன பயன்?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(706,'706','அடுத்தது காட்டும் பளிங்குபோல் நெஞ்சம்<br />கடுத்தது காட்டும் முகம்','கண்ணாடி, தனக்கு உள்ளத்தைக் காட்டுவதுபோல ஒருவரது மனத்தில் உள்ளத்தில் அவரது முகம் காட்டி விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(707,'707','முகத்தின் முதுக்குறைந்த துண்டோ உவப்பினும்<br />காயினும் தான்முந் துறும்','உள்ளத்தில் உள்ள விருப்பு வெறுப்புகளை முந்திக் கொண்டு வெளியிடுவதில் முகத்தைப் போல அறிவு மிக்கது வேறெதுவுமில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(708,'708','முகநோக்கி நிற்க அமையும் அகநோக்கி<br />உற்ற துணர்வார்ப் பெறின்','அகத்தில் உள்ளதை உணர்ந்து கொள்ளும் திறமையிருப்பின், அவர் ஒருவரின் முகத்துக்கு எதிரில் நின்றாலே போதுமானது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(709,'709','பகைமையும் கேண்மையும் கண்ணுரைக்கும் கண்ணின்<br />வகைமை உணர்வார்ப் பெறின்','பார்வையின் வேறுபாடுகளைப் புரிந்துகொள்ளக் கூடியவர்கள், ஒருவரின் கண்களைப் பார்த்தே அவர் மனத்தில் இருப்பது நட்பா, பகையா என்பதைக் கூறிவிடுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(710,'710','நுண்ணியம் என்பார் அளக்குங்கோல் காணுங்காற்<br />கண்ணல்ல தில்லை பிற','நுண்ணறிவாளர் எனப்படுவோர்க்கு பிறரின் மனத்தில் உள்ளதை அளந்தறியும் கோலாகப் பயன்படுவது அவரது கண் அல்லாமல் வேறு எதுவுமில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(711,'711','அவையறிந் தாராய்ந்து சொல்லுக சொல்லின்<br />தொகையறிந்த தூய்மை யவர்','ஒவ்வொரு சொல்லின் தன்மையும் உணர்ந்துள்ள நல்ல அறிஞர்கள், அவையில் கூடியிருப்போரின் தன்மையையும் உணர்ந்து அதற்கேற்ப ஆராய்ந்து பேசுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(712,'712','இடைதெரிந்து நன்குணர்ந்து சொல்லுக சொல்லின்<br />நடைதெரிந்த நன்மை யவர்','சொற்களின் வழிமுறையறிந்த நல்லறிவாளர்கள் அவையின் நேரத்தையும், நிலைமையையும் உணர்ந்து உரையாற்ற வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(713,'713','அவையறியார் சொல்லல்மேற் கொள்பவர் சொல்லின்<br />வகையறியார் வல்லதூஉம் இல்','அவையின் தன்மை அறியாமல் சொற்களைப் பயன்படுத்துகிறவர்களுக்கு அந்தச் சொற்களின் வகையும் தெரியாது; பேசும் திறமையும் கிடையாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(714,'714','ஒளியார்முன் ஒள்ளிய ராதல் வெளியார்முன்<br />வான்சுதை வண்ணங் கொளல்','அறிவாளிகளுக்கு முன்னால் அவர்களையொத்த பாலின் தூய்மையுடன் விளங்கும் அறிஞர்கள், அறிவில்லாதவர்கள் முன்னால் வெண்சுண்ணாம்பு போல் தம்மையும் அறிவற்றவர்களாய்க் காட்டிக் கொள்ள வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(715,'715','நன்றென் றவற்றுள்ளும் நன்றே முதுவருள்<br />முந்து கிளவாச் செறிவு','அறிவாளிகள் கூடியிருக்கும் இடத்தில் முந்திரிக் கொட்டை போல் பேசாமல் இருக்கிற அடக்கமானது எல்லா நலன்களிலும் சிறந்த நலனாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(716,'716','ஆற்றின் நிலைதளர்ந் தற்றே வியன்புலம்<br />ஏற்றுணர்வார் முன்னர் இழுக்கு','அறிவுத்திறனால் பெருமை பெற்றோர் முன்னிலையில் ஆற்றிடும் உரையில் குற்றம் ஏற்படுமானால், அது ஒழுக்க நெறியிலிருந்து தளர்ந்து வீழ்ந்து விட்டதற்கு ஒப்பானதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(717,'717','கற்றறிந்தார் கல்வி விளங்கும் கசடறச்<br />சொற்றெரிதல் முன்னர் இழுக்கு','மாசற்ற சொற்களைத் தேர்ந்தெடுத்து உரை நிகழ்த்துவோரிடமே அவர் கற்றுத் தேர்ந்த கல்வியின் பெருமை விளங்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(718,'718','உணர்வ துடையார்முன் சொல்லல் வளர்வதன்<br />பாத்தியுள் நீர்சொரிந் தற்று','உணர்ந்து கொள்ளக்கூடிய ஆற்றல் உள்ளவர்களின் முன்னிலையில் பேசுதல், வளரக்கூடிய பயிர் உள்ள பாத்தியில் நீர் பாய்ச்சுவது போலப் பயன் விளைக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(719,'719','புல்லவையுள் பொச்சாந்தும் சொல்லற்க நல்லவையுள்<br />நன்கு செலச்சொல்லு வார்','நல்லோர் நிறைந்த அவையில் மனத்தில் பதியும்படி கருத்துக்களை சொல்லும் வல்லமை பெற்றவர்கள், அறிவற்ற பொல்லாதோர் உள்ள அவையில் அறவே பேசாமாலிருப்பதே நலம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(720,'720','அங்கணத்துள் உக்க அமிழ்தற்றால் தங்கணத்தர்<br />அல்லார்முன் கோட்டி கொளல்','அறிவுள்ளவர்கள், அறிவில்லாதவர்களின் அவையில் பேசுவது, தூய்மையில்லாத முற்றத்தில் சிந்திடும் அமிழ்தம்போல் வீணாகிவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(721,'721','வகையறிந்து வல்லவை வாய்சோரார் சொல்லின்<br />தொகையறிந்த தூய்மை யவர்','சொற்களை அளவறிந்து உரைத்திடும் தூயவர்கள் அவையிலிருப்போரின் வகையறியும் ஆற்றல் உடையவராயிருப்பின் பிழை நேருமாறு பேச மாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(722,'722','கற்றாருள் கற்றார் எனப்படுவர் கற்றார்முன்<br />கற்ற செலச்சொல்லு வார்','கற்றவரின் முன் தாம் கற்றவற்றை அவருடைய மனத்தில் பதியுமாறு சொல்ல வல்லவர், கற்றவர் எல்லாரினும் மேலானவராக மதித்துச் சொல்லப்படுவார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(723,'723','பகையகத்துச் சாவார் எளியர் அரியர்<br />அவையகத் தஞ்சா தவர்','அமர்க்களத்தில் சாவுக்கும் அஞ்சாமல் போரிடுவது பலருக்கும் எளிதான செயல், அறிவுடையோர் நிறைந்த அவைக்களத்தில் அஞ்சாமல் பேசக்கூடியவர் சிலரேயாவர்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(724,'724','கற்றார்முன் கற்ற செலச்சொல்லித் தாங்கற்ற<br />மிக்காருள் மிக்க கொளல்','அறிஞர்களின் அவையில் நாம் கற்றவைகளை அவர்கள் ஏற்றுக் கொள்ளும் அளவுக்கு எடுத்துச் சொல்லி நம்மைவிட அதிகம் கற்றவரிடமிருந்து மேலும் பலவற்றை நாம் அறிந்து கொள்ள வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(725,'725','ஆற்றின் அளவறிந்து கற்க அவையஞ்சா <br />மாற்றங் கொடுத்தற் பொருட்டு','அவையில் பேசும்போழுது குறுக்கீடுகளுக்கு அஞ்சாமல் மறுமொழி சொல்வதற்கு ஏற்ற வகையில் இலக்கணமும், தருக்கமெனப்படும் அளவைத் திறமும் கற்றிருக்க வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(726,'726','வாளொடென் வன்கண்ண ரல்லார்க்கு நூலொடென்<br />நுண்ணவை அஞ்சு பவர்க்கு','கோழைகளுக்குக் கையில் வாள் இருந்தும் பயனில்லை; அவையில் பேசிட அஞ்சுவோர் பலநூல் கற்றும் பயனில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(727,'727','பகையகத்துப் பேடிகை ஒள்வாள் அவையகத்<br />தஞ்சு மவன்கற்ற நூல்','அவை நடுவில் பேசப் பயப்படுகிறவன், என்னதான் அரிய நூல்களைப் படித்திருந்தாலும் அந்த நூல்கள் அனைத்தும் போர்க்களத்தில் ஒரு பேடியின் கையில் உள்ள கூர்மையான வாளைப் போலவே பயனற்றவைகளாகி விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(728,'728','பல்லவை கற்றும் பயமிலரே நல்லவையுள்<br />நன்கு செலச்சொல்லா தார்','அறிவுடையோர் நிறைந்த அவையில், அவர்கள் மனத்தில் பதியும் அளவுக்குக் கருத்துக்களைச் சொல்ல இயலாவிடின், என்னதான் நூல்களைக் கற்றிருந்தாலும் பயன் இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(729,'729','கல்லா தவரிற் கடையென்ப கற்றறிந்தும்<br />நல்லா ரவையஞ்சு வார்','ஆன்றோர் நிறைந்த அவையில் பேசுவதற்கு அஞ்சுகின்றவர்கள், எத்தனை நூல்களைக் கற்றிருந்த போதிலும், அவர்கள் கல்லாதவர்களாவிட இழிவானவர்களாகவே கருதப்படுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(730,'730','உளரெனினும் இல்லாரொ டொப்பர் களனஞ்சிக்<br />கற்ற செலச்சொல்லா தார்','தாம் கற்றவைகளைக் கேட்போரைக் கவரும் வண்ணம் கூற இயலாமல் அவைக்கு அஞ்சுவோர், உயிரோடு இருந்தாலும்கூட இறந்தவருக்குச் சமமானவராகவே கருதப்படுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(731,'731','தள்ளா விளையுளும் தக்காரும் தாழ்விலாச்<br />செல்வருஞ் சேர்வது நாடு','செழிப்புக் குறையாத விளைபொருள்களும், சிறந்த பெருமக்களும், செல்வத்தைத் தீயவழியில் செலவிடாதவர்களும் அமையப்பெற்றதே நல்ல நாடாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(732,'732','பெரும்பொருளாற் பெட்டக்க தாகி அருங்கேட்டால்<br />ஆற்ற விளைவது நாடு','பொருள் வளம் நிறைந்ததாகவும், பிறர் போற்றத் தக்கதாகவும், கேடற்றதாகவும், நல்ல விளைச்சல் கொண்டதாகவும் அமைவதே சிறந்த நாடாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(733,'733','பொறையொருங்கு மேல்வருங்கால் தாங்கி இறைவற்<br />கிறையொருங்கு நேர்வது நாடு','புதிய சுமைகள் ஒன்றுணிரண்டு வரும் போதும் அவற்றைத் தாங்கிக் கொண்டு, அரசுக்குரிய வரி வகைகளைச் செலுத்துமளவுக்கு வளம் படைத்ததே சிறந்த நாடாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(734,'734','உறுபசியும் ஓவாப் பிணியும் செறுபகையும்<br />சேரா தியல்வது நாடு','பசியும், பிணியும், பகையுமற்ற நாடுதான் சிறந்த நாடு எனப் பாராட்டப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(735,'735','பல்குழுவும் பாழ்செய்யும் உட்பகையும் வேந்தலைக்கும்<br />கொல்குறும்பும் இல்லது நாடு','பல குழுக்களாகப் பிரிந்து பாழ்படுத்தும் உட்பகையும், அரசில் ஆதிக்கம் செலுத்தும் கொலைகாரர்களால் விளையும் பொல்லாங்கும் இல்லாததே சிறந்த நாடாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(736,'736','கேடறியாக் கெட்ட இடத்தும் வளங்குன்றா<br />நாடென்ப நாட்டின் தலை','எந்த வகையிலும் கெடுதலை அறியாமல், ஒருவேளை கெடுதல் ஏற்படினும் அதனைச் சீர் செய்யுமளவுக்கு வளங்குன்றா நிலையில் உள்ள நாடுதான், நாடுகளிலேயே தலைசிறந்ததாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(737,'737','இருபுனலும் வாய்ந்த மலையும் வருபுனலும்<br />வல்லரணும் நாட்டிற் குறுப்பு','ஆறு, கடல் எனும் இருபுனலும், வளர்ந்தோங்கி நீண்டமைந்த மலைத் தொடரும், வருபுனலாம் மழையும், வலிமைமிகு அரணும், ஒரு நாட்டின் சிறந்த உறுப்புகளாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(738,'738','பிணியின்மை செல்வம் விளைவின்பம் ஏமம்<br />அணியென்ப நாட்டிற்கிவ் வைந்து','மக்களுக்கு நோயற்ற வாழ்வு, விளைச்சல் மிகுதி, பொருளாதார வளம், இன்ப நிலை, உரிய பாதுகாப்பு ஆகிய ஐந்தும் ஒரு நாட்டுக்கு அழகு எனக் கூறப்படுபவைகளாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(739,'739','நாடென்ப நாடா வளத்தன நாடல்ல<br />நாட வளந்தரு நாடு','இடைவிடாமல் முயற்சி மேற்கொண்டு வளம் பெறும் நாடுகளைவிட, இயற்கையிலேயே எல்லா வளங்களையும் உடைய நாடுகள் சிறந்த நாடுகளாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(740,'740','ஆங்கமை வெய்தியக் கண்ணும் பயமின்றே<br />வேந்தமை வில்லாத நாடு','நல்ல அரசு அமையாத நாட்டில் எல்லாவித வளங்களும் இருந்தாலும் எந்தப் பயனும் இல்லாமற் போகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(741,'741','ஆற்று பவர்க்கும் அரண்பொருள் அஞ்சித்தற் <br />போற்று பவர்க்கும் பொருள்','பகைவர் மீது படையெடுத்துச் செல்பவர்க்கும் கோட்டை பயன்படும்; பகைவர்க்கு அஞ்சித் தம்மைப் பாதுகாத்துக் கொள்ள முனைவோர்க்கும் கோட்டை பயன்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(742,'742','மணிநீரும் மண்ணும் மலையும் அணிநிழற்<br />காடும் உடைய தரண்','ஆழமும் அகலமும் கொண்ட அகழ், பரந்த நிலம், உயர்ந்து நிற்கும் மலைத்தொடர், அடர்ந்திருக்கும் காடு ஆகியவற்றை உடையதே அரணாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(743,'743','உயர்வகலந் திண்மை அருமையிந் நான்கின்<br />அமைவரண் என்றுரைக்கும் நூல்','உயரம், அகலம், உறுதி, பகைவரால் அழிக்க இயலாத அமைப்பு ஆகிய நான்கும் அமைந்திருப்பதே அரணுக்குரிய இலக்கணமாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(744,'744','சிறுகாப்பிற் பேரிடத்த தாகி உறுபகை<br />ஊக்கம் அழிப்ப தரண்','உட்பகுதி பரந்த இடமாக அமைந்து, பாதுகாக்கப் படவேண்டிய பகுதி சிறிய இடமாக அமைந்து, கடும் பகையின் ஆற்றலை அழிக்கக் கூடியதே அரண் எனப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(745,'745','கொளற்கரிதாய்க் கொண்டகூழ்த் தாகி அகத்தார்<br />நிலைக்கெளிதாம் நீர தரண்','முற்றுகையிட்டுக் கைப்பற்ற முடியாமல், உள்ளேயிருக்கும் படையினர்க்கும் மக்களுக்���ும் வேண்டிய உணவுடன், எதிரிகளுடன் போர் புரிவதற்கு எளிதானதாக அமைக்கப்பட்டுள்ளதே அரண் ஆகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(746,'746','எல்லாப் பொருளும் உடைத்தா யிடத்துதவும்<br />நல்லா ளுடைய தரண்','போருக்குத் தேவையான எல்லாப் பொருள்களும் கொண்டதாகவும், களத்தில் குதிக்கும் வலிமை மிக்க வீரர்களை உடையதாகவும் இருப்பதே அரண் ஆகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(747,'747','முற்றியும் முற்றா தெறிந்தும் அறைப்படுத்தும்<br />பற்றற் கரிய தரண்','முற்றுகையிட்டோ, முற்கையிடாமலோ அல்லது வஞ்சனைச் சூழ்ச்சியாலோ பகைவரால் கைப்பற்றப்பட முடியாத வலிமையுடையதே அரண் எனப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(748,'748','முற்றாற்றி முற்றி யவரையும் பற்றாற்றிப்<br />பற்றியார் வெல்வ தரண்','முற்றுகையிடும் வலிமைமிக்க படையை எதிர்த்து, உள்ளேயிருந்து கொண்டே போர் செய்து வெல்வதற்கு ஏற்ற வகையில் அமைந்ததே அரண் ஆகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(749,'749','முனைமுகத்து மாற்றலர் சாய வினைமுகத்து<br />வீறெய்தி மாண்ட தரண்','போர் முனையில் பகைவரை வீழ்த்துமளவுக்கு உள்nயிருந்து கொண்டே தாக்குதல் நடத்தும் வண்ணம் தனிச்சிறப்புப் பெற்றுத் திகழ்வதே அரண் ஆகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(750,'750','எனைமாட்சித் தாகியக் கண்ணும் வினைமாட்சி<br />இல்லார்கண் இல்ல தரண்','கோட்டைக்குத் தேவையான எல்லாவித சிறப்புகளும் இருந்தாலும்கூட உள்ளிருந்து செயல்படுவோர் திறமையற்றவர்களாக இருந்தால் எந்தப் பயனும் கிடையாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(751,'751','பொருளல் லவரைப் பொருளாகச் செய்யும்<br />பொருளல்ல தில்லை பொருள்','மதிக்கத் தகாதவர்களையும் மதிக்கக்கூடிய அளவுக்கு உயர்த்திவிடுவது அவர்களிடம் குவிந்துள்ள பணத்தைத் தவிர வேறு எதுவும் இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(752,'752','இல்லாரை எல்லாரும் எள்ளுவர் செல்வரை<br />எல்லாரும் செய்வர் சிறப்பு','பொருள் உள்ளவர்களைப் புகழ்ந்து போற்றுவதும் இல்லாதவர்களை இகழ்ந்து தூற்றுவதும்தான் இந்த உலக நடப்பாக உள்ளது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(753,'753','பொருளென்னும் பொய்யா விளக்கம் இருளறுக்கும்<br />எண்ணிய தேயத்துச் சென்று','பொருள் என்னும் அணையா விளக்கு மட்டும் கையில் இருந்துவிட்டால் நினைத்த இடத்துக்குச் சென்று இருள் என்னும் துன்பத்தைத் துரத்தி விட முடிகிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(754,'754','அறனீனும் இன்பமும் ஈனும் திறனறிந்து<br />தீதின்றி வந்த பொருள்','தீய வழியை மேற்கொண்டு திரட்டப்படாத செல்வம்தான் ஒருவருக்கு அறநெறியை எடுத்துக்காட்டி, அவருக்கு இன்பத்தையும் தரும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(755,'755','அருளொடும் அன்பொடும் வாராப் பொருளாக்கம்<br />புல்லார் புரள விடல்','பெரும் செல்வமாக இருப்பினும் அது அருள் நெறியிலோ அன்பு வழியிலோ வராதபோது அதனைப் புறக்கணித்துவிட வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(756,'756','உறுபொருளும் உல்கு பொருளுந்தன் ஒன்னார்த்<br />தெறுபொருளும் வேந்தன் பொருள்','வரியும், சுங்கமும், வெற்றி கொள்ளப்பட்ட பகை நாடு செலுத்தும் கப்பமும் அரசுக்குரிய பொருளாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(757,'757','அருளென்னும் அன்பீன் குழவி பொருளென்னும்<br />செல்வச் செவிலியால் உண்டு','அன்பு என்கிற அன்னை பெற்றெடுக்கும் அருள் என்கிற குழந்தை, பொருள் என்கிற செவிலித் தாயால் வளரக் கூடியதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(758,'758','குன்றேறி யானைப்போர் கண்டற்றால் தன்கைத்தொன்<br />றுண்டாகச் செய்வான் வினை','தன் கைப்பொருளைக்கொண்டு ஒரு தொழில் செய்வது என்பது யானைகள் ஒன்றோடொன்று போரிடும் போது இடையில் சிக்கிக் கொள்ளாமல் அந்தப் போரை ஒரு குன்றின் மீது நின்று காண்பதைப் போன்று இலகுவானது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(759,'759','செய்க பொருளைச் செறுநர் செருக்கறுக்கும்<br />எஃதனிற் கூரிய தில்','பகைவரின் செருக்கை அழிக்கும் தகுதியான கருவி பொருளைத் தவிர வேறொன்றும் இல்லாததால் அதனைச் சேமிக்க வேண்டியுள்ளது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(760,'760','ஒண்பொருள் காழ்ப்ப இயற்றியார்க் கெண்பொருள்<br />ஏனை இரண்டும் ஒருங்கு','அறம் பொருள் இன்பம் எனும் மூன்றினுள் பொருந்தும் வழியில் பொருளை மிகுதியாக ஈட்டியவர்களுக்கு ஏனைய இரண்டும் ஒன்றாகவே எளிதில் வந்து சேரும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(761,'761','உறுப்பமைந் தூறஞ்சா வெல்படை வேந்தன்<br />வெறுக்கையுள் எல்லாம் தலை','எல்லா வகைகளும் நிறைந்ததாகவும், இடையூறுகளுக்கு அஞ்சாமல் போரிடக்கூடியதாகவும் உள்ள படை ஓர் அரசின் மிகச்சிறந்த செல்வமாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(762,'762','உலைவிடத் தூறஞ்சா வன்கண் தொலைவிடத்துத்<br />தொல்படைக் கல்லால் அரிது','போரில் சேதமுற்று வலிமை குன்றியபோதும், எவ்வித இடையூறுகளுக்கும் அஞ்சாத நெஞ்சுறுதி, பழம்பெருமை கொண்ட படைக்கு அல்லாமல் வேறு எந்தப் படைக்கும் இருக்க முடியாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(763,'763','ஒலித்தக்கால் என்னாம் உவரி எலிப்பகை <br />நாகம் உயிர்ப்பக் கெடும்','எலிகள் கூடி கடல்போல் முழங்கிப், பகையைக் கக்கினாலும், நாகத்தின் மூச்சொலிக்கு முன்னால் நிற்க முடியுமா? அதுபோலத்தான் வீரன் வெகுண்டு எழுந்தால் வீணர்கள் வீழந்துபடுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(764,'764','அழிவின் றறைபோகா தாகி வழிவந்த<br />வன்க ணதுவே படை','எந்த நிலையிலும் அழியாததும், சூழ்ச்சிக்கு இரையாகததும், பரம்பரையாகவே பயமற்ற உறுதி உடையதும்தான் உண்மையான படை எனப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(765,'765','கூற்றுடன்று மேல்வரினும் கூடி எதிர்நிற்கும்<br />ஆற்ற லதுவே படை','உயிரைப் பறிக்கும் சாவு எதிர்கொண்டு வந்தாலும் அஞ்சாமல் ஒன்றுபட்டு எதிர்த்து நிற்கும் ஆற்றல் உடையதற்கே படை என்ற பெயர் பொருந்தும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(766,'766','மறமானம் மாண்ட வழிச்செலவு தேற்றம்<br />எனநான்கே ஏமம் படைக்கு','வீரம், மான உணர்வு, முன்னோர் சென்ற வழி நடத்தல், தலைவனின் நம்பிக்கையைப் பெறுதல் ஆகிய நான்கும் படையைப் பாதுகாக்கும் பண்புகளாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(767,'767','தார்தாங்கிச் செல்வது தானை தலைவந்த<br />போர்தாங்கும் தன்மை அறிந்து','களத்தில், முதலில் எதிர்கொள்ளும் போரைத் தாங்கித் தகர்க்கும் ஆற்றலை அறிந்திருப்பின், அதுவே வெற்றி மாலை தாங்கிச் செல்லக்கூடிய சிறந்த படையாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(768,'768','அடல்தகையும் ஆற்றலும் இல்லெனினும் தானை<br />படைத்தகையால் பாடு பெறும்','போர் புரியும் வீரம், எதிர்த்து நிற்கும் வல்லமை ஆகிய இரண்டையும் விட ஒரு படையின் அணிவகுப்புத் தோற்றம் சிறப்புடையதாக அமைய வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(769,'769','சிறுமையும் செல்லாத் துனியும் வறுமையும்<br />இல்லாயின் வெல்லும் படை','சிறுத்துவிடாமலும், தலைவனை வெறுத்து விடாமலும், பயன்படாத நிலை இல்லாமலும் உள்ள படைதான் வெற்றி பெற முடியும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(770,'770','நிலைமக்கள் சால உடைத்தெனினும் தானை<br />தலைமக்கள் இல்வழி இல்','உறுதிவாய்ந்த வீரர்களை அதிகம் உடையதாக இருந்தாலும் தலைமை தாங்கும் தலைவர்கள் இல்லாவிட்டால் அந்தப் படை நிலைத்து நிற்க முடியாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(771,'771','என்னைமுன் நில்லன்மின் தெவ்விர் பலரென்னை<br />முன்னின்று கல்நின் றவர்','போர்களத்து வீரன் ஒருவன், ``பகைவயர்களே என் தலைவனை எதிர்த்து நிற்காதீர்; அவனை எதிர்த்து நடுகல்லாய்ப் போனவர்கள் பலர் என முழங்குகிறான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(772,'772','கான முயலெய்த அம்பினில் யானை<br />பிழைத்தவேல் ஏந்தல் இனிது','வலிவு மிகுந்த யானைக்குக் குறிவைத்து, அந்தக் குறிதப்பினாலும்கூட அது, வலிவற்ற முயலுக்குக் குறிவைத்து அதனை வீழ்த்துவதைக் காட்டிலும் சிறப்புடையது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(773,'773','பேராண்மை என்ப தறுகனொன் றுற்றக்கால்<br />ஊராண்மை மற்றதன் எஃகு','பகைவர்க்கு அஞ்சாத வீரம் பெரும் ஆண்மை என்று போற்றப்படும் அந்தப் பகைவர்க்கு ஒரு துன்பம் வரும்போது அதைத் தீர்க்க உதவிடுவது ஆண்மையின் உச்சம் எனப் புகழப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(774,'774','கைவேல் களிற்றொடு போக்கி வருபவன்<br />மெய்வேல் பறியா நகும்','கையிலிருந்த வேலினை யானையின் மீது வீசி விட்டதால் களத்தில் போரினைத் தொடர வேறு வேல் தேடுகிற வீரன், தன் மார்பின்மீதே ஒரு வேல் பாயந்திருப்பது கண்டு மகிழ்ந்து அதனைப் பறித்துப் பகையை எதிர்த்திடுகின்றான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(775,'775','விழித்தகண் வேல்கொண் டெறிய அழித்திமைப்பின்<br />ஓட்டன்றோ வன்க ணவர்க்கு','களத்தில் பகைவர் வீசிடும் வேல் பாயும்போது விழிகளை இமைத்து விட்டால்கூட அது புறமுதுகுகாட்டி ஓடுவதற்குஒப்பாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(776,'776','விழுப்புண் படாதநாள் எல்லாம் வழுக்கினுள்<br />வைக்குந்தன் நாளை எடுத்து','ஒரு வீரன், தான் வாழ்ந்த நாட்களைக் கணக்குப் பார்த்து அந்த நாட்களில் தன்னுடலில் விழுப்புண்படாத நாட்களையெல்லாம் வீணான நாட்கள் என்று வெறுத்து ஒதுக்குவான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(777,'777','சுழலும் இசைவேண்டி வேண்டா உயிரார்<br />கழல்யாப்புக் காரிகை நீர்த்து','சூழ்ந்து பரவிடும் புகழை மட்டுமே விரும்பி உயிரைப் பற்றிக் கவலைப்படாத வீரர்களின் காலில் கட்டப்படும் வீரக்கழல் தனிப் பெருமை உடையதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(778,'778','உறினுயிர் அஞ்சா மறவர் இறைவன்<br />செறினுஞ்சீர் குன்றல் இலர்','தலைவன் சினந்தாலும் சிறப்புக் குறையாமல் கடமை ஆற்றுபவர்கள்தான், போர்களத்தில் உயிரைப் பற்றிக் கலங்காத வீர மறவர்கள் எனப் போற்றப்படுவர்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(779,'779','இழைத்த திகவாமைச் சாவாரை யாரே<br />பிழைத்த தொறுக்கிற் பவர்','சபதம் செய்தவாறு களத்தில் சாவதற்குத் துணிந்த வீரனை யாராவது இழித்துப் பேச முடியுமா? முடியாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(780,'780','புரந்தார்கண் நீர்மல்கச் சாகிற்பின் சாக்கா<br />டிரந்துகோட் டக்க துடைத்து','தன்னைக் காத்த தலைவனுடைய கண்களில் நீர் பெருகுமாறு வீரமரணம் அடைந்தால், அத்தகைய மரணத்தை யாசித்தாவது பெற்றுக் கொள்வதில் பெருமை உண்டு',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(781,'781','செயற்கரிய யாவுள நட்பின் அதுபோல்<br />வினைக்கரிய யாவுள காப்பு','நட்புக் கொள்வது போன்ற அரிய செயல் இல்லை அதுபோல் பாதுகாப்புக்கு ஏற்ற செயலும் வேறொன்றில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(782,'782','நிறைநீர நீரவர் கேண்மை பிறைமதிப்<br />பின்னீர பேதையார் நட்பு','அறிவுள்ளவர்களுடன் கொள்ளும் நட்பு பிறைநிலவாகத் தொடங்கி முழுநிலவாக வளரும், அறிவில்லாதவர்களுடன் கொள்ளும் நட்போ முழுமதிபோல் முளைத்துப் பின்னர் தேய்பிறையாகக் குறைந்து மறைந்து போகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(783,'783','நவில்தொறும் நூனயம் போலும் பயில்தொறும்<br />பண்புடை யாளர் தொடர்பு','படிக்கப் படிக்க இன்பம் தரும் நூலின் சிறப்பைப் போல் பழகப் பழக இன்பம் தரக்கூடியது பண்புடையாளர்களின் நட்பு',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(784,'784','நகுதற் பொருட்டன்று நட்டல் மிகுதிக்கண்<br />மேற்சென் றிடித்தற் பொருட்டு','நட்பு என்பது சிரித்து மகிழ்வதற்காக அல்ல; நண்பர்கள் நல்வழி தவறிச் செல்லும்பொழுது இடித்துரைத்துத் திருத்துவதற்காகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(785,'785','புணர்ச்சி பழகுதல் வேண்டா உணர்ச்சிதான்<br />நட்பாங் கிழமை தரும்','இருவருக்கிடையே நட்புரிமை முகிழ்ப்பதற்கு ஏற்கனவே தொடர்பும் பழக்கமும் வேண்டுமென்பதில்லை இருவரின் ஒத்த மன உணர்வே போதுமானது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(786,'786','முகநக நட்பது நட்பன்று நெஞ்சத்<br />தகநக நட்பது நட்பு','இன்முகம் காட்டுவது மட்டும் நட்புக்கு அடையாளமல்ல; இதயமார நேசிப்பதே உண்மையான நட்பாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(787,'787','அழிவி னவைநீக்கி ஆறுய்த் தழிவின்கண்<br />அல்லல் உழப்பதாம் நட்பு','நண்பனைத் தீயவழி சென்று கெட்டுவிடாமல் தடுத்து, அவனை நல்வழியில் நடக்கச் செய்து, அவனுக்குத் தீங்கு வருங்காலத்தில் அந்தத் தீங்கின் துன்பத்தைப் பகிர்ந்து கொள்வதே உண்மையான நட்பாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(788,'788','உடுக்கை இழந்தவன் கைபோல ஆங்கே<br />இடுக்கண் களைவதாம் நட்பு','அணிந்திருக்கும் உடை உடலைவிட்டு நழுவும்போது எப்படிக் கைகள் உடனடியாகச் செயல்பட்டு அதனைச் சரிசெய்ய உதவுகின்றனவோ அதைப்போல நண்பனுக்கு வரும் துன்பத்தைப் போக்கத் துடித்து செல்வதே நப்புக்கு இலக்கணமாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(789,'789','நட்பிற்கு வீற்றிருக்கை யாதெனிற் கொட்பின்றி<br />ஒல்லும்வாய் ஊன்றும் நிலை','மனவேறுபாடு கொள்ளாமல் தன்னால் இயலும் வழிகளிலெல்லாம் துணைநின்று நண்பனைத் தாங்குவது தான் நட்பின் சிறப்பாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(790,'790','இனையர் இவரெமக் கின்னம்யாம் என்று<br />புனையினும் புல்லென்னும் நட்பு','நண்பர்கள் ஒருவருக்கொருவர் ``இவர் எமக்கு இத்தன்மையுடைவர்; யாம் இவருக்கு இத்தன்மையுடையோம் என்று செயற்கையாகப் புகழ்ந்து பேசினாலும் அந்த நட்பின் பெருமை குன்றிவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(791,'791','நாடாது நட்டலிற் கேடில்லை நட்டபின்<br />வீடில்லை நட்பாள் பவர்க்கு','ஆராய்ந்து பாராமல் கொண்டிடும் தீய நட்பு, அந்த நட்பிலிருந்து விடுபட முடியாத அளவுக்குக் கேடுகளை உண்டாக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(792,'792','ஆய்ந்தாய்ந்து கொள்ளாதான் கேண்மை கடைமுறை<br />தான்சாம் துயரம் தரும்','திரும்பத் திரும்ப ஆராய்ந்து பார்க்காமல் ஏற்படுத்திக் கொள்கிற நட்பு, கடைசியாக ஒருவர் சாவுக்குக் காரணமாகிற அளவுக்குத் துயரத்தை உண்டாக்கி விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(793,'793','குணனும் குடிமையும் குற்றமும் குன்றா<br />இனனும் அறிந்தியாக்க நட்பு','குணமென்ன? குடிப்பிறப்பு எத்தகையது? குற்றங்கள் யாவை? குறையாத இயல்புகள் எவை? என்று அனைத்தையும் அறிந்தே ஒருவருடன் நட்புக் கொள்ள வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(794,'794','குடிப்பிறந்து தன்கண் பழிநாணு வானைக்<br />கொடுத்தும் கொளல்வேண்டும் நட்பு','பழிவந்து சேரக் கூடாது என்ற அச்ச உணர்வுடன் நடக்கும் பண்பார்ந்த குடியில் பிறந்தவருடைய நட்பை எந்த வகையிலாவது பெற்றிருப்பது பெரும் சிறப்புக்குரியதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(795,'795','அழச்சொல்லி அல்ல திடித்து வழக்கறிய<br />வல்லார்நட் பாய்ந்து கொளல்','தவறு செய்கின்றவர் கண்ணீர் விடுமளவுக்குக் கண்டித்து, அறிவுரை வழங்கக் கூடிய ஆற்றலுடையவரின் நட்பையே தெளிவான நட்பாக எண்ண வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(796,'796','கேட்டினும் உண்டோர் உறுதி கிளைஞரை<br />நீட்டி அளப்பதோர் கோல்','தீமை வந்தால் அதிலும் ஒரு நன்மை உண்டு அந்தத் தீமைதான் நண்பர்கள் எப்படிப்பட்டவர்கள் என்று அளந்து காட்டும் கருவியாகிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(797,'797','ஊதியம் என்ப தொருவற்குப் பேதையார்<br />கேண்மை ஒரீஇ விடல்','ஒருவருக்குக் கிடைத்த நற்பயன் என்பது அவர் அறிவில்லாத ஒருவருடன் கொண்டிருந்த நட்பைத் துறந்து விடுவதேயாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(798,'798','உள்ளற்க உள்ளஞ் சிறுகுவ கொள்ளற்க<br />அல்லற்கண் ஆற்றறுப்பார் நட்பு','ஊக்கத்தைச் சிதைக்கக்கூடிய செயல்களையும், துன்பம் வரும்போது விலகிவிடக்கூடிய நண்பர்களையும் நினைத்துப் பார்ககாமலே இருந்து விட வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(799,'799','கெடுங்காலைக் கைவிடுவார் கேண்மை அடுங்காலை<br />உள்ளினும் உள்ளஞ் சுடும்','ஒருவர் கொலைக்கு ஆளாகும் போது கூட, தனக்குக் கேடு வந்த நேரம் கைவிட்டு ஒதுங்கி ஓடிவிட்ட நண்பர்களை நினைத்து விட்டால் அந்த நினைப்பு அவரது நெஞ்சத்தைச் சுட்டுப் பொசுக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(800,'800','மருவுக மாசற்றார் கேண்மையொன் றீத்தும<br />ஒருவுக ஒப்பிலார் நட்பு','மனத்தில் மாசு இல்லாதவர்களையே நண்பர்களாகப் பெற வேண்டும் மாசு உள்ளவர்களின் நட்பை, விலை கொடுத்தாவது விலக்கிட வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(801,'801','பழைமை எனப்படுவ தியாதெனின் யாதும்<br />கிழமையைக் கீழ்ந்திடா நட்பு','பழைமை பாராட்டுவது என்னவென்றால், பழகிய நண்பர்கள், தங்களின் உறவை அழியாமல் பாதுகாப்பதுதான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(802,'802','நட்பிற் குறுப்புக் கெழுதகைமை மற்றதற்<br />குப்பாதல் சான்றோர் கடன்','பழைமையான நண்பர்களின் உரிமையைப் பாராட்டுகிற சான்றோர்க்குரிய கடமைதான் உண்மையான நட்புக்கு அடையாளமாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(803,'803','பழகிய நட்பெவன் செய்யுங் கெழுதகைமை<br />செய்தாங் கமையாக் கடை','பழைய நண்பர்கள் உரிமையோடு செய்த காரியங்களைத்தாமே செய்ததுபோல உடன்பட்டு இருக்காவிட்டால், அதுவரை பழகிய நட்பு பயனற்றுப்போகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(804,'804','விழைதகையான் வேண்டி இருப்பர் கெழுதகையாற்<br />கேளாது நட்டார் செயின்','பழகிய நட்பின் உரிமை காரணமாக தமது நண்பர் தம்மைக் கேளாமலே ஒரு செயல் புரிந்து விட்டாலும்கூட நல்ல நண்பராயிருப்பவர் அதனை ஏற்றுக் கொள்ளவே செய்வார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(805,'805','பேதைமை ஒன்றோ பெருங்கிழமை என்றுணர்க<br />நோதக்க நட்டார் செயின்','வருந்தக் கூடிய செயலை நண்பர்கள் செய்தால் அது அறியாமையினாலோ அல்லது உரிமையின் காரணமாகவோ செய்யப்பட்டது என்றுதான் எடுத்துக் கொள்ள வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(806,'806','எல்லைக்கண் நின்றார் துறவார் தெலைவிடத்தும்<br />தொல்லைக்கண் நின்றார் தொடர்பு','நீண்டகால நண்பர்கள் தமக்குக் கேடு தருவதாக இருந்தால்கூட நட்பின் இலக்கணம் உணர்ந்தவர்கள் அவர்களது நட்பைத் துறக்க மாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(807,'807','அழிவந்த செய்யினும் அன்பறார் அன்பின்<br />வழிவந்த கேண்மை யவர்','தம்முடன் பழகியவர்கள் தமக்கே எதிராக அழிவுதரும் காரியத்தைச் செய்தாலும்கூட அன்பின் அடிப்படையில் நட்புக் கொண்டவர் அதற்காக அந்த அன்பை விலக்கிக் கொள்ள மாட்டார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(808,'808','கேளிழுக்கங் கேளாக் கெழுதகைமை வல்லார்க்கு<br />நாளிழுக்கம் நட்டார் செயின்','நண்பர்கள் செய்யும் குற்றத்தைப் பிறர்கூறி அதனை ஏற்றுக் கொள்ளாத அளவுக்கு நம்பிக்கையான நட்புரிமை கொண்டவரிடத்திலேயே அந்த நண்பர்கள் தவறாக நடந்து கொண்டால் அவர்களுடன் நட்புக் கொண்டிருந்த நாளெல்லாம் வீணான நாளாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(809,'809','கெடாஅ வழிவந்த கேண்மையார் கேண்மை<br />விடாஅர் விழையும் உலகு','தொன்றுதொட்டு உரிமையுடன் பழகிய நட்புறவைக் கைவிடாமல் இருப்பவரை உலகம் போற்றும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(810,'810','விழையார் விழையப் படுப பழையார்கண்<br />பண்பின் தலைப்பிரியா தார்','பழமையான நண்பர்கள் தவறு செய்த போதிலும், அவர்களிடம் தமக்குள்ள அன்பை நீக்கிக் கொள்ளாதவர்களைப் பகைவரும் விரும்பிப் பாராட்டுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(811,'811','பருகுவார் போலினும் பண்பிலார் கேண்மை<br />பெருகலிற் குன்றல் இனிது','நல்ல பண்பு இல்லாதவர்கள் அன்பு வெள்ளத்தில் நம்மை மூழ்கடிப்பதுபோல் தோன்றினாலும் அவர்களது நட்பை, மேலும் வளர்த்துக் கொள்ளாமல் குறைத்துக் கொள்வதே நல்லது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(812,'812','உறினட் ��றினொரூஉம் ஒப்பிலார் கேண்மை<br />பெறினும் இழப்பினும் என்','தமக்குப் பயன்கிடைக்கும்போது நண்பராக இருந்துவிட்டு பயனில்லாதபோது பிரிந்து விடுகின்றவர்களின் நட்பு, இருந்தால் என்ன? இழந்தால்தான் என்ன?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(813,'813','உறுவது சீர்தூக்கும் நட்பும் பெறுவது<br />கொள்வாரும் கள்வரும் நேர்','பயனை எண்ணிப்பார்த்து அதற்காகவே நட்புக் கொள்பவரும், விலைமகளிரும், கள்வரும் ஆகிய இந்த மூவரும் ஒரே மாதிரியானவர்களே ஆவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(814,'814','அமரகத் தாற்றறுக்கும் கல்லாமா அன்னார்<br />தமரின் தனிமை தலை','போர்க்களத்தில் கீழே தள்ளி விட்டுத் தப்பித்து ஓடிப்போகும் குதிரையைப் போன்றவர்களின் நட்பைப் பெறுவதைக் காட்டிலும் தனித்து இருப்பது எவ்வளவோ சிறப்புடையதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(815,'815','செய்தேமஞ் சாராச் சிறியவர் புன்கேண்மை<br />எய்தலின் எய்தாமை நன்று','கீழ்மக்களின் நட்பு, பாதுகாப்பாக அமையாத தீயதன்மை கொண்டது என்பதனால், அவர்களுடன் நட்பு ஏற்படுவதைவிட, ஏற்படாமல் இருப்பதே நலம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(816,'816','பேதை பெருங்கெழீஇ நட்பின் அறிவுடையார்<br />ஏதின்மை கோடி உறும்','அறிவில்லாதவனிடம் நெருங்கிய நட்புக் கொண்டிருப்பதை விட, அறிவுடைய ஒருவரிடம் பகை கொண்டிருப்பது கோடி மடங்கு மேலானதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(817,'817','நகைவகைய ராகிய நட்பின் பகைவரால்<br />பத்தடுத்த கோடி உறும்','சிரித்துப் பேசி நடிப்பவர்களின் நட்பைக் காட்டிலும் பகைவர்களால் ஏற்படும் துன்பம் பத்துக்கோடி மடங்கு நன்மையானது என்று கருதப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(818,'818','ஒல்லும் கருமம் உடற்று பவர்கேண்மை<br />சொல்லாடார் சோர விடல்','நிறைவேற்றக் கூடிய செயலை, நிறைவேற்ற முடியாமல் கெடுப்பவரின் உறவை, அவருக்குத் தெரியாமலேயே மெல்ல மெல்ல விட்டு விட வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(819,'819','கனவினும் இன்னாது மன்னோ வினைவேறு<br />சொல்வேறு பட்டார் தொடர்பு','சொல்லுக்கும் செயலுக்கும் தொடர்பில்லாதவரின் நட்பு கனவிலேகூடத் துன்பத்தைத்தான் கொடுக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(820,'820','எனைத்துங் குறுகுதல் ஓம்பல் மனைக்கெழீஇ<br />மன்றிற் பழிப்பார் தொடர்பு','தனியாகச் சிந்திக்கும் போத இனிமையாகப் பழகிவிட்டுப் பொது மன்றத்தில் பழித்துப் பேசுபவரின் நட்பு தம்மை அணுகாமல் விலக்கிக் கொள்ளப்படவேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(821,'821','சீரிடங் காணின் எறிதற்குப் பட்டடை<br />நேரா நிரந்தவர் நட்பு','மனதார இல்லாமல் வெளியுலகிற்கு நண்பரைப்போல் நடிப்பவரின் நட்பானது, ஒரு கேடு செய்வதற்குச் சரியான சந்தர்ப்பம் கிடைக்கும்போது இரும்பைத் துண்டாக்கத் தாங்கு பலகை போல் இருக்கும் பட்டடைக் கல்லுக்கு ஒப்பாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(822,'822','இனம்போன் றினமல்லார் கேண்மை மகளிர்<br />மனம்போல வேறு படும்','உற்றாராக இல்லாமல் உற்றார்போல நடிப்பவர்களின் நட்பு, மகளிருக்குரிய நற்பண்பு இல்லாமல் அப்பண்பு உள்ளவர் போல நடிக்கும் விலை மகளிரின் மனம்போல உள்ளொன்றும் புறமொன்றுமாக இருக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(823,'823','பலநல்ல கற்றக் கடைத்து மனநல்லர்<br />ஆகுதல் மாணார்க் கரிது','அரிய நூல்கள் பலவற்றைக் கற்றிருந்த போதிலும், பகையுணர்வு படைத்தோர் மனம் திருந்தி நடப்பதென்பது அரிதான காரியமாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(824,'824','முகத்தின் இனிய நகாஅ அகத்தின்னா <br />வஞ்சரை அஞ்சப் படும்','சிரித்துப் பேசி நம்மைச் சீரழிக்க நினைக்கும் வஞ்சகரின் நட்புக்கு அஞ்சி ஒதுங்கிட வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(825,'825','மனத்தின் அமையா தவரை எனைத்தொன்றும்<br />சொல்லினால் தேறற்பாற் றன்று','மனம் வேறு செயல் வேறாக இருப்பவர்களின் வார்த்தைகளை நம���பி எந்தவொரு தெளிவான முடிவையும் எடுக்க இயலாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(826,'826','நட்டார்போல் நல்லவை சொல்லினும் ஒட்டார்சொல்<br />ஒல்லை உணரப் படும்','பகைவர், நண்பரைப்போல இனிமையாகப் பேசினாலும் அந்தச் சொற்களில் கிடக்கும் சிறுமைக் குணம் வெளிப்பட்டே தீரும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(827,'827','சொல்வணக்கம் ஒன்னார்கண் கொள்ளற்க வில்வணக்கம்<br />தீங்கு குறித்தமை யான்','பகைவரிடம் காணப்படும் சொல் வணக்கம் என்பது வில்லின் வணக்கத்தைப் போல் தீங்கு விளைவிக்கக் கூடியது என்பதால், அதனை நம்பக் கூடாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(828,'828','தொழுதகை யுள்ளும் படையொடுங்கும் ஒன்னார்<br />அழுதகண் ணீரும் அனைத்து','பகைவர்கள் வணங்குகின்ற போதுகூட அவர்களின் கைக்குள்ளே கொலைக்கருவி மறைந்திருப்பது போலவே, அவர்கள், கண்ணீர் கொட்டி அழுதிடும் போதும் சதிச்செயலே அவர்களின் நெஞ்சில் நிறைந்திருக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(829,'829','மிகச்செய்து தம்மெள்ளு வாரை நகச்செய்து <br />நட்பினுட் சாப்புல்லற் பாற்று','வெளித்தோற்றத்திற்கு நண்பரைப்போல் நகைமுகம் காட்டி மகிழ்ந்து, உள்ளுக்குள் பகையுணர்வுடன் இகழ்பவரின் நட்பை, நலிவடையுமாறு செய்திட நாமும் அதே முறையைக் கடைப்பிடிக்க வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(830,'830','பகைநட்பாங் காலம் வருங்கால் முகநட்<br />டகநட் பொரீஇ விடல்','பகைவருடன் பழகிடும் காலம் வருமேயானால் அகத்தளவில் இல்லாமல் முகத்தளவில் மட்டும் நட்புச் செய்து பின்னர் நட்பையும் விட்டுவிட வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(831,'831','பேதைமை என்பதொன் றியாதெனின் ஏதங்கொண்<br />டூதியம் போக விடல்','கேடு விளைவிப்பது எது? நன்மை தருவது எது? என்று தெளிவடையாமல் நன்மையை விடுத்துத் தீமையை நாடுவதே பேதைமை என்பதற்கான எடுத்துக்காட்டாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(832,'832','பேதைமையுள் எல்லாம் பேதைமை காதன்மை<br />கையல்ல தன்கண் செயல்','தன்னால் இயலாத செயல்களை விரும்பி, அவற்றில் தலையிடுவது, என்பது பேதைமைகளில் எல்லாம் மிகப்பெரிய பேதைமையாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(833,'833','நாணாமை நாடாமை நாரின்மை யாதொன்றும்<br />பேணாமை பேதை தொழில்','வெட்கப்பட வேண்டியதற்கு வெட்கப்படாமலும், தேடவேண்டியதைத் தேடிப் பெறாமலும், அன்புகாட்ட வேண்டியவரிடத்தில் அன்பு காட்டாமலும், பேணிப் பாதுக்காக்கப்பட வேண்டியவற்றைப் பாதுகாக்காமலும் இருப்பது பேதைகளின் இயல்பாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(834,'834','ஓதி உணர்ந்தும் பிறர்க்குரைத்தும் தானடங்காப்<br />பேதையிற் பேதையார் இல்','படித்தும், படித்ததை உணர்ந்தும், உணர்ந்ததைப் பலருக்கு உணர்த்திடவும் கூடியவர்கள், தாங்கள் மட்டும் அவ்வாறு நடக்காமலிருந்தால் அவர்களைவிடப் பேதைகள் யாரும் இருக்க முடியாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(835,'835','ஒருமைச் செயலாற்றும் பேதை எழுமையும்<br />தான்புக் கழுந்தும் அளறு','தன்னிச்சையாகச் செயல்படும் பேதை, எக்காலத்திலும் துன்பமெனும் சகதியில் அழுந்திக் கிடக்க நேரிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(836,'836','பொய்படும் ஒள்றோ புனைபூணும் கையறியாப்<br />பேதை வினைமேற் கொளின்','நேர்மை வழி அறியாத மூடர், மேற்கொண்ட செயலைத் தொடர முடியாமல், அதனால் அச்செயலும் கெட்டுத் தம்மையும் தண்டித்துக் கொள்வர்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(837,'837','ஏதிலார் ஆரத் தமர்பசிப்பர் பேதை<br />பெருஞ்செல்வம் உற்றக் கடை','அறிவில்லாப் பேதைகளிடம் குவியும் செல்வம், அயலார் சுருட்டிக் கொள்ளப் பயன்படுமேயல்லாமல் பசித்திருக்கும் பாசமுள்ள சுற்றத்தாருக்குப் பயன்படாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(838,'838','மையல் ஒருவன் களித்தற்றாற் பேதைதன்<br />கையொன் றுடைமை பெறின்','நல்லது கெட்டது தெரியாதவன் பேதை; அந்தப் பேதையின் கையில் ஒரு ப��ருளும் கிடைத்துவிட்டால் பித்துப் பிடித்தவர்கள் கள்ளையும் குடித்துவிட்ட கதையாக ஆகிவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(839,'839','பெரிதினிது பேதையார் கேண்மை பிரிவின்கண்<br />பீழை தருவதொன் றில்','அறிவற்ற பேதைகளுடன் கொள்ளும் நட்பு மிகவும் இனிமையானது; ஏனென்றால் அவர்களிடமிருந்து புரியும்போது எந்தத் துன்பமும் ஏற்படுவதில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(840,'840','கழாஅக்கால் பள்ளியுள் வைத்தற்றாற் சான்றோர்<br />குழாஅத்துப் பேதை புகல்','அறிஞர்கள் கூடியுள்ள மன்றத்தில் ஒரு முட்டாள் நுழைவது என்பது, அசுத்தத்தை மிதித்த காலைக் கழுவாமலே படுக்கையில் வைப்பதைப் போன்றது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(841,'841','அறிவின்மை அன்மையுள் இன்மை பிறிதின்மை<br />இன்மையா வையா துலகு','அறிவுப் பஞ்சம்தான் மிகக் கொடுமையான பஞ்சமாகும் மற்ற பஞ்சங்களைக்கூட உலகம் அவ்வளவாகப் பொருட்படுத்தாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(842,'842','அறிவிலான் நெஞ்சுவந் தீதல் பிறிதியாதும்<br />இல்லை பெறுவான் தவம்','அறிவில்லாத ஒருவன் வள்ளலைப்போல ஒரு பொருளை மகிழ்ச்சியுடன் வழங்குவதற்குக் காரணம் வேறொன்றுமில்லை; அது அப்பொருளைப் பெறுகிறவன் பெற்றபேறு என்றுதான் கருதவேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(843,'843','அறிவிலார் தாந்தம்மைப் பீழிக்கும் பீழை<br />செறுவார்க்கும் செய்தல் அரிது','எதிரிகளால்கூட வழங்க முடியாத வேதனையை, அறிவில்லாதவர்கள் தங்களுக்குத் தாங்களே வழங்கிக் கொள்வார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(844,'844','வெண்மை எனப்படுவ தியாதெனின் ஒண்மை<br />உடையம்யாம் என்னும் செருக்கு','ஒருவன் தன்னைத்தானே அறிவுடையவனாக மதித்துக் கொள்ளும் ஆணவத்திற்குப் பெயர்தான் அறியாமை எனப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(845,'845','கல்லாத மேற்கொண் டொழுகல் கசடற<br />வல்லதூஉம் ஐயம் தரும்','அறிந்து கொள்ளாதவைகளையும் அறிந்தவர் போல ஒருவர் போலித்தனமாகக் காட்டிக் கொள்ளும் போது, அவர் ஏற்கனவே எந்தத் துறையில் திறமையுடையவராக இருக்கிறாரோ அதைப் பற்றிய சந்தேகமும் மற்றவர்களுக்கு உருவாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(846,'846','அற்ற மறைத்தலோ புல்லறிவு தம்வயின்<br />குற்றம் மறையா வழி','நமது குற்றத்தை உணர்ந்து அதை நீக்காமல் உடலை மறைக்க மட்டும் உடை அணிவது மடமையாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(847,'847','அருமறை சோரும் அறிவிலான் செய்யும்<br />பெருமிறை தானே தனக்கு','நல்வழிக்கான அறிவுரைகளைப் போற்றி அவ்வழி நடக்காத அறிவிலிகள், தமக்குத் தாமே பெருந்துன்பத்தைத் தேடிக் கொள்வார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(848,'848','ஏவவுஞ் செய்கலான் தான்தேறான் அவ்வுயிர்<br />போஒம் அளவுமோர் நோய்','சொந்தப் புத்தியும் இல்லாமல் சொல் புத்தியும் கேட்காதவருக்கு அதுவே அவர் வாழ்நாள் முழுதும் அவரை விட்டு நீங்காத நோயாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(849,'849','காணாதான் காட்டுவான் தான்காணான் காணாதான்<br />கண்டானாம் தான்கண்ட வாறு','அறிவற்ற ஒருவன், தான் அறிந்ததை மட்டும் வைத்துக் கொண்டு, தன்னை அறிவுடையவனாகக் காட்டிக் கொள்வான் அவனை உண்மையிலேயே அறிவுடையவனாக்க முயற்சி செய்பவன் தன்னையே அறிவற்ற நிலைக்கு ஆளாக்கிக் கொள்வான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(850,'850','உலகத்தார் உண்டென்ப தில்லென்பான் வையத்<br />தலகையா வைக்கப் படும்','ஆதாரங்களைக் காட்டி இதுதான் உண்மை என்று தெளிவாகக் கூறப்படுகிற ஒன்றை, வேண்டுமென்றே இல்லை என மறுத்துரைப்பவரைப் ``பேய்களின் பட்டியலின்தான் வைக்க வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(851,'851','இகலென்ப எல்லா உயிர்க்கும் பகலென்னும்<br />பண்பின்மை பாரிக்கும் நோய்','மனமாறுபாடு காரணமாக ஏற்படுகிற பகையுணர்வு மக்களை ஒன்று சேர்ந்து வாழ முடியாமல் செய்கிற தீய பண்பாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(852,'852','பகல்கரு���ிப் பற்றா செயினும் இகல்கருதி<br />இன்னாசெய் யாமை தலை','வேற்றுமை கருதி வெறுப்பான செயல்களில் ஒருவன் ஈடுபடுகிறான் என்றாலும் அவனோடு கொண்டுள்ள மாறுபாடு காரணமாக அவனுக்குத் துன்பம் தரும் எதனையும் செய்யாதிருப்பதே சிறந்த பண்பாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(853,'853','இகலென்னும் எவ்வநோய் நீக்கின் தவலில்லாத்<br />தாவில் விளக்கம் தரும்','மனமாறுபாடு என்றும் நோயை யார் தங்கள் மனத்தை விட்டு அகற்றிவிடுகிறார்களோ அவர்களுக்கு மாசற்ற நீடித்த புகழ் உண்டாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(854,'854','இன்பத்துள் இன்பம் பயக்கும் இகலென்னும்<br />துன்பத்துள் துன்பங் கெடின்','துன்பத்திலேயே பெருந்துன்பம் பகையுணர்வுதான் அந்த உணர்வை ஒருவன் அகற்றி விடுவானேயானால், அது இன்பத்திலேயே பெரும் இன்பமாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(855,'855','இகலெதிர் சாய்ந்தொழுக வல்லாரை யாரே <br />மிகலூக்கும் தன்மை யவர்','மனத்தில் மாறுபாடான எண்ணம் உருவானால் அதற்கு இடம் தராமல் நடக்கக்கூடிய ஆற்றலுடையவர்களை வெல்லக்கூடியவர்கள் யாருமில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(856,'856','இகலின் மிகலினி தென்பவன் வாழ்க்கை<br />தவலும் கெடலும் நணித்து','மாறுபாடு கொண்டு எதிர்ப்பதால் வெற்றி பெறுவது எளிது என எண்ணிச் செயல்படுபவரின் வாழ்க்கை விரைவில் தடம்புரண்டு கெட்டொழியும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(857,'857','மிகல்மேவல் மெய்ப்பொருள் காணார் இகல்மேவல்<br />இன்னா அறிவி னவர்','பகை உணர்வு கொள்ளும் தீய அறிவுடையவர்கள் வெற்றிக்கு வழிகாட்டும் உண்மைப் பொருளை அறியமாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(858,'858','இகலிற் கெதிர்சாய்தல் ஆக்கம் அதனை<br />மிகலூக்கின் ஊக்குமாங் கேடு','மனத்தில் தோன்றும் மாறுபாட்டை எதிர்கொண்டு நீக்கிக் கொண்டால் நன்மையும், அதற்கு மாறாக அதனை மிகுதியாக ஊக்கப்படுத்தி வளர்த்துக் கொண்டால் தீமையும் விளையும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(859,'859','இகல்காணான் ஆக்கம் வருங்கால் அதனை<br />மிகல்காணும் கேடு தரற்கு','ஒருவன் தனக்கு நன்மை வரும்போது மாறுபாட்டை நினைக்காமலே இருப்பான் ஆனால் தனக்குத் தானே கேடு தேடிக் கொள்வதென்றால் அந்த மாறுபாட்டைப் பெரிதுபடுத்திக் கொள்வான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(860,'860','இகலானாம் இன்னாத எல்லாம் நகலானாம்<br />நன்னயம் என்னும் செருக்கு','மனமாறுபாடு கொண்டு பகையுணர்வைக் காட்டுவோரைத் துன்பங்கள் தொடரும் நட்புணர்வோடு செயல்படுவோர்க்குப் பெருமகிழ்ச்சி எனும் நற்பயன் விளையும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(861,'861','வலியார்க்கு மாறேற்றல் ஓம்புக ஓம்பா<br />மெலியார்மேல் மேக பகை','மெலியோரை விடுத்து, வலியோரை எதிர்த்துப் போரிட விரும்புவதே பகைமாட்சி எனப் போற்றப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(862,'862','அன்பிலன் ஆன்ற துணையிலன் தான்றுவ்வான்<br />என்பரியும் ஏதிலான் துப்பு','உடனிருப்போரிடம் அன்பு இல்லாமல், வலிமையான துணையுமில்லாமல், தானும் வலிமையற்றிருக்கும்போது பகையை எப்படி வெல்ல முடியும்?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(863,'863','அஞ்சும் அறியான் அமைவிலன் ஈகலான்<br />தஞ்சம் எளியன் பகைக்கு','அச்சமும், மடமையும் உடையவனாகவும், இணைந்து வாழும் இயல்பும், இரக்க சிந்தையும் இல்லாதவனாகவும் ஒருவன் இருந்தால், அவன் பகைவரால் எளிதில் வெல்லப்படுவான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(864,'864','நீங்கான் வெகுளி நிறையிலன் எஞ்ஞான்றும்<br />யாங்கணும் யார்க்கும் எளிது','சினத்தையும் மனத்தையும் கட்டுப்படுத்த முடியாதவர்களை, எவர் வேண்டுமானாலும், எப்போது வேண்டுமானாலும், எங்கு வேண்டுமானாலும் எளிதில் தோற்கடித்து விடலாம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(865,'865','வழிநோக்கான் வாய்ப்பன செய்யான் பழிநோக்கான்<br />பண்பிலன் பற்றார்க் கினிது','நல்வழி நாடாமல், பொருத்தமானதைச��� செய்யாமல், பழிக்கு அஞ்சாமல், பண்பும் இல்லாமல் ஒருவன் இருந்தால் அவன் பகைவரால் எளிதில் வெல்லப்படுவான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(866,'866','காணாச் சினத்தான் கழிபெருங் காமத்தான் <br />பேணாமை பேணப் படும்','சிந்திக்காமலே சினம் கொள்பனாகவும், பேராசைக்காரனாகவும் இருப்பவனின் பகையை ஏற்று எதிர் கொள்ளலாம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(867,'867','கொடுத்துங் கொளல்வேண்டும் மன்ற அடுத்திருந்து<br />மாணாத செய்வான் பகை','தன்னோடு இருந்துகொண்டே தனக்குப் பொருந்தாத காரியங்களைச் செய்து கொண்டிருப்பவனைப் பொருள் கொடுத்தாவது பகைவனாக்கிக் கொள்ள வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(868,'868','குணனிலனாய்க் குற்றம் பலவாயின் மாற்றார்க்<br />கினனிலனாம் ஏமாப் புடைத்து','குணக்கேடராகவும், குற்றங்கள் மலிந்தவராகவும் ஒருவர் இருந்தால், அவர் பக்கத் துணைகளை இழந்து பகைவரால் எளிதாக வீழ்த்தப்படுவார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(869,'869','செறுவார்க்குச் சேணிகவா இன்பம் அறிவிலா<br />அஞ்சும் பகைவர்ப் பெறின்','அஞ்சிடும் கோழைகளாகவும், அறிவில்லாக் கோழைகளாகவும் பகைவர்கள் இருப்பின் அவர்களை எதிர்ப்போரை விடுத்து வெற்றியெனும் இன்பம் விலகாமலே நிலைத்து நிற்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(870,'870','கல்லான் வெகுளும் சிறுபொருள் எஞ்ஞான்றும்<br />ஒல்லானை ஒல்லா தொளி','போர்முறை கற்றிடாத பகைவர்களைக்கூட எதிர்ப்பதற்குத் தயக்கம் காட்டுகிறவர்கள், உண்மையான வீரர்களை எப்படி எதிர்கொள்வார்கள் எனக் கேலி புரிந்து, புகழ் அவர்களை அணுகாமலே விலகிப் போய்விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(871,'871','பகையென்னும் பண்பி லதனை ஒருவன்<br />நகையேயும் வேண்டற்பாற் றன்று','பகை உணர்வு என்பது பண்புக்கு மாறுபாடானது என்பதால் அதனை வேடிக்கை விளையாட்டாகக்கூட ஒருவன் கொள்ளக்கூடாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(872,'872','வில்லேர் உழவர் பகைகொளினும் கொள்ளற்க<br />சொல்லேர் உழவர் பகை','படைக்கலன்களை உடைய வீரர்களிடம் கூடப் பகை கொள்ளலாம் ஆனால் சொல்லாற்றல் மிக்க அறிஞர் பெருமக்களுடன் பகை கொள்ளக் கூடாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(873,'873','ஏமுற் றவரினும் ஏழை தமியனாய்ப்<br />பல்லார் பகைகொள் பவன்','தனியாக நின்று பலரின் பகையைத் தேடிக் கொள்பவனை ஆணவம் பிடித்தவன் என்பதைவிட அறிவிலி என்பதே பொருத்தமாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(874,'874','பகைநட்பாக் கொண்டொழுகும் பண்புடை யாளன்<br />தகைமைக்கண் தங்கிற் றுலகு','பகைவர்களையும் நண்பர்களாகக் கருதிப் பழகுகின்ற பெருந்தன்மையான பண்பை இந்த உலகமே போற்றிப் புகழும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(875,'875','தன்றுணை இன்றால் பகையிரண்டால் தானொருவன்<br />இன்றுணையாக் கொள்கவற்றின் ஒன்று','தனது பகைவர்கள் இரு பிரிவினராக இயங்கும் நிலையில் தனக்குத் துணையாக யாருமின்றித் தனியாக இருப்பவர், அந்தப் பகைவர்களில் ஒருவரைத் துணையாகக் கொள்ள வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(876,'876','தேறினுந் தேறா விடினும் அழிவின்கண்<br />தேறான் பகாஅன் விடல்','பகைவரைப்பற்றி ஆராய்ந்து தெளிவடைந்திருந்தாலும், இல்லாவிட்டாலும் அதற்கிடையே ஒரு கேடு வரும்போது அந்தப் பகைவருடன் அதிகம் நெருங்காமல் நட்புக் காட்டியும் அவர்களைப் பிரிந்து விடாமலேயே பகை கொண்டும் இருப்பதே நலமாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(877,'877','நோவற்க நொந்த தறியார்க்கு மேவற்க<br />மென்மை பகைவர் அகத்து','தனது துன்பத்தைப் பற்றி அதனை அறியாமல் இருக்கும் நண்பர்களிடம் சொல்லக்கூடாது தனது பலவீனத்தைப் பகைவரிடம் வெளிப்படுத்திவிடக் கூடாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(878,'878','வகையறிந்து தற்செய்து தற்காப்ப மாயும்<br />பகைவர்கண் பட்ட செருக்கு','வழிவகை உணர்ந்து, தன்னையும் வலிமைப்படுத்திக் கொண்டு, தற்காப்பும் தேடிக் கொண்டவரின் முன்னால் பகையின் ஆணவம் தானாகவே ஒடுங்கி விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(879,'879','இளைதாக முள்மரம் கொல்க களையுநர்<br />கைகொல்லும் காழ்த்த இடத்து','முள்மரத்தை, அது சிறிய கன்றாக இருக்கும்போதே கிள்ளி எறிவது போல, பகையையும், அது முற்றுவதற்கு முன்பே வீழ்த்திட வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(880,'880','உயிர்ப்ப உளரல்லர் மன்ற செயிர்ப்பவர்<br />செம்மல் சிதைக்கலா தார்','பகைவரின் ஆணவத்தைக் குலைக்க முடியாதவர்கள், சுவாசிக்கிற காரணத்தினாலேயே, உயிரோடிருப்பதாக நிச்சயமாகச் சொல்ல முடியாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(881,'881','நிழல்நீரும் இன்னாத இன்னா தமர்நீரும்<br />இன்னாவாம் இன்னா செயின்','இனிமையாகத் தெரியும் நிழலும் நீரும்கூடக் கேடு விளைவிக்கக் கூடியவையாக இருந்தால் அவை தீயவைகளாகவே கருதப்படும் அது போலவேதான் உற்றார் உறவினராக உள்ளவர்களின் உட்பகையும் ஆகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(882,'882','வாள்போல் பகைவரை அஞ்சற்க அஞ்சுக<br />கேள்போல் பகைவர் தொடர்பு','வெளிப்படையாக எதிரே வரும் பகைவர்களைவிட உறவாடிக் கெடுக்க நினைப்பவர்களிடம்தான் எச்சரிக்கையாக இருக்க வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(883,'883','உட்பகை அஞ்சித்தற் காக்க உலைவிடத்து<br />மட்பகையின் மாணத் தெறும்','உட்பகைக்கு அஞ்சி ஒருவன் தன்னைப் பாதுகாத்துக் கொள்ள வேண்டும் இல்லாவிட்டால் ஒரு சோதனையான நேரத்தில் பச்சை பாண்டத்தை அறுக்கும் கருவிபோல அந்த உட்பகை அழிவு செய்துவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(884,'884','மனமாணா உட்பகை தோன்றின் இனமாணா<br />ஏதம் பலவும் தரும்','மனம் திருந்தாத அளவுக்கு உட்பகை விளைவிக்கும் உணர்வு ஒருவனுக்கு ஏற்பட்டுவிடுமானால், அது அவனைச் சேர்ந்தவர்களையே பகைவராக்கும் கேட்டினை உண்டாக்கி விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(885,'885','உறல்முறையான் உட்பகை தோன்றின் இறல்முறையான்<br />ஏதம் பலவும் தரும்','நெருங்கிய உறவினருக்கிடையே தோன்றும் உட்பகையானது அவர்களுக்குக் கேடு விளைவிக்கக் கூடிய பல துன்பங்களை உண்டாக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(886,'886','ஒன்றாமை ஒன்றியார் கட்படின் எஞ்ஞான்றும்<br />பொன்றாமை ஒன்றல் அரிது','ஒன்றி இருந்தவர்களிடையே உட்பகை தோன்றி விடுமானால், அதனால் ஏற்படும் அழிவைத் தடுப்பது என்பது எந்தக் காலத்திலும் அரிதான செயலாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(887,'887','செப்பின் புணர்ச்சிபோற் கூடினும் கூடாதே<br />உட்பகை உற்ற குடி','செப்பு எனப்படும் சிமிழில் அதன் மூடி பொருந்தியிருப்பது போல வெளித்தோற்றத்துக்கு மட்டுமே தெரியும் அவ்வாறே உட்பகையுள்ளவர்கள் உளமாரப் பொருந்தியிருக்க மாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(888,'888','அரம்பொருத பொன்போலத் தேயும் உரம்பொரு<br />துட்பகை உற்ற குடி','அரத்தினால் தேய்க்கப்படும் இரும்பின் வடிவமும் வலிமையும் குறைவதைப் போல, உட்பகை உண்டான குலத்தின் வலிமையும் தேய்ந்து குறைந்து விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(889,'889','எட்பக வன்ன சிறுமைத்தே ஆயினும்<br />உட்பகை உள்ளதாங் கேடு','எள்ளின் பிளவுபோன்று சிறிதாக இருந்தாலும் உட்பகையால் பெருங்கேடு விளையும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(890,'890','உடம்பா டிலாதவர் வாழ்க்கை குடங்கருள்<br />பாம்போ டுடனுறைந் தற்று','உள்ளத்தால் ஒன்றுபடாதவர்கள் கூடிவாழ்வது என்பது ஒரு சிறிய குடிலுக்குள் பாம்புடன் இருப்பது போன்று ஒவ்வொரு நொடியும் அச்சம் தருவதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(891,'891','ஆற்றுவார் ஆற்றல் இகழாமை போற்றுவார்<br />போற்றலுள் எல்லாம் தலை','ஒரு செயலைச் செய்து முடிக்க வல்லவரின் ஆற்றலை இகழாது இருந்தால், அதுவே தம்மைக் காத்திடும் காவல்கள் அனைத்தையும் விடச் சிறந்த காவலாக அமையும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(892,'892','பெரியாரைப் பேணா தொழுகிற் பெரியாராற்<br />பேரா இடும்பை தரும்','பெரியோர்களை மதிக்காமல் நடந்து கொண்டால் நீங்காத பெருந்துன்பத்தை அடைய நேரிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(893,'893','கெடல்வேண்டிற் கேளாது செய்க அடல்வேண்டின்<br />ஆற்று பவர்கண் இழுக்கு','ஒருவன், தன்னைத்தானே கெடுத்துக் கொள்ள விரும்பினால் பகையை நினைத்த மாத்திரத்தில் அழிக்கக் கூடிய ஆற்றலுடையவர்களை யார் பேச்சையும் கேட்காமலே இழித்துப் பேசலாம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(894,'894','கூற்றத்தைக் கையால் விளித்தற்றால் ஆற்றுவார்க்<br />காற்றாதார் இன்னா செயல்','எந்தத் துன்பத்தையும் தாங்கக் கூடிய ஆற்றல் படைத்தவர்களுடன், சிறு துன்பத்தையும் தாங்க முடியாதவர்கள் மோதினால் அவர்களே தங்களின் முடிவுகாலத்தைக் கையசைத்துக் கூப்பிடுகிறார்கள் என்றுதான் பொருள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(895,'895','யாண்டுச்சென் றியாண்டும் உளராகார் வெந்துப்பின்<br />வேந்து செறப்பட் டவர்','மிக்க வலிமை பொருந்திய அரசின் கோபத்திற்கு ஆளானவர்கள் தப்பித்து எங்கே சென்றாலும் அங்கு அவர்களால் உயிர் வாழ முடியாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(896,'896','எரியாற் சுடப்படினும் உய்வுண்டாம் உய்யார்<br />பெரியார்ப் பிழைத்தொழுகு வார்','நெருப்புச் சூழ்ந்து சுட்டாலும்கூட ஒருவர் பிழைத்துக் கொள்ள முடியும்; ஆனால் ஆற்றல் மிகுந்த பெரியோரிடம் தவறிழைப்போர் தப்பிப் பிழைப்பது முடியாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(897,'897','வகைமாண்ட வாழ்க்கையும் வான்பொருளும் என்னாம்<br />தகைமாண்ட தக்கார் செறின்','பெருஞ்செல்வம் குவித்துக்கொண்டு என்னதான் வகைவகையான வாழ்க்கைச் சுகங்களை அனுபவித்தாலும், தகுதி வாய்ந்த பெரியோரின் கோபத்துக்கு முன்னால் அவையனைத்தும் பயனற்றுப் போகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(898,'898','குன்றன்னார் குன்ற மதிப்பிற் குடியொடு<br />நின்றன்னார் மாய்வர் நிலத்து','மலை போன்றவர்களின் பெருமையைக் குலைப்பதற்கு நினைப்பவர்கள், நிலைத்த பெரும் செல்வமுடையவர்களாக இருப்பினும் அடியோடு அழிந்து போய் விடுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(899,'899','ஏந்திய கொள்கையார் சீறின் இடைமுரிந்து<br />வேந்தனும் வேந்து கெடும்','உயர்ந்த கொள்கை உறுதி கொடண்வர்கள் சீறி எழுந்தால், அடக்குமுறை ஆட்சி நிலை குலைந்து அழிந்துவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(900,'900','இறந்தமைந்த சார்புடையர் ஆயினும் உய்யார்<br />சிறந்தமைந்த சீரார் செறின்','என்னதான் எல்லையற்ற வசதிவாய்ப்புகள், வலிமையான துணைகள் உடையவராக இருப்பினும், தகுதியிற் சிறந்த சான்றோரின் சினத்தை எதிர்த்துத் தப்பிப் பிழைக்க முடியாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(901,'901','மனைவிழைவார் மாண்பயன் எய்தார் வினைவிழைவார்<br />வேண்டாப் பொருளும் அது','கடமையுடன் கூடிய செயல்புரியக் கிளம்பியவர்கள் இல்லற சுகத்தைப் பெரிதெனக் கருதினால் சிறப்பான புகழைப் பெற மாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(902,'902','பேணாது பெண்விழைவான் ஆக்கம் பெரியதோர்<br />நாணாக நாணுத் தரும்','எற்றுக்கொண்ட கொள்கையினைப் பேணிக் காத்திடாமல் பெண்ணை நாடி அவள் பின்னால் திரிபவனுடைய நிலை வெட்கித் தலைகுனிய வேண்டியதாக ஆகிவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(903,'903','இல்லாள்கண் தாழ்ந்த இயல்பின்மை எஞ்ஞான்றும்<br />நல்லாருள் நாணுத் தரும்','நற்குணமில்லாத மனைவியைத் திருத்த முனையாமல் பணிந்து போகிற கணவன், நல்லோர் முன்னிலையில் நாணமுற்று நிற்கும் நிலைக்கு ஆளாக நேரிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(904,'904','மனையாளை யஞ்சும் மறுமையி லாளன்<br />வினையாண்மை வீறெய்த லின்று','மணம் புரிந்து புதுவாழ்வின் பயனை அடையாமல் குடும்பம் நடத்த அஞ்சுகின்றவனின் செயலாற்றல் சிறப்பாக அமைவதில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(905,'905','இல்லாளை யஞ்சுவா னஞ்சுமற் றெஞ்ஞான்றும்<br />நல்லார்க்கு நல்ல செயல்','எப்போதுமே நல்லோர்க்கு நன்மை செய்வதில் தவறு ஏற்பட்டுவிடக் கூடாதே என்று அஞ்சுகிறவன் தவறு நேராமல் கண்காணிக்கும் மனைவிக்கு அஞ்சி நடப்பான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(906,'906','இமையாரின் வாழினும் பாடிலரே யில்லாள்<br />அமையார்தோ ளஞ்சு பவர்','அறிவும் பண்பும் இல்லாத மனைவி, அழகாக இருக்கிறாள் என்பதற்காக மட்டும் அவளுக்கு அடங்கி நடப்பவர்கள், தங்களைத் தேவாம்சம் படைத்தவர்கள் என்று கற்பனையாகக் காட்டிக் கொண்டாலும் அவர்களுக்கு உண்மையில் எந்தப் பெருமையும் கிடையாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(907,'907','பெண்ணேவல் செய்தொழுகும் ஆண்மையின் நாணுடைப்<br />பெண்ணே பெருமை உடைத்து','ஒரு பெண்ணின் காலைச் சுற்றிக் கொண்டு கிடைக்கும் ஒருவனின் ஆண்மையைக் காட்டிலும், மான உணர்வுள்ள ஒருத்தியின் பெண்மையே பெருமைக்குரியதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(908,'908','நட்டார் குறைமுடியார் நன்றாற்றார் நன்னுதலாள்<br />பெட்டாங் கொழுகு பவர்','ஒரு பெண்ணின் அழகுக்காகவே அவளிடம் மயங்கி அறிவிழந்து நடப்பவர்கள், நண்பர்களைப்பற்றியும் கவலைப்படமாட்டார்கள்; நற்பணிகளையும் ஆற்றிட மாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(909,'909','அறவினையும் ஆன்ற பொருளும் பிறவினையும்<br />பெண்ணேவல் செய்வார்கண் இல்','ஆணவங்கொண்ட பெண்கள் இடுகின்ற ஆணைகளுக்கு அடங்கி இயங்குகின்ற பெண்பித்தர்களிடம் அறநெறிச் செயல்களையோ சிறந்த அறிவாற்றலையோ எதிர்பார்க்க முடியாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(910,'910','எண்சேர்ந்த நெஞ்சத் திடனுடையார்க் கெஞ்ஞான்றும்<br />பெண்சேர்ந்தாம் பேதைமை இல்','சிந்திக்கும் ஆற்றலும் நெஞ்சுறுதியும் கொண்டவர்கள் காமாந்தகாரர்களாகப் பெண்களையே சுற்றிக் கொண்டு கிடக்க மாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(911,'911','அன்பின் விழையார் பொருள்விழையும் ஆய்தொடியார்<br />இன்சொல் இழுக்குத் தரும்','அன்பே இல்லாமல் பொருள் திரட்டுவதையே குறிக்கோளாகக் கொண்ட பொதுமகளிர் இனிமையாகப் பேசுவதை நம்பி ஏமாறுகிறவர்களுக்கு இறுதியில் துன்பமே வந்து சேரும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(912,'912','பயன்தூக்கிப் பண்புரைக்கும் பண்பின் மகளிர்<br />நயன்தூக்கி நள்ளா விடல்','ஆதாயத்தைக் கணக்கிட்டு அதற்கேற்றவாறு பாகுமொழிபேசும் பொதுமகளிர் உறவை ஒருபோதும் நம்பி ஏமாறக்கூடாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(913,'913','பொருட்பெண்டிர் பொய்ம்மை முயக்கம் இருட்டறையில்<br />ஏதில் பிணந்தழீஇ அற்று','விலைமாதர்கள் பணத்துக்காக மட்டுமே ஒருவரைத் தழுவிப் பொய்யன்பு காட்டி நடிப்பது, இருட்டறையில் ஓர் அந்நியப் பிணத்தை அணைத்துக் கிடப்பது போன்றதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(914,'914','பொருட்பொருளார் புன்னலந் தோயார் அருட்பொருள்<br />ஆயும் அறிவி னவர்','அருளை விரும்பி ஆராய்ந்திடும் அறிவுடையவர்கள் பொருளை மட்டுமே விரும்பும் விலைமகளிரின் இன்பத்தை இழிவானதாகக் கருதுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(915,'915','பொதுநலத்தார் புன்னலந் தோயார் மதிநலத்தின்<br />மாண்ட அறிவி னவர்','இயற்கையறிவும் மேலும் கற்றுணர்ந்த அறிவும் கொண்டவர்கள் பொதுமகளிர் தரும் இன்பத்தில் மூழ்கமாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(916,'916','தந்நலம் பாரிப்பார் தோயார் தகைசெருக்கிப் <br />புன்னலம் பாரிப்பார் தோள்','புகழ்ச்சிக்குரிய சான்றோர் எவரும், இகழ்ச்சிக்குரிய இன்பவல்லிகளின் தோளில் சாய்ந்து கிடக்க மாட்டார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(917,'917','நிறைநெஞ்சம் இல்லவர் தோய்வர் பிறநெஞ்சிற்<br />பேணிப் புணர்பவர் தோள்','உள்ளத்தில் அன்பு இல்லாமல் தன்னலத்துக்காக உடலுறவு கொள்ளும் பொதுமகளிர் தோளை, உறுதியற்ற மனம் படைத்தோர் மட்டும் நம்பிக் கிடப்பர்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(918,'918','ஆயும் அறிவினர் அல்லார்க் க��ங்கென்ப<br />மாய மகளிர் முயக்கு','வஞ்சக எண்ணங்கொண்ட ``பொதுமகள் ஒருத்தியிடம் மயங்குவதை அறிவில்லாதவனுக்கு ஏற்பட்ட ``மோகினி மயக்கம் என்று கூறுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(919,'919','வரைவிலா மாணிழையார் மென்தோள் புரையிலாப்<br />பூரியர்கள் ஆழும் அளறு','விலைமகளை விரும்பி அவள் பின்னால் போவதற்கும் ``நரகம் எனச் சொல்லப்படும் சகதியில் விழுவதற்கும் வேறுபாடே இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(920,'920','இருமனப் பெண்டிரும் கள்ளும் கவறும்<br />திருநீக்கப் பட்டார் தொடர்பு','இருமனம் கொண்ட பொதுமகளிருடனும், மதுவுடனும், சூதாட்டத்தினிடமும் தொடர்பு கொண்டு உழல்வோரைவிட்டு வாழ்வில் அமைய வேண்டிய சிறப்பு அகன்றுவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(921,'921','உட்கப் படாஅர் ஒளியிழப்பர் எஞ்ஞான்றும்<br />கட்காதல் கொண்டொழுகு வார்','மதுப் பழக்கத்திற்கு அடிமையானவர்கள் தமது சிறப்பை இழப்பது மட்டுமல்ல; மாற்றாரும் அவர்களைக் கண்டு அஞ்ச மாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(922,'922','உண்ணற்க கள்ளை உணிலுண்க சான்றோரான்<br />எண்ணப் படவேண்டா தார்','மது அருந்தக் கூடாது; சான்றோர்களின் நன் மதிப்பைப் பெற விரும்பாதவர் வேண்டுமானால் அருந்தலாம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(923,'923','ஈன்றாள் முகத்தேயும் இன்னாதால் என்மற்றுச்<br />சான்றோர் முகத்துக் களி','கள்ளருந்தி மயங்கிவிடும் தன் மகனை, அவன் குற்றங்களை மன்னிக்கக் கூடிய தாயே காணச் சகிக்கமாட்டாள் என்கிறபோது ஏனைய சான்றோர்கள் அவனை எப்படிச் சகித்துக் கொள்வார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(924,'924','நாணென்னும் நல்லாள் புறங்கொடுக்கும் கள்ளென்னும்<br />பேணாப் பெருங்குற்றத் தார்க்கு','மது மயக்கம் எனும் வெறுக்கத்தக்க பெருங்குற்றத்திற்கு ஆளாகியிருப்போரின் முன்னால் நாணம் என்று சொல்லப்படும் நற்பண்பு நிற்காமல் ஓடிவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(925,'925','கையறி யாமை உடைத்தே பொருள்கொடுத்து<br />மெய்யறி யாமை கொளல்','ஒருவன் தன்னிலை மறந்து மயங்கியிருப்பதற்காகப், போதைப் பொருளை விலை கொடுத்து வாங்குதல் விவரிக்கவே முடியாத மூடத்தனமாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(926,'926','துஞ்சினார் செத்தாரின் வேறல்லர் எஞ்ஞான்றும்<br />நஞ்சுண்பார் கள்ளுண் பவர்','மது அருந்துவோர்க்கும் நஞ்சு அருந்துவோர்க்கும் வேறுபாடு கிடையாது என்பதால் அவர்கள் தூங்குவதற்கும் இறந்து கிடப்பதற்கும்கூட வேறுபாடு கிடையாது என்று கூறலாம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(927,'927','உள்ளொற்றி உள்ளூர் நகப்படுவர் எஞ்ஞான்றும் <br />கள்ளொற்றிக் கண்சாய் பவர்','மறைந்திருந்து மதுவருந்தினாலும் மறைக்க முடியாமல் அவர்களது கண்கள் சுழன்று மயங்குவதைக் கண்டு ஊரார் எள்ளி நகையாடத்தான் செய்வார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(928,'928','களித்தறியேன் என்பது கைவிடுக நெஞ்சத்<br />தொளித்ததூஉம் ஆங்கே மிகும்','மது அருந்துவதே இல்லை என்று ஒருவன் பொய் சொல்ல முடியாது; காரணம், அவன் மது மயக்கத்தில் இருக்கும் போது அந்த உண்மையைச் சொல்லி விடுவான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(929,'929','களித்தானைக் காரணம் காட்டுதல் கீழ்நீர்க்<br />குளித்தானைத் தீத்துரீஇ அற்று','குடிபோதைக்கு அடிமையாகி விட்டவனைத் திருத்த அறிவுரை கூறுவதும், தண்ணீருக்குள் மூழ்கிவிட்டவனைத் தேடிக்கண்டுபிடிக்கத் தீப்பந்தம் கொளுத்திக் கொண்டு செல்வதும் ஒன்றுதான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(930,'930','கள்ளுண்ணாப் போழ்திற் களித்தானைக் காணுங்கால்<br />உள்ளான்கொல் உண்டதன் சோர்வு','ஒரு குடிகாரன், தான் குடிக்காமல் இருக்கும்போது மற்றொரு குடிகாரன் மது மயக்கத்தில் தள்ளாடுவதைப் பார்த்த பிறகாவது அதன் கேட்டினை எண்ணிப் பார்க்க மாட்டானா?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(931,'931','வேண்டற்க வென்றிடினும் சூதினை வென்���தூஉம்<br />தூண்டிற்பொன் மீன்விழுங்கி அற்று','வெற்றியே பெறுவதாயினும் சூதாடும் இடத்தை நாடக்கூடாது அந்த வெற்றி, தூண்டிலின் இரும்பு முள்ளில் கோத்த இரையை மட்டும் விழுங்குவதாக நினைத்து மீன்கள் இரும்பு முள்ளையே கௌவிக் கொண்டது போலாகிவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(932,'932','ஒன்றெய்தி நூறிழக்கும் சூதர்க்கும் உண்டாங்கொல்<br />நன்றெய்தி வாழ்வதோர் ஆறு','ஒரு வெற்றியைப் பெற்ற மகிழ்ச்சியில் தொடர்ந்து ஆடி நூறு தோல்விகளைத் தழுவிக்கொள்ளும் சூதாடிகளின் வாழ்க்கையில் நலம் ஏற்பட வழி ஏது?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(933,'933','உருளாயம் ஓவாது கூறிற் பொருளாயம்<br />போஒய்ப் புறமே படும்','பணையம் வைத்து இடைவிடாமல் சூதாடுவதை ஒருவன் பழக்கமாகவே கொள்வானேயானால் அவன் செல்வமும் அந்தச் செல்வத்தை ஈட்டும் வழிமுறையும் அவனைவிட்டு நீங்கிவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(934,'934','சிறுமை பலசெய்து சீரழிக்கும் சூதின்<br />வறுமை தருவதொன் றில்','பல துன்பங்களுக்கு ஆளாக்கி, புகழைப் கெடுத்த, வறுமையிலும் ஆழ்த்துவதற்குச் சூதாட்டத்தைப் போன்ற தீமையான செயல் வேறொன்றும் இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(935,'935','கவறும் கழகமும் கையும் தருக்கி<br />இவறியார் இல்லாகி யார்','சூதாடும் இடம், அதற்கான கருவி, அதற்குரிய முயற்சி ஆகியவற்றைக் கைவிட மனமில்லாதவர்கள் எதுவும் இல்லாதவர்களாகவே ஆகிவிடுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(936,'936','அகடாரார் அல்லல் உழப்பர்சூ தென்னும்<br />முகடியான் மூடப்பட் டார்','சூது எனப்படும் தீமையின் வலையில் விழுந்தவர்கள் வயிறார உண்ணவும் விரும்பாமல் துன்பத்திலும் உழன்று வருந்துவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(937,'937','பழகிய செல்வமும் பண்பும் கெடுக்கும் <br />கழகத்துக் காலை புகின்','சூதாடும் இடத்திலேயே ஒருவர் தமது காலத்தைக் கழிப்பாரேயானால், அது அவருடைய மூதாதையர் தேடிவைத்த சொத்துகளையும் நற்பண்பையும் நாசமாக்கிவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(938,'938','பொருள்கெடுத்துப் பொய்மேற் கொளீஇ அருள்கெடுத்<br />தல்லல் உழப்பிக்கும் சூது','பொருளைப் பறித்துப் பொய்யனாக ஆக்கி, அருள் நெஞ்சத்தையும் மாற்றித், துன்ப இருளில் ஒருவனை உழலச் செய்வது சூது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(939,'939','உடைசெல்வம் ஊணொளி கல்வியென் றைந்தும்<br />அடையாவாம் ஆயங் கொளின்','சூதாட்டத்திற்கு அடிமையாகி விட்டவர்களை விட்டுப் புகழும், கல்வியும், செல்வமும், உணவும், உடையும் அகன்று ஒதுங்கி விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(940,'940','இழத்தொறூஉம் காதலிக்கும் சூதேபோல் துன்பம்<br />உழத்தொறூஉம் காதற் றுயிர்','பொருளை இழக்க இழக்கச் சூதாட்டத்தின் மீது ஏற்படுகிற ஆசையும், உடலுக்குத் துன்பம் தொடர்ந்து வரவர உயிர்மீது கொள்ளுகிற ஆசையும் ஒன்றேதான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(941,'941','மிகினும் குறையினும் நோய்செய்யும் நூலோர்<br />வளிமுதலா எண்ணிய மூன்று','வாதம், பித்தம், சிலேத்துமம் என்று மருத்துவ நூலோர் கணித்துள்ள மூன்றில் ஒன்று அளவுக்கு அதிகமானாலும் குறைந்தாலும் நோய் உண்டாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(942,'942','மருந்தென வேண்டாவாம் யாக்கைக் கருந்திய<br />தற்றது போற்றி உணின்','உண்ட உணவு செரிப்பதற்கான கால இடைவெளி தந்து, உணவு அருந்துகிறவர்களின் உடலுக்கு வேறு மருந்தே தேவையில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(943,'943','அற்றால் அளவறிந் துண்க அஃதுடம்பு<br />பொற்றான் நெடிதுய்க்கும் ஆறு','உண்ட உணவு செரித்ததையும், உண்ணும் உணவின் அளவையும் அறிந்து உண்பது நீண்ட நாள் வாழ்வதற்கு வழியாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(944,'944','அற்ற தறிந்து கடைப்பிடித்து மாறல்ல<br />துய்க்க துவரப் பசித்து','உண்டது செரித்ததா என்பதை உணர்ந்து, நன்கு பசியெடுத்த பிறகு உடலுக்கு ஒத்து வரக்கூடிய உணவை அருந்த வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(945,'945','மாறுபா டில்லாத உண்டி மறுத்துண்ணின்<br />ஊறுபா டில்லை உயிர்க்கு','உடலுக்கு ஒத்துவரக்கூடிய உணவைக்கூட அதிகமாகும்போது மறுத்து அளவுடன் உண்டால், உயிர் வாழ்வதற்குத் தொல்லை எதுவுமில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(946,'946','இழிவறிந் துண்பான்கண் இன்பம்போல் நிற்கும்<br />கழிபேர் இரையான்கண் நோய்','அளவோடு உண்பவர் உடல் நலமுடன் வாழ்வதும் அதிகம் உண்பவர் நோய்க்கு ஆளாவதம் இயற்கை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(947,'947','தீயள வன்றித் தெரியான் பெரிதுண்ணின்<br />நோயள வின்றிப் படும்','பசியின் அளவு அறியாமலும், ஆராயாமலும் அதிகம் உண்டால் நோய்களும் அளவின்றி வரும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(948,'948','நோய்நாடி நோய்முதல் நாடி அதுதணிக்கும்<br />வாய்நாடி வாய்ப்பச் செயல்','நோய் என்ன? நோய்க்கான காரணம் என்ன? நோய் தீர்க்கும் வழி என்ன? இவற்றை முறையாக ஆராய்ந்து சிகிச்சை செய்ய வேண்டும் (உடல் நோய்க்கு மட்டுமின்றிச் சமுதாய நோய்க்கும் இது பொருந்தும்)',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(949,'949','உற்றான் அளவும் பிணியளவும் காலமும்<br />கற்றான் கருதிச் செயல்','நோயாளியின் வயது, நோயின் தன்மை, மருத்துவம் செய்வதற்குரிய நேரம் என்பனவற்றை எல்லாம் மருத்துவம் கற்றவர் எண்ணிப் பார்த்தே செயல்பட வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(950,'950','உற்றவன் தீர்ப்பான் மருந்துழைச் செல்வானென்<br />றப்பானாற் கூற்றே மருந்து','நோயாளி, மருத்துவர், மருந்து, அருகிருந்து துணைபுரிபவர் என மருத்துவமுறை நான்கு வகையாக அமைந்துள்ளது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(951,'951','இற்பிறந்தார் கண்ணல்ல தில்லை இயல்பாகச் <br />செப்பமும் நாணும் ஒருங்கு','நடுநிலை தவறாத பண்பும், ஆரவாரமற்ற அடக்க உணர்வும் கொண்டவர்களையல்லாமல் மற்றவர்களை உயர்ந்த குடியில் பிறந்தவர்களாகக் கருத முடியாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(952,'952','ஒழுக்கமும் வாய்மையும் நாணுமிம் மூன்றும்<br />இழுக்கார் குடிப்பிறந் தார்','ஒழுக்கம், வாய்மை, மானம் ஆகிய இந்த மூன்றிலும் நிலைதவறி நடக்காதவர்களே உயர்ந்த குடியில் பிறந்தவர்களாகக் கருதப்படுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(953,'953','நகையீகை அன்சொல் இகழாமை நான்கும்<br />வகையென்ப வாய்மைக் குடிக்கு','முகமலர்ச்சி, ஈகைக்குணம், இனியசொல், பிறரை இகழாத பண்பாடு ஆகிய நான்கு சிறப்புகளும் உள்ளவர்களையே வாய்மையுள்ள குடிமக்கள் என்று வகைப்படுத்த முடியும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(954,'954','அடுக்கிய கோடி பெறினும் குடிப்பிறந்தார்<br />குன்றுவ செய்தல் இலர்','பலகோடிப் பொருள்களை அடுக்கிக் கொடுத்தாலும் சிறந்த குடியில் பிறந்தவர்கள் அந்தச் சிறப்புக் கெடுவதற்கான செயல்களுக்கு இடம் தரமாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(955,'955','வழங்குவ துள்வீழ்ந்தக் கண்ணும் பழங்குடி <br />பண்பின் தலைப்பிரிதல் இன்று','பழம் பெருமை வாய்ந்த குடியில் பிறந்தவர்கள் வறுமையால் தாக்குண்ட போதிலும், பிறருக்கு வழங்கும் பண்பை இழக்க மாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(956,'956','சலம்பற்றிச் சால்பில செய்யார்மா சற்ற<br />குலம்பற்றி வாழ்துமென் பார்','மாசற்ற பண்புடன் வாழ்வதாகக் கருதிக்கொண்டிருப்பவர்கள், வஞ்சக நினைவுடன் தகாத காரியங்களில் ஈடுபடமாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(957,'957','குடிப்பிறந்தார் கண்விளங்கும் குற்றம் விசும்பின்<br />மதிக்கண் மறுப்போல் உயர்ந்து','பிறந்த குடிக்குப் பெருமை சேர்ப்பவரிடமுள்ள சிறிய குறைகள், ஒளிவு மறைவு ஏதுமின்றி, வானத்து நிலவில் உள்ள குறைபோல வெளிப்படையாகத் தெரியக் கூடியதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(958,'958','நலத்தின்கண் நாரின்மை தோன்றின் அவனைக்<br />குலத்தின்கண் ஐயப் படும்','என்னதான் அழகும் புகழும் உடையவனாக இருந்தாலும் அன்பு எனும் ஒரு பண்பு இல்லாதவனாக இருந்தால் அவன் பிறந்த குலத்தையே சந்தேகிக்க வேண்டிய நிலை ஏற்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(959,'959','நிலத்திற் கிடந்தமை கால்காட்டும் காட்டும்<br />குலத்திற் பிறந்தார்வாய்ச் சொல்','விளைந்த பயிரைப் பார்த்தாலே இது எந்த நிலத்தில் விளைந்தது என்று அறிந்து கொள்ளலாம் அதேபோல் ஒருவரின் வாய்ச் சொல்லைக் கேட்டே அவர் எத்தகைய குடியில் பிறந்தவர் என்பதை உணர்ந்து கொள்ளலாம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(960,'960','நலம்வேண்டின் நாணுடைமை வேண்டும் குலம்வேண்டின்<br />வேண்டுக யார்க்கும் பணிவு','தகாத செயல் புரிந்திட அஞ்சி நாணுவதும், எல்லோரிடமும் ஆணவமின்றிப் பணிவுடன் நடந்து கொள்வதும் ஒருவரின் நலத்தையும் அவர் பிறந்த குலத்தையும் உயர்த்தக் கூடியவைகளாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(961,'961','இன்றி அமையாச் சிறப்பின ஆயினும்<br />குன்ற வருப விடல்','கட்டாயமாகச் செய்து தீர வேண்டிய செயல்கள் என்றாலும்கூட அவற்றால் தனது பெருமை குறையுமானால் அந்தச் செயல்களைத் தவிர்த்திடல் வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(962,'962','சீரினும் சீரல்ல செய்யாரே சீரொடு<br />பேராண்மை வேண்டு பவர்','புகழ்மிக்க வீர வாழ்க்கையை விரும்புகிறவர், தனக்கு எப்படியும் புகழ் வரவேண்டுமென்பதற்காக மான உணர்வுக்குப் புறம்பான காரியத்தில் ஈடுபடமாட்டார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(963,'963','பெருக்கத்து வேண்டும் பணிதல் சிறிய<br />சுருக்கத்து வேண்டும் உயர்வு','உயர்ந்த நிலை வரும்போது அடக்க உணர்வும், அந்த நிலை மாறிவிட்ட சூழலில் யாருக்கும் அடிமையாக அடங்கி நடக்காத மான உணர்வும் வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(964,'964','தலையின் இழிந்த மயிரனையர் மாந்தர்<br />நிலையின் இழிந்தக் கடை','மக்களின் நெஞ்சத்தில் உயர்ந்த இடம் பெற்றிருந்த ஒருவர் மானமிழந்து தாழ்ந்திடும்போது, தலையிலிருந்து உதிர்ந்த மயிருக்குச் சமமாகக் கருதப்படுவார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(965,'965','குன்றின் அனையாரும் குன்றுவர் குன்றுவ<br />குன்றி அனைய செயின்','குன்றினைப் போல் உயர்ந்து கம்பீரமாக நிற்பவர்களும் ஒரு குன்றிமணி அளவு இழிவான செயலில் ஈடுபட்டால் தாழ்ந்து குன்றிப் போய் விடுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(966,'966','புகழின்றால் புத்தேணாட் டுய்யாதால் என்மற்<br />றிகழ்வார்பின் சென்று நிலை','இகழ்வதையும் பொறுத்துக்கொண்டு, மானத்தை விட்டுவிட்டு ஒருவர் பின்னே பணிந்து செல்வதால் என்ன புகழ் கிடைக்கும்? இல்லாத சொர்க்கமா கிடைக்கும்?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(967,'967','ஒட்டார்பின் சென்றொருவன் வாழ்தலின் அந்நிலையே<br />கெட்டான் எனப்படுதல் நன்று','தன்னை மதிக்காதவரின் பின்னால் சென்று உயிர் வாழ்வதைவிடச் செத்தொழிவது எவ்வளவோ மேல்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(968,'968','மருந்தோமற் றூனோம்பும் வாழ்க்கை பெருந்தகைமை<br />பீடழிய வந்த இடத்து','சாகாமலே இருக்க மருந்து கிடையாது அப்படி இருக்கும்போது உயிரைவிட நிலையான மானத்தைப் போற்றாமல், வாழ்க்கை மேம்பாட்டுக்காக ஒருவர், தமது பெருமையைக் குறைத்துக் கொள்வது இழிவான செயலாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(969,'969','மயிர்நீப்பின் வாழாக் கவரிமா அன்னார்<br />உயிர்நீப்பர் மானம் வரின்','உடலில் உள்ள உரோமம் நீக்கப்பட்டால் உயிர் வாழாது கவரிமான் என்பார்கள் அதுபோல் மானம் அழிய நேர்ந்தால் உயர்ந்த மனிதர்கள் உயிரையே விட்டு விடுவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(970,'970','இளிவரின் வாழாத மானம் உடையார்<br />ஒளிதொழு தேத்தும் உலகு','மானம் அழியத்தக்க இழிவு வந்ததே என்று உயிரை மாய்த்துக் கொள்ளக் கூடியவர்களின் புகழை உலகம் எக்காலமும் போற்றி நிற்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(971,'971','ஒளியொருவற் குள்ள வெறுக்கை இளியொருவற்<br />கஃதிறந்து வாழ்தும் எனல்','ஒருவரின் வாழ்க்கைக்கு ஒளிதருவது ஊக்கமேயாகும் ஊக்கமின்றி உயிர்வாழ்வது இழிவு தருவதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(972,'972','பிறப்பொக்கும் எல்லா உயிர்க்கும் சிறப்பொவ்வா<br />செய்தொழில் வேற்றுமை யான்','பிறப்பினால் அனைவரும் சமம் செய்யும் தொழிலில் காட்டுகிற திறமையில் மட்டுமே வேறுபாடு காண முடியும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(973,'973','மேலிருந்துத் மேலல்லார் மேலல்லர் கீழிருந்தும்<br />கீழல்லார் கீழல் லவர்','பண்பு இல்லாதவர்கள் உயர்ந்த பதவியில் இருந்தாலும் உயர்ந்தோர் அல்லர்; இழிவான காரியங்களில் ஈடுபடாதவர்கள் தாழ்ந்த நிலையில் இருந்தாலும் உயர்ந்தோரேயாவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(974,'974','ஒருமை மகளிரே போலப் பெருமையும்<br />தன்னைத்தான் கொண்டொழுகின் உண்டு','தன்னிலை தவறாமல் ஒருவன் தன்னைத் தானே காத்துக்கொண்டு வாழ்வானேயானால், கற்புக்கரசிகளுக்குக் கிடைக்கும் புகழும் பெருமையும் அவனுக்குக் கிடைக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(975,'975','பெருமை யுடையவர் ஆற்றுவார் ஆற்றின்<br />அருமை யுடைய செயல்','அரிய செயல்களை அவற்றுக்கு உரிய முறையான வழியில் செய்து முடிக்கும் திறமையுடையவர்கள் பெருமைக்குரியவராவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(976,'976','சிறியார் உணர்ச்சியுள் இல்லை பெரியாரைப்<br />பேணிக்கொள் வேமென்னும் நோக்கு','பெரியோரைப் போற்றி ஏற்றுக்கொள்ளும் நோக்கம், அறிவிற் சிறியோரின் உணர்ச்சியில் ஒன்றியிருப்பதில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(977,'977','இறப்பே புரிந்த தொழிற்றாம் சிறப்புதான்<br />சீரல் லவர்கண் படின்','சிறப்பான நிலையுங்கூட அதற்குப் பொருந்தாத கீழ் மக்களுக்குக் கிட்டுமானால், அவர்கள் வரம்புமீறிச் செயல்படுவது இயற்கை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(978,'978','பணியுமாம் என்றும் பெருமை சிறுமை<br />அணியுமாம் தன்னை வியந்து','பண்புடைய பெரியோர் எல்லோரிடமும் எப்பொழுதும் பணிவுடன் பழகுவார்கள்; பண்பு இல்லாத சிறியோர், தம்மைத் தாமே புகழ்ந்து கொண்டு இறுமாந்து கிடப்பார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(979,'979','பெருமை பெருமிதம் இன்மை சிறுமை<br />பெருமிதம் ஊர்ந்து விடல்','ஆணவமின்றி அடக்கமாக இருப்பது பெருமை எனப்படும் ஆணவத்தின் எல்லைக்கே சென்று விடுவது சிறுமை எனப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(980,'980','அற்றம் மறைக்கும் பெருமை சிறுமைதான்<br />குற்றமே கூறி விடும்','பிறருடைய குறைகளை மறைப்பது பெருமைப் பண்பாகும் பிறருடைய குற்றங்களையே கூறிக்கொண்டிருப்பது சிறுமைக் குணமாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(981,'981','கடனென்ப நல்லவை யெல்லாம் கடனறிந்து<br />சான்றாண்மை மேற்கொள் பவர்க்கு','ஆற்ற வேண்டிய கடமைகளை உணர்ந்து, அவற்றைப் பண்பார்ந்த முறையில் நிறைவேற்ற மேற்கொள்ளப்படும் முயற்சிகள் அனைத்தும் நல்ல கடமைகள் என்றே கொள்ளப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(982,'982','குணநலஞ் சான்றோர் நலனே பிறநலம்<br />எந்நலத் துள்ளதூஉ மன்று','நற்பண்பு ஒன்றே சான்றோர்க்கான அழகாகும் வேறு எந்த அழகும் அழகல்ல',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(983,'983','அன்புநாண் ஒப்புரவு கண்ணோட்டம் வாய்மையொ<br />டைந்துசால் பூன்றிய தூண்','அன்பு கொள்ளுதல், பழிபுரிந்திட நாணுதல், உலக ஒழுக்கம் போற்றுதல், இரக்கச் செயலாற்றுதல், வாய்மை கடைப்பிடித்தல் ஆகிய ஐந்தும் சான்றாண்மையைத் தாங்கும் தூண்களாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(984,'984','கொல்லா நலத்தது நோன்மை பிறர்தீமை<br />சொல்லா நலத்தது சால்பு','உயிரைக் கொல்லாத அறத்தை அடிப்படையாகக் கொண்டது நோன்பு பிறர் செய்யும் தீமையைச் சுட்டிக் சொல்லாத பண்பைக் குறிப்பது சால்பு',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(985,'985','ஆற்றுவார் ஆற்றல் பணிதல் அதுசான்றோர்<br />மாற்றாரை மாற்றும் படை','ஆணவமின்றிப் பணிவுடன் நடத்தலே, ஆற்றலாளரின��� ஆற்றல் என்பதால் அதுவே பகைமையை மாற்றுகின்ற படையாகச் சான்றோர்க்கு அமைவதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(986,'986','சால்பிற்குக் கட்டளை யாதெனின் தோல்வி<br />துலையல்லார் கண்ணும் கொளல்','சமநிலையில் இல்லாதவர்களால் தனக்கு ஏற்படும் தோல்வியைக்கூட ஒப்புக் கொள்ளும் மனப்பக்குவம்தான் ஒருவரின் மேன்மைக்கு உரைகல்லாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(987,'987','இன்னாசெய் தார்க்கும் இனியவே செய்யாக்கால்<br />என்ன பயத்ததோ சால்பு','தமக்குத் தீமை செய்வதற்கும் திரும்ப நன்மை செய்யாமல் விட்டுவிட்டால் சான்றாண்மை எனும் நல்ல பண்பு இருந்தும் அதனால் என்ன பயன்?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(988,'988','இன்மை ஒருவற் கிளிவன்று சால்பென்னும்<br />திண்மையுண் டாகப் பெறின்','சால்பு என்கிற உறுதியைச் செல்வமெனக் கொண்டவருக்கு வறுமை என்பது இழிவு தரக் கூடியதல்ல',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(989,'989','ஊழி பெயரினும் தாம்பெயரார் சான்றாண்மைக்<br />காழி யெனப்படு வார்','தமக்குரிய கடமைகளைக் கண்ணியத்துடன் ஆற்றுகின்ற சான்றோர் எல்லாக் கடல்களும் தடம் புரண்டு மாறுகின்ற ஊழிக்காலம் ஏற்பட்டாலும்கூடத், தம்நிலை மாறாத கடலாகத் திகழ்வார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(990,'990','சான்றவர் சான்றாண்மை குன்றின் இருநிலத்தான்<br />தாங்காது மன்னோ பொறை','சான்றோரின் நற்பண்பே குறையத்தொடங்கினால் அதனை இந்த உலகம் பொறுமையுடன் தாங்கிக் கொள்ளாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(991,'991','எண்பதத்தால் எய்தல் எளிதென்ப யார்மாட்டும்<br />பண்புடைமை என்னும் வழக்கு','யாராயிருந்தாலும் அவர்களிடத்தில் எளிமையாகப் பழகினால், அதுவே பண்புடைமை என்கிற சிறந்த ஒழுக்கத்தைப் பெறுவதற்கு எளிதான வழியாக அமையும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(992,'992','அன்புடைமை ஆன்ற குடிப்பிறத்தல் இவ்விரண்டும்<br />பண்புடைமை என்னும் வழக்கு','அன்புடையவராக இருப்பதும், உயர்ந்த குடியில் பிறந்த இலக்கணத்துக்கு உரியவராக இருப்பதும்தான் பண்புடைமை எனக் கூறப்படுகிற சிறந்த நெறியாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(993,'993','உறுப்பொத்தல் மக்களொப் பன்றால் வெறுத்தக்க<br />பண்பொத்தல் ஒப்பதாம் ஒப்பு','நற்பண்பு இல்லாதவர்களை அவர்களின் உடல் உறுப்புகளை மட்டுமே ஒப்பிட்டுப் பார்த்து மக்கள் இனத்தில் சேர்த்துப் பேசுவது சரியல்ல; நற்பண்புகளால் ஒத்திருப்பவர்களே மக்கள் எனப்படுவர்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(994,'994','நயனொடு நன்றி புரிந்த பயனுடையார்<br />பண்புள ராட்டும் உலகு','நீதி வழுவாமல் நன்மைகளைச் செய்து பிறருக்குப் பயன்படப் பணியாற்றுகிறவர்களின் நல்ல பண்பை உலகம் பாராட்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(995,'995','நகையுள்ளும் இன்னா திகழ்ச்சி பகையுள்ளும்<br />பண்புள பாடறிவார் மாட்டு','விளையாட்டாகக்கூட ஒருவரை இகழ்ந்து பேசுவதால் கேடு உண்டாகும் அறிவு முதிர்ந்தவர்கள், பகைவரிடமும் பண்புகெடாமல் நடந்து கொள்வார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(996,'996','பண்புடையார்ப் பட்டுண் டுலகம் அதுவின்றேல்<br />மண்புக்கு மாய்வது மன்','உலக நடைமுறைகள், பண்பாளர்களைச் சார்ந்து இயங்க வேண்டும் இல்லையேல் அந்த நடைமுறைகள் நாசமாகிவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(997,'997','அரம்போலும் கூர்மைய ரேனும் மரம்போல்வர்<br />மக்கட்பண் பில்லா தவர்','அரம் போன்ற கூர்மையான அறிவுடைய மேதையாக இருந்தாலும், மக்களுக்குரிய பண்பு இல்லாதவர் மரத்துக்கு ஒப்பானவரேயாவார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(998,'998','நண்பாற்றார் ஆகி நயமில செய்வார்க்கும்<br />பண்பாற்றார் ஆதல் கடை','நட்புக்கு ஏற்றவராக இல்லாமல் தீமைகளையே செய்து கொண்டிருப்பவரிடம், நாம் பொறுமை காட்டிப் பண்புடையவராக நடந்து கொள்ளாவிட்டால் அது இழிவான செயலாகக் கருதப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(999,'999','நகல்வல்லர் அல���லார்க்கு மாயிரு ஞாலம்<br />பகலும்பாற் பட்டன் றிருள்','நண்பர்களுடன் பழகி மகிழத் தெரியாதவர்களுக்கு உலகம் என்பது பகலில் கூட இருட்டாகத்தான் இருக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1000,'1000','பண்பிலான் பெற்ற பெருஞ்செல்வம் நன்பால்<br />கலந்தீமை யால்திரிந் தற்று','பாத்திரம் களிம்பு பிடித்திருந்தால், அதில் ஊற்றி வைக்கப்படும் பால் எப்படிக் கெட்டுவிடுமோ அதுபோலப் பண்பு இல்லாதவர்கள் பெற்ற செல்வமும் பயனற்றதாகி விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1001,'1001','வைத்தான்வாய் சான்ற பெரும்பொருள் அஃதுண்ணான்<br />செத்தான் செயக்கிடந்த தில்','அடங்காத ஆசையினால் வீடு கொள்ளாத அளவுக்குச் செல்வத்தைச் சேர்த்து வைத்து அதனை அனுபவிக்காமல் செத்துப் போகிறவனுக்கு, அப்படிச் சேர்க்கப்பட்ட செல்வத்தினால் என்ன பயன்?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1002,'1002','பொருளானாம் எல்லாமென் றீயா திவறும்<br />மருளானாம் மாணாப் பிறப்பு','யாருக்கும் எதுவும் கொடுக்காமல், தன்னிடமுள்ள பொருளால் எல்லாம் ஆகுமென்று, அதனைவிடாமல் பற்றிக் கொண்டிருப்பவன் எந்தச் சிறப்புமில்லாத இழி பிறவியாவான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1003,'1003','ஈட்டம் இவறி இசைவேண்டா ஆடவர்<br />தோற்றம் நிலக்குப் பொறை','புகழை விரும்பாமல் பொருள் சேர்ப்பது ஒன்றிலேயே குறியாக இருப்பவர்கள் பிறந்து வாழ்வதே இந்தப் பூமிக்குப் பெரும் சுமையாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1004,'1004','எச்சமென் றென்னெண்ணுங் கொல்லோ ஒருவரால்<br />நச்சப் படாஅ தவன்','யாராலும் விரும்பப்படாத ஒருவன், தன் மரணத்திற்குப் பிறகு எஞ்சி நிற்கப் போவது என்று எதனை நினைத்திட முடியும்?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1005,'1005','கொடுப்பதூஉம் துய்ப்பதூஉம் இல்லார்க் கடுக்கிய<br />கோடியுண் டாயினும் இல்','கொடுத்து உதவும் பண்பினால் இன்பமுறும் இயல்பு இல்லாதவரிடம், கோடி கோடியாகச் செல்வம் குவிந்தாலும் அதனால் பயன் எதுவுமில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1006,'1006','ஏதம் பெருஞ்செல்வம் தான்துவ்வான் தக்கார்க்கொன்<br />றீத லியல்பிலா தான்','தானும் அனுபவிக்காமல் தக்கவர்களுக்கு உதவிடும் இயல்பும் இல்லாமல் வாழ்கிறவன், தன்னிடமுள்ள பெருஞ்செல்வத்தைத் தொற்றிக்கொண்ட நோயாவான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1007,'1007','அற்றார்க்கொன் றாற்றாதான் செல்வம் மிகநலம்<br />பெற்றாள் தமியள்மூத் தற்று','வறியவர்க்கு எதுவும் வழங்கி உதவாதவனுடைய செல்வம், மிகுந்த அழகியொருத்தி, தன்னந்தனியாகவே இருந்து முதுமையடைவதைப் போன்றது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1008,'1008','நச்சுப் படாதவன் செல்வம் நடுவூருள்<br />நச்சு மரம்பழுத் தற்று','வெறுக்கப்படுகிறவரிடம் குவிந்துள்ள செல்வமும், ஊர் நடுவே நச்சு மரத்தில் காய்த்துக் குலுங்குகின்ற பழமும் வெவ்வேறானவையல்ல!',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1009,'1009','அன்பொரீஇத் தற்செற் றறநோக்கா தீட்டிய<br />ஒண்பொருள் கொள்வார் பிறர்','அன்பெனும் பண்பை அறவே நீக்கி, தன்னையும் வருத்திக் கொண்டு, அறவழிக்குப் புறம்பாகச் சேர்த்துக் குவித்திடும் செல்வத்தைப் பிறர் கொள்ளை கொண்டு போய் விடுவர்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1010,'1010','சீருடைச் செல்வர் சிறுதுனி மாரி <br />வறங்கூர்ந் தனைய துடைத்து','சிறந்த உள்ளம் கொண்ட செல்வர்களுக்கேற்படும் சிறிதளவு வறுமையின் நிழல்கூட, மழை பொய்த்து விட்டதற்கு ஒப்பானதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1011,'1011','கருமத்தால் நாணுதல் நாணுத் திருநுதல்<br />நல்லவர் நாணுப் பிற','ஒருவர் தமது தகாத நடத்தையின் காரணமாக நாணுவதற்கும், நல்ல பெண்களுக்கு இயல்பாக ஏற்படும் நாணத்துக்கும் மிகுந்த வேறுபாடு உண்டு',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1012,'1012','ஊணுடை எச்சம் உயிர்க்கெல்லாம் வேறல்ல<br />நாணுடைமை மாந்தர் சிறப்பு','உணவு, உடை போன்ற அனைத்தும் எல்லோருக்கும் பொ���ுவான தேவைகளாக அமைகின்றன; ஆனால் சிறப்புக்குரிய தேவையாக இருப்பது, பிறரால் பழிக்கப்படும் செயல்களைத் தவிர்த்து வாழும் நாணுடைமைதான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1013,'1013','ஊனைக் குறித்த உயிரெல்லாம் நாணென்னும்<br />நன்மை குறித்தது சால்பு','உடலுடன் இணைந்தே உயிர் இருப்பது போல், மாண்பு என்பது நாண உணர்வுடன் இணைந்து இருப்பதேயாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1014,'1014','அணியன்றோ நாணுடைமை சான்றோர்க் கஃதின்றேற்<br />பிணியன்றோ பீடு நடை','நடந்த தவறு காரணமாகத் தமக்குள் வருந்துகிற நாணம் எனும் உணர்வு, பெரியவர்களுக்கு அணிகலன் ஆக விளங்கும் அந்த அணிகலன் இல்லாமல் என்னதான் பெருமிதமாக நடைபோட்டாலும், அந்த நடையை ஒரு நோய்க்கு ஒப்பானதாகவே கருத முடியும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1015,'1015','பிறர்பழியும் தம்பழியும் நாணுவார் நாணுக்<br />குறைபதி என்னும் உலகு','தமக்கு வரும் பழிக்காக மட்டுமின்றிப் பிறர்க்கு வரும் பழிக்காகவும் வருந்தி நாணுகின்றவர், நாணம் எனும் பண்பிற்கான உறைவிடமாவார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1016,'1016','நாண்வேலி கொள்ளாது மன்னோ வியன்ஞாலம்<br />பேணலர் மேலா யவர்','பரந்த இந்த உலகில் பாதுகாப்பையும்விட, நாணம் எனும் வேலியைத்தான் உயர்ந்த மனிதர்கள், தங்களின் பாதுகாப்பாகக் கொள்வார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1017,'1017','நாணால் உயிரைத் துறப்பர் உயிர்ப்பொருட்டால்<br />நாண்துறவார் நாணாள் பவர்','நாண உணர்வுடையவர்கள், மானத்தைக் காப்பாற்றிக் கொள்ள உயிரையும் விடுவார்கள் உயிரைக் காப்பாற்றிக் கொள்வதற்காக மானத்தை விடமாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1018,'1018','பிறர்நாணத் தக்கது தானாணா னாயின்<br />அறநாணத் தக்க துடைத்து','வெட்கப்படவேண்டிய அளவுக்குப் பழிக்கு ஆளானவர்கள் அதற்காக வெட்கப்படாமல் இருந்தால் அவர்களை விட்டு அறநெறி வெட்கப்பட்டு அகன்று விட்டதாகக் கருத வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1019,'1019','குலஞ்சுடும் கொள்கை பிழைப்பின் நலஞ்சுடும்<br />நாணின்மை நின்றக் கடை','கொண்ட கொள்கையில் தவறினால் குலத்துக்கு இழுக்கு நேரும் அதற்கு நாணாமல் பிறர் பழிக்கும் செயல் புரிந்தால் நலமனைத்தும் கெடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1020,'1020','நாணகத் தில்லார் இயக்கம் மரப்பாவை<br />நாணால் உயிர்மருட்டி அற்று','உயிர் இருப்பது போலக் கயிறுகொண்டு ஆட்டி வைக்கப்படும் மரப்பொம்மைக்கும், மனத்தில் நாணமெனும் ஓர் உணர்வு இல்லாமல் உலகில் நடமாடுபவருக்கும் வேறுபாடு இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1021,'1021','கருமம் செயவொருவன் கைதூவேன் என்னும்<br />பெருமையிற் பீடுடைய தில்','உரிய கடமையைச் செய்வதில் சோர்வு காணாமல் எவனொருவன் முயற்சிகளை விடாமல் மேற்கொள்கிறானோ அந்தப் பெருமைக்கு மேலாக வேறொரு பெருமை கிடையாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1022,'1022','ஆள்வினையும் ஆன்ற அறிவும் எனவிரண்டின்<br />நீள்வினையான் நீளும் குடி','ஆழ்ந்த அறிவும், விடாமுயற்சியும் கொண்டு ஒருவன் அயராது பாடுபட்டால், அவனைச் சேர்ந்துள்ள குடிமக்களின் பெருமை உயரும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1023,'1023','குடிசெய்வல் என்னும் ஒருவற்குத் தெய்வம்<br />மடிதற்றுத் தான்முந் துறும்','தன்னைச் சேர்ந்த குடிமக்களை உயர்வடையச் செய்திட ஓயாது உழைப்பவனுக்குத் தெய்வச் செயல் எனக்கூறப்படும் இயற்கையின் ஆற்றல் கூட வரிந்து கட்டிக்கொண்டு வந்து துணைபுரியும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1024,'1024','சூழாமல் தானே முடிவெய்தும் தங்குடியைத்<br />தாழா துஞற்று பவர்க்கு','தம்மைச் சார்ந்த குடிகளை உயர்த்தும் செயல்களில் காலம் தாழ்த்தாமல் ஈடுபட்டு முயலுகிறவர்களுக்குத் தாமாகவே வெற்றிகள் வந்து குவிந்துவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1025,'1025','குற்றம் இலனாய்க் குடிசெய்து வாழ்வானைச்<br />சுற்றமாச் சு���்றும் உலகு','குற்றமற்றவனாகவும், குடிமக்களின் நலத்திற்குப் பாடுபடுபவனாகவும் இருப்பவனைத் தமது உறவினனாகக் கருதி, மக்கள் சூழ்ந்து கொள்வார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1026,'1026','நல்லாண்மை என்ப தொருவற்குத் தான்பிறந்த<br />இல்லாண்மை ஆக்கிக் கொளல்','நல்ல முறையில் ஆளும் திறமை பெற்றவர், தான் பிறந்த குடிக்கே பெருமை சேர்ப்பவராவார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1027,'1027','அமரகத்து வன்கண்ணர் போலத் தமரகத்தும்<br />ஆற்றுவார் மேற்றே பொறை','போர்க்களத்தில் எதிர்ப்புகளைத் தாங்கிப் படை நடத்தும் பொறுப்பு அதற்கான ஆற்றல் படைத்தவர்களிடம் இருப்பது போலத்தான் குடிமக்களைக் காப்பாற்றி உயர்வடையச் செய்யும் பொறுப்பும் அவர்களைச் சேர்ந்த ஆற்றலாளர்களுக்கே உண்டு',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1028,'1028','குடிசெய்வார்க் கில்லை பருவம் மடிசெய்து<br />மானங் கருதக் கெடும்','தன்மீது நடத்தப்படும் இழிவான தாக்குதலைக் கண்டு கலங்கினாலோ, பணியாற்றக் காலம் வரட்டும் என்று சோர்வுடன் தயக்கம் காட்டினாலோ குடிமக்களின் நலன் சீர்குலைத்துவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1029,'1029','இடும்பைக்கே கொள்கலம் கொல்லோ குடும்பத்தைக்<br />குற்ற மறைப்பான் உடம்பு','தன்னைச் சார்ந்துள்ள குடிகளுக்குத் துன்பம் வராமல் தடுத்துத் தொடர்ந்து அக்குடிகளைக் காப்பாற்ற முயலுகிற ஒருவன், துன்பத்தைத் தாங்கி கொள்ளவே பிறந்தவனாகப் போற்றப்படுவான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1030,'1030','இடுக்கண்கால் கொன்றிட வீழும் அடுத்தூன்றும்<br />நல்லாள் இலாத குடி','வரும் துன்பத்தை எதிர் நின்று தாங்கக் கூடிய ஆற்றலுடையவர் இல்லாத குடியை அத்துன்பம், வென்று வீழ்த்திவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1031,'1031','சுழன்றும்ஏர்ப் பின்ன துலகம் அதனால்<br />உழந்தும் உழவே தலை','பல தொழில்களைச் செய்து சுழன்று கொண்டிருக்கும் இந்த உலகம், ஏர்த்தொழிலின் பின்னேதான் சுற்ற வேண்டியிருக்கிறது எனவே எவ்வளவுதான் துன்பம் இருப்பினும் உழவுத் தொழிலே சிறந்தது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1032,'1032','உழுவார் உலகத்தார்க் காணியஃ தாற்றா<br />தெழுவாரை எல்லாம் பொறுத்து','பல்வேறு தொழில் புரிகின்ற மக்களின் பசி போக்கிடும் தொழிலாக உழவுத் தொழில் இருப்பதால் அதுவே உலகத்தாரைத் தாங்கி நிற்கும் அச்சாணி எனப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1033,'1033','உழுதுண்டு வாழ்வாரே வாழ்வார்மற் றெல்லாம்<br />தொழுதுண்டு பின்செல் பவர்','உழுதுண்டு வாழ்பவர்களே உயர்ந்த வாழ்வினர்; ஏனென்றால், மற்றவர்கள் அவர்களைத் தொழுதுண்டு வாழ வேண்டியிருக்கிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1034,'1034','பலகுடை நீழலும் தங்குடைக்கீழ்க் காண்பர்<br />அலகுடை நீழ லவர்','பல அரசுகளின் நிழல்களைத் தமது குடைநிழலின் கீழ் கொண்டு வரும் வலிமை பெற்றவர்கள் உழவர்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1035,'1035','இரவார் இரப்பார்க்கொன் றீவர் கரவாது<br />கைசெய்தூண் மாலை யவர்','தாமே தொழில் செய்து ஊதியம் பெற்று உண்ணும் இயல்புடையவர், பிறரிடம் சென்று கையேந்த மாட்டார், தம்மிடம் வேண்டி நின்றவர்க்கும் ஒளிக்காமல் வழங்குவார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1036,'1036','உழவினார் கைம்மடங்கின் இல்லை விழைவதூஉம்<br />விட்டேமென் பார்க்கும் நிலை','எல்லாப் பற்றையும் விட்டுவிட்டதாகக் கூறும் துறவிகள்கூட உழவரின் கையை எதிர்பார்த்துதான் வாழ வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1037,'1037','தொடிப்புழுதி கஃசா உணக்கின் பிடித்தெருவும்<br />வேண்டாது சாலப் படும்','ஒருபலம் புழுதி, காற்பலம் ஆகிற அளவுக்குப் பலமுறை உழுதாலே ஒரு பிடி எருவும் தேவையின்றிப் பயிர் செழித்து வளரும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1038,'1038','ஏரினும் நன்றால் எருவிடுதல் கட்டபின்<br />நீரினும் நன்றதன் காப்பு','உழுவதைக் காட்டிலும் உரம் இடுதல் நல்லது; களை எடுப்பதும், நீர் பாய்ச்சுவதும் மிகவும் நல்லது; அதைவிட நல்லது அந்தப் பயிரைப் பாதுகாப்பது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1039,'1039','செல்லான் கிழவன் இருப்பின் நிலம்புலந்<br />தில்லாளின் ஊடி விடும்','உழவன், தனது நிலத்தை நாள்தோறும் சென்று கவனிக்காமல் இருந்தால், அவனால் வெறுப்புற்று விலகியிருக்கும் மனைவிபோல அது விளைச்சலின்றிப் போய்விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1040,'1040','இலமென் றசைஇ இருப்பாரைக் காணின்<br />நிலமென்னும் நல்லாள் நகும்','வாழ வழியில்லை என்று கூறிக்கொண்டு சோம்பலாய் இருப்பவரைப் பார்த்துப் பூமித்தாய் கேலி புரிவாள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1041,'1041','இன்மையின் இன்னாத தியாதெனின் இன்மையின்<br />இன்மையே இன்னா தது','வறுமைத் துன்பத்துக்கு உவமையாகக் காட்டுவதற்கு வறுமைத் துன்பத்தைத் தவிர வேறு துன்பம் எதுவுமில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1042,'1042','இன்மை எனவொரு பாவி மறுமையும்<br />இம்மையும் இன்றி வரும்','பாவி என இகழப்படுகின்ற வறுமைக் கொடுமை ஒருவருக்கு ஏற்பட்டுவிட்டால் அவருக்கு நிகழ்காலத்திலும், வருங்காலத்திலும் நிம்மதி என்பது கிடையாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1043,'1043','தொல்வரவும் தோலும் கெடுக்கும் தொகையாக<br />நல்குர வென்னும் நசை','ஒருவனுக்கு வறுமையின் காரணமாகப் பேராசை ஏற்படுமேயானால், அது அவனுடைய பரம்பரைப் பெருமையையும், புகழையும் ஒரு சேரக் கெடுத்துவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1044,'1044','இற்பிறந்தார் கண்ணேயும் இன்மை இளிவந்த<br />சொற்பிறக்கும் சோர்வு தரும்','இல்லாமை எனும் கொடுமை, நல்ல குடியில் பிறந்தவர்களிடம் இழிந்த சொல் பிறப்பதற்கான சோர்வை உருவாக்கி விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1045,'1045','நல்குர வென்னும் இடும்பையுள் பல்குரைத்<br />துன்பங்கள் சென்று படும்','வறுமையெனும் துன்பத்திற்குள்ளிருந்து பல்வேறு வகையான துன்பங்கள் கிளர்ந்தெழும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1046,'1046','நற்பொருள் நன்குணர்ந்து சொல்லினும் நல்கூர்ந்தார்<br />சொற்பொருள் சோர்வு படும்','அரிய பல் நூல்களின் கருத்துகளையும் ஆய்ந்துணர்ந்து சொன்னாலும், அதனைச் சொல்பவர் வறியவராக இருப்பின் அக்கருத்து எடுபடாமற் போகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1047,'1047','அறஞ்சாரா நல்குர வீன்றதா யானும்<br />பிறன்போல நோக்கப் படும்','வறுமை வந்தது என்பதற்காக, அறநெறியிலிருந்து விலகி நிற்பவனை, அவன் தாய்கூட அயலானைப் போல்தான் கருதுவாள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1048,'1048','இன்றும் வருவது கொல்லோ நெருநலும்<br />கொன்றது போலும் நிரப்பு','கொலை செய்வதுபோல நேற்றுக் கொடுமைப்படுத்திய வறுமை, தொடர்ந்து இன்றைக்கும் வராமல் இருக்க வேண்டுமே என்று வறியவன் ஏங்குவான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1049,'1049','நெருப்பினுள் துஞ்சலும் ஆகும் நிரப்பினுள்<br />யாதொன்றும் கண்பா டரிது','நெருப்புக்குள் படுத்துக் தூங்குவதைகூட ஒரு மனிதனால் முடியும்; ஆனால் வறுமை படுத்தும் பாட்டில் தூங்குவது என்பது இயலாத ஒன்றாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1050,'1050','துப்புர வில்லார் துவரத் துறவாமை<br />உப்பிற்கும் காடிக்கும் கூற்று','ஒழுங்குமறையற்றதால் வறுமையுற்றோர், முழுமையாகத் தம்மைத் துறக்காமல் உயிர்வாழ்வது, உப்புக்கும் கஞ்சிக்கும்தான் கேடு',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1051,'1051','இரக்க இரத்தக்கார்க் காணின் கரப்பின்<br />அவர்பழி தம்பழி அன்று','கொடுக்கக்கூடிய தகுதி படைத்தவரிடத்திலே ஒன்றைக் கேட்டு, அதை அவர் இருந்தும் இல்லையென்று சொன்னால், அப்படிச் சொன்னவருக்குத்தான் பழியே தவிர கேட்டவருக்கு அல்ல',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1052,'1052','இன்பம் ஒருவற் கிரத்தல் இரந்தவை<br />துன்பம் உறாஅ வரின்','வழங்குபவர், வாங்குபவர் ஆகிய இருவர் மனத்திற்கும் துன்பம் எதுவுமின்றி ஒரு பொர��ள் கிடைக்குமானால், அப்பொருள் இரந்து பெற்றதாக இருப்பினும் அதனால் இன்பமே உண்டாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1053,'1053','கரப்பிலா நெஞ்சிற் கடனறிவார் முன்னின்<br />றிரப்புமோர் ஏஎர் உடைத்து','உள்ளதை ஒளிக்காத உள்ளமும், கடமையுணர்வும் கொண்டவரிடத்தில் தனது வறுமை காரணமாக இரந்து கேட்பதும் பெருமையுடையதே யாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1054,'1054','இரத்தலும் ஈதலே போலும் கரத்தல்<br />கனவிலும் தேற்றாதார் மாட்டு','இருக்கும்போது இல்லையென்று கைவிரிப்பதைக் கனவிலும் நினைக்காதவரிடத்தில், இல்லாதார் இரந்து கேட்பது பிறருக்கு ஈவது போன்ற பெருமையுடையதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1055,'1055','கரப்பிலார் வையகத் துண்மையாற் கண்ணின் <br />றிரப்பவர் மேற்கொள் வது','உள்ளதை இல்லையென்று மறைக்காமல் வழங்கிடும் பண்புடையோர் உலகில் இருப்பதால்தான் இல்லாதவர்கள், அவர்களிடம் சென்று இரத்தலை மேற்கொண்டுள்ளனர்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1056,'1056','கரப்பிடும்பை யில்லாரைக் காணின் நிரப்பிடும்பை<br />யெல்லா மொருங்கு கெடும்','இருப்பதைக் கொடுக்க மனமின்றி மறைத்திடும் இழிநிலை இல்லாதவர்களைக் கண்டாலே, இரப்போரின் வறுமைத் துன்பம் அகன்று விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1057,'1057','இகழ்ந்தெள்ளா தீவாரைக் காணின் மகிழ்ந்துள்ளம்<br />உள்ளுள் உவப்ப துடைத்து','இழித்துப் பேசாமலும், ஏளனம் புரியாமலும் வழங்கிடும் வள்ளல் தன்மை உடையவர்களைக் காணும்போது, இரப்போர் உள்ளம் மகிழ்ச்சியால் இன்பமுறும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1058,'1058','இரப்பாரை யில்லாயின் ஈர்ங்கண்மா ஞாலம்<br />மரப்பாவை சென்றுவந் தற்று','வறுமையின் காரணமாக யாசிப்பவர்கள், தம்மை நெருங்கக் கூடாது என்கிற மனிதர்களுக்கும், மரத்தால் செய்யப்பட்டு இயக்கப்படும் பதுமைகளுக்கும் வேறுபாடே இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1059,'1059','ஈவார்கண் என்னுண்டாம் தோற்றம் இரந்துகோள்<br />மேவார் இலாஅக் கடை','இரந்து பொருள் பெறுபவர் இல்லாத நிலையில், பொருள் கொடுத்துப் புகழ் பெறுவதற்கான வாய்ப்பு இல்லாமற் போய்விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1060,'1060','இரப்பான் வெகுளாமை வேண்டும் நிரப்பிடும்பை<br />தானேயும் சாலும் கரி','இல்லை என்பவரிடம், இரப்பவன் கோபம் கொள்ளக்கூடாது தன்னைப் போலவே பிறர் நிலைமையும் இருக்கலாம் என்பதற்குத் தன் வறுமையே சான்றாக இருக்கிறதே',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1061,'1061','கரவா துவந்தீயும் கண்ணன்னார் கண்ணும்<br />இரவாமை கோடி உறும்','இருப்பதை ஒளிக்காமல் வழங்கிடும் இரக்கச்சிந்தையுடைவரிடம்கூட, இரவாமல் இருப்பது கோடி மடங்கு உயர்வுடையதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1062,'1062','இரந்தும் உயிர்வாழ்தல் வேண்டின் பரந்து<br />கெடுக உலகியற்றி யான்','பிச்சையெடுத்துதான் சிலர் உயிர்வாழ வேண்டும் என்ற நிலையிருந்தால் இந்த உலகத்தைப் படைத்தவனாகச் சொல்லப்படுபவனும் கெட்டொழிந்து திரியட்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1063,'1063','இன்மை இடும்பை இரந்துதீர் வாமென்னும்<br />வன்மையின் வன்பாட்ட தில்','வறுமைக்கொடுமையைப் பிறரிடம் இரந்து போக்கிக் கொள்ளலாம் என்று கருதும் கொடுமையைப் போல் வேறொரு கொடுமை இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1064,'1064','இடமெல்லாம் கொள்ளாத் தகைத்தே இடமில்லாக்<br />காலும் இரவொல்லாச் சால்பு','வாழ்வதற்கு ஒரு வழியும் கிடைக்காத நிலையிலும் பிறரிடம் கையேந்திட நினைக்காத பண்புக்கு, இந்த வையகமே ஈடாகாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1065,'1065','தெண்ணீர் அடுபுற்கை ஆயினும் தாள்தந்த<br />துண்ணலின் ஊங்கினிய தில்','கூழ்தான் குடிக்கவேண்டிய நிலை என்றாலும், அதையும் தானே உழைத்துச் சம்பாதித்துக் குடித்தால் அதைவிட இனிமையானது வேறொன்றும் இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1066,'1066','ஆவிற்கு நீரென் றிரப்பினும��� நாவிற்<br />கிரவின் இளிவந்த தில்','தாகம் கொண்டு தவிக்கும் பசுவுக்காகத் தண்ணீர் வேண்டுமென இரந்து கேட்டாலும்கூட, அப்படிக் கேட்கும் நாவுக்கு, அதைவிட இழிவானது வேறொன்றுமில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1067,'1067','இரப்பான் இரப்பாரை எல்லாம் இரப்பிற்<br />கரப்பார் இரவன்மின் என்று','கையில் உள்ளதை மறைத்து இல்லை என்போரிடம் கையேந்த வேண்டாமென்று கையேந்துபவர்களையெல்லாம் கையேந்திக்கேட்டு கொள்கிறேன்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1068,'1068','இரவென்னும் ஏமாப்பில் தோணி கரவென்னும்<br />பார்தாக்கப் பக்கு விடும்','இருப்பதை மறைத்து இல்லையென்று கூறும் கல் நெஞ்சின் மீது, இரத்தல் எனப்படும் பாதுகாப்பற்ற தோணி மோதினால் பிளந்து நொறுங்கிவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1069,'1069','இரவுள்ள உள்ளம் உருகும் கரவுள்ள<br />உள்ளதூஉம் இன்றிக் கெடும்','இரந்து வாழ்வோர் நிலையை நினைக்கும் போது உள்ளம் உருகுகிறது, இருப்பதைக் கொடுக்க மனமின்றி மறைத்து வாழ்பவரை நினைத்தால் உருகிடவும் வழியின்றி உள்ளமே ஒழிந்து விடுகிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1070,'1070','கரப்பவர்க் கியாங்கொளிக்கும் கொல்லோ இரப்பவர்<br />சொல்லாடப் போஒம் உயிர்','இருப்பதை ஒளித்துக்கொண்டு `இல்லை என்பவர்களின் சொல்லைக் கேட்டவுடன், இரப்போரின் உயிரே போய் விடுகிறதே; அப்படிச் சொல்பவர்களின் உயிர் மட்டும் எங்கே ஒளிந்துகொண்டு இருக்குமோ?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1071,'1071','மக்களே போல்வர் கயவர் அவரன்ன<br />ஒப்பாரி யாங்கண்ட தில்','குணத்தில் கயவராக இருப்பர் ஆனால், நல்லவரைப் போலக் காட்டிக் கொள்வார் மனிதர்களிடம் மட்டும்தான் இப்படி இருவகையான நிலைகளை ஒரே உருவத்தில் காண முடியும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1072,'1072','நன்றறி வாரிற் கயவர் திருவுடையர்<br />நெஞ்சத் தவலம் இலர்','எப்போதும் நல்லவை பற்றியே சிந்தித்துக் கவலைப்பட்டுக் கொண்டிருப்பவர்களைவிட எதைப் பற்றியும் கவலைப்படாமலிருக்கும் கயவர்கள் ஒரு வகையில் பாக்கியசாலிகள்தான்!',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1073,'1073','தேவ ரனையர் கயவர் அவருந்தாம்<br />மேவன செய்தொழுக லான்','புராணங்களில் வரும் தேவர்களைப் போல் மனம் விரும்பியதையெல்லாம் செய்யக்கூடியவர்கள் கயவர்கள் என்பதால், இருவரையும் சமமாகக் கருதலாம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1074,'1074','அகப்பட்டி ஆவாரைக் காணின் அவரின் <br />மிகப்பட்டுச் செம்மாக்கும் கீழ்','பண்பாடு இல்லாத கயவர்கள், தம்மைக் காட்டிலும் இழிவான குணமுடையோரைக் கண்டால், அவர்களைவிடத் தாம் சிறந்தவர்கள் என்ற கர்வம் கொள்வார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1075,'1075','அச்சமே கீழ்கள தாசாரம் எச்சம்<br />அவாவுண்டேல் உண்டாம் சிறிது','தாங்கள் விரும்புவது கிடைக்கும் என்ற நிலையேற்படும்போது கீழ்மக்கள், தங்களை ஒழுக்கமுடையவர்கள் போலக் காட்டிக் கொள்வார்கள் மற்ற சமயங்களில் அவர்கள் பயத்தின் காரணமாக மட்டுமே ஓரளவு ஒழுக்கமுள்ளவர்களாக நடந்து கொள்வார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1076,'1076','அறைபறை அன்னர் கயவர்தாம் கேட்ட<br />மறைபிறர்க் குய்த்துரைக்க லான்','மறைக்கப்பட வேண்டிய இரகசியம் ஒன்றைக் கேட்ட மாத்திரத்தில், ஓடிச் சென்று பிறருக்குச் சொல்லுகிற கயவர்களைத், தமுக்கு என்னும் கருவிக்கு ஒப்பிடலாம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1077,'1077','ஈர்ங்கை விதிரார் கயவர் கொடிறுடைக்குங்<br />கூன்கையர் அல்லா தவர்க்கு','கையை மடக்கிக் கன்னத்தில் ஒரு குத்துவிடுகின்ற முரடர்களுக்குக் கொடுப்பார்களேயல்லாமல், ஈகைக் குணமில்லாத கயவர்கள் ஏழை எளியோருக்காகத் தமது எச்சில் கைகைக்கூட உதற மாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1078,'1078','சொல்லப் பயன்படுவர் சான்றோர் கரும்புபோல<br />கொல்லப் பயன்படும் கீழ்','குறைகளைச் சொன்னவுடனே சான்றோரிடம் கோரி பயனைப் பெற முடியும்; ஆனால் கயவரிடமோ கரும்பை நசுக்கிப் பிழிவதுபோல், போராடித்தான் கோரிய பயனைப் பெற முடியும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1079,'1079','உடுப்பதூஉம் உண்பதூஉம் காணின் பிறர்மேல்<br />வடுக்காண வற்றாகும் கீழ்','ஒருவர் உடுப்பதையும் உண்பதையும் கண்டுகூட பெறாமைப்படுகிற கயவன், அவர்மீது வேண்டுமென்றே குற்றம் கூறுவதில் வல்லவனாக இருப்பான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1080,'1080','எற்றிற் குரியர் கயவரொன் றுற்றக்கால்<br />விற்றற் குரியர் விரைந்து','ஒரு துன்பம் வரும்போது அதிலிருந்து தப்பித்துக் கொள்ள, தம்மையே பிறரிடம் விற்றுவிடுகிற தகுதிதான் கயவர்களுக்குரிய தகுதியாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1081,'1081','அணங்குகொல் ஆய்மயில் கொல்லோ கனங்குழை<br />மாதர்கொல் மாலுமென் நெஞ்சு','எனை வாட்டும் அழகோ! வண்ண மயிலோ! இந்த மங்கையைக் கண்டு மயங்குகிறதே நெஞ்சம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1082,'1082','நோக்கினாள் நோக்கெதிர் நோக்குதல் தாக்கணங்கு<br />தானைக்கொண் டன்ன துடைத்து','அவள் வீசிடும் விழிவேலுக்கு எதிராக நான் அவளை நோக்க, அக்கணமே அவள் என்னைத் திரும்ப நோக்கியது தானெருத்தி மட்டும் தாக்குவது போதாதென்று, ஒரு தானையுடன் வந்து என்னைத் தாக்குவது போன்று இருந்தது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1083,'1083','பண்டறியேன் கூற்றென் பதனை இனியறிந்தேன்<br />பெண்டகையால் பேரமர்க் கட்டு','கூற்றுவன் எனப்படும் பொல்லாத எமனை, எனக்கு முன்பெல்லாம் தெரியாது; இப்போது தெரிந்து கொண்டேன் அந்த எமன் என்பவன் பெண்ணுருவத்தில் வந்து போர் தொடுக்கக்கூடிய விழியம்புகளை உடையவன் என்ற உண்மையை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1084,'1084','கண்டார் உயிருண்ணும் தோற்றத்தால் பெண்டகைப்<br />பேதைக் கமர்த்தன கண்','பெண்மையின் வார்ப்படமாகத் திகழுகிற இந்தப் பேதையின் கண்கள் மட்டும் உயிரைப் பறிப்பதுபோல் தோன்றுகின்றனவே! ஏனிந்த மாறுபாடு?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1085,'1085','கூற்றமோ கண்ணோ பிணையோ மடவரல்<br />நோக்கமிம் மூன்றும் உடைத்து','உயிர்பறிக்கும் கூற்றமோ? உறவாடும் விழியோ? மருட்சிகொள்ளும் பெண்மானோ? இளம் பெண்ணின் பார்வை இந்த மூன்று கேள்விகளையும் எழுப்புகிறதே',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1086,'1086','கொடும்புருவம் கோடா மறைப்பின் நடுங்கஞர்<br />செய்யல மன்னிவள் கண்','புருவங்கள் வளைந்து கோணாமல் நேராக இருந்து மறைக்குமானால், இவள் கண்கள், நான் நடுங்கும்படியான துன்பத்தைச் செய்யமாட்டா',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1087,'1087','கடாஅக் களிற்றின்மேற் கட்படாம் மாதர்<br />படாஅ முலைமேல் துகில்','மதங்கொண்ட யானையின் மத்தகத்தின் மேலிட்ட முகபடாம் கண்டேன்; அது மங்கையொருத்தியின் சாயாத கொங்கை மேல் அசைந்தாடும் ஆடைபோல் இருந்தது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1088,'1088','ஒண்ணுதற் கோஒ உடைந்ததே ஞாட்பினுள்<br />நண்ணாரும் உட்குமென் பீடு','களத்தில் பகைவரைக் கலங்கவைக்கும் என் வலிமை இதோ இந்தக் காதலியின் ஒளி பொருந்திய நெற்றிக்கு வளைந்து கொடுத்துவிட்டதே!',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1089,'1089','பிணையேர் மடநோக்கும் நாணும் உடையாட்<br />கணியெவனோ ஏதில தந்து','பெண்மானைப் போன்ற இளமை துள்ளும் பார்வையையும், நாணத்தையும் இயற்கையாகவே அணிகலன்களாகக் கொண்ட இப்பேரழகிக்குச் செயற்கையான அணிகலன்கள் எதற்காக?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1090,'1090','உண்டார்க ணல்லது அடுநறாக் காமம்போல்<br />கண்டார் மகிழ்செய்தல் இன்று','மதுவை உண்டால்தான் மயக்கம் வரும்; ஆனால், கண்டாலே மயக்கம் தருவது காதல்தான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1091,'1091','இருநோக் கிவளுண்கண் உள்ள தொருநோக்கு<br />நோய்நோக்கொன் றந்நோய் மருந்து','காதலியின் மைதீட்டிய கண்களில் இரண்டு வகையான பார்வைகள் இருக்கின்றன; ஒரு பார்வை காதல் நோயைத் தரும் பார்வை; மற்றொரு பார்வை அந்த நோய்க்கு மருந்���ளிக்கும் பார்வை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1092,'1092','கண்களவு கொள்ளும் சிறுநோக்கம் காமத்தில்<br />செம்பாகம் அன்று பெரிது','கள்ளத்தனமான அந்தக் கடைக்கண் பார்வை, காம இன்பத்தின் பாதியளவைக் காட்டிலும் பெரிது!',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1093,'1093','நோக்கினாள் நோக்கி இறைஞ்சினாள் அஃதவள்<br />யாப்பினுள் அட்டிய நீர்','கடைக்கண்ணால் அவள் என்னைப் பார்த்த பார்வையில் நாணம் மிகுந்திருந்தது; அந்தச் செயல் அவள் என்மீது கொண்ட அன்புப் பயிருக்கு நீராக இருந்தது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1094,'1094','யானோக்கும் காலை நிலனோக்கும் நோக்காக்கால்<br />தானோக்கி மெல்ல நகும்','நான் பார்க்கும்போது குனிந்து நிலத்தைப் பார்ப்பதும், நான் பார்க்காத போது என்னைப் பார்த்துத் தனக்குள் மகிழ்ந்து புன்னகை புரிவதும் என் மீது கொண்டுள்ள காதலை அறிவிக்கும் குறிப்பல்லவா?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1095,'1095','குறிக்கொண்டு நோக்காமை அல்லால் ஒருகண்<br />சிறக்கணித்தாள் போல நகும்','அவள் என்னை நேராக உற்றுப் பார்க்கவில்லையே தவிர, ஒரு கண்ணைச் சுருக்கி வைத்துக் கொண்டதைப் போல என்னை நோக்கியவாறு தனக்குள் மகிழ்கிறாள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1096,'1096','உறாஅ தவர்போற் சொலினும் செறாஅர்சொல்<br />ஒல்லை உணரப் படும்','காதலை மறைத்துக் கொண்டு, புறத்தில் அயலார் போலக் கடுமொழி கூறினாலும், அவள் அகத்தில் கோபமின்றி அன்பு கொண்டிருப்பது விரைவில் வெளிப்பட்டுவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1097,'1097','செறாஅச் சிறுசொல்லும் செற்றார்போல் நோக்கும்<br />உறாஅர்போன் றுற்றார் குறிப்பு','பகையுணர்வு இல்லாத கடுமொழியும், பகைவரை நோக்குவது போன்ற கடுவிழியும், வெளியில் அயலார் போல நடித்துக்கொண்டு உள்ளத்தால் அன்பு கொண்டிருப்பவரை அடையாளம் காட்டும் குறிப்புகளாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1098,'1098','அசையியற் குண்டாண்டோர் ஏஎர்யான் நோக்கப் <br />பசையினள் பைய நகும்','நான் பார்க்கும் போது என் மீது பரிவு கொண்டவளாக மெல்லச் சிரிப்பாள்; அப்போது, துவளுகின்ற அந்தத் துடியிடையாள் ஒரு புதிய பொலிவுடன் தோன்றுகிறாள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1099,'1099','ஏதிலார் போலப் பொதுநோக்கு நோக்குதல்<br />காதலார் கண்ணே உள','காதலர்களுக்கு ஓர் இயல்பு உண்டு; அதாவது, அவர்கள் பொது இடத்தில் ஒருவரையொருவர் அந்நியரைப் பார்ப்பதுபோலப் பார்த்துக்கொள்வர்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1100,'1100','கண்ணொடு கண்இணை நோக்கொக்கின் வாய்ச்சொற்கள்<br />என்ன பயனும் இல','ஒத்த அன்புடன் கண்களோடு கண்கள் கலந்து ஒன்றுபட்டு விடுமானால், வாய்ச்சொற்கள் தேவையற்றுப் போகின்றன',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1101,'1101','கண்டுகேட் டுண்டுயிர்த் துற்றறியும் ஐம்புலனும்<br />ஒண்தொடி கண்ணே உள','வளையல் அணிந்த இந்த வடிவழகியிடம்; கண்டு மகிழவும், கேட்டு மகிழவும், தொட்டு மகிழவும், முகர்ந்துண்டு மகிழவுமான ஐம்புல இன்பங்களும் நிறைந்துள்ளன',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1102,'1102','பிணிக்கு மருந்து பிறமன் அணியிழை<br />தன்னோய்க்குத் தானே மருந்து','நோய்களைத் தீர்க்கும் மருந்துகள் பல உள்ளன; ஆனால் காதல் நோயைத் தீர்க்கும் மருந்து அந்தக் காதலியே ஆவாள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1103,'1103','தாம்வீழ்வார் மென்றோள் துயிலின் இனிதுகொல்<br />தாமரைக் கண்ணான் உலகு','தாமரைக் கண்ணான் உலகம் என்றெல்லாம் சொல்கிறார்களே, அது என்ன! அன்பு நிறைந்த காதலியின் தோளில் சாய்ந்து துயில்வது போல அவ்வளவு இனிமை வாய்ந்ததா?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1104,'1104','நீங்கின் தெறூஉம் குறுகுங்கால் தண்ணென்னும்<br />தீயாண்டுப் பெற்றாள் இவள்','நீங்கினால் சுடக்கூடியதும் நெருங்கினால் குளிரக் கூடியதுமான புதுமையான நெருப்பை இந்த மங்கை எங்கிருந்து பெற்றாள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1105,'1105','வேட்ட பொழுத���ன் அவையவை போலுமே<br />தோட்டார் கதுப்பினாள் தோள்','விருப்பமான பொருள் ஒன்று, விரும்பிய பொழுதெல்லாம் வந்து இன்பம் வழங்கினால் எப்படியிருக்குமோ அதைப் போலவே பூ முடித்த பூவையின் தோள்கள் இன்பம் வழங்குகின்றன',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1106,'1106','உறுதோ றுயிர்தளிர்ப்பத் தீண்டலாற் பேதைக்<br />கமிழ்தின் இயன்றன தோள்','இந்த இளமங்கையைத் தழுவும் போதெல்லாம் நான் புத்துயிர் பெறுவதற்கு இவளின் அழகிய தோள்கள் அமிழ்தத்தினால் ஆனவை என்பதுதான் காரணம் போலும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1107,'1107','தம்மில் இருந்து தமதுபாத் துண்டற்றால்<br />அம்மா அரிவை முயக்கு','தானே உழைத்துச் சேர்த்ததைப் பலருக்கும் பகுத்து வழங்கி உண்டு களிப்பதில் ஏற்படும் இன்பம், தனது அழகிய காதல் மனைவியைத் தழுவுகின்ற இன்பத்துக்கு ஒப்பானது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1108,'1108','வீழும் இருவர்க் கினிதே வளியிடை<br />போழப் படாஅ முயக்கு','காதலர்க்கு மிக இனிமை தருவது, காற்றுகூட இடையில் நுழைய முடியாத அளவுக்கு இருவரும் இறுகத் தழுவி மகிழ்வதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1109,'1109','ஊடல் உணர்தல் புணர்தல் இவைகாமம்<br />கூடியார் பெற்ற பயன்','ஊடல் கொள்வதும், அதனால் விளையும் இன்பம் போதுமென உணர்ந்து அதற்கும் மேலான இன்பம் காணப் புணர்ந்து மயங்குவதும் காதல் வாழ்வினர் பெற்றிடும் பயன்களாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1110,'1110','அறிதோ றறியாமை கண்டற்றால் காமம்<br />செறிதோறும் சேயிழை மாட்டு','மாம்பழ மேனியில் அழகிய அணிகலன்கள் பூண்ட மங்கையிடம் இன்பம் நுகரும் போதெல்லாம் ஏற்படும் காதலானது, இதுவரை அறியாதவற்றைப் புதிதுபுதிதாக அறிவதுபோல் இருக்கிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1111,'1111','நன்னீரை வாழி அனிச்சமே நின்னினும்<br />மென்னீரள் யாம்வீழ் பவள்','அனிச்ச மலரின் மென்மையைப் புகழ்ந்து பாராட்டுகிறேன்; ஆனால் அந்த மலரைவிட மென்மையானவள் என் காதலி',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1112,'1112','மலர்காணின் மையாத்தி நெஞ்சே இவள்கண்<br />பலர்காணும் பூவொக்கும் என்று','மலரைக்கண்டு மயங்குகின்ற நெஞ்சமே! இவளுடைய கண்ணைப் பார்; பலரும் கண்டு வியக்கும் மலராகவே திகழ்கிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1113,'1113','முறிமேனி முத்தம் முறுவல் வெறிநாற்றம்<br />வேலுண்கண் வேய்த்தோ ளவட்கு','முத்துப்பல் வரிசை, மூங்கிலனைய தோள், மாந்தளிர் மேனி, மயக்கமூட்டும் நறுமணம், மையெழுதிய வேல்விழி; அவளே என் காதலி!',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1114,'1114','காணின் குவளை கவிழ்ந்து நிலனோக்கும்<br />மாணிழை கண்ணொவ்வேம் என்று','என் காதலியைக் குவளை மலர்கள் காண முடிந்தால், ``இவள் கண்களுக்கு நாம் ஒப்பாக முடியவில்லையே! எனத் தலைகுனிந்து நிலம் நோக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1115,'1115','அனிச்சப்பூக் கால்களையாள் பெய்தாள் நுசுப்பிற்கு<br />நல்ல படாஅ பறை','அவளுக்காக நல்லபறை ஒலிக்கவில்லை; ஏனெனில் அவள் இடை ஒடிந்து வீழ்ந்துவிட்டாள்; காரணம், அவள் அனிச்ச மலர்களைக் காம்பு நீக்காமல் தலையில் வைத்துக்கொண்டதுதான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1116,'1116','மதியும் மடந்தை முகனும் அறியா<br />பதியின் கலங்கிய மீன்','மங்கையின் முகத்துக்கும், நிலவுக்கும் வேறுபாடு தெரியாமல் விண்மீன்கள் மயங்கிக் தவிக்கின்றன',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1117,'1117','அறுவாய் நிறைந்த அவிர்மதிக்குப் போல<br />மறுவுண்டோ மாதர் முகத்து','தேய்ந்தும், வளர்ந்தும் ஒளிபொழியும் நிலவில் உள்ள சிறுகளங்கம்கூட, இந்த மங்கை நல்லாள் முகத்தில் கிடையாதே!',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1118,'1118','மாதர் முகம்போல் ஒளிவிட வல்லையேல்<br />காதலை வாழி மதி','முழுமதியே! என் காதலுக்குரியவளாக நீயும் ஆக வேண்டுமெனில், என் காதலியின் முகம் போல ஒளிதவழ நீடு வாழ்வாயாக',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1119,'1119','மலரன்ன கண்ணாள் முகமொத��தி யாயின்<br />பலர்காணத் தோன்றல் மதி','நிலவே! முலரனைய கண்களையுடைய என் காதல் மங்கையின் முகத்திற்கு ஒப்பாக நீயிருப்பதாய் பெருமைப்பட்டுக் கொள்ள வேண்டுமேயானால் (அந்தப் போட்டியில் நீ தோல்வியுறாமல் இருந்திட) பலரும் காணும்படியாக நீ தோன்றாது இருப்பதே மேல்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1120,'1120','அனிச்சமும் அன்னத்தின் தூவியு மாதர்<br />அடிக்கு நெருஞ்சிப் பழம்','அனிச்ச மலராயினும், அன்னப்பறவை இறகாயினும் இரண்டுமே நெருஞ்சி முள் தைத்தது போல் துன்புறுத்தக் கூடிய அளவுக்கு, என் காதலியின் காலடிகள்அவ்வளவு மென்மையானவை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1121,'1121','பாலொடு தேன்கலந் தற்றே பணிமொழி<br />வாலேயி றூறிய நீர்','இனியமொழி பேசுகினற இவளுடைய வெண்முத்துப் பற்களிடையே சுரந்து வரும் உமிழ்நீர், பாலும் தேனும் கலந்தாற்போல் சுவை தருவதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1122,'1122','உடம்பொ டுயிரிடை என்னமற் றன்ன<br />மடந்தையொ டெம்மிடை நட்பு','உயிரும் உடலும் ஒன்றையொன்று பிரிந்து தனித்தனியாக இருப்பதில்லை; அத்தகையதுதான் எமது உறவு',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1123,'1123','கருமணியிற் பாவாய்நீ போதாயாம் வீழும்<br />திருநுதற் கில்லை யிடம்','நான் விரும்புகின்ற அழகிக்கு என் கண்ணிலேயே இடம் கொடுப்பதற்காக என் கண்ணின் கருமணியில் உள்ள பாவையே! அவளுக்கு இடமளித்து விட்டு நீ போய்விடு!',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1124,'1124','வாழ்தல் உயிர்க்கன்னள் ஆயிழை சாதல்<br />அதற்கன்னள் நீங்கும் இடத்து','ஆய்ந்து தேர்ந்த அரிய பண்புகளையே அணிகலனாய்ப் பூண்ட ஆயிழை என்னோடு கூடும்போது, உயிர் உடலோடு கூடுவது போலவும், அவள் என்னைவிட்டு நீங்கும்போது என்னுயிர் நீங்குவது போலவும் உணருகிறேன்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1125,'1125','உள்ளுவன் மன்யான் மறப்பின் மறப்பறியேன்<br />ஒள்ளமர்க் கண்ணாள் குணம்','ஒளி கொண்டிருக்கும் விழிகளையுடைய காதலியின் பண்புகளை நினைப்பதேயில்லை; காரணம் அவற்றை மறந்தால் அல்லவா நினைப்பதற்கு',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1126,'1126','கண்ணுள்ளிற் போகார் இமைப்பிற் பருவரார்<br />நுண்ணியர்எம் காத லவர்','காதலர், கண்ணுக்குள்ளிருந்து எங்கும் போக மாட்டார்; கண்ணை மூடி இமைத்தாலும் வருந்த மாட்டார்; காரணம், அவர் அவ்வளவு நுட்பமானவர்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1127,'1127','கண்ணுள்ளார் காத லவராகக் கண்ணும்<br />எழுதேம் கரப்பாக் கறிந்து','காதலர் கண்ணுக்குள்ளேயே இருக்கிற காரணத்தினால், மைதீட்டினால் எங்கே மறைந்துவிடப் போகிறாரோ எனப் பயந்து மை தீட்டாமல் இருக்கிறேன்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1128,'1128','நெஞ்சத்தார் காத லவராக வெய்துண்டல்<br />அஞ்சுதும் வேபாக் கறிந்து','சூடான பண்டத்தைச் சாப்பிட்டால் நெஞ்சுக்குள் இருக்கின்ற காதலருக்குச் சுட்டுவிடும் என்று அஞ்சுகின்ற அளவுக்கு நெஞ்சோடு நெஞ்சாகக் கலந்திருப்பவர்களே காதலர்களாவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1129,'1129','இமைப்பிற் கரப்பாக் கறிவல் அனைத்திற்கே<br />ஏதிலர் என்னுமிவ் வூர்','கண்ணுக்குள் இருக்கும் காதலர் மறைவார் என அறிந்து கண்ணை இமைக்காமல் இருக்கின்றேன்; அதற்கே இந்த ஊர் தூக்கமில்லாத துன்பத்தை எனக்குத் தந்த அன்பில்லதாவர் என்று அவரைக் கூறும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1130,'1130','உவந்துறைவர் உள்ளத்துள் என்றும் இகந்துறைவர்<br />ஏதிலர் என்னுமிவ் வூர்','காதலர், எப்போதும் உள்ளதோடு உள்ளமாய் வாழ்ந்து கொண்டிருக்கும்போது, அதை உணராத ஊர்மக்கள் அவர்கள் ஒருவரையொருவர் பிரிந்து வாழ்வதாகப் பழித்துரைப்பது தவறு',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1131,'1131','காமம் உழந்து வருந்தினார்க் கேம<br />மடலல்ல தில்லை வலி','காதலால் துன்புறும் காளையொருவனுக்குப் பாதுகாப்பு முறையாக மடலூர்தலைத் தவிர, வலிமையான துணைவேறு எதுவுமில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1132,'1132','நோனா உடம்பும் உயிரும் மடலேறும்<br />நாணினை நீக்கி நிறுத்து','எனது உயிரும், உடலும் காதலியின் பிரிவைத் தாங்க முடியாமல் தவிப்பதால், நாணத்தைப் புறந்தள்ளிவிட்டு மடலூர்வதற்குத் துணிந்து விட்டேன்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1133,'1133','நாணொடு நல்லாண்மை பண்டுடையேன் இன்றுடையேன்<br />காமுற்றார் ஏறும் மடல்','நல்ல ஆண்மையும், நாண உணர்வையும் முன்பு கொண்டிருந்த நான், இன்று அவற்றை மறந்து, காதலுக்காக மடலூர்வதை மேற்கொண்டுள்ளேன்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1134,'1134','காமக் கடும்புனல் உய்க்குமே நாணொடு<br />நல்லாண்மை என்னும் புணை','காதல் பெருவெள்ளமானது நாணம், நல்ல ஆண்மை எனப்படும் தோணிகளை அடித்துக்கொண்டு போய்விடும் வலிமை வாய்ந்தது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1135,'1135','தொடலைக் குறுந்தொடி தந்தாள் மடலொடு<br />மாலை உழக்கும் துயர்','மேகலையையும் மெல்லிய வளையலையும் அணிந்த மங்கை மாலை மலரும் நோயான காதலையும், மடலூர்தல் எனும் வேலையையும் எனக்குத் தந்து விட்டாள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1136,'1136','மடலூர்தல் யாமத்தும் உள்ளுவேன் மன்ற<br />படலொல்லா பேதைக்கென் கண்','காதலிக்காக என் கண்கள் உறங்காமல் தவிக்கின்றன; எனவே மடலூர்தலைப் பற்றி நள்ளிரவிலும் நான் உறுதியாக எண்ணிக் கொண்டிருக்கிறேன்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1137,'1137','கடலன்ன காமம் உழந்தும் மடலேறாப்<br />பெண்ணிற் பெருந்தக்க தில்','கொந்தளிக்கும் கடலாகக் காதல் நோய் துன்புறுத்தினாலும்கூடப் பொறுத்துக்கொண்டு, மடலேறாமல் இருக்கும் பெண்ணின் பெருமைக்கு நிகரில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1138,'1138','நிறையரியர் மன்னளியர் என்னாது காமம்<br />மறையிறந்து மன்று படும்','பாவம்; இவர், மனத்தில் உள்ளதை ஒளிக்கத் தெரியாதவர்; பரிதாபத்திற்குரியவர்; என்றெல்லாம் பார்க்காமல், ஊர் அறிய வெளிப்பட்டு விடக்கூடியது காதல்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1139,'1139','அறிகிலார் எல்லாரும் என்றேயென் காமம்<br />மறுகின் மறுகும் மருண்டு','என்னைத் தவிர யாரும் அறியவில்லை என்பதற்காக என் காதல் தெருவில் பரவி மயங்கித் திரிகின்றது போலும்!',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1140,'1140','யாம்கண்ணிற் காண நகுப அறிவில்லார்<br />யாம்பட்ட தாம்படா ஆறு','காதல் நோயினால் வாடுவோரின் துன்பத்தை அனுபவித்தறியாதவர்கள்தான், அந்த நோயினால் வருந்துவோரைப் பார்த்து நகைப்பார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1141,'1141','அலரெழ ஆருயிர் நிற்கும் அதனைப்<br />பலரறியார் பாக்கியத் தால்','எம் காதலைப் பற்றிப் பழிதூற்றிப் பேசுவதால் அதுவே எம் காதல் கைகூட வாய்ப்பாக அமையும் என்ற நம்பிக்கையில் எம் உயிர் போகாமல் இருக்கிறது என்பதை ஊரார் அறிய மாட்டார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1142,'1142','மலரன்ன கண்ணாள் அருமை அறியா<br />தலரெமக் கீந்ததிவ் வூர்','அந்த மலர்விழியாளின் மாண்பினை உணராமல் எம்மிடையே காதல் என்று இவ்வூரார் பழித்துரைத்தது மறைமுக உதவியாகவே எமக்கு அமைந்தது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1143,'1143','உறாஅதோ ஊரறிந்த கௌவை அதனைப்<br />பெறாஅது பெற்றன்ன நீர்த்து','எமது காதலைப்பற்றி ஊரறியப் பேச்சு எழாதா? அந்தப் பேச்சு, இன்னும் எமக்குக் கிட்டாத காதல் கிட்டியது போன்று இன்பத்தைத் தரக்கூடியதாயிற்றே!',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1144,'1144','கவ்வையாற் கவ்விது காமம் அதுவின்றேல்<br />தவ்வென்னும் தன்மை இழந்து','ஊரார் அலர் தூற்றுவதால் எம் காதல் வளர்கிறது; இல்லையேல் இக்காதல்கொடி வளமிழந்து வாடிப்போய் விடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1145,'1145','களித்தொறும் கள்ளுண்டல் வேட்டற்றால் காமம்<br />வெளிப்படுந் தோறும் இனிது','காதல் வெளிப்பட வெளிப்பட இனிமையாக இருப்பது கள்ளுண்டு மயங்க மயங்க அக்கள்ளையே விரும்புவது போன்றதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1146,'1146','கண்டது ம��்னும் ஒருநாள் அலர்மன்னும்<br />திங்களைப் பாம்புகொண் டற்று','காதலர் சந்தித்துக் கொண்டது ஒருநாள்தான் என்றாலும், சந்திரனைப் பாம்பு விழுங்குவதாகக் கற்பனையாகக் கூறப்படும் ``கிரகணம் எனும் நிகழ்ச்சியைப் போல அந்தச் சந்திப்பு ஊர் முழுவதும் அலராகப் பரவியது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1147,'1147','ஊரவர் கௌவை எருவாக அன்னைசொல்<br />நீராக நீளுமிந் நோய்','ஒருவரையொருவர் விரும்பி மலர்ந்த காதலானது ஊர்மக்கள் பேசும் பழிச்சொற்களை எருவாகவும் அன்னையின் கடுஞ்சொற்களை நீராகவும் கொண்டு வளருமே தவிரக் கருகிப் போய்விடாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1148,'1148','நெய்யால் எரிநுதுப்பேம் என்றற்றால் கௌவையால்<br />காமம் நுதுப்பேம் எனல்','ஊரார் பழிச்சொல்லுக்குப் பயந்து காதல் உணர்வு அடங்குவது என்பது, எரிகின்ற தீயை நெய்யை ஊற்றி அணைப்பதற்கு முயற்சி செய்வதைப் போன்றதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1149,'1149','அலர்நாண ஒல்வதோ அஞ்சலோம் பென்றார்<br />பலர்நாண நீத்தக் கடை','உன்னை விட்டுப் பிரியேன் அஞ்ச வேண்டாம் என்று உறுதியளித்தவர் பலரும் நாணும்படியாக என்னை விட்டுப் பிரிந்து சென்றிருக்கும் போது நான் மட்டும் ஊரார் தூற்றும் அலருக்காக நாண முடியுமா?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1150,'1150','தாம்வேண்டின் நல்குவர் காதலர் யாம்வேண்டும்<br />கௌவை எடுக்குமிவ் வூர்','யாம் விரும்புகின்றவாறு ஊரார் அலர் தூற்றுகின்றனர்; காதலரும் விரும்பினால் அதை ஒப்புக் கொள்வார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1151,'1151','செல்லாமை உண்டேல் எனக்குரை மற்றுநின்<br />வல்வரவு வாழ்வார்க் குரை','பிரிந்து செல்வதில்லையென்றால் அந்த மகிழ்ச்சியான செய்தியை என்னிடம் சொல் நீ போய்த்தான் தீர வேண்டுமென்றால் நீ திரும்பி வரும்போது யார் உயிரோடு இருப்பார்களோ அவர்களிடம் இப்போது விடைபெற்றுக் கொள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1152,'1152','இன்கண் உடைத்தவர் பார்வல் பிரிவஞ்சும்<br />புன்கண் உடைத்தால் புணர்வு','முன்பெல்லாம் அவரைக் கண்களால் தழுவிக் கொண்டதே இன்பமாக இருந்தது; ஆனால், இப்போது உடல் தழுவிக் களிக்கும் போதுகூடப் பிரிவை எண்ணும் அச்சத்தால் துன்பமல்லவா வருத்துகிறது!',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1153,'1153','அரிதரோ தேற்றம் அறிவுடையார் கண்ணும்<br />பிரிவோ ரிடத்துண்மை யான்','பிரிவுத் துன்பத்தை அறிந்துள்ள காதலரும் நம்மைப் பிரிந்த செல்ல நேரிடுவதால்; ``பிரிந்திடேன் என அவர் கூறவதை உறுதி செய்திட இயலாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1154,'1154','அளித்தஞ்சல் என்றவர் நீப்பின் தெளித்தசொல்<br />தேறியார்க் குண்டோ தவறு','பிரிந்திடேன்; அஞ்சாதே எனச் சொல்லியவர் எனைப்பிரிந்து செல்வாரானால், அவர் சொன்னதை நம்பியதில் என்ன குற்றமிருக்க முடியும்?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1155,'1155','ஓம்பின் அமைந்தார் பிரிவோம்பல் மற்றவர்<br />நீங்கின் அரிதால் புணர்வு','காதலர் பிரிந்து சென்றால் மீண்டும் கூடுதல் எளிதல்ல என்பதால், அவர் பிரிந்து செல்லாமல் முதலியேயே காத்துக் கொள்ள வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1156,'1156','பிரிவுரைக்கும் வன்கண்ணர் ஆயின் அரிதவர்<br />நல்குவர் என்னும் நசை','போய் வருகிறேன் என்று கூறிப் பிரிகிற அளவுக்குக் கல் மனம் கொண்டவர் திரும்பி வந்து அன்பு காட்டுவார் என ஆவல் கொள்வது வீண்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1157,'1157','துறைவன் துறந்தமை தூற்றாகொல் முன்கை<br />இறைஇறவா நின்ற வளை','என்னை விட்டுத் தலைவன் பிரிந்து சென்றுள்ள செய்தியை என் முன்கை மூட்டிலிருந்து கழன்று விழும் வளையல் ஊரறியத் தூற்றித் தெரிவித்து விடுமே!',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1158,'1158','இன்னா தினன்இல்லூர் வாழ்தல் அதனினும்<br />இன்னா தினியார்ப் பிரிவு','நம்மை உணர்ந்து அன்பு காட்டுபவர் இல்லாத ஊரில் வாழ்வது துன்பமானது; அதைக் காட்டிலும் து��்பமானது இனிய காதலரைப் பிரிந்து வாழ்வது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1159,'1159','தொடிற்சுடின் அல்லது காமநோய் போல<br />விடிற்சுடல் ஆற்றுமோ தீ','ஒருவரையொருவர் காணாமலும் தொடாமலும் பிரிந்திருக்கும் போது காதல் நோய் உடலையும் உள்ளத்தையும் சுடுவது போன்ற நிலை நெருப்புக்கு இல்லை; நெருப்பு தொட்டால் சுடும்; இது, பிரிவில் சுடுகிறதே!',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1160,'1160','அரிதாற்றி அல்லல்நோய் நீக்கிப் பிரிவாற்றிப்<br />பின்இருந்து வாழ்வார் பலர்','காதலர் பிரிந்து செல்வதற்கு ஒப்புதல் அளித்து, அதனால் ஏற்படும் துன்பத்தைப் போக்கிக் கொண்டு, பிரிந்த பின்னும் பொறுத்திருந்து உயிரோடு வாழ்பவர் பலர் இருக்கலாம்; ஆனால் நான்?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1161,'1161','மறைப்பேன்மன் யானிஃதோ நோயை தறைப்பவர்க்<br />கூற்றுநீர் போல மிகும்','இறைக்க இறைக்கப் பெருகும் ஊற்றுநீர் போல, பிறர் அறியாமல் மறைக்க மறைக்கக் காதல் நோயும் பெருகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1162,'1162','கரத்தலும் ஆற்றேன்இந் நோயைநோய் செய்தார்க்<br />குரைத்தலும் நாணுத் தரும்','காதல் நோயை என்னால் மறைக்கவும் முடியவில்லை; இதற்குக் காரணமான காதலரிடம் நாணத்தால் உரைக்கவும் முடியவில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1163,'1163','காமமும் நாணும் உயிர்காவாத் தூங்குமென்<br />நோனா உடம்பின் அகத்து','பிரிவைத் தாங்கமுடியாது உயிர் துடிக்கும் என் உடலானது, ஒருபுறம் காதல் நோயும் மறுபுறம் அதனை வெளியிட முடியாத நாணமும் கொண்டு காவடி போல விளங்குகிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1164,'1164','காமக் கடல்மன்னும் உண்டே அதுநீந்தும்<br />ஏமப் புணைமன்னும் இல்','காதல் கடல்போலச் சூழ்ந்துகொண்டு வருத்துகிறது ஆனால் அதை நீந்திக் கடந்து செல்லப் பாதுகாப்பான தோணிதான் இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1165,'1165','துப்பின் எவனாவர் மன்கொல் துயர்வரவு<br />நட்பினுள் ஆற்று பவர்','நட்பாக இருக்கும்போதே பிரிவுத்துயரை நமக்குத் தரக்கூடியவர், பகைமை தோன்றினால் எப்படிப்பட்டவராய் இருப்பாரோ?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1166,'1166','இன்பம் கடல்மற்றுக் காமம் அஃதடுங்கால்<br />துன்பம் அதனிற் பெரிது','காதல் இன்பம் கடல் போன்றது காதலர் பிரிவு ஏற்படுத்தும் துன்பமோ, கடலைவிடப் பெரியது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1167,'1167','காமக் கடும்புனல் நீந்திக் கரைகாணேன்<br />யாமத்தும் யானே உளேன்','நள்ளிரவிலும் என் துணையின்றி நான் மட்டுமே இருக்கிறேன்; அதனால், காதலின்பக் கடும் வெள்ளத்தில் நீந்தி, அதன் கரையைக் காண இயலாமல் கலங்குகிறேன்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1168,'1168','மன்னுயிர் எல்லாம் துயிற்றி அளித்திரா<br />என்னல்ல தில்லை துணை','`இரவே! உலகில் உள்ள எல்லா உயிர்களையும் நீ உறங்கச் செய்துவிட்டுப் பாவம் இப்போது என்னைத்தவிர வேறு துணையில்லாமல் இருக்கிறாய்.',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1169,'1169','கொடியார் கொடுமையின் தாம்கொடிய இந்நாள்<br />நெடிய கழியும் இரா','இந்த இரவுகள் நீண்டுகொண்டே போவதுபோல் தோன்றும் கொடுமை இருக்கிறதே அது காதலரின் பிரிவால் ஏற்படும் கொடுமையைவிடப் பெரிதாக உள்ளது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1170,'1170','உள்ளம்போன் றுள்வழிச் செல்கிற்பின் வெள்ளநீர்<br />நீந்தல மன்னோவென் கண்','காதலர் இருக்குமிடத்துக்கு என் நெஞ்சத்தைப் போலச் செல்ல முடியுமானால், என் கருவிழிகள், அவரைக் காண்பதற்குக் கண்ணீர் வெள்ளத்தில் நீந்த வேண்டிய நிலை ஏற்பட்டிருக்காது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1171,'1171','கண்டாம் கலுழ்வ தெவன்கொலோ தண்டாநோய்<br />தாங்காட்ட யாங்கண் டது','கண்கள் செய்த குற்றத்தால்தானே காதல் நோய் ஏற்பட்டது? அதே கண்கள் அந்தக் காதலரைக் காட்டுமாறு கேட்டு அழுவது ஏன்?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1172,'1172','தெரிந்துணரா நோக்கிய உண்கண் பரிந்துணராப்<br />பைதல் உழப்ப தெவன்','விளைவுகளை உணராமல் மயங்கி நோக்கிய மைவிழிகள், இன்று, காதலரைப் பிரிந்ததால் துன்பமுறுவது தம்மால் தான் என அறியாமல் தவிப்பது ஏன்?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1173,'1173','கதுமெனத் தாநோக்கித் தாமே கலுழும்<br />இதுநகத் தக்க துடைத்து','தாமாகவே பாய்ந்து சென்று அவரைப் பார்த்து மகிழ்ந்த கண்கள், இன்று தாமாகவே அழுகின்றன இது நகைக்கத்தக்க ஒன்றாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1174,'1174','பெயலாற்றா நீருலந்த உண்கண் உயலாற்றா<br />உய்வில்நோய் என்கண் நிறுத்து','தப்பிப் பிழைக்க முடியாத, தீராத காதல் நோயை எனக்குத் தருவதற்குக் காரணமான என் கண்கள், தாமும் அழ முடியாமல் வற்றிப் போய்விட்டன',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1175,'1175','படலாற்றா பைதல் உழக்கும் கடலாற்றக்<br />காமநோய் செய்தவென் கண்','கடல் கொள்ளாத அளவுக்குக் காதல் நோய் உருவாகக் காரணமாக இருந்த என் கண்கள், இப்போது தூங்க முடியாமல் துன்பத்தால் வாடுகின்றன',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1176,'1176','ஓஒ இனிதே எமக்கிந் நோய் செய்தகண்<br />தாஅம் இதற்பட் டது','ஓ! என் காதல் நோய்க்குக் காரணமான கண்கள், என்னைப் போலவே வாடி வருந்துகின்றன. இது எனக்கு மகிழ்ச்சியே!',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1177,'1177','உழந்துழந் துள்நீர் அறுக விழைந்திழைந்து<br />வேண்டி அவர்க்கண்ட கண்','அன்று, இழைந்து குழைந்து ஆசையுடன் அவரைக் கண்ட கண்களே! இன்று பிரிந்து சென்றுள்ள அவரை நினைத்துக் தூங்காமலும், துளிக் கண்ணீரும் அற்றுப்போகும் நிலையிலும் துன்பப்படுங்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1178,'1178','பேணாது பெட்டார் உளர்மன்னோ மற்றவர்க்<br />காணா தமைவில கண்','என்னை அரவணைக்கும் எண்ணமின்றிக் காதலித்த ஒருவர் இருக்கின்றனர்; அவரைக் காணாமல் என் கண்களுக்கு அமைதியில்லையே!',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1179,'1179','வாராக்கால் துஞ்சா வரின்துஞ்சா ஆயிடை<br />ஆரஞர் உற்றன கண்','இன்னும் வரவில்லையே என்பதாலும் தூங்குவதில்லை; வந்துவிட்டாலும் பிறகு தூங்குவதில்லை இப்படியொரு துன்பத்தை அனுபவிப்பவை காதலர்களின் கண்களாகத் தானே இருக்க முடியும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1180,'1180','மறைபெறல் ஊராhக் கரிதன்றால் எம்போல்<br />அறைபறை கண்ணார் அகத்து','காதல் வேதனையைப் பறைசாற்றிக் காட்டிக் கொடுக்க எம் கண்களேயிருக்கும்போது, யாம் மறைப்பதை அறிந்து கொள்வது ஊரார்க்குக் கடினமல்ல',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1181,'1181','நயந்தவர்க்கு நல்காமை நேர்ந்தேன் பசந்தவென்<br />பண்பியார்க் குரைக்கோ பிற','என்னைப் பிரிந்து செல்வதற்கு என் காதலர்க்கு ஒப்புதல் அளித்துவிட்டேன்; ஆனால், இப்போது பிரிவுத் துன்பத்தால் என்னுடலில் பசலை படர்வதை, யாரிடம் போய்ச் சொல்வேன்?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1182,'1182','அவர்தந்தார் என்னும் தகையால் இவர்தந்தென்<br />மேனிமேல் ஊரும் பசப்பு','பிரிவு காரணமாகக் காதலர் உண்டாக்கினார் எனும் பெருமிதம் பொங்கிடப் பசலை நிறம் என் உடலில் ஏறி ஊர்ந்து பரவுகின்றது!',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1183,'1183','சாயலும் நாணும் அவர்கொண்டார் கைம்மாறா<br />நோயும் பசலையும் தந்து','காதல் நோயையும், பசலை நிறத்தையும் கைம்மாறாகக் கொடுத்து விட்டு அவர் என் அழகையும், நாணத்தையும் எடுத்துக் கொண்டு பிரிந்து சென்று விட்டார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1184,'1184','உள்ளுவன் மன்யான் உரைப்ப தவர்திறமால்<br />கள்ளம் பிறவோ பசப்பு','யான் நினைப்பதும், உரைப்பதும் அவரது நேர்மைத் திறன் பற்றியதாகவே இருக்கும்போது, என்னையறியாமலோ வேறு வழியிலோ இப்பசலை நிறம் வந்தது எப்படி?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1185,'1185','உவக்காணெம் காதலர் செல்வார் இவக்காணென்<br />மேனி பசப்பூர் வது','என்னைப் பிரிந்து காதலர் சிறிது தொலைவுகூடச் செல்லவில்லை; அதற்குள்ளாக என் மேனியில் படர்ந்து விட்டதே பசலை நிறம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1186,'1186','விளக்கற்றம் பார்க்கும் இருளேபோல் கொண்கண்<br />முயக்கற்றம் பார்க்கும் பசப்பு','விளக்கின் ஒளிகுறையும் சமயம் பார்த்துப் பரவிடும் இருளைப்போல, இறுகத் தழுவிய காதலன்பிடி, சற்றுத் தளரும்போது காதலியின் உடலில் பசலைநிறம் படர்ந்து விடுகிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1187,'1187','புல்லிக் கிடந்தேன் புடைபெயர்ந்தேன் அவ்வளவில்<br />அள்ளிக்கொள் வற்றே பசப்பு','தழுவிக் கிடந்தேன்; சற்றுத் தள்ளிப் படுத்தேன்; அவ்வளவுதான்; என்னை அள்ளிக் கொண்டு விட்டதே பசலை நிறம்!',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1188,'1188','பசந்தாள் இவளென்ப தல்லால் இவளைத்<br />துறந்தார் அவரென்பார் இல்','இவள் உடலில் பசலை நிறம் படர்ந்தது எனப் பழித்துக் கூறுகிறார்களே அல்லாமல், இதற்குக் காரணம், காதலன் பிரிந்து சென்றிருப்பது தான் என்று சொல்பவர் இல்லையே',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1189,'1189','பசக்கமற் பட்டாங்கென் மேனி நயப்பித்தார்<br />நன்னிலையர் ஆவர் எனின்','பிரிந்து சென்றிட என்னை ஒப்புக் கொள்ளுமாறு செய்த காதலர் நலமாக இருப்பார் என்றால் என்னுடல் பசலை படர்ந்தே விளங்கிடுமாக!',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1190,'1190','பசப்பெனப் பேர்பெறுதல் நன்றே நயப்பித்தார்<br />நல்காமை தூற்றார் எனின்','என்னைப் பிரிவுக்கு உடன்படுமாறு செய்த காதலரை அன்பில்லாதவர் என்று யாரும் தூற்றமாட்டார்கள் எனில், பசலை படர்ந்தவள் என நான் பெயரெடுப்பது நல்லது தான்!',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1191,'1191','தாம்வீழ்வார் தம்வீழப் பெற்றவர் பெற்றாரே<br />காமத்துக் காழில் கனி','தம்மால் விரும்பப்படும் காதலர், தம்மை விரும்புகிற பேறு பெற்றவர் விதையில்லாத பழத்தைப் போன்ற காதல் வாழ்க்கையின் பயனைப் பெற்றவராவார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1192,'1192','வாழ்வார்க்கு வானம் பயந்தற்றால் வீழ்வார்க்கு<br />வீழ்வார் அளிக்கும் அளி','காதலர்கள் ஒருவரையொருவர் உரிய நேரத்தில் சந்தித்து அன்பு பொழிவது, வாழ்வதற்குத் தேவையான பருவமழை பொழிவது போன்றதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1193,'1193','வீழுநர் வீழப் படுவார்க் கமையுமே<br />வாழுநம் என்னும் செருக்கு','காதலன்பில் கட்டுண்டு பிரியாமல் இணைந்திருப்பவர்களுக்குத்தான் இன்புற்று வாழ்கிறோம் எனும் பெருமிதம் ஏற்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1194,'1194','வீழப் படுவார் கெழீஇயிலர் தாம்வீழ்வார்<br />வீழப் படாஅர் எனின்','விரும்பப்படாத நிலை ஏற்படின், அந்தக் காதலர் நட்புணர்வு இல்லாதவராகவே கருதப்படுவார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1195,'1195','நாம்காதல் கொண்டார் நமக்கெவன் செய்பவோ<br />தாம்காதல் கொள்ளாக் கடை','நான் விரும்பிக் காதல் கொள்வது போன்று அவர் என்னை விரும்பிக் காதல் கொள்ளாத நிலையில் அவரால் எனக்கு என்ன இன்பம் கிடைக்கப் போகிறது?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1196,'1196','ஒருதலையான் இன்னாது காமங்காப் போல<br />இருதலை யானும் இனிது','காவடித் தண்டின் இரண்டு பக்கங்களும் ஒரே அளவு கனமாக இருப்பதுபோல், காதலும் ஆண், பெண் எனும் இருவரிடத்திலும் மலர வேண்டும்; ஒரு பக்கம் மட்டுமே ஏற்படும் காதலால் பயனுமில்லை; துயரமும் உருவாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1197,'1197','பருவரலும் பைதலும் காணான்கொல் காமன்<br />ஒருவர்கண் நின்றொழுகு வான்','காமன், ஒரு பக்கமாக மட்டும் இருப்பதால், என்னைக் காதல் நோய் வருத்துவதையும், என் மேனியில் பசலை படர்வதையும் கண்டு கொள்ளமாட்டான் போலும்!',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1198,'1198','வீழ்வாரின் இன்சொல் பெறாஅ துலகத்து<br />வாழ்வாரின் வன்கணார் இல்','பிரிந்து சென்ற காதலரிடமிருந்து ஓர் இனிய சொல்கூட வராத நிலையில், உலகில் வாழ்கின்றவரைப் போல், கல் நெஞ்சம் உடையவர் யாரும் இருக்க முடியாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1199,'1199','நசைஇயார் நல்கார் எனினும் அவர்மாட்<br />டிசையும் இனிய ச���விக்கு','என் அன்புக்குரியவர் என்னிடம் அன்பு காட்டாதவராகப் பிரிந்து இருப்பினும், அவரைப் பற்றிய புகழ் உரை என் செவிக்குச் செந்தேனாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1200,'1200','உறாஅர்க் குறுநோய் உரைப்பாய் கடலைச்<br />செறாஅஅய் வாழிய நெஞ்சு','நெஞ்சமே! நீ வாழ்க! உன்னிடம் அன்பு இல்லாதவரிடம் உனது துன்பத்தைச் சொல்லி ஆறுதல் பெறுவதைக் காட்டிலும் கடலைத் தூர்ப்பது எளிதான வேலையாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1201,'1201','உள்ளினும் தீராப் பெருமகிழ் செய்தலால்<br />கள்ளினும் காமம் இனிது','உண்டபோது மட்டும் மகிழ்ச்சி தரும் கள்ளைவிட நினைத்தாலே நெஞ்சினிக்கச் செய்யும் காதல் இன்பமானதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1202,'1202','எனைத்தொன் றினிதேகாண் காமந்தாம் வீழ்வார்<br />நினைப்ப வருவதொன் றில்','விரும்பி இணைந்த காதலரை நினைத்தலால், பிரிவின் போது வரக்கூடிய துன்பம் வருவதில்லை எனவே எந்த வகையிலும் காதல் இனிதேயாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1203,'1203','நினைப்பவர் போன்று நினையார்கொல் தும்மல்<br />சினைப்பது போன்று கெடும்','வருவது போலிருந்து வராமல் நின்று விடுகிறதே தும்மல்; அதுபோலவே என் காதலரும் என்னை நினைப்பது போலிருந்து, நினைக்காது விடுகின்றாரோ?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1204,'1204','யாமும் உளேங்கொல் அவர்நெஞ்சத் தெந்நெஞ்சத்<br />தோஒ உளரே அவர்','என் நெஞ்சைவிட்டு நீங்காமல் என் காதலர் இருப்பது போல, அவர் நெஞ்சை விட்டு நீங்காமல் நான் இருக்கின்றேனா?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1205,'1205','தம்நெஞ்சத் தெம்மைக் கடிகொண்டார் நாணார்கொல்<br />எம்நெஞ்சத் தோவா வரல்','அவருடைய நெஞ்சில் எமக்கு இடம் தராமல் இருப்பவர்; எம் நெஞ்சில் மட்டும் இடைவிடாமல் வந்து புகுந்து கொள்வதற்காக வெட்கப்படமாட்டார் போலும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1206,'1206','மற்றியான் என்னுளேன் மன்னோ அவரொடுயான்<br />உற்றநாள் உள்ள உளேன்','நான் அவரோடு சேர்ந்திருந்த நாட்களை நினைத்துத் தான் உயிரோடு இருக்கிறேன்; வேறு எதை நினைத்து நான் உயிர்வாழ முடியும்?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1207,'1207','மறப்பின் எவனாவன் மற்கொல் மறப்பறியேன்<br />உள்ளினும் உள்ளம் சுடும்','மறதி என்பதே இல்லாமல் நினைத்துக் கொண்டிருக்கும் பொழுதே பிரிவுத்துன்பம் சுட்டுப் பொசுக்குகிறதே! பினைக்காமல் மறந்துவிட்டால் என்ன ஆகுமோ?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1208,'1208','எனைத்து நினைப்பினும் காயார் அனைத்தன்றோ<br />காதலர் செய்யும் சிறப்பு','எவ்வளவு அதிகமாக நினைத்தாலும், அதற்காகக் காதலர் என் மீது சினம் கொள்ளமாட்டார். அவர் எனக்குச் செய்யும் பெரும் உதவி அதுவல்லவா?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1209,'1209','விளியுமென் இன்னுயிர் வேறல்லம் என்பார்<br />அளியின்மை ஆற்ற நினைந்து','``நாம் ஒருவரே; வேறு வேறு அல்லர் எனக்கூறிய காதலர் இரக்கமில்லாதவராக என்னைப் பிரிந்து சென்றுள்ளதை நினைத்து வருந்துவதால் என்னுயிர் கொஞ்சம் கொஞ்சமாகப் போய்க் கொண்டிருக்கிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1210,'1210','விடாஅது சென்றாரைக் கண்ணினால் காணப்<br />படாஅதி வாழி மதி','நிலவே! நீ வாழ்க; இணைபிரியாமலிருந்து, பிரிந்து சென்றுள்ள காதலரை நான் என் கண்களால் தேடிக் கண்டுபிடித்திடத் துணையாக நீ மறையாமல் இருப்பாயாக',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1211,'1211','காதலர் தூதொடு வந்த கனவினுக்<br />கியாதுசெய் வேன்கொல் விருந்து','வந்த கனவு காதலர் அனுப்பிய தூதுடன் வந்ததே; அந்தக் கனவுக்குக் கைம்மாறாக என்ன விருந்து படைத்துப் பாராட்டுவது?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1212,'1212','கயலுண்கண் யானிரப்பத் துஞ்சின் கலந்தார்க்<br />குயலுண்மை சாற்றுவேன் மன்','நான் வேண்டுவதற்கு இணங்கி என் மை எழுதிய கயல் விழிகள் உறங்கிடுமானால், அப்போது என் கனவில் வரும் காதலர்க்கு நான் இன்னமும் உயிரோடு இருப்பத��ச் சொல்லுவேன்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1213,'1213','நனவினான் நல்கா தவரைக் கனவினால்<br />காண்டலின் உண்டென் உயிர்','நனவில் வந்து அன்பு காட்டாதவரைக் கனவிலாவது காண்பதால்தான் இன்னும் என்னுயிர் நிலைத்திருக்கிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1214,'1214','கனவினான் உண்டாகும் காமம் நனவினான்<br />நல்காரை நாடித் தரற்கு','நேரில் என்னிடம் வந்து அன்பு காட்டாத காதலரைத் தேடிக் கொண்டு வந்து காட்டுகிற கனவால் எனக்குக் காதல் இன்பம் கிடைக்கிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1215,'1215','நனவினாற் கண்டதூஉம் ஆங்கே கனவுந்தான்<br />கண்ட பொழுதே இனிது','காதலரை நேரில் கண்ட இன்பம் அப்போது இனிமை வழங்கியது போலவே, இப்போது அவரைக் கனவில் காணும் இன்பமும் இனிமை வழங்குகிறது!',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1216,'1216','நனவென ஒன்றில்லை ஆயின் கனவினாற்<br />காதலர் நீங்கலர் மன்','நனவு மட்டும் திடிரென வந்து கெடுக்காமல் இருந்தால், கனவில் சந்தித்த காதலர் பிரியாமலே இருக்க முடியுமே',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1217,'1217','நனவினான் நல்காக் கொடியார் கனவினான்<br />என்னெம்மைப் பீழிப் பது','நேரில் வந்து அன்பு காட்டாத கொடிய நெஞ்சமுடையவர், கனவில் வந்து பிரிவுத் துயரைப் பெரிதாக்குவது என்ன காரணத்தால்?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1218,'1218','துஞ்சுங்கால் தோள்மேலர் ஆகி விழிக்குங்கால்<br />நெஞ்சத்தார் ஆவர் விரைந்து','தூக்கத்தில் கனவில் வந்து என் தோள் மீது சாய்ந்து இன்பம் தந்தவர், விழித்தபோது எங்கும் போய் விடவில்லை; என் நெஞ்சில் தாவி அமர்ந்து கொண்டார்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1219,'1219','நனவினான் நல்காரை நோவர் கனவினான்<br />காதலர்க் காணா தவர்','கனவில் காதலரைக் காணாதவர்கள்தான் அவர் நேரில் வந்து காணவில்லையே என்று நொந்து கொள்வர்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1220,'1220','நனவினான் நம்நீத்தார் என்பர் கனவினான்<br />காணார்கொல் இவ்வூ ரவர்','என் காதலர் என்னைப் பிரிந்திருப்பதாக அவரைக் குற்றம் சாட்டுகிறார்களே, இந்த ஊரார், பிரிந்து சென்ற தமது காதலனைக் கனவில் காண்பது கிடையாதோ?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1221,'1221','மாலையோ அல்லை மணந்தார் உயிருண்ணும்<br />வேலைநீ வாழி பொழுது','நீ மாலைப் பொழுதாக இல்லாமல் காதலரைப் பிரிந்திருக்கும் மகளிர் உயிரைக் குடிக்கும் வேலாக இருப்பதற்காக உனக்கோர் வாழ்த்து!',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1222,'1222','புன்கண்ணை வாழி மருள்மாலை எங்கேள்போல்<br />வன்கண்ண தோநின் துணை','மயங்கும் மாலைப் பொழுதே! நீயும் எம்மைப் போல் துன்பப்படுகின்றாயே! எம் காதலர் போல் உன் துணையும் இரக்கம் அற்றதோ?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1223,'1223','பனியரும்பிப் பைதல்கொள் மாலை துனியரும்பித்<br />துன்பம் வளர வரும்','பக்கத்தில் என் காதலர் இருந்த போது பயந்து, பசலை நிறத்துடன் வந்த மாலைப் பொழுது, இப்போது என் உயிரை வெறுக்குமளவுக்குத் துன்பத்தை மிகுதியாகக் கொண்டு வருகிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1224,'1224','காதலர் இல்வழி மாலை கொலைக்களத்<br />தேதிலர் போல வரும்','காதலர் பிரிந்திருக்கும்போது வருகிற மாலைப் பொழுது கொலைக் களத்தில் பகைவர் ஓங்கி வீசுகிற வாளைப்போல் வருகிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1225,'1225','காலைக்குச் செய்தநன் றென்கொல் எவன்கொல்யான்<br />மாலைக்குச் செய்த பகை','மாலைப் பொழுதாகிவிட்டால் காதல் துன்பம் அதிகமாக வருத்துகிறது அதனால் பிரிந்திருக்கும் காதலர் உள்ளம் ``காலை நேரத்துக்கு நான் செய்த நன்மை என்ன? மாலை நேரத்துக்குச் செய்த தீமைதான் என்ன? என்று புலம்புகிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1226,'1226','மாலைநோய் செய்தல் மணந்தார் அகலாத<br />காலை அறிந்த திலேன்','மாலைக்காலம் இப்படியெல்லாம் இன்னல் விளைவிக்கக் கூடியது என்பதைக் காதலர் என்னை விட்டுப் பிரியாமல் இருந்த போது நான் அறிந்திருக்கவில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1227,'1227','காலை அரும்பிப் பகலெல்லாம் போதாகி<br />மாலை மலருமிந் நோய்','காதல் என்பது காலையில் அரும்பாகி, பகல் முழுதும் முதிர்ச்சியடைந்து, மாலையில் மலரும் ஒரு நோயாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1228,'1228','அழல்போலும் மாலைக்குக் தூதாகி ஆயன்<br />குழல்போலும் கொல்லும் படை','காதலர் பிரிவால் என்னைத் தணலாகச் சுடுகின்ற மாலைப்பொழுதை அறிவிக்கும் தூதாக வருவது போல வரும் ஆயனின் புல்லாங்குழலோசை என்னைக் கொல்லும் படைக்கருவியின் ஓசைபோல் அல்லவா காதில் ஒலிக்கிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1229,'1229','பதிமருண்டு பைதல் உழக்கும் மதிமருண்டு<br />மாலை படர்தரும் போழ்து','என் அறிவை மயக்கும் மாலைப் பொழுது, இந்த ஊரையே மயக்கித் துன்பத்தில் ஆழ்த்துவது போல் எனக்குத் தோன்றுகிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1230,'1230','பொருள்மாலை யாளரை உள்ளி மருள்மாலை<br />மாயுமென் மாயா உயிர்','பொருள் ஈட்டுவதற்கச் சென்றுள்ள காதலரை எண்ணி மாய்ந்து போகாத என்னுயிர், மயக்கும் இந்த மாலைப் பொழுதில் மாய்ந்து போகின்றது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1231,'1231','சிறுமை நமக்கொழியச் சேட்சென்றார் உள்ளி<br />நறுமலர் நாணின கண்','பிரிவுத் துன்பத்தை நமக்களித்துவிட்டு நெடுந்தொலைவு சென்று விட்டாரேயென்று வருந்திடும் காதலியின் கண்கள் அழகிழந்துபோய், மலர்களுக்கு முன்னால் நாணிக் கிடக்கின்றன',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1232,'1232','நயந்தவர் நல்காமை சொல்லுவ போலும்<br />பசந்து பனிவாரும் கண்','பசலை நிறம் கொண்டு நீர் பொழியும் கண்கள், விரும்பிய காதலர் அன்பு காட்டவில்லை யென்பதை சொல்லி காட்டுகின்றன',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1233,'1233','தணந்தமை சால அறிவிப்ப போலும்<br />மணந்தநாள் வீங்கிய தோள்','தழுவிக் கிடந்த போது பூரித்திருந்த தோள், இப்போது மெலிந்து காணப்படுவது; காதலன் பிரிவை அறிவிப்பதற்காகத்தான் போலும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1234,'1234','பணைநீங்கிப் பைந்தொடி சோரும் துணைநீங்கித்<br />தொல்கவின் வாடிய தோள்','பருத்திருந்த பருவத் தோள்கள் பழைய எழில் குலைந்து, பசும்பொன் வளையல்களும் கழன்று விழுகின்றன காதலனைப் பிரிந்து வாடுவதன் காரணமாக',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1235,'1235','கொடியார் கொடுமை உரைக்கும் தொடியொடு<br />தொல்கவின் வாடிய தோள்','வளையல்களும் கழன்று விழ, இருந்த அழகையும் இழந்த தோள்கள் என்னைப் பிரிந்திருக்கும் காதலரின் கொடுமையை ஊருக்கு உரைக்கின்றன',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1236,'1236','தொடியொடு தோள்நேகிழ நோவல் அவரைக்<br />கொடியார் எனக்கூறல் நொந்து','என் தோள்கள் மெலிவதையும், வளையல்கள் கழன்று விழுவதையும் காண்போர் என்னுடையவர் இரக்கமற்றவர் என இயம்புவது கேட்டு இதயம் நொந்து போகிறேன்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1237,'1237','பாடு பெறுதியோ நெஞ்சே கொடியார்க்கென்<br />வாடுதோட் பூசல் உரைத்து','நெஞ்சே! இரக்கமற்று என்னைப் பிரிந்திருக்கும் அவருக்கும் வாடி வதங்கும் என் தோள்களின் துன்பத்தை உரைத்துப் பெருமை அடைய மாட்டாயோ?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1238,'1238','முயங்கிய கைகளை ஊக்கப் பசந்தது<br />பைந்தொடிப் பேதை நுதல்','இறுகத் தழுவியிருந்த கைகளைக் கொஞ்சம் தளர்த்தவே அந்தச் சிறு இடைவெளியையும் பொறுத்துக் கொள்ள முடியாமல் காதலியின் நெற்றி, பசலைநிறம் கொண்டு விட்டது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1239,'1239','முயக்கிடைத் தண்வளி போழப் பசப்புற்ற<br />பேதை பெருமழைக் கண்','இறுகத் தழுவியிருந்த போது, இடையே குளிர்ந்த காற்று நுழைந்ததால் அதையே ஒரு பிரிவு எனக் கருதிக் காதலியின் அகன்று நீண்ட கண்கள் பசலை நிறம் கொண்டன',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1240,'1240','கண்ணின் பசப்போ பருவரல் எய்தின்றே<br />ஒண்ணுதல் செய்தது கண்டு','பிரிவுத் துயரால் பிறைநுதல் பசலை நிறமடைந்ததைக் கண்டு அவளது கண்களின் பச���ையும் பெருந்துன்பம் அடைந்துவிட்டது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1241,'1241','நினைத்தொன்று சொல்லாயோ நெஞ்சே எனைத்தொன்றும்<br />எவ்வநோய் தீர்க்கு மருந்து','எந்த மருந்தினாலும் தீராத என் காதல் நோய் தீர்ந்திட ஏதாவது ஒரு மருந்தை நினைத்துப் பார்த்து, நெஞ்சே! உன்னால் சொல்ல முடியுமா?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1242,'1242','காத லவரிலர் ஆகநீ நோவது<br />பேதமை வாழியென் நெஞ்சு','அவர் நமது காதலை மதித்து நம்மிடம் வராத போது, நெஞ்சே! நீ மட்டும் அவரை நினைத்து வருந்துவது அறியாமையாகும்; நீ வாழ்க',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1243,'1243','இருந்துள்ளி என்பரிதல் நெஞ்சே பரிந்துள்ளல்<br />பைதல்நோய் செய்தார்கண் இல்','பிரிவுத் துன்பம் தந்த காதலருக்கு நம்மிடம் இரக்கமில்லாத போது, நெஞ்சே! நீ மட்டும் இங்கிருந்து கொண்டு அவரை எண்ணிக் கலங்குவதால் என்ன பயன்?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1244,'1244','கண்ணும் கொளச்சேறி நெஞ்சே இவையென்னைத்<br />தின்னும் அவர்க்காணல் உற்று','நெஞ்சே! நீ காதலரிடம் செல்லும் போது கண்களையும்கூட அழைத்துக்கொண்டு போ; இல்லையேல் அவரைக் காண வேண்டுமென்று என்னையே அவை தின்று விடுவது போல் இருக்கின்றன',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1245,'1245','செற்றார் எனக்கை விடலுண்டோ நெஞ்சேயாம்<br />உற்றால் உறாஅ தவர்','நெஞ்சே! நாம் விரும்பினாலும் நம்மை விரும்பி வராத அவர், நம்மை வெறுத்து விட்டார் என நினைத்து அவர் மீது கொண்ட காதலைக் கைவிட்டு விட முடியுமா?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1246,'1246','கலந்துணர்த்தும் காதலர்க் கண்டாற் புலந்துணராய்<br />பொய்க்காய்வு காய்தியென் நெஞ்சு','நெஞ்சே! கூடிக் கலந்து ஊடலை நீக்கும் காதலரைக் கண்டால் ஒரு தடவைகூடப் பிணங்கியறியாத நீ இப்போது அவர் மீது கொள்ளுகிற கோபம் பொய்யானது தானே?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1247,'1247','காமம் விடுவொன்றோ நாண்விடு நன்னெஞ்சே<br />யானோ பொறேனிவ் விரண்டு','நல்ல நெஞ்சமே! ஒன்று காதலால் துடிப்பதையாவது விட்டு விடு; அல்லது அதனைத் துணிந்து சொல்ல முடியாமல் தடுக்கும் நாணத்தையாவது விட்டு விடு இந்த இரண்டு செய்லகளையும் ஒரே நேரத்தில் தாங்கிக் கொள்ள என்னால் முடியாது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1248,'1248','பரிந்தவர் நல்காரென் றேங்கிப் பிரிந்தவர்<br />பின்செல்வாய் பேதையென் நெஞ்சு','நம்மீது இரக்கமின்றிப் பிரிந்து விட்டாரேயென்று ஏங்கிடும் அதே வேளையில் பிரிந்தவர் பின்னாலேயே சென்று கொண்டிருக்கும் என் நெஞ்சம் ஓர் அறிவற்ற பேதை போன்றதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1249,'1249','உள்ளத்தார் காத லவராக உள்ளிநீ<br />யாருழைச் சேறியென் நெஞ்சு','உள்ளத்திலேயே காதலர் குடி கொண்டிருக்கும்போது, நெஞ்சமே! நீ அவரை நினைத்து வெளியே எவரிடம் தேடி அலைகிறாய்?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1250,'1250','துன்னாத் துறந்தாரை நெஞ்சத் துடையேமா<br />இன்னும் இழத்தும் கவின்','சேராமல் பிரிந்து சென்ற காதலரைச் சிந்தையில் வைத்திருப்பதால் மேலும் மேனியெழில் இழந்து மெலிந்து அழிய வேண்டியுள்ளது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1251,'1251','காமக் கணிச்சி உடைக்கும் நிறையென்னும்<br />நாணுத்தாழ் வீழ்த்த கதவு','காதல் வேட்கை இருக்கிறதே, அது ஒரு கோடரியாக மாறி, நாணம் எனும் தாழ்ப்பாள் போடப்பட்ட மன அடக்கம் என்கிற கதவையே உடைத்தெறிந்து விடுகின்றது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1252,'1252','காமம் எனவொன்றோ கண்ணின்றென் நெஞ்சத்தை<br />யாமத்தும் ஆளும் தொழில்','காதல் வேட்கை எனப்படும் ஒன்று இரக்கமே இல்லாதது; ஏனெனில் அது என் நெஞ்சில் நள்ளிரவிலும் ஆதிக்கம் செலுத்தி அலைக்கழிக்கிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1253,'1253','மறைப்பேன்மன் காமத்தை யானோ குறிப்பின்றித்<br />தும்மல்போல் தோன்றி விடும்','எவ்வளவுதான் அடக்க முயன்றாலும் கட்டுப்படாமல் தும்மல் நம்மையும் மீறி வெளிப்படுகிறதல்ல���ா; அதைப் போன்றதுதான் காதல் உணர்ச்சியும்; என்னதான் மறைத்தாலும் காட்டிக் கொடுத்துவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1254,'1254','நிறையுடையேன் என்பேன்மன் யானோவென் காமம்<br />மறையிறந்து மன்று படும்','மன உறுதிகொண்டவள் நான் என்பதே என் நம்பிக்கை; ஆனால் என் காதல், நான் மறைப்பதையும் மீறிக்கொண்டு மன்றத்திலேயே வெளிப்பட்டு விடுகிறதே',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1255,'1255','செற்றார்பின் செல்லாப் பெருந்தகைமை காமநோய்<br />உற்றார் அறிவதொன் றன்று','தம்மைப் பிரிந்து சென்ற காதலரைப் பகையாகக் கருதி அவரைத் தொடர்ந்து மன அடக்கம், காதல் நோயுற்றவர்க்கு இருப்பதில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1256,'1256','செற்றவர் பின்சேறல் வேண்டி அளித்தரோ<br />எற்றென்னை உற்ற துயர்','வெறுத்துப் பிரிந்ததையும் பொறுத்துக் கொண்டு அவர் பின்னே செல்லும் நிலையை என் நெஞ்சுக்கு ஏற்படுத்திய காதல் நோயின் தன்மைதான் என்னே',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1257,'1257','நாணென ஒன்றோ அறியலம் காமத்தால்<br />பேணியார் பெட்ப செயின்','நமது அன்புக்குரியவர் நம்மீது கொண்ட காதலால் நமக்கு விருப்பமானவற்றைச் செய்யும்போது, நாணம் எனும் ஒரு பண்பு இருப்பதையே நாம் அறிவதில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1258,'1258','பன்மாயக் கள்வன் பணிமொழி அன்றோநம்<br />பெண்மை உடைக்கும் படை','நம்முடைய பெண்மை எனும் உறுதியை உடைக்கும் படைக்கலனாக இருப்பது, பல மாயங்களில் வல்ல கள்வராம் காதலரின் பணிவான பாகுமொழியன்றோ?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1259,'1259','புலப்பல் எனச்சென்றேன் புல்லினேன் நெஞ்சம்<br />கலத்தல் உறுவது கண்டு','ஊடல் கொண்டு பிணங்குவோம் என நினைத்துதான் சென்றேன்; ஆனால் என் நெஞ்சம் என்னை விடுத்து அவருடன் கூடுவதைக் கண்டு என் பிடிவாதத்தை மறந்து தழுவிக் கொண்டேன்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1260,'1260','நிணந்தீயில் இட்டன்ன நெஞ்சினார்க் குண்டோ<br />புணர்ந்தூடி நிற்பேம் எனல்','நெருப்பிலிட்ட கொழுப்பைப் போல் உருகிடும் நெஞ்சம் உடையவர்கள், கூடிக் களித்தபின் ஊடல் கொண்டு அதில் உறுதியாக இருக்க முடியுமா?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1261,'1261','வாளற்றுப் புற்கென்ற கண்ணும் அவர்சென்ற<br />நாளொற்றித் தேய்ந்த விரல்','வருவார் வருவார் என வழி பார்த்துப் பார்த்து விழிகளும் ஒளியிழந்தன; பிரிந்து சென்றுள்ள நாட்களைச் சுவரில் குறியிட்டு அவற்றைத் தொட்டுத் தொட்டு எண்ணிப் பார்த்து விரல்களும் தேய்ந்தன',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1262,'1262','இலங்கிழாய் இன்று மறப்பினென் தோள்மேல்<br />கலங்கழியும் காரிகை நீத்து','காதலரைப் பிரிந்திருக்கும் நான், பிரிவுத் துன்பம் வாராதிருக்க அவரை மறந்திருக்க முனைந்தால், என் தோள்கள் அழகு நீங்கி மெலிந்து போய் வளையல்களும் கழன்று விழுவது உறுதியடி என் தோழி',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1263,'1263','உரன்நசைஇ உள்ளம் துணையாகச் சென்றார்<br />வரல்நசைஇ இன்னும் உளேன்','ஊக்கத்தையே உறுதுணையாகக் கொண்டு வெற்றியை விரும்பிச் சென்றுள்ள காதலன், திரும்பி வருவான் என்பதற்காகவே நான் உயிரோடு இருக்கிறேன்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1264,'1264','கூடிய காமம் பிரிந்தார் வரவுள்ளிக்<br />கோடுகொ டேறுமென் நெஞ்சு','காதல் வயப்பட்டுக் கூடியிருந்து பிரிந்து சென்றவர் எப்போது வருவார் என்று என் நெஞ்சம், மரத்தின் உச்சிக் கொம்பில் ஏறிப் பார்க்கின்றது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1265,'1265','காண்கமன் கொண்கனைக் கண்ணாரக் கண்டபின்<br />நீங்குமென் மென்தோள் பசப்பு','கண்ணார என் கணவனைக் காண்பேனாக; கண்டபிறகே என் மெல்லிய தோளில் படர்ந்துள்ள பசலை நிறம் நீங்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1266,'1266','வருகமன் கொண்கண் ஒருநாட் பருகுவன்<br />பைதல்நோய் எல்லாம் கெட','என்னை வாடவிட்டுப் பிரிந்துள்ள காதலன், ஒருநாள் வந்துதான் ஆகவேண்டும் வந்தால் என் துன��பம் முழுவதும் தீர்ந்திட அவனிடம் இன்பம் துய்ப்பேன்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1267,'1267','புலப்பேன்கொல் புல்லுவேன் கொல்லோ கலப்பேன்கொல்<br />கண்ணன்ன கேளிர் வரின்','கண்ணின் மணியாம் என் காதலர் வந்தவுடன், பிரிந்திருந்த துயரின் காரணமாக அவருடன் ஊடல், கொள்வேனோ? அல்லது கட்டித் தழுவிக் கொள்வேனோ? அல்லது ஊடுதல் கூடுதல் ஆகிய இரண்டையும் இணைத்துச் செய்வேனோ? ஒன்றுமே புரியவில்லையே எனக்கு; அந்த இன்பத்தை நினைக்கும்போது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1268,'1268','வினைகலந்து வென்றீக வேந்தன் மனைகலந்து<br />மாலை அயர்கம் விருந்து','தலைவன், தான் மேற்கொண்டுள்ள செயலில் வெற்றி பெறுவானாக; அவன் வெண்றால் என் மனைவியுடன் எனக்கு மாலைப்பொழுதில் இன்ப விருந்துதான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1269,'1269','ஒருநாள் எழுநாள்போல் செல்லும்சேண் சென்றார்<br />வருநாள்வைத் தேங்கு பவர்க்கு','நெடுந்தொலைவு சென்ற காதலர் திரும்பி வரும் நாளை எதிர்பார்த்து ஏங்குபவர்க்கு ஒவ்வொரு நாளும் ஒவ்வொரு யுகமாகத் தோன்றும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1270,'1270','பெறினென்னாம் பெற்றக்கால் என்னாம் உறினென்னாம்<br />உள்ளம் உடைந்துக்கக் கால்','துன்பத்தைத் தாங்கிக் கொள்ள முடியாமல் மனம் நிலையிழந்து போயிவிடுமானால், பிறகு ஒருவரையொருவர் திரும்பச் சந்திப்பதனாலோ, சந்தித்துக் கூடவதினாலோ, என்ன பயன்?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1271,'1271','கரப்பினுங் கையிகந் தொல்லாநின் உண்கண்<br />உரைக்கல் உறுவதொன் றுண்டு','வெளியில் சொல்லாமல் மறைக்கப் பார்த்தாலும், நிற்காமல் தடைகடந்து விழிகள் சொல்லக்கூடிய செய்தி ஒன்று உண்டு; அதுதான் பிரிவை விரும்பாத காதல்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1272,'1272','கண்ணிறைந்த காரிகைக் காம்பேர்தோட் பேதைக்குப்<br />பெண்நிறைந்த நீர்மை பெரிது','கண்நிறைந்த அழகும் மூங்கில் போன்ற தோளும் கொண்ட என் காதலிக்குப் பெண்மைப் பண்பு நிறைந்திருப்பதே பேரழகாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1273,'1273','மணியில் திகழ்தரு நூல்போல் மடந்தை<br />அணியில் திகழ்வதொன் றுண்டு','மணியாரத்திற்குள் மறைந்திருக்கும் நூலைப்போல இந்த மடந்தையின் அழகுக்குள்ளே என்னை மயக்கும் குறிப்பு ஒன்று உளது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1274,'1274','முகைமொக்குள் உள்ளது நாற்றம்போல் பேதை<br />நகைமொக்குள் உள்ளதொன் றுண்டு','மலராத அரும்புக்குள் நறுமணம் அடங்கியிருப்பது போலத்தான் ஒரு பெண்ணின் புன்னகையென்ற அரும்புக்குள் அவளது காதலனைப்பற்றிய நினைவும் நிரம்பியிருக்கிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1275,'1275','செறிதொடி செய்திறந்த கள்ளம் உறுதுயர்<br />தீர்க்கும் மருந்தொன் றுடைத்து','வண்ணமிகு வளையல்கள் அணிந்த என் வடிவழகியின் குறும்புத்தனமான பார்வையில், என்னைத் துளைத்தெடுக்கும் துன்பத்தைத் தீர்க்கும் மருந்தும் இருக்கிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1276,'1276','பெரிதாற்றிப் பெட்பக் கலத்தல் அரிதாற்றி<br />அன்பின்மை சூழ்வ துடைத்து','ஆரத் தழுவி அளவற்ற அன்பு காட்டி அவர் என்னைக் கூடுவதானது மீண்டும் அவர் என்னைப் பிரிந்து செல்லப் போகிற குறிப்பை உணர்த்துவது போல் இருக்கிறதே',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1277,'1277','தண்ணந் துறைவன் தணந்தமை நம்மினும்<br />முன்னம் உணர்ந்த வளை','குளிர்ந்த நீர்த் துறைக்கு உரிய காதலன் உடலால் கூடியிருக்கும் போது, உள்ளத்தால் பிரியும் நினைவு கொண்டதை என் வளையல்கள் எனக்கு முன்னரே உணர்ந்து கழன்றன போலும்!',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1278,'1278','நெருநற்றுச் சென்றாரெங் காதலர் யாமும்<br />எழுநாளேம் மேனி பசந்து','நேற்றுத்தான் எம் காதலர் பிரிந்து சென்றார்; எனினும், பல நாட்கள் கழிந்தன என்பது போல் பசலை நிறம் எம்மைப் பற்றிக் கொண்டதே',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1279,'1279','தொடிநோக்கி மென்தோளும் நோக்கி அடிநோக்கி<br />அஃதாண் டவள்செய் தது','பிரிவு காரணமாகக் கழலக் கூடிய வளையலையும், மெலிந்து போகக் கூடிய மென்மையான தோளையும் நோக்கியவன் காதலனைத் தொடர்ந்து செல்வதென்ற முடிவைத் தன் அடிகளை நோக்கும் குறிப்பால் உணர்த்தினான்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1280,'1280','பெண்ணினாற் பெண்மை உடைத்தென்ப கண்ணினால்<br />காமநோய் சொல்லி இரவு','காதல் வேட்கையைக் கண்களால் உணர்த்திக் காதலனுடன் போவதற்கு இரந்து நிற்கும்போது பெண்மைக்குப் பெண்மை சேர்த்தாற் போன்று இருக்கின்றது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1281,'1281','உள்ளக் களித்தலும் காண மகிழ்தலும்<br />கள்ளுக்கில் காமத்திற் குண்டு','மதுவை அருந்தினால்தான் இன்பம், ஆனால் காதல் அப்படியல்ல; நினைத்தாலே இன்பம்; காதலர்கள் ஒருவரையொருவர் கண்டாலே இன்பம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1282,'1282','தினைத்துணையும் ஊடாமை வேண்டும் பனைத்துணையும்<br />காமம் நிறைய வரின்','பனையளவாகக் காதல் பெருகிடும் போது தினையளவு ஊடலும் கொள்ளாமல் இருக்க வேண்டும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1283,'1283','பேணாது பெட்பவே செய்யினும் கொண்கனைக்<br />காணா தமையல கண்','என்னை அரவணைக்காமல் தமக்கு விருப்பமானவற்றையே செய்து கொண்டிருந்தாலும், என் கண்கள் அவரைக் காணாமல் அமைதி அடைவதில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1284,'1284','ஊடற்கண் சென்றேன்மன் தோழி அதுமறந்து<br />கூடற்கண் சென்றதென் னெஞ்சு','ஊடுவதற்காகச் சென்றாலும்கூட அதை நெஞ்சம் மறந்து விட்டுக் கூடுவதற்கு இணங்கி விடுவதே காதலின் சிறப்பு',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1285,'1285','எழுதுங்கால் கோல்காணாக் கண்ணேபோல் கொண்கண்<br />பழிகாணேன் கண்ட இடத்து','கண்ணில் மை தீட்டிக் கொள்ளும் பொழுது அந்த மை தீட்டும் கோலைக் காணாதது போலவே, காதலனைக் காணும்பொழுது அவன் என்னைப் பிரிந்து சென்ற குற்றத்தை மறந்து விடுகிறேன்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1286,'1286','காணுங்கால் காணேன் தவறாய காணாக்கால்<br />காணேன் தவறல் லவை','அவரைக் காணும்பொழுது அவர் குற்றங்களை நான் காண்பதில்லை; அவரைக் காணாதபொழுது அவர் குற்றங்களைத் தவிர வேறொன்றையும் நான் காண்பதில்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1287,'1287','உய்த்தல் அறிந்து புனல்பாய் பவரேபோல்<br />பொய்த்தல் அறிந்தென் புலந்து','வெள்ளம் அடித்துக் கொண்டு போய் விடுமெனத் தெரிந்திருந்தும் நீரில் குதிப்பவரைப் போல, வெற்றி கிடைக்காது எனப் புரிந்திருந்தும், ஊடல் கொள்வதால் பயன் என்ன?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1288,'1288','இளித்தக்க இன்னா செயினும் களித்தார்க்குக்<br />கள்ளற்றே கள்வநின் மார்பு','என்னுள்ளம் கவர்ந்த கள்வனே! இழிவு தரக்கூடிய துன்பத்தை நீ எனக்கு அளித்தாலும் கூட, கள்ளை உண்டு களித்தவர்க்கு மேலும் மேலும் அந்தக் கள்ளின் மீது விருப்பம் ஏற்படுவது போலவே என்னையும் மயங்கச் செய்கிறது உன் மார்பு',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1289,'1289','மலரினும் மெல்லிது காமம் சிலரதன்<br />செவ்வி தலைப்படு வார்','காதல் இன்பம், மலரைவிட மென்மையானது அதனை அதே மென்மையுடன் நுகருபவர்கள் சிலரே ஆவார்கள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1290,'1290','கண்ணின் துனித்தே கலங்கினாள் புல்லுதல்<br />என்னினும் தான்விதுப் புற்று','விழிகளால் ஊடலை வெளியிட்டவள், கூடித் தழுவுவதில் என்னைக் காட்டிலும் விரைந்து செயல்பட்டு என்னோடு கலந்து விட்டாள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1291,'1291','அவர்நெஞ் சவர்க்காதல் கண்டும் எவன்நெஞ்சே<br />நீயெமக் காகா தது','நெஞ்சே! நம்மை நினைக்காமல் இருப்பதற்கு அவருடைய நெஞ்சு அவருக்குத் துணையாக இருக்கும்போது நீ எமக்குத் துணையாக இல்லாமல் அவரை நினைத்து உருகுவது ஏன்?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1292,'1292','உறாஅ தவர்க்கண்ட கண்ணும் அவரைச்<br />செறாஅரெனச் சேறியென் நெஞ்சு','நெஞ்சே! நம்மிடம் அன்பு காட்டாதவர் அவர் எனக் கண்ட பிறகும், ந��்மை வெறுக்க மாட்டார் என நம்பி அவரிடம் செல்கின்றாயே',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1293,'1293','கெட்டார்க்கு நட்டாரில் என்பதோ நெஞ்சேநீ<br />பெட்டாங் கவர்பின் செலல்','நெஞ்சே! நீ எனை விடுத்து அவரை விரும்பிப் பின் தொடர்ந்து செல்வது, துன்பத்தால் அழிந்தோர்க்கு நண்பர்கள் துணையிருக்க மாட்டார்கள் என்று சொல்வது போலவோ?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1294,'1294','இனியன்ன நின்னொடு சூழ்வார்யார் நெஞ்சே<br />துனிசெய்து துவ்வாய்காண் மற்று','நெஞ்சே! முதலில் ஊடல் செய்து பிறகு அதன் பயனைக் கூடலில் நுகர்வோம் என நினைக்க மாட்டாய்; எனவே அதைப்பற்றி உன்னிடம் யார் பேசப் போகிறார்கள்? நான் பேசுவதாக இல்லை',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1295,'1295','பெறாஅமை அஞ்சும் பெறினபிரி வஞ்சும்<br />அறாஅ இடும்பைத்தென் நெஞ்சு','என் நெஞ்சத்துக்குத் துன்பம் தொடர் கதையாகவே இருக்கிறது காதலரைக் காணவில்லையே என்று அஞ்சும்; அவர் வந்து விட்டாலோ பிரிந்து செல்வாரே என நினைத்து அஞ்சும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1296,'1296','தனியே இருந்து நினைத்தக்கால் என்னைத் <br />தினிய இருந்ததென் நெஞ்சு','காதலர் பிரிவைத் தனியே இருந்து நினைத்தபோது என் நெஞ்சம் என்னைத் தின்பது போலக் கொடுமையாக இருந்தது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1297,'1297','நாணும் மறந்தேன் அவர்மறக் கல்லாவென்<br />மாணா மடநெஞ்சிற் பட்டு','அவரை மறக்க முடியாமல் வாடும் என்னுடைய சிறப்பில்லாத மட நெஞ்சத்துடன் சேர்ந்து மறக்கக் கூடாது நாணத்தையும் மறந்து விட்டேன்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1298,'1298','எள்ளின் இளிவாமென் றெண்ணி அவர்திறம்<br />உள்ளும் உயிர்க்காதல் நெஞ்சு','பிரிந்து சென்ற காதலரை இகழ்வது தனக்கே இழிவாகும் என்பதால், அவருடைய பெருமையைப் பற்றியே என்னுயிர்க் காதல் நெஞ்சம் எண்ணிக் கொண்டிருக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1299,'1299','துன்பத்திற் கியாரே துணையாவார் தாமுடைய<br />நெஞ்சந் துணையல் வழி','துன்பம் வரும்போது அதனைத் தாங்குவதற்கு நெஞ்சமே துணையாக இல்லாவிட்டால் பிறகு யார் துணையாக இருப்பார்?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1300,'1300','தஞ்சம் தமரல்லர் ஏதிலார் தாமுடைய<br />நெஞ்சம் தமரல் வழி','நமக்குரிய நெஞ்சமே நம்முடன் உறவாக இல்லாத போது, மற்றவர் உறவில்லாதவராக இருத்தல் என்பது எளிதேயாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1301,'1301','புல்லா திராஅப் புலத்தை அவருறும்<br />அல்லல்நோய் காண்கம் சிறிது','ஊடல் கொள்வதால் அவர் துன்ப நோயினால் துடிப்பதைச் சிறிது நேரம் காண்பதற்கு அவரைத் தழுவிடத் தயங்கிப் பிணங்குவாயாக',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1302,'1302','உப்பமைந் தற்றால் புலவி அதுசிறிது<br />மிக்கற்றால் நீள விடல்','ஊடலுக்கும் கூடலுக்கும் இடையில் உள்ள காலம் உணவில் இடும் உப்பு போல் ஓரளவுடன் இருக்க வேண்டும் அந்தக் கால அளவு நீடித்தால் உணவில் உப்பு மிகுதியானதற்கு ஒப்பாக ஆகிவிடும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1303,'1303','அலந்தாரை அல்லல்நோய் செய்தற்றால் தம்மைப்<br />புலந்தாரைப் புல்லா விடல்','ஊடல் கொண்டவரின் ஊடல் நீக்கித் தழுவாமல் விடுதல் என்பது, ஏற்கனவே துன்பத்தால் வருந்துவோரை மேலும் துன்பநோய்க்கு ஆளாக்கி வருத்துவதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1304,'1304','ஊடி யவரை உணராமை வாடிய <br />வள்ளி முதலரிந் தற்று','ஊடல் புரிந்து பிணங்கியிருப்பவரிடம் அன்பு செலுத்திடாமல் விலகியே இருப்பின், அது ஏற்கனவே வாடியுள்ள கொடியை அதன் அடிப்பாகத்தில் அறுப்பது போன்றதாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1305,'1305','நலத்தகை நல்லவர்க் கேஎர் புலத்தகை <br />பூவன்ன கண்ணார் அகத்து','மலர் விழி மகளிர் நெஞ்சில் விளையும் ஊடலே பண்பார்ந்த நல்ல காதலர்க்கு அழகு சேர்க்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1306,'1306','துனியும் புலவியும் இல்லாயின் காமம்<br />கனியும் கருக்காயும் அற்று','பெரும்பிணக்கும், சிறுபிணக்கும் ஏற்பட்டு இன்பம் தரும் காதல் வாழ்க்கை அமையாவிட்டால் அது முற்றிப் பழுத்து அழுகிய பழம் போலவும், முற்றாத இளம் பிஞ்சைப் போலவும் பயனற்றதாகவே இருக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1307,'1307','ஊடலின் உண்டாங்கோர் துன்பம் புணர்வது<br />நீடுவ தன்றுகொல் என்று','கூடி மயங்கிக் களித்திருக்கும் இன்பமான காலத்தின் அளவு குறைந்து விடுமோ என எண்ணுவதால் ஊடலிலும் ஒருவகைத் துன்பம் காதலர்க்கு உண்டு',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1308,'1308','நோதல் எவன்மற்று நொந்தாரென் றஃதறியும்<br />காதலர் இல்லா வழி','நம்மை நினைத்தல்லவோ வருந்துகிறார் என்பதை உணர்ந்திடும் காதலர் இல்லாத போது வருந்துவதால் என்ன பயன்?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1309,'1309','நீரும் நிழல தினிதே புலவியும்<br />வீழுநர் கண்ணே இனிது','நிழலுக்கு அருகில் உள்ள நீர்தான் குளிர்ந்து இனிமையாக இருக்கும்; அதுபோல அன்புள்ளவர்களிடம் கொள்ளும் ஊடல்தான் இன்பமானதாக இருக்கும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1310,'1310','ஊடல் உணங்க விடுவாரோ டென்னெஞ்சம்<br />கூடுவேம் என்ப தவா','ஊடலைத் தணிக்காமல் வாடவிட்டு வேடிக்கை பார்ப்பவருடன் கூடியிருப்போம் என்று என் நெஞ்சம் துடிப்பதற்கு அதன் அடங்காத ஆசையே காரணம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1311,'1311','பெண்ணியலார் எல்லாரும் கண்ணிற் பொதுவுண்பர்<br />நண்ணேன் பரத்தநின் மார்பு','பெண்ணாக இருப்போர் எல்லோருமே, பொதுவாக நினைத்துக் கண்களால் உண்பதால் கற்பு நெறிகெட்ட உன் பரந்த மார்பைப் பாவை நான் தழுவ மாட்டேன்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1312,'1312','ஊடி இருந்தேமாத் தும்மினார் யாம்தம்மை<br />நீடுவாழ் கென்பாக் கறிந்து','ஊடல் கொண்டிருந்தபோது அவர் தும்மினார்; ஊடலை விடுத்து அவரை ``நீடுவாழ்க என வாழ்த்துவேன் என்று நினைத்து',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1313,'1313','கோட்டுப்பூச் சூடினும் காயும் ஒருத்தியைக்<br />காட்டிய சூடினீர் என்று','கிளையில் மலர்ந்த பூக்களைக் கட்டி நான் அணிந்து கொண்டிருந்தாலும், வேறொருத்திக்குக் காட்டுவதற்காகவே அணிந்திருக்கிறீர் எனக்கூறி சினம் கொள்வாள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1314,'1314','யாரினுங் காதலம் என்றேனா ஊடினாள்<br />யாரினும் யாரினும் என்று','``யாரைக் காட்டிலும் உன்னிடம் நான் காதல் மிகுதியாகக் கொண்டுள்ளேன் என்று இயல்பாகச் சொன்னதைக் கூடக் காதலி தவறாக எடுத்துக் கொண்டு ``யாரைக்காட்டிலும் யாரைக் காட்டிலும் எனக் கேட்டு ஊடல் புரியத் தொடங்கி விட்டாள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1315,'1315','இம்மைப் பிறப்பில் பிரியலம் என்றேனாக்<br />கண்நிறை நீர்கொண் டனள்','``இப்பிறப்பில் யாம் பிரியமாட்டோம் என்று நான் சொன்னவுடன் ``அப்படியானால் மறு பிறப்பு என ஒன்று உண்டோ? அப்போது நம்மிடையே பிரிவு ஏற்படுமெனக் கூறுகிறாயா? எனக் கேட்டு கண்கலங்கினாள் காதலி',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1316,'1316','உள்ளினேன் என்றேன்மற் றென்மறந்தீர் என்றென்னைப்<br />புல்லாள் புலத்தக் கனள்','``உன்னை நினைத்தேன் என்று காதலியிடம் சொன்னதுதான் தாமதம்; அப்படியானால் நீர் என்னை மறந்திருந்தால்தானே நினைத்திருக்க முடியும்? எனக்கேட்டு ``ஏன் மறந்தீர்? என்று அவள் ஊடல் கொண்டாள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1317,'1317','வழுத்தினாள் தும்மினேன் ஆக அழித்தழுதாள்<br />யாருள்ளித் தும்மினீர் என்று','தும்மினேன்; வழக்கப்படி அவள் என்னை வாழ்த்தினாள் உடனே என்ன சந்தேகமோ ``யார் உம்மை நினைத்ததால் தும்மினீர் என்று கேட்டு, முதலில் அளித்த வாழ்த்துக்கு மாறாக அழத் தொடங்கிவிட்டாள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1318,'1318','தும்முச் செறுப்ப அழுதாள் நுமருள்ளல்<br />எம்மை மறைத்திரோ என்று','ஊடல் கொள்வாளோ எனப் பயந்து நான் தும்மலை அடக்கிக் கொள்வதைப் பார்த்த அவள் ``ஓ உமக்கு நெருங்கியவர் உம்மை நினைப்பதை நான் அறியாதபடி மறைக்கிறீரோ? எனக் கேட்டு அழுதாள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1319,'1319','தன்னை உணர்த்தினும் காயும் பிறர்க்கும்நீர்<br />இந்நீரர் ஆகுதிர் என்று','நான் பணிந்து போய் அவள் ஊடலை நீக்கி மகிழ்வித்தாலும், உடனே அவள் ``ஓ! நீர் இப்படித்தான் மற்ற பெண்களிடமும் நடந்து கொள்வீரோ? என்று சினந்தெழுவாள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1320,'1320','நினைத்திருந்து நோக்கினும் காயும் அனைத்துநீர்<br />யாருள்ளி நோக்கினீர் என்று','ஒப்பற்ற அவளுடைய அழகை நினைத்து அவளையே இமை கொட்டாமல் பார்த்துக் கொண்டிருந்தாலும், யாருடன் என்னை ஒப்பிட்டு உற்றுப் பார்க்கிறீர் என்று கோபம் கொள்வாள்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1321,'1321','இல்லை தவறவர்க் காயினும் ஊடுதல்<br />வல்ல தவரளிக்கும் ஆறு','எந்த தவறும் இல்லாத நிலையிலும்கூட காதலர்க்கிடையே தோன்றும் ஊடல், அவர்களின் அன்பை மிகுதியாக வளர்க்கக் கூடியது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1322,'1322','ஊடலின் தோன்றும் சிறுதுனி நல்லளி<br />வாடினும் பாடு பெறும்','காதலரிடையே மலர்நதுள்ள நல்லன்பு சற்று வாடுவதற்கு, ஊடுதல் காரணமாக இருந்தாலும் அதனால் விளைகிற சிறிய துன்பம் பெருமையுடையதேயாகும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1323,'1323','புலத்தலிற் புத்தேள்நா டுண்டோ நிலத்தொடு<br />நீரியைந் தன்னார் அகத்து','நிலத்தோடு நீர் கலந்தது போல அன்புடன் கூடியிருக்கும் காதலரிடத்தில் ஊடல் கொள்வதை விடப் புதிய உலகம் வேறொன்று இருக்க முடியுமா?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1324,'1324','புல்லி விடாஅப் புலவியுள் தோன்றுமென்<br />உள்ளம் உடைக்கும் படை','இறுகத் தழுவி இணை பிரியாமல் இருப்பதற்குக் காரணமாக ஊடல் அமைகிறது அந்த ஊடலில்தான் என் உள்ளத்து உறுதியைக் குலைக்கும் படைக்கலனும் இருக்கிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1325,'1325','தவறிலர் ஆயினும் தாம்வீழ்வார் மென்றோள்<br />அகறலின் ஆங்கொன் றுடைத்து','தவறே செய்யாத நிலையிலும்கூட தன்னுள்ளம் கொள்ளை கொண்டவளின் ஊடலுக்கு ஆளாகி அவளது மெல்லிய தோள்களைப் பிரிந்திருப்பதில் ஓர் இன்பம் இருக்கிறது',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1326,'1326','உணலினும் உண்ட தறலினிது காமம்<br />புணர்தலின் ஊடல் இனிது','உணவு அருந்துவதைவிட, அருந்திய உணவு செரிப்பதிலே ஒரு சுகம் அதைப்போல் உடலுறவைவிட ஊடல் கொள்வதிலேயே காதலர்க்கு ஒரு சுகம்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1327,'1327','ஊடலின் தோற்றவர் வென்றார் அதுமன்னும்<br />கூடலிற் காணப் படும்','ஊடல் என்கிற இனிய போரில் தோற்றவர்தான் வெற்றி பெற்றவராவார் இந்த உண்மை ஊடல் முடிந்து கூடிமகிழும் போது உணரப்படும்',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1328,'1328','ஊடிப் பெறுகுவம் கொல்லோ நுதல்வெயர்ப்பக்<br />கூடலில் தோன்றிய உப்பு','நெற்றியில் வியர்வை அரும்பிடக் கூடுவதால் ஏற்படும் இன்பத்தை, மீண்டும் ஒருமுறை ஊடல் தோன்றினால், அதன் வாயிலாகப் பெற முடியுமல்லவா?',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1329,'1329','ஊடுக மன்னோ ஒளியிழை யாமிரப்ப<br />நீடுக மன்னோ இரா','ஒளி முகத்தழகி ஊடல் புரிவாளாக; அந்த ஊடலைத் தீர்க்கும் பொருட்டு நான் அவளிடம் இரந்து நிற்கும் இன்பத்தைப் பெறுவதற்கும் இராப்பொழுது இன்னும் நீடிப்பதாக',1);
INSERT INTO item(item_id,item_name,item_content,item_exp,cat_id) values(1330,'1330','ஊடுதல் காமத்திற் கின்பம் அதற்கின்பம்<br />கூடி முயங்கப் பெறின்','ஒருவருக்கொருவர் செல்லமாகச் சினங்கொண்டு பிரிந்திருப்பது எனப்படும் ஊடல், இருவரும் சேர்ந்த பிறகு காதல் இன்பத்தை அதிகமாகப் பருகிட உதவும் எனவே ஊடல் கொள்வதே ஒரு இன்பமான செயல்தான்',1);